INSERT INTO `tblarticle` VALUES (1,'S&#7913; m&#7879;nh','T&#7853;p th&#7875; l&atilde;nh &#273;&#7841;o v&agrave; to&agrave;n th&#7875; nh&acirc;n vi&ecirc;n Mikado h&#7871;t m&igrave;nh v&igrave; s&#7921; t&#7891;n t&#7841;i v&agrave; ph&aacute;t tri&#7875;n c&#7911;a c&ocirc;ng ty v&#7899;i t&acirc;m ni&#7879;m c&ocirc;ng ty ph&aacute;t tri&#7875;n th&igrave; m&igrave;nh ph&aacute;t tri&#7875;n, c&ocirc;ng ty t&#7891;n t&#7841;i th&igrave; m&igrave;nh t&#7891;n t&#7841;i.','&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;300&quot; height=&quot;300&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/Generate/vision-mission.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7899;i s&#7913; m&#7879;nh n&amp;acirc;ng cao kh&#7843; n&#259;ng c&#7841;nh tranh c&#7911;a c&amp;ocirc;ng ty b&#7857;ng c&amp;aacute;ch t&#7841;o ra c&amp;aacute;c s&#7843;n ph&#7849;m c&amp;oacute; ch&#7845;t l&#432;&#7907;ng cao, &#7893;n &#273;&#7883;nh v&amp;agrave; li&amp;ecirc;n t&#7909;c c&#7843;i ti&#7871;n m&#7851;u m&amp;atilde; c&#361;ng nh&#432; c&#7853;p nh&#7853;t c&amp;aacute;c c&amp;ocirc;ng ngh&#7879; m&#7899;i nh&#7845;t &#273;&#7875; h&amp;agrave;i l&amp;ograve;ng kh&amp;aacute;ch h&amp;agrave;ng trong n&#432;&#7899;c, xu&#7845;t kh&#7849;u v&amp;agrave; &#273;em l&#7841;i l&#7907;i nhu&#7853;n cho c&amp;ocirc;ng ty c&#361;ng nh&#432; l&#7907;i &amp;iacute;ch c&#7911;a b&#7843;n th&amp;acirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;M&#7895;i c&amp;aacute; nh&amp;acirc;n &#273;&#7873;u &#273;em h&#7871;t t&amp;acirc;m huy&#7871;t c&#7911;a m&amp;igrave;nh &#273;&#7875; t&#7841;o n&amp;ecirc;n m&#7897;t t&#7853;p th&#7875; v&#7919;ng ch&#7855;c, trung th&amp;agrave;nh v&amp;agrave; t&#7853;n t&#7909;y &#273;&#7875; t&#7841;o ra m&#7897;t m&amp;ocirc;i tr&#432;&#7901;ng l&amp;agrave;m vi&#7879;c th&amp;acirc;n thi&#7879;n, hi&#7879;u qu&#7843;. S&#7913; m&#7879;nh c&#7911;a Mikado l&amp;agrave; &#273;em s&#7843;n ph&#7849;m ra ti&amp;ecirc;u th&#7909; kh&#7855;p to&amp;agrave;n c&#7847;u, t&#7841;o n&amp;ecirc;n m&#7897;t th&#432;&#417;ng hi&#7879;u qu&#7889;c t&#7871;.&lt;/p&gt;\r\n','18/10/2011','18/10/2011','/home/uploads/image/Generate/vision-mission.jpg',50,3,4674,'admin',1,'','S&#7913; m&#7879;nh, mikado, mikadoceramic',NULL,NULL,NULL,'S&#7913; m&#7879;nh, mikado, mikadoceramic',0,0,0,'','','',4,'Mikado',1,0,0,0),(2,'T&#7847;m nh&igrave;n','Vi&#7879;t nam l&agrave; m&#7897;t qu&#7889;c gia &#273;ang ph&aacute;t tri&#7875;n, c&#417; h&#7897;i cho c&aacute;c s&#7843;n ph&#7849;m x&acirc;y d&#7921;ng c&ograve;n nhi&#7873;u do di&#7879;n t&iacute;ch nh&agrave; &#7903; tr&ecirc;n &#273;&#7847;u ng&#432;&#7901;i c&ograve;n th&#7845;p, &#273;&#7863;c bi&#7879;t &#273;&acirc;y l&agrave; ng&agrave;nh c&ocirc;ng nghi&#7879;p m&agrave; c&aacute;c n&#432;&#7899;c ph&aacute;t tri&#7875;n kh&ocirc;ng c&oacute; l&#7907;i th&#7871; c&#7841;nh tranh n&ecirc;n ch&uacute;ng ta c&ograve;n nhi&#7873;u c&#417; h&#7897;i ph&aacute;t tri&#7875;n v&agrave; chi&#7871;m th&#7883; ph&#7847;n tr&ecirc;n th&#7871; gi&#7899;i.','&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;300&quot; height=&quot;299&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/Generate/vision.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong th&#7901;i gian 20 n&#259;m n&#7919;a vi&#7879;c s&#7917; d&#7909;ng c&amp;aacute;c s&#7843;n ph&#7849;m &#273;&#7845;t s&amp;eacute;t nung l&amp;agrave; s&#7869; d&amp;agrave;nh cho c&amp;aacute;c gi&#7899;i thu nh&#7853;p kh&amp;aacute; do nguy&amp;ecirc;n li&#7879;u th&amp;ocirc; ng&amp;agrave;y c&amp;agrave;ng c&#7841;n ki&#7879;t v&amp;agrave; gi&amp;aacute; th&amp;agrave;nh s&#7869; cao l&amp;ecirc;n v&amp;agrave; s&#7869; c&amp;oacute; c&amp;aacute;c s&#7843;n ph&#7849;m thay th&#7871; gi&amp;aacute; r&#7867; c&#7841;nh tranh nh&#432; c&amp;aacute;c s&#7843;n ph&#7849;m composit, s&#7843;n ph&#7849;m g&#7841;ch kh&amp;ocirc;ng nung, s&#417;n n&#7873;n trang tr&amp;iacute;,&amp;hellip;do v&#7853;y Mikado quy&#7871;t t&amp;acirc;m theo &#273;u&#7893;i m&#7909;c ti&amp;ecirc;u tr&#7903; th&amp;agrave;nh s&#7843;n ph&#7849;m cao c&#7845;p, chi&#7871;m l&#297;nh l&#7847;n th&#7883; ph&#7847;n b&#7853;c cao nh&#7857;m duy tr&amp;igrave; v&amp;agrave; t&#7891;n t&#7841;i ph&amp;aacute;t tri&#7875;n &#273;&#7871;n n&#259;m 2022 v&amp;agrave; c&amp;aacute;c n&#259;m ti&#7871;p theo.&lt;/p&gt;\r\n\r\n&lt;p&gt;Mikado c&#361;ng d&#7847;n thi&#7871;t l&#7853;p c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y s&#7843;n xu&#7845;t &#7903; n&#432;&#7899;c ngo&amp;agrave;i nh&#432; Campuchia, Ch&amp;acirc;u phi, Ch&amp;acirc;u m&#7929;,.. &#273;&#7875; gi&#7843;m vi&#7879;c s&#7917; d&#7909;ng t&amp;agrave;i nguy&amp;ecirc;n v&amp;agrave; ki&#7871;m t&amp;igrave;m th&#7883; tr&#432;&#7901;ng m&#7899;i. &#272;&#7891;ng th&#7901;i nghi&amp;ecirc;n c&#7913;u ph&amp;aacute;t tri&#7875;n &#273;&#7875; s&#7843;n xu&#7845;t ra c&amp;aacute;c s&#7843;n ph&#7849;m thay th&#7871; d&#7847;n g&#7841;ch &#273;&#7845;t s&amp;eacute;t nung nh&#7857;m h&#7841; gi&amp;aacute; th&amp;agrave;nh v&amp;agrave; b&#7843;o v&#7879; m&amp;ocirc;i tr&#432;&#7901;ng kh&amp;ocirc;ng s&#7917; d&#7909;ng nhi&#7873;u t&amp;agrave;i nguy&amp;ecirc;n ng&amp;agrave;y c&amp;agrave;ng khan hi&#7871;m.&lt;/p&gt;\r\n\r\n&lt;p&gt;Mikado c&#361;ng t&#259;ng c&#432;&#7901;ng &#273;&amp;agrave;o t&#7841;o nh&amp;acirc;n s&#7921; &#273;&#7875; c&amp;oacute; &#273;&#432;&#7907;c m&#7897;t &#273;&#7897;i ng&#361; t&amp;agrave;i n&#259;ng, nhi&#7879;t huy&#7871;t theo k&#7883;p &#273;&amp;agrave; ph&amp;aacute;t tri&#7875;n c&#7911;a c&amp;ocirc;ng ty.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;300&quot; height=&quot;300&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/Generate/vision2.jpg&quot; /&gt;&lt;/p&gt;\r\n','18/10/2011','18/10/2011','/home/uploads/image/Generate/vision.jpg',40,3,5011,'admin',1,'','T&#7847;m nh&igrave;n, Vision, mikado, mikadoceramic',NULL,NULL,NULL,'T&#7847;m nh&igrave;n, Vision, mikado, mikadoceramic',0,0,0,'','','',4,'Mikado',1,0,0,0),(3,'C&ocirc;ng ty C&#7893; ph&#7847;n K&#7929; th&#432;&#417;ng Thi&ecirc;n Ho&agrave;ng nh&#7853;n &ldquo;C&uacute;p V&agrave;ng nh&atilde;n hi&#7879;u c&#7841;nh tranh&rdquo; n&#259;m 2007','Ng&agrave;y 14/04/2007, C&ocirc;ng ty C&#7893; ph&#7847;n K&#7929; Th&#432;&#417;ng Thi&ecirc;n Ho&agrave;ng &#273;&atilde; r&#7845;t vinh d&#7921; &#273;&#432;&#7907;c Ban t&#7893; ch&#7913;c Ch&#432;&#417;ng tr&igrave;nh t&#432; v&#7845;n &amp; b&igrave;nh ch&#7885;n Nh&atilde;n hi&#7879;u c&#7841;nh tranh - n&#7893;i ti&#7871;ng qu&#7889;c gia trao &ldquo;C&uacute;p V&agrave;ng nh&atilde;n hi&#7879;u c&#7841;nh tranh&rdquo; n&#259;m 2007...','&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;400&quot; height=&quot;158&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/Generate/mikadologo2.png&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;L&#7877; trao gi&#7843;i &#273;&amp;atilde; &#273;&#432;&#7907;c truy&#7873;n h&amp;igrave;nh tr&#7921;c ti&#7871;p v&amp;agrave;o l&amp;uacute;c 20h ng&amp;agrave;y 14/04/2007 tr&amp;ecirc;n k&amp;ecirc;nh VTV3 c&#7911;a &#272;&amp;agrave;i truy&#7873;n h&amp;igrave;nh Vi&#7879;t Nam v&#7899;i s&#7921; c&amp;oacute; m&#7863;t c&#7911;a c&amp;aacute;c c&#417; quan ban ng&amp;agrave;nh li&amp;ecirc;n quan, Ban t&#7893; ch&#7913;c v&amp;agrave; &#273;&#7841;i di&#7879;n c&amp;aacute;c doanh nghi&#7879;p tham gia.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;Ch&#432;&#417;ng tr&amp;igrave;nh t&#432; v&#7845;n &amp;amp; b&amp;igrave;nh ch&#7885;n Nh&amp;atilde;n hi&#7879;u c&#7841;nh tranh - n&#7893;i ti&#7871;ng qu&#7889;c gia&amp;rdquo; do C&#7909;c S&#7903; h&#7919;u tr&amp;iacute; tu&#7879;, Vi&#7879;n S&#7903; h&#7919;u tr&amp;iacute; tu&#7879; &amp;amp; &#272;&amp;agrave;i truy&#7873;n h&amp;igrave;nh Vi&#7879;t Nam ph&#7889;i h&#7907;p t&#7893; ch&#7913;c h&amp;agrave;ng n&#259;m nh&#7857;m t&amp;ocirc;n vinh &amp;amp; khuy&#7871;n kh&amp;iacute;ch c&amp;aacute;c doanh nghi&#7879;p &#273;&amp;atilde; c&amp;oacute; nh&#7919;ng ph&amp;aacute;t tri&#7875;n t&amp;iacute;ch c&#7921;c cho ho&#7841;t &#273;&#7897;ng s&#7903; h&#7919;u tr&amp;iacute; tu&#7879; trong s&#7843;n xu&#7845;t kinh doanh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7875; &#273;&#432;&#7907;c x&amp;eacute;t duy&#7879;t &amp;amp; b&amp;igrave;nh ch&#7885;n c&amp;aacute;c nh&amp;atilde;n hi&#7879;u tham gia ph&#7843;i th&#7887;a m&amp;atilde;n c&amp;aacute;c &#273;i&#7873;u ki&#7879;n nh&#432; sau:&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;1- &#272;&#7889;i v&#7899;i c&amp;aacute;c nh&amp;atilde;n hi&#7879;u n&#7893;i ti&#7871;ng:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;a) Nh&amp;atilde;n hi&#7879;u ph&#7843;i th&#7887;a m&amp;atilde;n 8 ti&amp;ecirc;u ch&amp;iacute; &#273;&amp;aacute;nh gi&amp;aacute; theo &#272;i&#7873;u 75 Lu&#7853;t S&#7903; h&#7919;u tr&amp;iacute; tu&#7879; - 2005. C&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; &#273;&amp;oacute; bao g&#7891;m:&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n    &lt;li&gt;S&#7889; l&#432;&#7907;ng ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng li&amp;ecirc;n quan &#273;&amp;atilde; bi&#7871;t &#273;&#7871;n nh&amp;atilde;n hi&#7879;u th&amp;ocirc;ng qua vi&#7879;c mua b&amp;aacute;n, s&#7917; d&#7909;ng h&amp;agrave;ng h&amp;oacute;a, d&#7883;ch v&#7909; mang nh&amp;atilde;n hi&#7879;u ho&#7863;c th&amp;ocirc;ng qua qu&#7843;ng c&amp;aacute;o&lt;/li&gt;\r\n    &lt;li&gt;Ph&#7841;m vi l&amp;atilde;nh th&#7893; m&amp;agrave; h&amp;agrave;ng h&amp;oacute;a ho&#7863;c d&#7883;ch v&#7909; mang nh&amp;atilde;n hi&#7879;u &#273;&amp;atilde; &#273;&#432;&#7907;c l&#432;u h&amp;agrave;nh&lt;/li&gt;\r\n    &lt;li&gt;Doanh s&#7889; t&#7915; vi&#7879;c b&amp;aacute;n h&amp;agrave;ng h&amp;oacute;a ho&#7863;c cung c&#7845;p d&#7883;ch v&#7909; mang nh&amp;atilde;n hi&#7879;u ho&#7863;c s&#7889; l&#432;&#7907;ng h&amp;agrave;ng h&amp;oacute;a &#273;&#432;&#7907;c b&amp;aacute;n ra, l&#432;&#7907;ng d&#7883;ch v&#7909; &#273;&amp;atilde; &#273;&#432;&#7907;c cung c&#7845;p&lt;/li&gt;\r\n    &lt;li&gt;Th&#7901;i gian s&#7917; d&#7909;ng li&amp;ecirc;n t&#7909;c nh&amp;atilde;n hi&#7879;u&lt;/li&gt;\r\n    &lt;li&gt;Uy t&amp;iacute;n r&#7897;ng r&amp;atilde;i c&#7911;a h&amp;agrave;ng h&amp;oacute;a ho&#7863;c d&#7883;ch v&#7909; mang nh&amp;atilde;n hi&#7879;u&lt;/li&gt;\r\n    &lt;li&gt;S&#7889; l&#432;&#7907;ng qu&#7889;c gia b&#7843;o h&#7897; nh&amp;atilde;n hi&#7879;u&lt;/li&gt;\r\n    &lt;li&gt;S&#7889; l&#432;&#7907;ng qu&#7889;c gia c&amp;ocirc;ng nh&#7853;n nh&amp;atilde;n hi&#7879;u l&amp;agrave; n&#7893;i ti&#7871;ng&lt;/li&gt;\r\n    &lt;li&gt;Gi&amp;aacute; tr&#7883; c&#7911;a nh&amp;atilde;n hi&#7879;u d&#432;&#7899;i h&amp;igrave;nh th&#7913;c chuy&#7875;n nh&#432;&#7907;ng quy&#7873;n &#273;&#7889;i v&#7899;i nh&amp;atilde;n hi&#7879;u, chuy&#7875;n giao quy&#7873;n s&#7917; d&#7909;ng, g&amp;oacute;p v&#7889;n &#273;&#7847;u t&#432;&lt;/li&gt;\r\n    &lt;li&gt;&amp;nbsp;&#431;u ti&amp;ecirc;n trong &#273;&amp;aacute;nh gi&amp;aacute; nh&amp;atilde;n hi&#7879;u g&#7855;n tr&amp;ecirc;n s&#7843;n ph&#7849;m, d&#7883;ch v&#7909; mang t&amp;iacute;nh: &#273;&#7897;c &#273;&amp;aacute;o; c&amp;oacute; t&#7889;c &#273;&#7897; ph&amp;aacute;t tri&#7875;n nhanh, m&#7841;nh m&#7869;; &#7843;nh h&#432;&#7903;ng th&#7883; tr&#432;&#7901;ng cao; l&amp;agrave; s&#7843;n ph&#7849;m xu&#7845;t kh&#7849;u ch&#7911; l&#7921;c; s&#7843;n ph&#7849;m &#273;&#432;&#7907;c &#432;a chu&#7897;ng t&#7841;i th&#7883; tr&#432;&#7901;ng c&#7911;a c&amp;aacute;c n&#7873;n kinh t&#7871; ph&amp;aacute;t tri&#7875;n;&amp;hellip;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;b) C&amp;aacute; nh&amp;acirc;n, ph&amp;aacute;p nh&amp;acirc;n ch&#7911; nh&amp;atilde;n hi&#7879;u &#273;&#7841;t c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; v&#7873; n&#259;ng l&#7921;c c&#7841;nh tranh, qu&#7843;n l&amp;yacute; ch&#7845;t l&#432;&#7907;ng s&#7843;n ph&#7849;m - d&#7883;ch v&#7909;; s&#7843;n xu&#7845;t th&amp;acirc;n thi&#7879;n v&amp;agrave; b&#7843;o v&#7879; m&amp;ocirc;i tr&#432;&#7901;ng; th&#7921;c hi&#7879;n t&#7889;t lu&#7853;t Lao &#273;&#7897;ng,&amp;hellip;; th&#7921;c hi&#7879;n &#273;&#7847;y &#273;&#7911; ngh&#297;a v&#7909; thu&#7871;; t&amp;iacute;ch c&#7921;c tham gia c&amp;aacute;c ho&#7841;t &#273;&#7897;ng mang t&amp;iacute;nh x&amp;atilde; h&#7897;i.&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;2- &#272;&#7889;i v&#7899;i c&amp;aacute;c nh&amp;atilde;n hi&#7879;u c&#7841;nh tranh:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;a) Nh&amp;atilde;n hi&#7879;u ph&#7843;i th&#7887;a m&amp;atilde;n h&#417;n 2/3 trong s&#7889; c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; &#273;&amp;aacute;nh gi&amp;aacute; c&#7911;a nh&amp;atilde;n hi&#7879;u n&#7893;i ti&#7871;ng, 1/3 ti&amp;ecirc;u ch&amp;iacute; c&amp;ograve;n l&#7841;i ph&#7843;i &#273;&#7841;t tr&amp;ecirc;n y&amp;ecirc;u c&#7847;u th&#7887;a m&amp;atilde;n l&amp;agrave; 50% c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; c&#7911;a nh&amp;atilde;n hi&#7879;u n&#7893;i ti&#7871;ng.&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;b) C&amp;aacute; nh&amp;acirc;n, ph&amp;aacute;p nh&amp;acirc;n ch&#7911; nh&amp;atilde;n hi&#7879;u &#273;&#7841;t c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; v&#7873; n&#259;ng l&#7921;c c&#7841;nh tranh, qu&#7843;n l&amp;yacute; ch&#7845;t l&#432;&#7907;ng s&#7843;n ph&#7849;m - d&#7883;ch v&#7909;; s&#7843;n xu&#7845;t th&amp;acirc;n thi&#7879;n v&amp;agrave; b&#7843;o v&#7879; m&amp;ocirc;i tr&#432;&#7901;ng; th&#7921;c hi&#7879;n t&#7889;t lu&#7853;t Lao &#273;&#7897;ng,&amp;hellip;; th&#7921;c hi&#7879;n &#273;&#7847;y &#273;&#7911; ngh&#297;a v&#7909; thu&#7871;; t&amp;iacute;ch c&#7921;c tham gia c&amp;aacute;c ho&#7841;t &#273;&#7897;ng mang t&amp;iacute;nh x&amp;atilde; h&#7897;i.&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Sau h&#417;n 3 th&amp;aacute;ng t&#7853;p h&#7907;p h&#7891; s&#417; &amp;amp; x&amp;eacute;t duy&#7879;t, ban t&#7893; ch&#7913;c &amp;ldquo;Ch&#432;&#417;ng tr&amp;igrave;nh t&#432; v&#7845;n &amp;amp; b&amp;igrave;nh ch&#7885;n Nh&amp;atilde;n hi&#7879;u c&#7841;nh tranh - n&#7893;i ti&#7871;ng qu&#7889;c gia&amp;rdquo; &#273;&amp;atilde; ch&#7885;n ra 47 doanh nghi&#7879;p &#273;&#432;&#7907;c trao &amp;ldquo;C&amp;uacute;p V&amp;agrave;ng nh&amp;atilde;n hi&#7879;u c&#7841;nh tranh&amp;rdquo; &amp;amp; 49 doanh nghi&#7879;p &#273;&#432;&#7907;c trao &amp;ldquo;C&amp;uacute;p V&amp;agrave;ng nh&amp;atilde;n hi&#7879;u n&#7893;i ti&#7871;ng&amp;rdquo; tr&amp;ecirc;n to&amp;agrave;n qu&#7889;c. C&amp;ocirc;ng ty C&#7893; ph&#7847;n K&#7929; Th&#432;&#417;ng Thi&amp;ecirc;n Ho&amp;agrave;ng &#273;&amp;atilde; vinh d&#7921; &#273;&#432;&#7907;c trao &amp;ldquo;C&amp;uacute;p V&amp;agrave;ng nh&amp;atilde;n hi&#7879;u c&#7841;nh tranh&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7875; c&amp;oacute; &#273;&#432;&#7907;c vinh d&#7921; n&amp;agrave;y, C&amp;ocirc;ng ty C&#7893; ph&#7847;n K&#7929; Th&#432;&#417;ng Thi&amp;ecirc;n Ho&amp;agrave;ng ch&amp;uacute;ng t&amp;ocirc;i lu&amp;ocirc;n hi&#7875;u r&#7857;ng &#273;&amp;oacute; kh&amp;ocirc;ng &#273;&#417;n thu&#7847;n l&amp;agrave; k&#7871;t qu&#7843; c&#7911;a nh&#7919;ng g&amp;igrave; ch&amp;uacute;ng t&amp;ocirc;i &#273;&amp;atilde; l&amp;agrave;m &#273;&#432;&#7907;c, m&amp;agrave; ph&#7847;n l&#7899;n k&#7871;t qu&#7843; n&amp;agrave;y d&#7921;a v&amp;agrave;o s&#7921; t&amp;iacute;n nhi&#7879;m tin y&amp;ecirc;u c&#7911;a c&amp;aacute;c qu&amp;iacute; kh&amp;aacute;ch h&amp;agrave;ng v&amp;agrave; c&amp;aacute;c qu&amp;iacute; &#273;&#7889;i t&amp;aacute;c &#273;&amp;atilde; h&#7871;t l&amp;ograve;ng &#7911;ng h&#7897; C&amp;ocirc;ng ty C&#7893; ph&#7847;n K&#7929; Th&#432;&#417;ng Thi&amp;ecirc;n Ho&amp;agrave;ng trong th&#7901;i gian qua.&lt;/p&gt;\r\n\r\n&lt;p&gt;Thay m&#7863;t to&amp;agrave;n th&#7875; c&amp;aacute;n b&#7897; nh&amp;acirc;n vi&amp;ecirc;n C&amp;ocirc;ng ty C&#7893; ph&#7847;n K&#7929; Th&#432;&#417;ng Thi&amp;ecirc;n Ho&amp;agrave;ng, t&amp;ocirc;i xin &#273;&#432;&#7907;c g&#7917;i l&#7901;i c&#7843;m &#417;n ch&amp;acirc;n th&amp;agrave;nh nh&#7845;t &#273;&#7871;n t&#7845;t c&#7843; c&amp;aacute;c qu&amp;iacute; kh&amp;aacute;ch h&amp;agrave;ng v&amp;agrave; c&amp;aacute;c qu&amp;iacute; &#273;&#7889;i t&amp;aacute;c c&#7911;a C&amp;ocirc;ng ty C&#7893; ph&#7847;n K&#7929; Th&#432;&#417;ng Thi&amp;ecirc;n Ho&amp;agrave;ng.&lt;/p&gt;\r\n','18/10/2011','18/10/2011','/home/uploads/image/Generate/mikadologo2.png',21,2,2587,'admin',1,'','nh&atilde;n hi&#7879;u c&#7841;nh tranh, mikado ceramic',NULL,NULL,NULL,'nh&atilde;n hi&#7879;u c&#7841;nh tranh, mikado ceramic',0,0,0,'','','admin',4,'Mikado',1,1,0,0),(4,'Bu&ocirc;ng l&#7887;ng qu&#7843;n l&yacute; trong &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&aacute;t','Hi&#7879;n nay, n&#259;ng l&#7921;c s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&aacute;t ceramic v&agrave; granit r&#7845;t l&#7899;n, nh&#432;ng th&#7921;c t&#7871; ti&ecirc;u th&#7909; hai lo&#7841;i s&#7843;n ph&#7849;m n&agrave;y r&#7845;t ch&#7853;m. &ETH;i&#7873;u &#273;&oacute; kh&ocirc;ng ch&#7881; g&acirc;y kh&oacute; kh&#259;n cho c&#417; s&#7903; s&#7843;n xu&#7845;t m&agrave; quan tr&#7885;ng h&#417;n l&agrave; l&atilde;ng ph&iacute; v&#7889;n &#273;&#7847;u t&#432;. V&#7853;y &#273;&acirc;u l&agrave; nguy&ecirc;n nh&acirc;n c&#7911;a t&igrave;nh tr&#7841;ng n&ecirc;u tr&ecirc;n?','&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;300&quot; height=&quot;300&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/VD845202977.png&quot; /&gt;&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;C&#7843; n&#432;&#7899;c c&amp;oacute; 51 c&#417; s&#7903; s&#7843;n xu&#7845;t;&lt;br /&gt;\r\nS&#7843;n l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t ceramic: 145 tri&#7879;u m2/n&#259;m;&lt;br /&gt;\r\nS&#7843;n l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t granit: 24,5 tri&#7879;u m2/n&#259;m;&lt;br /&gt;\r\nT&#7893;ng v&#7889;n &#273;&#7847;u t&#432; : h&#417;n 7.000 t&#7927; &#273;&#7891;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&#7871;u nh&#432; n&#259;m 1994, c&#7843; n&#432;&#7899;c ch&#7881; c&amp;oacute; m&#7897;t nh&amp;agrave; m&amp;aacute;y s&#7843;n xu&#7845;t g&#7841;ch ceramic v&#7899;i c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; 1 tri&#7879;u m2/n&#259;m, nh&#432;ng s&#7843;n xu&#7845;t ch&#7881; &#273;&#7841;t 340 ngh&amp;igrave;n m2/n&#259;m v&amp;agrave; ch&#432;a t&#7915;ng &#273;&#432;&#7907;c th&#7871; gi&#7899;i bi&#7871;t &#273;&#7871;n v&#7873; l&#297;nh v&#7921;c s&#7843;n xu&#7845;t m&#7899;i m&#7867; n&amp;agrave;y, nh&#432;ng trong h&#417;n 10 n&#259;m qua, v&#7899;i t&#7889;c &#273;&#7897; t&#259;ng b&amp;igrave;nh qu&amp;acirc;n li&amp;ecirc;n t&#7909;c h&#417;n 50%/n&#259;m, &#273;&#7871;n nay n&#259;ng l&#7921;c s&#7843;n xu&#7845;t c&#7911;a hai lo&#7841;i s&#7843;n ph&#7849;m n&amp;agrave;y &#273;&amp;atilde; g&#7845;p 500 l&#7847;n so v&#7899;i n&#259;m 1994, v&amp;agrave; &#273;&#7913;ng v&amp;agrave;o top 10 n&#432;&#7899;c s&#7843;n xu&#7845;t h&amp;agrave;ng &#273;&#7847;u th&#7871; gi&#7899;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ETH;&#7889;i v&#7899;i c&amp;aacute;c n&#432;&#7899;c ph&amp;aacute;t tri&#7875;n, trong v&#7853;t li&#7879;u &#7889;p l&amp;aacute;t ceramic v&amp;agrave; granit, th&amp;igrave; granit th&#432;&#7901;ng chi&#7871;m t&#7927; l&#7879; kho&#7843;ng t&#7915; 7 &#273;&#7871;n 15%, c&amp;ograve;n c&amp;aacute;c n&#432;&#7899;c &#273;ang ph&amp;aacute;t tri&#7875;n th&amp;igrave; t&#7927; l&#7879; n&amp;agrave;y &#273;&amp;aacute;ng l&#7869; ph&#7843;i th&#7845;p h&#417;n, do s&#7843;n xu&#7845;t granit &#273;&amp;ograve;i h&#7887;i quy tr&amp;igrave;nh c&amp;ocirc;ng ngh&#7879; v&amp;agrave; k&#7929; thu&#7853;t s&#7843;n xu&#7845;t c&#361;ng nh&#432; nguy&amp;ecirc;n v&#7853;t li&#7879;u ph&#7913;c t&#7841;p h&#417;n. Nh&#432;ng ng&#432;&#7907;c l&#7841;i, &#7903; n&#432;&#7899;c ta, n&#7871;u n&#259;m 2000 c&#7843; n&#432;&#7899;c m&#7899;i s&#7843;n xu&#7845;t &#273;&#432;&#7907;c 6,5 tri&#7879;u m2 granit, th&amp;igrave; &#273;&#7871;n nay &#273;&amp;atilde; l&amp;ecirc;n t&#7899;i 24,5 tri&#7879;u m2, t&#7913;c l&amp;agrave; chi&#7871;m h&#417;n 20% so v&#7899;i t&#7893;ng c&amp;ocirc;ng su&#7845;t c&#7843; hai lo&#7841;i s&#7843;n ph&#7849;m. Th&#7883; tr&#432;&#7901;ng trong n&#432;&#7899;c m&#7845;t c&amp;acirc;n &#273;&#7889;i, cung v&#432;&#7907;t c&#7847;u, t&#7841;o n&amp;ecirc;n m&#7897;t cu&#7897;c c&#7841;nh tranh quy&#7871;t li&#7879;t gi&#7919;a c&amp;aacute;c doanh nghi&#7879;p. &amp;ETH;&amp;acirc;y l&amp;agrave; h&#7853;u qu&#7843; c&#7911;a s&#7921; bu&amp;ocirc;ng l&#7887;ng c&#7911;a c&amp;aacute;c c&#417; quan qu&#7843;n l&amp;yacute; nh&amp;agrave; n&#432;&#7899;c v&#7873; v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng. &amp;ETH;&amp;oacute; l&amp;agrave; y&#7871;u k&amp;eacute;m v&#7873; bi&#7879;n ph&amp;aacute;p qu&#7843;n l&amp;yacute; v&amp;agrave; ki&#7875;m so&amp;aacute;t quy ho&#7841;ch ph&amp;aacute;t tri&#7875;n. &amp;ETH;&amp;oacute; c&amp;ograve;n l&amp;agrave; s&#7921; bu&amp;ocirc;ng l&#7887;ng &#273;&#7875; cho r&#7845;t nhi&#7873;u doanh nghi&#7879;p thu&#7897;c c&amp;aacute;c th&amp;agrave;nh ph&#7847;n kinh t&#7871;, t&#7915; doanh nghi&#7879;p (DN) qu&#7889;c doanh, t&#432; nh&amp;acirc;n, li&amp;ecirc;n doanh, t&#7899;i c&amp;aacute;c DN c&amp;oacute; v&#7889;n &#273;&#7847;u t&#432; n&#432;&#7899;c ngo&amp;agrave;i tham gia &#273;&#7847;u t&#432; &#7891; &#7841;t v&amp;agrave;o l&#297;nh v&#7921;c n&amp;agrave;y, v&#7899;i t&#7889;c &#273;&#7897; qu&amp;aacute; n&amp;oacute;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong s&#7921; ph&amp;aacute;t tri&#7875;n &#7891; &#7841;t c&#7911;a c&amp;aacute;c c&#417; s&#7903; s&#7843;n xu&#7845;t g&#7841;ch ceramic v&amp;agrave; granit, nh&#7845;t l&amp;agrave; c&amp;aacute;c DN t&#432; nh&amp;acirc;n, trong hai n&#259;m g&#7847;n &#273;&amp;acirc;y &#273;&amp;atilde; ph&amp;aacute;t tri&#7875;n nhanh ch&amp;oacute;ng c&#7843; v&#7873; s&#7889; l&#432;&#7907;ng v&amp;agrave; s&#7843;n l&#432;&#7907;ng, v&#7899;i 15 DN, t&#7893;ng c&amp;ocirc;ng su&#7845;t g&#7847;n 68 tri&#7879;u m2/n&#259;m, v&amp;agrave; hi&#7879;n chi&#7871;m kho&#7843;ng 44% th&#7883; ph&#7847;n. Ri&amp;ecirc;ng t&#7853;p &#273;o&amp;agrave;n t&#432; nh&amp;acirc;n Primer Group (V&#297;nh Ph&amp;uacute;c) c&amp;oacute; s&#7843;n l&#432;&#7907;ng l&amp;ecirc;n t&#7899;i g&#7847;n 35 tri&#7879;u m2 g&#7841;ch ceramic/n&#259;m. C&amp;aacute;c DN n&amp;agrave;y tuy sinh sau &#273;&#7867; mu&#7897;n, nh&#432;ng l&#7841;i th&#7915;a h&#432;&#7903;ng nh&#7919;ng kinh nghi&#7879;m v&amp;agrave; c&amp;ocirc;ng ngh&#7879; &#273;&amp;atilde; &#273;&#432;&#7907;c c&amp;aacute;c DN &#273;i tr&#432;&#7899;c &#273;&#7847;u t&#432; t&#7915; nh&#7919;ng n&#259;m cu&#7889;i c&#7911;a th&#7871; k&#7927; tr&#432;&#7899;c, v&amp;igrave; v&#7853;y c&amp;oacute; &#273;i&#7873;u ki&#7879;n s&amp;agrave;ng l&#7885;c, t&amp;iacute;ch l&#361;y kinh nghi&#7879;m &#273;&#7875; &#273;&#7847;u t&#432; m&#7899;i d&amp;acirc;y chuy&#7873;n thi&#7871;t b&#7883; hi&#7879;n &#273;&#7841;i, gi&amp;aacute; th&#7845;p. Th&amp;ecirc;m v&amp;agrave;o &#273;&amp;oacute; l&amp;agrave; tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng ng&amp;agrave;y c&amp;agrave;ng xu&#7845;t hi&#7879;n nhi&#7873;u lo&#7841;i s&#7843;n ph&#7849;m v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng kh&amp;aacute;c d&amp;ugrave;ng cho &#7889;p t&#432;&#7901;ng, l&amp;aacute;t n&#7873;n t&#7915; b&amp;igrave;nh d&amp;acirc;n &#273;&#7871;n cao c&#7845;p c&amp;oacute; th&#7875; thay th&#7871; g&#7841;ch &#7889;p l&amp;aacute;t ceramic v&amp;agrave; granit, nh&#432; &#273;&amp;aacute; x&#7867;, g&#7895; v&amp;aacute;n s&amp;agrave;n...&lt;/p&gt;\r\n\r\n&lt;p&gt;Nh&#7857;m g&amp;oacute;p ph&#7847;n h&#7841;n ch&#7871; l&amp;atilde;ng ph&amp;iacute; trong &#273;&#7847;u t&#432;; &#273;&#7891;ng th&#7901;i, gi&amp;uacute;p c&amp;aacute;c DN kh&#7855;c ph&#7909;c t&amp;igrave;nh tr&#7841;ng s&#7843;n ph&#7849;m s&#7843;n xu&#7845;t ra kh&amp;ocirc;ng ti&amp;ecirc;u th&#7909; &#273;&#432;&#7907;c, tr&#432;&#7899;c h&#7871;t, c&amp;aacute;c doanh nghi&#7879;p ho&#7863;c ph&#7843;i gi&#7843;m l&#432;&#7907;ng s&#7843;n xu&#7845;t so v&#7899;i n&#259;ng l&#7921;c hi&#7879;n c&amp;oacute; b&#7857;ng c&amp;aacute;c bi&#7879;n ph&amp;aacute;p d&#7915;ng s&#7843;n xu&#7845;t &#273;&#7875; s&#7917;a ch&#7919;a, b&#7843;o d&#432;&#7905;ng thi&#7871;t b&#7883;, v&#7879; sinh c&amp;ocirc;ng nghi&#7879;p,.. &amp;ETH;&#7847;u th&amp;aacute;ng 9 v&#7915;a qua, C&amp;ocirc;ng ty g&#7841;ch &#7889;p l&amp;aacute;t H&amp;agrave; N&#7897;i - DN s&#7843;n xu&#7845;t g&#7841;ch ceramic &#273;&#7847;u ti&amp;ecirc;n &#7903; Vi&#7879;t Nam &#273;&amp;atilde; t&#7841;m d&#7915;ng s&#7843;n xu&#7845;t &#273;&#7875; &#273;&#7849;y m&#7841;nh ti&amp;ecirc;u th&#7909; v&amp;agrave; chu&#7849;n b&#7883; ph&#432;&#417;ng &amp;aacute;n di d&#7901;i v&#7873; &#273;&#7883;a &#273;i&#7875;m m&#7899;i t&#7841;i H&#7843;i D&#432;&#417;ng. Ho&#7863;c nh&#432; nhi&#7873;u DN ngo&amp;agrave;i qu&#7889;c doanh &#7903; V&#297;nh Ph&amp;uacute;c &#273;&amp;atilde; ch&#7911; &#273;&#7897;ng gi&#7843;m n&#259;ng l&#7921;c s&#7843;n xu&#7845;t xu&#7889;ng 50% so v&#7899;i c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871;. Hi&#7879;n nhi&#7873;u DN t&amp;igrave;m c&amp;aacute;c bi&#7879;n ph&amp;aacute;p gi&#7843;m c&amp;aacute;c chi ph&amp;iacute; &#273;&#7847;u v&amp;agrave;o, th&amp;ocirc;ng qua t&#7893; ch&#7913;c &#273;&#7845;u th&#7847;u mua s&#7855;m nguy&amp;ecirc;n v&#7853;t li&#7879;u &#273;&#7875; t&amp;igrave;m &#273;&#432;&#7907;c ngu&#7891;n cung c&#7845;p t&#7889;t nh&#7845;t v&#7873; ch&#7845;t l&#432;&#7907;ng, nh&#432;ng gi&amp;aacute; h&#7841;; t&#7893; ch&#7913;c l&#7841;i l&#7921;c l&#432;&#7907;ng s&#7843;n xu&#7845;t h&#7907;p l&amp;yacute;; t&amp;igrave;m bi&#7879;n ph&amp;aacute;p c&amp;ocirc;ng ngh&#7879; t&#7889;i &#432;u &#273;&#7875; n&amp;acirc;ng cao ch&#7845;t l&#432;&#7907;ng s&#7843;n ph&#7849;m, ho&#7863;c l&#7921;a ch&#7885;n s&#7843;n xu&#7845;t c&amp;aacute;c s&#7843;n ph&#7849;m cao c&#7845;p, v&#7899;i m&#7851;u m&amp;atilde; m&#7899;i, ch&#7845;t l&#432;&#7907;ng t&#7889;t &#273;&#432;&#7907;c ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng &#432;a chu&#7897;ng v&amp;agrave; cu&#7889;i c&amp;ugrave;ng l&amp;agrave; t&amp;igrave;m ki&#7871;m m&#7903; r&#7897;ng th&#7883; tr&#432;&#7901;ng xu&#7845;t kh&#7849;u,.. ch&#7881; c&amp;oacute; nh&#432; v&#7853;y m&#7899;i kh&#7855;c ph&#7909;c &#273;&#432;&#7907;c t&amp;igrave;nh tr&#7841;ng &#7913; &#273;&#7885;ng s&#7843;n ph&#7849;m nh&#432; hi&#7879;n nay.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7873; ph&amp;iacute;a c&#417; quan qu&#7843;n l&amp;yacute; nh&amp;agrave; n&#432;&#7899;c, qua b&amp;agrave;i h&#7885;c xi-m&#259;ng l&amp;ograve; &#273;&#7913;ng, v&amp;agrave; nay l&amp;agrave; g&#7841;ch ceramic v&amp;agrave; granit, thi&#7871;t ngh&#297; &#273;&amp;atilde; &#273;&#7871;n l&amp;uacute;c c&#7847;n ch&#7845;n ch&#7881;nh c&amp;ocirc;ng t&amp;aacute;c qu&#7843;n l&amp;yacute; nh&amp;agrave; n&#432;&#7899;c v&#7873; v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng n&amp;oacute;i chung v&amp;agrave; s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&amp;aacute;t ceramic v&amp;agrave; granit n&amp;oacute;i ri&amp;ecirc;ng. Tr&#432;&#7899;c m&#7855;t, c&#7847;n c&amp;oacute; s&#7921; ph&#7889;i h&#7907;p gi&#7919;a c&amp;aacute;c ng&amp;agrave;nh ch&#7913;c n&#259;ng, c&amp;aacute;c &#273;&#7883;a ph&#432;&#417;ng trong vi&#7879;c r&amp;agrave; so&amp;aacute;t l&#7841;i quy ho&#7841;ch t&#7893;ng th&#7875; c&amp;aacute;c khu c&amp;ocirc;ng nghi&#7879;p, c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&amp;atilde; &#273;&#432;&#7907;c duy&#7879;t, ki&amp;ecirc;n quy&#7871;t d&#7915;ng c&amp;aacute;c d&#7921; &amp;aacute;n m&#7899;i chu&#7849;n b&#7883; &#273;&#7847;u t&#432;, &#273;&#7891;ng th&#7901;i c&amp;oacute; bi&#7879;n ph&amp;aacute;p gi&amp;uacute;p c&amp;aacute;c DN c&#7911;ng c&#7889;, ph&amp;aacute;t huy hi&#7879;u qu&#7843; c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&amp;atilde; &#273;&#7847;u t&#432; n&amp;acirc;ng cao ch&#7845;t l&#432;&#7907;ng s&#7843;n ph&#7849;m, h&#7841; gi&amp;aacute; th&amp;agrave;nh s&#7843;n ph&#7849;m; &#273;&#7891;ng th&#7901;i t&#7841;o m&#7885;i &#273;i&#7873;u ki&#7879;n thu&#7853;n l&#7907;i v&#7873; c&#417; ch&#7871;, ch&amp;iacute;nh s&amp;aacute;ch &#273;&#7875; xu&#7845;t kh&#7849;u s&#7843;n ph&#7849;m g&#7841;ch &#7889;p l&amp;aacute;t ceramic v&amp;agrave; granit ra th&#7883; tr&#432;&#7901;ng ngo&amp;agrave;i n&#432;&#7899;c.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i g&#7889;m s&#7913; x&amp;acirc;y d&#7921;ng Vi&#7879;t Nam &amp;ETH;INH QUANG HUY: Trong t&amp;igrave;nh h&amp;igrave;nh cung v&#432;&#7907;t qu&amp;aacute; c&#7847;u t&#7841;i th&#7883; tr&#432;&#7901;ng trong n&#432;&#7899;c, c&amp;aacute;c DN &#273;&amp;atilde; &#273;&#7849;y m&#7841;nh c&amp;ocirc;ng t&amp;aacute;c xu&#7845;t kh&#7849;u, &#273;i &#273;&#7847;u l&amp;agrave; c&amp;aacute;c DN Viglacera, g&#7841;ch &amp;ETH;&#7891;ng T&amp;acirc;m, Taicera,.. Nh&#432;ng v&#7851;n chi&#7871;m t&#7927; tr&#7885;ng qu&amp;aacute; nh&#7887; so v&#7899;i n&#259;ng l&#7921;c c&#7911;a c&amp;aacute;c DN, v&amp;igrave; v&#7853;y c&#7847;n c&amp;oacute; bi&#7879;n ph&amp;aacute;p ti&#7871;p th&#7883;, m&#7903; r&#7897;ng th&#7883; tr&#432;&#7901;ng ngo&amp;agrave;i n&#432;&#7899;c &#273;&#7875; &#273;&#7849;y m&#7841;nh ti&amp;ecirc;u th&#7909;, nh&#7845;t l&amp;agrave; c&amp;aacute;c DN li&amp;ecirc;n doanh hay c&amp;oacute; v&#7889;n &#273;&#7847;u t&#432; n&#432;&#7899;c ngo&amp;agrave;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;K&#7929; s&#432; v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng (C&amp;ocirc;ng ty &#273;&#7847;u t&#432; v&amp;agrave; x&amp;acirc;y d&#7921;ng Ph&#432;&#417;ng B&#7855;c) TRUNG TH&amp;Agrave;NH: Hi&#7879;n tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng, ngo&amp;agrave;i g&#7841;ch &#7889;p l&amp;aacute;t ceramic v&amp;agrave; granit, c&amp;ograve;n c&amp;oacute; c&amp;aacute;c lo&#7841;i &#273;&amp;aacute; x&#7867;, v&amp;agrave; nh&#7845;t l&amp;agrave; g&#7895; v&amp;aacute;n s&amp;agrave;n &#273;&#432;&#7907;c nh&#7853;p kh&#7849;u ho&#7863;c s&#7843;n xu&#7845;t tr&amp;ecirc;n c&amp;aacute;c d&amp;acirc;y chuy&#7873;n thi&#7871;t b&#7883; hi&#7879;n &#273;&#7841;i, b&#7843;o &#273;&#7843;m m&#7847;u s&#7855;c t&#7921; nhi&amp;ecirc;n h&amp;agrave;i h&amp;ograve;a, kh&amp;ocirc;ng thay &#273;&#7893;i theo th&#7901;i gian, kh&#7843; n&#259;ng ch&#7889;ng ch&amp;aacute;y, ch&#7889;ng tr&#7847;y x&#432;&#7899;c v&amp;agrave; kh&amp;aacute;ng n&#432;&#7899;c r&#7845;t cao,.. &#273;&#432;&#7907;c d&amp;ugrave;ng kh&amp;aacute; ph&#7893; bi&#7871;n trong vi&#7879;c &#7889;p t&#432;&#7901;ng, l&amp;aacute;t n&#7873;n nh&amp;agrave;,.. c&amp;oacute; th&#7875; thay th&#7871; c&amp;aacute;c s&#7843;n ph&#7849;m g&#7841;ch ceramic v&amp;agrave; granit.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng &amp;ETH;INH DUY X&#431;&#416;NG, s&#7889; nh&amp;agrave; 80, ng&amp;otilde; 6 V&#7841;n Ph&amp;uacute;c, qu&#7853;n Ba &amp;ETH;&amp;igrave;nh, H&amp;agrave; N&#7897;i: G&#7841;ch &#7889;p l&amp;aacute;t ceramic &#273;&#7865;p, nh&#432;ng kh&amp;ocirc;ng b&#7873;n, c&amp;ograve;n g&#7841;ch granit b&#7873;n, nh&#432;ng gi&amp;aacute; l&#7841;i cao, v&amp;igrave; v&#7853;y nhi&#7873;u gia &#273;&amp;igrave;nh &#273;&amp;atilde; ch&#7885;n c&amp;aacute;c lo&#7841;i v&#7853;t li&#7879;u kh&amp;aacute;c v&#7915;a b&#7873;n, &#273;&#7865;p, m&amp;agrave; gi&amp;aacute; c&#7843; l&#7841;i h&#7907;p t&amp;uacute;i ti&#7873;n. V&#7899;i gi&amp;aacute; b&amp;aacute;n nh&#432; hi&#7879;n nay, c&amp;ograve;n l&amp;acirc;u c&amp;aacute;c gia &#273;&amp;igrave;nh n&amp;ocirc;ng d&amp;acirc;n m&#7899;i &#273;&#432;&#7907;c s&#7917; d&#7909;ng g&#7841;ch l&amp;aacute;t cao c&#7845;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7913; tr&#432;&#7903;ng B&#7897; X&amp;acirc;y d&#7921;ng T&#7888;NG V&#258;N NGA: B&#7897; X&amp;acirc;y d&#7921;ng s&#7869; ti&#7871;p t&#7909;c c&#7843;nh b&amp;aacute;o v&#7873; t&amp;igrave;nh tr&#7841;ng d&#432; th&#7915;a s&#7843;n ph&#7849;m g&#7841;ch l&amp;aacute;t ceramic v&amp;agrave; granit. Tr&#432;&#7899;c m&#7855;t, c&amp;aacute;c doanh nghi&#7879;p c&#7847;n ch&amp;uacute; tr&#7885;ng s&#7843;n xu&#7845;t, kinh doanh g&#7855;n v&#7899;i th&#7883; tr&#432;&#7901;ng ti&amp;ecirc;u th&#7909; v&amp;agrave; &#273;&#7849;y m&#7841;nh xu&#7845;t kh&#7849;u; ph&amp;aacute;t tri&#7875;n nh&#7919;ng s&#7843;n ph&#7849;m c&amp;oacute; l&#7907;i th&#7871; c&#7841;nh tranh cao. Chuy&#7875;n h&#432;&#7899;ng s&#7843;n xu&#7845;t c&amp;aacute;c s&#7843;n ph&#7849;m cao c&#7845;p thay th&#7871; c&amp;aacute;c s&#7843;n ph&#7849;m kh&amp;ocirc;ng c&amp;ograve;n ph&amp;ugrave; h&#7907;p th&#7883; hi&#7871;u c&#7911;a ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng. &amp;ETH;&#7891;ng th&#7901;i t&#7853;p trung cho vi&#7879;c gi&#7843;i quy&#7871;t h&amp;agrave;ng t&#7891;n &#273;&#7885;ng, thu h&#7891;i c&amp;ocirc;ng n&#7907;, kh&amp;ocirc;ng &#273;&#7875; t&#259;ng th&amp;ecirc;m d&#432; n&#7907; vay ng&amp;acirc;n h&amp;agrave;ng, ki&amp;ecirc;n quy&#7871;t d&#7915;ng s&#7843;n xu&#7845;t c&amp;aacute;c s&#7843;n ph&#7849;m c&amp;oacute; s&#7889; l&#432;&#7907;ng t&#7891;n kho qu&amp;aacute; m&#7913;c d&#7921; tr&#7919; cho ph&amp;eacute;p, kh&amp;oacute; c&amp;oacute; kh&#7843; n&#259;ng ti&amp;ecirc;u th&#7909;.&lt;/p&gt;\r\n','18/10/2011','18/10/2011','/home/uploads/image/news/10-2011/VD845202977.png',22,2,2783,'admin',1,'','G&#7841;ch &#7889;p l&aacute;t, mikado ceramic',NULL,NULL,NULL,'G&#7841;ch &#7889;p l&aacute;t, mikado ceramic',0,0,0,'','','admin',4,'Mikado',1,0,0,1),(5,'Gi&#7899;i thi&#7879;u chung','C&ocirc;ng ty c&#7893; ph&#7847;n k&#7929; th&#432;&#417;ng Thi&ecirc;n Ho&agrave;ng &#273;&#432;&#7907;c th&agrave;nh l&#7853;p n&#259;m 2002 c&oacute; tr&#7909; s&#7903; ch&iacute;nh &#7903; qu&#7853;n C&#7847;u Gi&#7845;y &ndash; H&agrave; N&#7897;i. Ng&agrave;nh ngh&#7873; kinh doanh ch&iacute;nh l&agrave; s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&aacute;t ceramic.','&lt;p&gt;Hi&#7879;n t&#7841;i C&amp;ocirc;ng ty c&amp;oacute; hai nh&amp;agrave; m&amp;aacute;y &#273;&amp;oacute; l&amp;agrave; Nh&amp;agrave; m&amp;aacute;y g&#7841;ch men Mikado v&amp;agrave; Nh&amp;agrave; m&amp;aacute;y g&#7841;ch trang tr&amp;iacute; Mikado.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;358&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/Generate/nhamay1.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;Nh&amp;agrave; m&amp;aacute;y 1&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;356&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/Generate/nhamay2.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;Nh&amp;agrave; m&amp;aacute;y 2&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c nh&amp;agrave; m&amp;aacute;y tr&amp;ecirc;n s&#7917; d&#7909;ng c&amp;ocirc;ng ngh&#7879; nung hai l&#7847;n v&amp;agrave; thi&#7871;t b&#7883; ti&amp;ecirc;n ti&#7871;n c&#7911;a Italy v&amp;agrave; T&amp;acirc;y Ban Nha. S&#7843;n ph&#7849;m ch&#7911; y&#7871;u l&amp;agrave; g&#7841;ch &#7889;p l&amp;aacute;t c&amp;oacute; k&amp;iacute;ch th&#432;&#7899;c 20 x 20 cm, 20 x 25 cm, 25 x 40 cm 40 x 40 cm v&amp;agrave; g&#7841;ch vi&#7873;n trang tr&amp;iacute; n&#7893;i c&amp;oacute; k&amp;iacute;ch th&#432;&#7899;c 7 x 20 cm, 8 x 25 cm, 12 x 40 cm......&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;ocirc;ng ty &#273;&amp;atilde; &amp;aacute;p d&#7909;ng h&#7879; th&#7889;ng qu&#7843;n l&amp;yacute; ch&#7845;t l&#432;&#7907;ng theo ti&amp;ecirc;u chu&#7849;n ISO 9001:2000 v&amp;agrave; c&amp;aacute;c s&#7843;n ph&#7849;m &#273;&amp;atilde; &#273;&#7841;t ti&amp;ecirc;u chu&#7849;n Ch&amp;acirc;u &amp;Acirc;u E159-1984. S&#7843;n ph&#7849;m g&#7841;ch men MIKADO &#273;&amp;atilde; &#273;&#432;&#7907;c xu&#7845;t kh&#7849;u sang c&amp;aacute;c n&#432;&#7899;c nh&#432; Ph&amp;aacute;p, &#272;&amp;agrave;i Loan, H&amp;agrave;n Qu&#7889;c, Angeri, &amp;Uacute;c...&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7863;c bi&#7879;t g&#7841;ch men Mikado &#273;&amp;atilde; &#273;&#432;&#7907;c ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng Vi&#7879;t Nam &#432;a chu&#7897;ng v&amp;agrave; tin d&amp;ugrave;ng. V&#7899;i m&#7841;ng l&#432;&#7899;i ph&amp;acirc;n ph&#7889;i &#7903; t&#7845;t c&#7843; c&amp;aacute;c t&#7881;nh th&amp;agrave;nh trong c&#7843; n&#432;&#7899;c g&#7841;ch men Mikado &#273;&amp;aacute;p &#7913;ng nhanh nh&#7845;t m&#7885;i nhu c&#7847;u c&#7911;a ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng trong n&#432;&#7899;c.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong th&#7901;i gian t&#7899;i c&amp;ocirc;ng ty ti&#7871;p t&#7909;c th&#7921;c hi&#7879;n chi&#7871;n l&#432;&#7907;c ph&amp;aacute;t tri&#7875;n c&#7911;a m&amp;igrave;nh &#273;&amp;oacute; l&amp;agrave; tr&#7903; th&amp;agrave;nh m&#7897;t trong c&amp;aacute;c c&amp;ocirc;ng ty h&amp;agrave;ng &#273;&#7847;u trong l&#297;nh v&#7921;c s&#7843;n xu&#7845;t v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng. C&amp;ocirc;ng ty s&#7869; &#273;&#7847;u t&#432; c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y s&#7843;n xu&#7845;t g&#7841;ch Granit, s&#7913; v&#7879; sinh, s&#7913; m&#7929; ngh&#7879; xu&#7845;t kh&#7849;u, s&#417;n t&#432;&#7901;ng, men m&amp;agrave;u v&amp;agrave; chuy&#7875;n giao c&amp;ocirc;ng ngh&#7879;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&amp;uacute;ng t&amp;ocirc;i t&#7921; h&amp;agrave;o l&amp;agrave; c&amp;oacute; &#273;&#432;&#7907;c m&#7897;t &#273;&#7897;i ng&#361; c&amp;aacute;n b&#7897; k&#7929; thu&#7853;t l&amp;agrave;nh ngh&#7873;, c&amp;aacute;c nh&amp;acirc;n vi&amp;ecirc;n kinh doanh, marketing n&#259;ng &#273;&#7897;ng s&amp;aacute;ng t&#7841;o v&amp;agrave; b&#7897; ph&#7853;n nghi&#7879;p v&#7909; y&amp;ecirc;u ngh&#7873; v&amp;agrave; lu&amp;ocirc;n s&#7861;n s&amp;agrave;ng ph&#7909;c v&#7909; kh&amp;aacute;ch h&amp;agrave;ng.&lt;/p&gt;\r\n','18/10/2011','18/10/2011','/home/uploads/image/Generate/nhamay1.jpg',39,3,13396,'admin',1,'','nh&agrave; m&aacute;y, mikado, granit, ceramic',NULL,NULL,NULL,'nh&agrave; m&aacute;y, mikado, granit, ceramic',0,0,0,'','','',4,'Mikado',1,0,0,0),(6,'Ch&#7885;n v&#7853;t li&#7879;u x&acirc;y d&#7921;ng th&#7901;i l&#7841;m ph&aacute;t','X&acirc;y nh&agrave; l&agrave; m&#7897;t quy&#7871;t &#273;&#7883;nh r&#7845;t l&#7899;n &#273;&#7889;i v&#7899;i m&#7895;i gia &#273;&igrave;nh, &#273;&#7863;c bi&#7879;t l&agrave; trong giai &#273;o&#7841;n b&atilde;o gi&aacute; nh&#432; hi&#7879;n nay. Gi&aacute; c&#7843; c&aacute;c lo&#7841;i v&#7853;t li&#7879;u nh&#432; c&aacute;t, xi m&#259;ng, s&#7855;t, th&eacute;p ... bi&#7871;n &#273;&#7897;ng v&#7899;i xu h&#432;&#7899;ng gia t&#259;ng m&#7895;i ng&agrave;y.','&lt;p&gt;Th&#7871; nh&#432;ng, v&#7851;n r&#7845;t nhi&#7873;u c&amp;ocirc;ng tr&amp;igrave;nh d&amp;acirc;n d&#7909;ng r&#7909;c r&#7883;ch &#273;&amp;agrave;o m&amp;oacute;ng, x&amp;acirc;y nh&amp;agrave; t&#7915; m&#7845;y th&amp;aacute;ng nay, k&#7875; c&#7843; trong th&amp;aacute;ng 7 &amp;acirc;m l&#7883;ch v&#7915;a qua.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Tranh th&#7911; x&amp;acirc;y nh&amp;agrave; d&amp;ugrave;... l&#7841;m ph&amp;aacute;t&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;T&amp;igrave;nh h&amp;igrave;nh v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng t&#259;ng gi&amp;aacute; &#273;&#432;&#7907;c c&#7853;p nh&#7853;t h&amp;agrave;ng ng&amp;agrave;y tr&amp;ecirc;n c&amp;aacute;c k&amp;ecirc;nh b&amp;aacute;o &#273;&amp;agrave;i, nh&#432;ng h&#7847;u h&#7871;t c&amp;aacute;c ch&#7911; nh&amp;agrave; &#273;&amp;atilde; c&amp;oacute; &#273;&#7845;t v&#7851;n quy&#7871;t &#273;&#7883;nh x&amp;acirc;y trong n&#259;m nay. Nhi&#7873;u ng&#432;&#7901;i b&amp;agrave;y t&#7887; quan &#273;i&#7875;m, th&#7901;i bu&#7893;i b&amp;acirc;y gi&#7901;, ch&#7881; to&amp;agrave;n th&#7845;y gi&amp;aacute; l&amp;ecirc;n ch&#7913; kh&amp;ocirc;ng gi&#7843;m, nh&#432; chuy&#7879;n x&#259;ng d&#7847;u, gi&#7843;m m&#7897;t ch&amp;uacute;t l&amp;agrave; s&#7869; l&#7841;i t&#259;ng nhi&#7873;u h&#417;n, n&amp;ecirc;n th&amp;ocirc;i, &#273;&amp;atilde; quy&#7871;t c&amp;oacute; nh&amp;agrave; th&amp;igrave; x&amp;acirc;y ngay ch&#7913; &#273;&#7907;i n&#259;m sau, ch&#7855;c g&amp;igrave; s&#7889; ti&#7873;n d&amp;agrave;nh d&#7909;m s&#7869; &#273;&#7911;.&lt;/p&gt;\r\n\r\n&lt;p&gt;M&#7897;t s&#7889; nh&amp;agrave; th&#7847;u chia s&#7867;: &amp;ldquo;N&#259;m nay th&#7883; tr&#432;&#7901;ng kh&amp;oacute; kh&#259;n, nhi&#7873;u d&#7921; &amp;aacute;n b&#7845;t &#273;&#7897;ng s&#7843;n &#273;&amp;oacute;ng b&#259;ng, tuy nhi&amp;ecirc;n l&#432;&#7907;ng h&#7907;p &#273;&#7891;ng x&amp;acirc;y nh&amp;agrave; d&amp;acirc;n d&#7909;ng v&#7851;n kh&amp;aacute; &#7893;n &#273;&#7883;nh. Ch&#7911; nh&amp;agrave; &#273;&amp;atilde; chu&#7849;n b&#7883; s&#7861;n t&amp;acirc;m l&amp;yacute; v&amp;agrave; chu&#7849;n b&#7883; m&#7897;t kho&#7843;n b&amp;ugrave; &#273;&#7855;p t&#7881; l&#7879; l&#7841;m ph&amp;aacute;t c&#7911;a th&#7883; tr&#432;&#7901;ng.&amp;rdquo;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;350&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/xaydungdandung.JPG&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7883; tr&#432;&#7901;ng kh&amp;oacute; kh&#259;n, nhi&#7873;u d&#7921; &amp;aacute;n b&#7845;t &#273;&#7897;ng s&#7843;n &#273;&amp;oacute;ng b&#259;ng, tuy nhi&amp;ecirc;n l&#432;&#7907;ng h&#7907;p &#273;&#7891;ng x&amp;acirc;y nh&amp;agrave; d&amp;acirc;n d&#7909;ng v&#7851;n kh&amp;aacute; &#7893;n &#273;&#7883;nh&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;i m&#7897;t v&amp;ograve;ng c&amp;aacute;c khu d&amp;acirc;n c&#432; m&#7899;i nh&#432; 6B Intresco, B&amp;igrave;nh Ch&amp;aacute;nh ho&#7863;c An Ph&amp;uacute; An Kh&amp;aacute;nh, Qu&#7853;n 2, c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh v&#7851;n n&#7889;i ti&#7871;p nhau x&amp;acirc;y d&#7921;ng b&#7845;t ch&#7845;p th&amp;aacute;ng 7 ki&amp;ecirc;ng k&#7925;, v&amp;igrave; nhi&#7873;u ch&#7911; nh&amp;agrave; cho r&#7857;ng cu&#7889;i n&#259;m, s&#7889; l&#432;&#7907;ng ng&#432;&#7901;i x&amp;acirc;y nh&amp;agrave; t&#259;ng cao v&amp;agrave; nh&#432; nhi&#7873;u n&#259;m tr&#432;&#7899;c gi&amp;aacute; v&#7853;t li&#7879;u s&#7869; c&amp;ograve;n t&#259;ng h&#417;n n&#7919;a.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&#272;i&#7873;u quan tr&#7885;ng nh&#7845;t c&#7911;a quy&#7871;t &#273;&#7883;nh x&amp;acirc;y nh&amp;agrave;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Khi &#273;&amp;atilde; quy&#7871;t &#273;&#7883;nh x&amp;acirc;y nh&amp;agrave;, th&amp;igrave; ngo&amp;agrave;i v&#7845;n &#273;&#7873; l&#7899;n thi&#7871;t k&#7871; c&amp;ocirc;ng tr&amp;igrave;nh, th&amp;igrave; c&amp;aacute;c gia &#273;&amp;igrave;nh c&#361;ng &#273;&#7873;u quan t&amp;acirc;m &#273;&#7871;n chi ph&amp;iacute; v&amp;agrave; ch&#7845;t l&#432;&#7907;ng c&#7911;a c&amp;aacute;c v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng v&amp;igrave; n&amp;oacute; quy&#7871;t &#273;&#7883;nh &#273;&#7871;n to&amp;agrave;n b&#7897; c&#7897;ng tr&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo ch&#7883; Thanh B&amp;igrave;nh, m&#7897;t ch&#7911; nh&amp;agrave; &#273;ang x&amp;acirc;y &#7903; Qu&#7853;n 2 trao &#273;&#7893;i, t&amp;igrave;nh h&amp;igrave;nh kinh t&#7871; kh&amp;oacute; kh&#259;n, ai c&#361;ng th&#7855;t ch&#7863;t vi&#7879;c chi ti&amp;ecirc;u c&#361;ng nh&#432; c&amp;acirc;n nh&#7855;c r&#7845;t nhi&#7873;u trong c&amp;aacute;c kho&#7843;n chi ph&amp;iacute;, ch&#7883; c&#361;ng kh&amp;ocirc;ng ngo&#7841;i l&#7879;. Khi k&amp;yacute; h&#7907;p &#273;&#7891;ng v&#7899;i nh&amp;agrave; th&#7847;u, ch&#7883; quan t&amp;acirc;m r&#7845;t k&#7929; &#273;&#7871;n &#273;&#417;n gi&amp;aacute;, lu&amp;ocirc;n mu&#7889;n ti&#7871;t ki&#7879;m m&#7897;t c&amp;aacute;ch h&#7907;p l&amp;yacute; nh&#432;ng v&#7851;n &#273;&#7843;m b&#7843;o ch&#7845;t l&#432;&#7907;ng c&amp;ocirc;ng tr&amp;igrave;nh. Ch&amp;iacute;nh v&amp;igrave; th&#7871;, &#273;&#7875; ch&#7883; v&amp;agrave; nh&amp;agrave; th&#7847;u &#273;i &#273;&#7871;n th&#7889;ng nh&#7845;t c&amp;aacute;c v&#7853;t li&#7879;u c&#7911;a ph&#7847;n x&amp;acirc;y th&amp;ocirc; c&#361;ng m&#7845;t kh&amp;aacute; nhi&#7873;u th&#7901;i gian, v&amp;igrave; ch&#7883; hi&#7875;u r&amp;otilde;, ph&#7847;n th&amp;ocirc; l&amp;agrave; c&#7889;t l&amp;otilde;i, quan tr&#7885;ng nh&#7845;t cho c&#7843; c&amp;ocirc;ng tr&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;350&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/xaydungdandung2.JPG&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nVi&#7879;c t&amp;igrave;m &#273;&#432;&#7907;c nh&amp;agrave; cung c&#7845;p c&amp;aacute;c s&#7843;n ph&#7849;m ch&#7845;t l&#432;&#7907;ng v&#7899;i m&#7913;c gi&amp;aacute; th&amp;agrave;nh h&#7907;p l&amp;yacute; kh&amp;ocirc;ng ph&#7843;i &#273;&#417;n gi&#7843;n&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;Khi x&amp;acirc;y nh&amp;agrave;, t&amp;ocirc;i &#273;&#7863;c bi&#7879;t quan t&amp;acirc;m &#273;&#7871;n vi&#7879;c ch&#7885;n v&#7853;t li&#7879;u s&#7855;t th&amp;eacute;p, xi m&#259;ng v&amp;agrave; l&#7921;a ch&#7885;n ph&#432;&#417;ng &amp;aacute;n s&#7917; d&#7909;ng b&amp;ecirc; t&amp;ocirc;ng t&#432;&#417;i ch&#7845;t l&#432;&#7907;ng cao. C&amp;oacute; th&#7875; ti&#7871;t ki&#7879;m nhi&#7873;u th&#7913; trong qu&amp;aacute; tr&amp;igrave;nh ho&amp;agrave;n thi&#7879;n, trang tr&amp;iacute; nh&amp;agrave; c&#7917;a, nh&#432;ng b&#7897; khung nh&amp;agrave; th&amp;igrave; t&amp;ocirc;i kh&amp;ocirc;ng th&#7875; ch&#7875;nh m&#7843;ng, nh&amp;agrave; th&#7847;u ph&#7843;i mua &#273;&amp;uacute;ng lo&#7841;i ch&#7845;t l&#432;&#7907;ng v&amp;agrave; &#273;&#7843;m b&#7843;o y&amp;ecirc;u c&#7847;u thi c&amp;ocirc;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Vi&#7879;c t&amp;igrave;m &#273;&#432;&#7907;c nh&amp;agrave; cung c&#7845;p c&amp;aacute;c s&#7843;n ph&#7849;m ch&#7845;t l&#432;&#7907;ng v&#7899;i m&#7913;c gi&amp;aacute; th&amp;agrave;nh h&#7907;p l&amp;yacute; kh&amp;ocirc;ng ph&#7843;i &#273;&#417;n gi&#7843;n. Tuy nhi&amp;ecirc;n, nh&#432; l&#7901;i anh Ho&amp;agrave;ng L&amp;acirc;n, m&#7897;t nh&amp;agrave; th&#7847;u c&amp;oacute; ti&#7871;ng trong gi&#7899;i x&amp;acirc;y d&#7921;ng S&amp;agrave;i G&amp;ograve;n, th&amp;igrave;: &amp;ldquo;Ch&#7885;n v&#7853;t li&#7879;u t&#7889;t v&amp;agrave; chi ph&amp;iacute; ch&#7845;p nh&#7853;n &#273;&#432;&#7907;c l&amp;agrave; &#273;i&#7873;u ki&#7879;n &#432;u ti&amp;ecirc;n s&#7889; 1 c&#7911;a c&amp;aacute;c ch&#7911; &#273;&#7847;u t&#432;. &#272;&#7875; t&amp;igrave;m &#273;&#432;&#7907;c ngu&#7891;n cung &#273;&amp;aacute;p &#7913;ng nhu c&#7847;u &#273;&amp;oacute; c&#361;ng t&#432;&#417;ng &#273;&#7889;i kh&amp;oacute; kh&#259;n, nh&#432;ng gi&amp;aacute; tr&#7883; mang l&#7841;i th&amp;igrave; ho&amp;agrave;n to&amp;agrave;n x&#7913;ng &#273;&amp;aacute;ng. C&amp;ocirc;ng tr&amp;igrave;nh ch&#7845;t l&#432;&#7907;ng, ch&#7911; &#273;&#7847;u t&#432; &#273;&amp;aacute;nh gi&amp;aacute; cao, t&#259;ng uy t&amp;iacute;n trong ngh&#7873;, v.v&amp;hellip; &#272;&#417;n c&#7917; nh&#432; v&#7899;i ph&#7847;n th&amp;ocirc;, ch&amp;uacute;ng t&amp;ocirc;i s&#7917; d&#7909;ng b&amp;ecirc; t&amp;ocirc;ng t&#432;&#417;i Holcim. L&#7921;a ch&#7885;n n&amp;agrave;y gi&amp;uacute;p nh&amp;agrave; th&#7847;u thi c&amp;ocirc;ng thu&#7853;n ti&#7879;n nhanh ch&amp;oacute;ng, an to&amp;agrave;n lao &#273;&#7897;ng v&amp;agrave; v&#7879; sinh m&amp;ocirc;i tr&#432;&#7901;ng trong khi v&#7851;n &#273;&#7843;m b&#7843;o ch&#7845;t l&#432;&#7907;ng c&amp;ocirc;ng tr&amp;igrave;nh &#7903; m&#7913;c t&#7889;t nh&#7845;t.&amp;rdquo;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nMong r&#7857;ng d&amp;ugrave; ch&#7883;u &amp;aacute;p l&#7921;c l&#7841;m ph&amp;aacute;t, c&amp;aacute;c ch&#7911; &#273;&#7847;u t&#432; v&amp;agrave; nh&amp;agrave; th&#7847;u v&#7851;n s&amp;aacute;ng su&#7889;t l&#7921;a ch&#7885;n &#273;&#432;&#7907;c c&amp;aacute;c ngu&#7891;n cung v&#7853;t li&#7879;u gi&amp;aacute; &amp;ldquo;v&#7915;a chi&amp;rdquo; m&amp;agrave; v&#7851;n &#273;&#7843;m b&#7843;o an to&amp;agrave;n thi c&amp;ocirc;ng v&amp;agrave; ch&#7845;t l&#432;&#7907;ng c&amp;ocirc;ng tr&amp;igrave;nh &#7903; m&#7913;c cao nh&#7845;t.&lt;/p&gt;\r\n','20/10/2011','20/10/2011','/home/uploads/image/news/10-2011/xaydungdandung.JPG',23,2,1702,'admin',1,'','v&#7853;t li&#7879;u x&acirc;y d&#7921;ng, mikado ceramic, ceramic',NULL,NULL,NULL,'v&#7853;t li&#7879;u x&acirc;y d&#7921;ng, mikado ceramic, ceramic',0,0,0,'','','',4,'Theo Th&ocirc;ng tin doanh nghi&#7879;p',1,0,0,0),(7,'Ng&agrave;nh VLXD: Ng&#7855;c ngo&#7843;i t&igrave;m l&#7889;i tho&aacute;t','Trong b&#7889;i c&#7843;nh &#7843;m &#273;&#7841;m chung c&#7911;a th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n (B&#272;S), ng&agrave;nh v&#7853;t li&#7879;u x&acirc;y d&#7921;ng (VLXD) c&#361;ng &#273;ang v&#7853;t l&#7897;n t&igrave;m c&aacute;ch th&aacute;o g&#7905; kh&oacute; kh&#259;n. Th&#7871; nh&#432;ng, b&#7871; t&#7855;c &#273;&#7847;u ra, ng&#7863;t ngh&egrave;o v&#7873; v&#7889;n c&#7897;ng v&#7899;i s&#7921; c&#7841;nh tranh quy&#7871;t li&#7879;t tr&ecirc;n th&#7883; tr&#432;&#7901;ng &#273;ang &#273;&#7849;y nhi&#7873;u doanh nghi&#7879;p xi m&#259;ng, th&eacute;p&hellip; &#273;&#7871;n b&#7901; v&#7921;c ph&aacute; s&#7843;n.','&lt;p&gt;&lt;strong&gt;Ti&amp;ecirc;u th&#7909; ch&#7853;m, h&amp;agrave;ng t&#7891;n cao&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;K&#7875; t&#7915; &#273;&#7847;u n&#259;m 2011, c&amp;ugrave;ng v&#7899;i s&#7921; kh&amp;oacute; kh&#259;n chung c&#7911;a n&#7873;n kinh t&#7871; v&amp;agrave; s&#7921; &#7843;m &#273;&#7841;m tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng B&#272;S, c&amp;aacute;c doanh nghi&#7879;p ng&amp;agrave;nh VLXD ph&#7843;i &#273;&#7889;i m&#7863;t v&#7899;i v&amp;ocirc; v&amp;agrave;n &amp;aacute;p l&#7921;c. Th&#7889;ng k&amp;ecirc; c&#7911;a Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam (VSA) cho bi&#7871;t t&amp;iacute;nh &#273;&#7871;n gi&#7919;a th&amp;aacute;ng 9, t&#7893;ng l&#432;&#7907;ng t&#7891;n kho c&#7911;a th&amp;eacute;p x&amp;acirc;y d&#7921;ng l&amp;ecirc;n &#273;&#7871;n 400.000 t&#7845;n v&amp;agrave; ph&amp;ocirc;i th&amp;eacute;p 520.000 t&#7845;n, g&#7845;p &#273;&amp;ocirc;i so v&#7899;i m&#7913;c d&#7921; tr&#7919; b&amp;igrave;nh qu&amp;acirc;n h&amp;agrave;ng th&amp;aacute;ng c&#7911;a doanh nghi&#7879;p th&amp;eacute;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n nhi&#7873;u nh&amp;agrave; m&amp;aacute;y th&amp;eacute;p ch&#7881; v&#7853;n h&amp;agrave;nh 50-60% c&amp;ocirc;ng su&#7845;t, th&#7853;m ch&amp;iacute; ng&#7915;ng s&#7843;n xu&#7845;t &#273;&#7875; h&#7841;n ch&#7871; thua l&#7895;. V&#7899;i l&#432;&#7907;ng t&#7891;n kho n&amp;agrave;y, l&amp;atilde;i doanh nghi&#7879;p ph&#7843;i tr&#7843; h&amp;agrave;ng th&amp;aacute;ng g&#7847;n 150 t&#7927; &#273;&#7891;ng. T&#432;&#417;ng t&#7921; doanh nghi&#7879;p s&#7843;n xu&#7845;t xi m&#259;ng c&#361;ng &#273;ang ch&#7853;t v&#7853;t t&amp;igrave;m c&amp;aacute;ch gi&#7843;i ph&amp;oacute;ng h&amp;agrave;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo d&#7921; b&amp;aacute;o t&#7915; nay &#273;&#7871;n cu&#7889;i n&#259;m, kho&#7843;ng 20% doanh nghi&#7879;p th&amp;eacute;p c&amp;oacute; th&#7875; s&#7869; b&#7883; ph&amp;aacute; s&#7843;n. Do s&#7913;c mua gi&#7843;m m&#7841;nh trong 2 th&amp;aacute;ng g&#7847;n &#273;&amp;acirc;y, nhi&#7873;u nh&amp;agrave; m&amp;aacute;y th&amp;eacute;p bu&#7897;c ph&#7843;i c&#7855;t gi&#7843;m c&amp;ocirc;ng su&#7845;t &#273;&amp;aacute;ng k&#7875; v&amp;agrave; c&amp;aacute;c doanh nghi&#7879;p s&#7917; d&#7909;ng c&amp;ocirc;ng ngh&#7879; s&#7843;n xu&#7845;t l&#7841;c h&#7853;u c&amp;oacute; kh&#7843; n&#259;ng b&#7883; ph&amp;aacute; s&#7843;n tr&#432;&#7899;c nh&#7845;t. &#272;i&#7873;u n&amp;agrave;y &#273;&amp;atilde; &#273;&#432;&#7907;c hi&#7879;p h&#7897;i c&#7843;nh b&amp;aacute;o v&amp;agrave;i n&#259;m tr&#432;&#7899;c, b&amp;acirc;y gi&#7901; c&amp;aacute;c doanh nghi&#7879;p n&amp;agrave;y m&#7899;i b&#7855;t &#273;&#7847;u th&#7845;m th&amp;iacute;a khi th&amp;eacute;p l&amp;agrave;m ra b&amp;aacute;n kh&amp;ocirc;ng &#273;&#432;&#7907;c, c&amp;ocirc;ng su&#7845;t c&#7855;t gi&#7843;m &#273;&#7871;n 50%, l&amp;acirc;m v&amp;agrave;o th&#7871; b&amp;iacute; tr&#432;&#7899;c &amp;aacute;p l&#7921;c l&amp;atilde;i su&#7845;t ng&amp;acirc;n h&amp;agrave;ng cao&amp;hellip;(&amp;Ocirc;ng Nguy&#7877;n Ti&#7871;n Ngh&#7883;, Ph&amp;oacute; Ch&#7911; t&#7883;ch VSA)&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo s&#7889; li&#7879;u c&#7911;a T&#7893;ng c&amp;ocirc;ng ty C&amp;ocirc;ng nghi&#7879;p Xi m&#259;ng Vi&#7879;t Nam (Vicem), h&#7871;t th&amp;aacute;ng 9-2011, t&#7893;ng l&#432;&#7907;ng xi m&#259;ng ti&amp;ecirc;u th&#7909; m&#7899;i &#273;&#7841;t 14,53 tri&#7879;u t&#7845;n so v&#7899;i m&#7909;c ti&amp;ecirc;u 20,7 tri&#7879;u t&#7845;n c&#7843; n&#259;m 2011 (ti&amp;ecirc;u th&#7909; n&#7897;i &#273;&#7883;a 19,55 tri&#7879;u t&#7845;n, xu&#7845;t kh&#7849;u 1,15 tri&#7879;u t&#7845;n).&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n nay l&#432;&#7907;ng xi m&#259;ng v&amp;agrave; clinker t&#7891;n kho c&#7911;a Vicem v&amp;agrave;o kho&#7843;ng 2 tri&#7879;u t&#7845;n. Nhi&#7873;u nh&amp;agrave; m&amp;aacute;y s&#7843;n xu&#7845;t th&amp;eacute;p v&amp;agrave; xi m&#259;ng ho&#7841;t &#273;&#7897;ng c&#7847;m ch&#7915;ng, ch&#7881; 20% tr&amp;ecirc;n d&#432;&#7899;i c&amp;ocirc;ng su&#7845;t, th&#7853;m ch&amp;iacute; nhi&#7873;u nh&amp;agrave; m&amp;aacute;y ph&#7843;i t&#7841;m d&#7915;ng s&#7843;n xu&#7845;t d&amp;ugrave; ch&#432;a tuy&amp;ecirc;n b&#7889; ph&amp;aacute; s&#7843;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong th&amp;aacute;ng 8, tr&#7915; 3 doanh nghi&#7879;p l&amp;agrave; Tam &#272;i&#7879;p, B&amp;uacute;t S&#417;n v&amp;agrave; B&#7881;m S&#417;n, c&amp;aacute;c doanh nghi&#7879;p c&amp;ograve;n l&#7841;i thu&#7897;c Vicem &#273;&#7873;u kh&amp;ocirc;ng &#273;&#7841;t ch&#7881; ti&amp;ecirc;u v&#7873; doanh s&#7889; ti&amp;ecirc;u th&#7909;. M&#7897;t s&#7889; doanh nghi&#7879;p th&amp;eacute;p kh&amp;aacute; n&#7893;i ti&#7871;ng nh&#432; V&#7841;n L&#7907;i, Th&amp;eacute;p &#272;&amp;igrave;nh V&#361; (H&#7843;i Ph&amp;ograve;ng), CTCP luy&#7879;n th&amp;eacute;p S&amp;ocirc;ng &#272;&amp;agrave;, CTCP th&amp;eacute;p C&#7917;u Long Vinashin&amp;hellip; c&#361;ng &#273;&#7873;u &#7903; trong t&amp;igrave;nh c&#7843;nh &amp;ldquo;ng&#7855;c ngo&#7843;i&amp;rdquo;, ph&#7843;i t&#7841;m ng&#432;ng s&#7843;n xu&#7845;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c VLXD kh&amp;aacute;c nh&#432; c&amp;aacute;t, s&#7887;i, &#273;&amp;aacute; &#7889;p l&amp;aacute;t, trang tr&amp;iacute; n&#7897;i th&#7845;t, b&amp;ecirc; t&amp;ocirc;ng&amp;hellip; c&#361;ng chung s&#7889; ph&#7853;n. Theo th&#7889;ng k&amp;ecirc; c&#7911;a Hi&#7879;p h&#7897;i G&#7889;m s&#7913; x&amp;acirc;y d&#7921;ng Vi&#7879;t Nam, l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t t&#7891;n kho &#273;&amp;atilde; l&amp;ecirc;n t&#7899;i h&#417;n 30 tri&#7879;u m2 d&amp;ugrave; c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y ch&#7881; ch&#7841;y kho&#7843;ng 70% c&amp;ocirc;ng su&#7845;t. H&#7897;i VLXD Vi&#7879;t Nam c&#361;ng cho bi&#7871;t m&#7897;t s&#7889; nh&amp;agrave; m&amp;aacute;y b&amp;ecirc; t&amp;ocirc;ng t&#7891;n kho l&amp;ecirc;n t&#7899;i h&amp;agrave;ng t&#7927; &#273;&#7891;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Nguy&#7877;n Xu&amp;acirc;n Trung, T&#7893;ng th&#432; k&amp;yacute; Hi&#7879;p h&#7897;i K&amp;iacute;nh Vi&#7879;t Nam, cho bi&#7871;t n&#259;m nay l&#432;&#7907;ng k&amp;iacute;nh t&#7891;n kho g&#7845;p 2 l&#7847;n n&#259;m 2010, gi&amp;aacute; b&amp;aacute;n t&#7909;t gi&#7843;m t&#7899;i 48% trong v&amp;ograve;ng 5 th&amp;aacute;ng tr&#7903; l&#7841;i &#273;&amp;acirc;y. Hi&#7879;p h&#7897;i K&amp;iacute;nh c&amp;oacute; 120 h&#7897;i vi&amp;ecirc;n, ch&#7881; v&amp;agrave;i h&#7897;i vi&amp;ecirc;n c&amp;oacute; l&amp;atilde;i c&amp;ograve;n &#273;a s&#7889; h&amp;ograve;a v&amp;agrave; l&#7895; v&#7889;n. Tr&amp;ecirc;n th&#7921;c t&#7871;, c&amp;aacute;c doanh nghi&#7879;p ng&amp;agrave;nh VLXD &#273;ang tr&#7843;i qua th&#7901;i k&#7923; kh&#7911;ng ho&#7843;ng k&amp;eacute;o d&amp;agrave;i nh&#7845;t t&#7915; tr&#432;&#7899;c &#273;&#7871;n nay.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong nhi&#7873;u th&amp;aacute;ng qua, c&amp;aacute;c c&#7917;a h&amp;agrave;ng kinh doanh VLXD t&#7841;i H&amp;agrave; N&#7897;i, TPHCM lu&amp;ocirc;n trong t&amp;igrave;nh tr&#7841;ng &#7871; &#7849;m. Gi&amp;aacute; VLXD th&#7901;i gian qua h&#7847;u nh&#432; kh&amp;ocirc;ng t&#259;ng nh&#432;ng v&#7851;n r&#7845;t &amp;iacute;t ng&#432;&#7901;i mua. Tuy nhi&amp;ecirc;n, &#273;i&#7873;u &#273;&amp;aacute;ng b&amp;aacute;o &#273;&#7897;ng hi&#7879;n nay l&amp;agrave; c&amp;aacute;c doanh nghi&#7879;p th&amp;eacute;p &#273;ang s&#7917; d&#7909;ng c&amp;ocirc;ng ngh&#7879; s&#7843;n xu&#7845;t l&#7841;c h&#7853;u, &#273;&#7847;u t&#432; manh m&amp;uacute;n, ti&amp;ecirc;u t&#7889;n &#273;i&#7879;n nhi&#7873;u, ch&#7845;t l&#432;&#7907;ng s&#7843;n ph&#7849;m th&#7845;p&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nguy c&#417; hi&#7875;n hi&#7879;n&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&amp;oacute;i v&#7889;n l&amp;agrave; bi&#7875;u hi&#7879;n d&#7877; th&#7845;y &#7903; t&#7845;t c&#7843; doanh nghi&#7879;p VLXD th&#7901;i &#273;i&#7875;m n&amp;agrave;y. C&amp;aacute;ch &#273;&amp;acirc;y v&amp;agrave;i th&amp;aacute;ng, trong cu&#7897;c h&#7885;p v&#7899;i B&#7897; X&amp;acirc;y d&#7921;ng, Ch&#7911; t&#7883;ch H&#272;QT T&#7893;ng c&amp;ocirc;ng ty Xi m&#259;ng Vi&#7879;t Nam, L&amp;ecirc; V&#259;n Chung, t&#7915;ng b&#7913;c x&amp;uacute;c: &amp;ldquo;&#272;i vay l&amp;uacute;c n&amp;agrave;y c&#361;ng ch&#7881; l&amp;agrave; &#273;&#7875; n&#7845;u ch&amp;aacute;o &#259;n qua b&#7919;a. L&#7907;i nhu&#7853;n ch&#7881; v&amp;agrave;i ph&#7847;n tr&#259;m l&amp;agrave;m sao c&amp;oacute; ti&#7873;n &#273;&#7875; tr&#7843; l&amp;atilde;i tr&amp;ecirc;n 20%. Th&#7855;t ch&#7863;t t&amp;iacute;n d&#7909;ng m&amp;agrave; d&#7851;n &#273;&#7871;n s&#7843;n xu&#7845;t &#273;&amp;igrave;nh &#273;&#7889;n th&amp;igrave; kh&amp;ocirc;ng h&#7907;p l&amp;yacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&#7871;u si&#7871;t &#273;&#7871;n m&#7913;c c&amp;aacute;c &#273;&#417;n v&#7883; s&#7843;n xu&#7845;t, kinh doanh &#273;&#7893; b&#7875;, ng&amp;acirc;n h&amp;agrave;ng c&#361;ng kh&amp;ocirc;ng th&#7875; &#273;&amp;ograve;i &#273;&#432;&#7907;c n&#7907;. T&#7893;ng c&amp;ocirc;ng ty Xi m&#259;ng l&amp;agrave; m&#7897;t &#273;&#417;n v&#7883; s&#7843;n xu&#7845;t th&#7921;c s&#7921; nh&#432;ng l&#7907;i nhu&#7853;n ch&#7881; kho&#7843;ng 2-3%. N&#7871;u t&amp;iacute;nh l&#7907;i nhu&#7853;n tr&amp;ecirc;n t&#7893;ng v&#7889;n b&#7887; ra 12.000 t&#7927; &#273;&#7891;ng, coi nh&#432; doanh nghi&#7879;p kh&amp;ocirc;ng c&amp;oacute; l&amp;atilde;i&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, doanh nghi&#7879;p xi m&#259;ng &#273;ang ph&#7843;i &#273;&#7889;i m&#7863;t v&#7899;i s&#7921; c&#7841;nh tranh quy&#7871;t li&#7879;t khi th&#7915;a c&amp;ocirc;ng su&#7845;t. Theo d&#7921; b&amp;aacute;o c&#7911;a &amp;ocirc;ng L&amp;ecirc; V&#259;n Chung, ti&amp;ecirc;u th&#7909; xi m&#259;ng s&#7869; c&amp;ograve;n c&#259;ng th&#7859;ng v&amp;igrave; th&#7901;i gian t&#7899;i ngu&#7891;n cung xi m&#259;ng tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng v&#7851;n ti&#7871;p t&#7909;c t&#259;ng. Ri&amp;ecirc;ng trong n&#259;m 2011 to&amp;agrave;n ng&amp;agrave;nh xi m&#259;ng s&#7869; c&amp;oacute; th&amp;ecirc;m 11 d&#7921; &amp;aacute;n m&#7899;i &#273;&#432;&#7907;c &#273;&#432;a v&amp;agrave;o ho&#7841;t &#273;&#7897;ng, t&#7913;c l&#432;&#7907;ng xi m&#259;ng d&#432; th&#7915;a n&#259;m nay d&#7921; ki&#7871;n 4-5 tri&#7879;u t&#7845;n v&amp;agrave; v&amp;agrave;o n&#259;m 2012 s&#7869; x&#7845;p x&#7881; 8 tri&#7879;u t&#7845;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo d&#7921; b&amp;aacute;o, l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; xi m&#259;ng m&#7895;i n&#259;m t&#259;ng kho&#7843;ng 10%. Nh&#432;ng v&#7899;i t&#7889;c &#273;&#7897; s&#7909;t gi&#7843;m c&#7911;a th&#7883; tr&#432;&#7901;ng nh&#432; hi&#7879;n nay, d&#7921; b&amp;aacute;o n&amp;agrave;y tr&#7903; th&amp;agrave;nh l&#7841;c h&#7853;u. B&#7903;i th&amp;aacute;ng 7-2010 l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; xi m&#259;ng c&#7843; n&#432;&#7899;c h&#417;n 4 tri&#7879;u t&#7845;n, c&amp;ograve;n th&amp;aacute;ng 7-2011 ch&#7881; ti&amp;ecirc;u th&#7909; &#273;&#432;&#7907;c 3,7 tri&#7879;u t&#7845;n, b&#7857;ng 67% so v&#7899;i th&amp;aacute;ng 3 c&amp;ugrave;ng n&#259;m. Kh&amp;oacute; kh&#259;n n&#7919;a c&#7911;a ng&amp;agrave;nh th&amp;eacute;p l&amp;agrave; ng&amp;acirc;n h&amp;agrave;ng r&#7845;t ng&#7841;i cho doanh nghi&#7879;p th&amp;eacute;p vay v&#7889;n. Theo &#273;&amp;oacute;, tr&#432;&#7899;c &#273;&amp;acirc;y doanh nghi&#7879;p vay th&#7871; ch&#7845;p ch&amp;iacute;nh b&#7857;ng h&amp;agrave;ng h&amp;oacute;a nay ng&amp;acirc;n h&amp;agrave;ng y&amp;ecirc;u c&#7847;u ph&#7843;i c&amp;oacute; th&amp;ecirc;m t&amp;agrave;i s&#7843;n kh&amp;aacute;c &#273;&#7843;m b&#7843;o.&lt;/p&gt;\r\n\r\n&lt;p&gt;T&amp;iacute;nh &#273;&#7871;n nay, t&#7893;ng c&amp;ocirc;ng su&#7845;t th&amp;eacute;p x&amp;acirc;y d&#7921;ng c&#7843; n&#432;&#7899;c kho&#7843;ng 9 tri&#7879;u t&#7845;n/n&#259;m. Trong khi &#273;&amp;oacute;, th&amp;aacute;ng 6 v&amp;agrave; th&amp;aacute;ng 7, m&#7913;c ti&amp;ecirc;u th&#7909; th&amp;eacute;p x&amp;acirc;y d&#7921;ng m&#7895;i th&amp;aacute;ng t&#7889;i &#273;a kho&#7843;ng 300.000 t&#7845;n/th&amp;aacute;ng, d&#432;&#7899;i 50% c&amp;ocirc;ng su&#7845;t hi&#7879;n t&#7841;i c&#7911;a to&amp;agrave;n ng&amp;agrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;i&#7873;u n&amp;agrave;y &#273;ang khi&#7871;n nhi&#7873;u doanh nghi&#7879;p th&amp;eacute;p &amp;ldquo;ho&#7843;ng&amp;rdquo; khi d&#7921; ki&#7871;n &#273;&#7871;n n&#259;m 2015, c&#7843; n&#432;&#7899;c ch&#7881; c&#7847;n kho&#7843;ng 15 tri&#7879;u t&#7845;n th&amp;eacute;p v&amp;agrave; n&#259;m 2020 l&amp;agrave; 20 tri&#7879;u t&#7845;n th&amp;eacute;p, trong khi t&#7893;ng c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; c&#7911;a c&amp;aacute;c d&#7921; &amp;aacute;n l&amp;ecirc;n t&#7899;i 26 tri&#7879;u t&#7845;n/n&#259;m, ch&#432;a k&#7875; d&#7921; &amp;aacute;n 5 tri&#7879;u t&#7845;n c&#7911;a T&#7853;p &#273;o&amp;agrave;n Tata (&#7844;n &#272;&#7897;) li&amp;ecirc;n doanh v&#7899;i T&#7893;ng c&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam t&#7841;i H&amp;agrave; T&#297;nh, d&#7921; &amp;aacute;n th&amp;eacute;p t&#7845;m c&amp;aacute;n n&amp;oacute;ng B&amp;agrave; R&#7883;a - V&#361;ng T&amp;agrave;u (T&#7893;ng c&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam mua l&#7841;i c&#7911;a T&#7853;p &#273;o&amp;agrave;n Essar) 2 tri&#7879;u t&#7845;n/n&#259;m&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;333&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/vlxd2.JPG&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;T&#7847;m nh&amp;igrave;n quy ho&#7841;ch?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Kh&amp;ocirc;ng kh&amp;oacute; &#273;&#7875; &amp;ldquo;b&#7855;t b&#7879;nh&amp;rdquo; th&#7883; tr&#432;&#7901;ng VLXD Vi&#7879;t Nam. Theo c&amp;aacute;c chuy&amp;ecirc;n gia, nguy&amp;ecirc;n nh&amp;acirc;n tr&#7921;c ti&#7871;p d&#7851;n &#273;&#7871;n t&amp;igrave;nh tr&#7841;ng tr&amp;ecirc;n do &#273;&#7875; ki&#7873;m ch&#7871; l&#7841;m ph&amp;aacute;t, Ch&amp;iacute;nh ph&#7911; &amp;aacute;p d&#7909;ng nhi&#7873;u bi&#7879;n ph&amp;aacute;p c&#7855;t gi&#7843;m &#273;&#7847;u t&#432; c&amp;ocirc;ng, t&#7841;m d&#7915;ng, ho&amp;atilde;n c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh ch&#432;a th&#7853;t c&#7847;n thi&#7871;t khi&#7871;n th&#7883; tr&#432;&#7901;ng B&#272;S r&#417;i v&amp;agrave;o t&amp;igrave;nh tr&#7841;ng &#273;&amp;oacute;ng b&#259;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, nguy&amp;ecirc;n nh&amp;acirc;n s&amp;acirc;u xa l&amp;agrave; do quy ho&#7841;ch ng&amp;agrave;nh VLXD nh&#432; th&amp;eacute;p, xi m&#259;ng b&#7845;t h&#7907;p l&amp;yacute;. T&amp;igrave;nh tr&#7841;ng c&amp;aacute;c &#273;&#7883;a ph&#432;&#417;ng - d&amp;ugrave; kh&amp;ocirc;ng c&amp;oacute; l&#7907;i th&#7871; - nh&#432;ng v&#7851;n &#273;ua nhau m&#7901;i g&#7885;i &#273;&#7847;u t&#432; x&amp;acirc;y d&#7921;ng nh&amp;agrave; m&amp;aacute;y xi m&#259;ng, th&amp;eacute;p. Theo s&#7889; li&#7879;u c&#7911;a B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng, &#273;&#7871;n nay trong 65 d&#7921; &amp;aacute;n th&amp;eacute;p c&amp;oacute; c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; t&#7915; 100.000 t&#7845;n/n&#259;m tr&#7903; l&amp;ecirc;n, c&amp;oacute; t&#7899;i 32 d&#7921; &amp;aacute;n kh&amp;ocirc;ng thu&#7897;c danh m&#7909;c quy ho&#7841;ch &#273;&amp;atilde; &#273;&#432;&#7907;c c&#7845;p gi&#7845;y ch&#7913;ng nh&#7853;n &#273;&#7847;u t&#432;, t&#7853;p trung &#7903; c&amp;aacute;c t&#7881;nh B&amp;agrave; R&#7883;a - V&#361;ng T&amp;agrave;u, H&#7843;i Ph&amp;ograve;ng, Thanh H&amp;oacute;a, H&#7843;i D&#432;&#417;ng...&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong s&#7889; n&amp;agrave;y c&amp;oacute; h&#417;n 20 d&#7921; &amp;aacute;n l&amp;agrave; &#273;&#7883;a ph&#432;&#417;ng c&#7845;p sai th&#7849;m quy&#7873;n&amp;hellip; C&amp;ograve;n c&amp;acirc;u chuy&#7879;n v&#7873; &amp;ldquo;v&#7905;&amp;rdquo; quy ho&#7841;ch xi m&#259;ng d&#432;&#7901;ng nh&#432; qu&amp;aacute; quen thu&#7897;c, &#273;&amp;atilde; &#273;&#432;&#7907;c &#273;&#7873; c&#7853;p nhi&#7873;u t&#7915; v&amp;agrave;i n&#259;m tr&#432;&#7899;c.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nh&#432; v&#7853;y c&amp;oacute; th&#7875; th&#7845;y, vi&#7879;c ph&amp;aacute; v&#7905; quy ho&#7841;ch VLXD v&amp;agrave; nh&#7919;ng h&#7879; l&#7909;y m&amp;agrave; n&amp;oacute; mang l&#7841;i, &#273;&#7863;c bi&#7879;t v&#7899;i ng&amp;agrave;nh th&amp;eacute;p, xi m&#259;ng r&#7845;t r&amp;otilde; r&amp;agrave;ng. Sau giai &#273;o&#7841;n &amp;ldquo;&#273;&amp;oacute;i g&amp;oacute;p&amp;rdquo;, th&#7901;i &#273;i&#7875;m &amp;ldquo;no d&#7891;n&amp;rdquo; l&#7841;i g&#7863;p ph&#7843;i kh&amp;oacute; kh&#259;n v&#7873; kinh t&#7871; n&amp;ecirc;n c&amp;aacute;c doanh nghi&#7879;p lao &#273;ao. M&#7863;t kh&amp;aacute;c, trong khi t&amp;igrave;nh h&amp;igrave;nh xu&#7845;t kh&#7849;u kh&amp;ocirc;ng m&#7845;y kh&#7843; quan, nhi&#7873;u s&#7843;n ph&#7849;m d&#432; th&#7915;a c&amp;ocirc;ng su&#7845;t g&#7845;p &#273;&amp;ocirc;i so v&#7899;i nhu c&#7847;u nh&#432;ng nhi&#7873;u s&#7843;n ph&#7849;m l&#7841;i ph&#7843;i nh&#7853;p kh&#7849;u v&#7899;i s&#7889; l&#432;&#7907;ng l&#7899;n nh&#432; th&amp;eacute;p t&#7845;m, th&amp;eacute;p cu&#7897;n c&amp;aacute;n n&amp;oacute;ng&amp;hellip; khi&#7871;n cho ng&amp;agrave;nh m&#7845;t c&amp;acirc;n &#273;&#7889;i nghi&amp;ecirc;m tr&#7885;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n doanh nghi&#7879;p ng&amp;agrave;nh th&amp;eacute;p c&#361;ng &#273;ang &#273;&#7889;i di&#7879;n v&#7899;i nguy c&#417; b&#7883; ki&#7879;n ch&#7889;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; t&#7915; Hoa K&#7923;, Th&amp;aacute;i Lan, Indonesia. R&amp;otilde; r&amp;agrave;ng, l&amp;uacute;c n&amp;agrave;y, cu&#7897;c kh&#7911;ng ho&#7843;ng th&#7915;a th&amp;eacute;p, xi m&#259;ng &#273;&amp;atilde; kh&amp;ocirc;ng c&amp;ograve;n &#7903; d&#7841;ng &amp;ldquo;nguy c&#417;&amp;rdquo; m&amp;agrave; &#273;ang hi&#7879;n h&#7919;u, t&amp;aacute;c &#273;&#7897;ng ti&amp;ecirc;u c&#7921;c &#273;&#7871;n ch&amp;iacute;nh c&amp;aacute;c doanh nghi&#7879;p, &#273;&#7849;y ng&amp;agrave;nh th&amp;eacute;p, xi m&#259;ng v&amp;agrave; nhi&#7873;u doanh nghi&#7879;p VLXD kh&amp;aacute;c v&amp;agrave;o t&amp;igrave;nh tr&#7841;ng kh&amp;oacute; kh&#259;n kh&amp;ocirc;ng l&#7889;i tho&amp;aacute;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;M&#7863;c d&amp;ugrave; ch&#432;a tuy&amp;ecirc;n b&#7889; ph&amp;aacute; s&#7843;n nh&#432;ng c&amp;ocirc;ng ty g&#7847;n nh&#432; kh&amp;ocirc;ng ho&#7841;t &#273;&#7897;ng t&#7915; 2 th&amp;aacute;ng nay. Hi&#7879;n l&#432;&#7907;ng th&amp;eacute;p x&amp;acirc;y d&#7921;ng t&#7891;n kho h&amp;agrave;ng ch&#7909;c ngh&amp;igrave;n t&#7845;n, v&#7889;n &#7913; &#7903; &#273;&amp;acirc;y n&amp;ecirc;n kh&amp;ocirc;ng c&amp;oacute; ti&#7873;n &#273;&#7875; tr&#7843; ti&#7873;n &#273;i&#7879;n, ti&#7873;n l&amp;atilde;i&amp;hellip; Ch&amp;uacute;ng t&amp;ocirc;i th&#7921;c s&#7921; ch&#432;a bi&#7871;t xoay ra sao&amp;rdquo;- m&#7897;t doanh nghi&#7879;p th&amp;eacute;p &#7903; H&#7843;i Ph&amp;ograve;ng cho bi&#7871;t. C&#361;ng theo doanh nghi&#7879;p n&amp;agrave;y, n&#7871;u Nh&amp;agrave; n&#432;&#7899;c kh&amp;ocirc;ng s&#7899;m n&#7899;i t&amp;iacute;n d&#7909;ng, kh&amp;ocirc;ng h&#7841;n ch&#7871; nh&#7853;p kh&#7849;u&amp;hellip; &#273;&#7871;n cu&#7889;i n&#259;m nay s&#7869; c&amp;oacute; h&amp;agrave;ng lo&#7841;t doanh nghi&#7879;p ph&amp;aacute; s&#7843;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n c&amp;aacute;c doanh nghi&#7879;p s&#7843;n xu&#7845;t VLXD ch&#7881; c&amp;ograve;n bi&#7871;t tr&amp;ocirc;ng ch&#7901; v&amp;agrave;o d&#7845;u hi&#7879;u kh&#7903;i s&#7855;c c&#7911;a th&#7883; tr&#432;&#7901;ng B&#272;S v&amp;agrave; chi ph&amp;iacute; &#273;&#7847;u v&amp;agrave;o s&#7869; gi&#7843;m b&#7899;t khi c&amp;oacute; ch&#7911; tr&#432;&#417;ng gi&#7843;m l&amp;atilde;i su&#7845;t c&#7911;a ng&amp;acirc;n h&amp;agrave;ng. Tuy nhi&amp;ecirc;n tri&#7875;n v&#7885;ng th&#7883; tr&#432;&#7901;ng c&amp;ograve;n ph&#7909; thu&#7897;c v&amp;agrave;o t&#7889;c &#273;&#7897; t&#259;ng tr&#432;&#7903;ng c&#7911;a n&#7873;n kinh t&#7871; v&amp;agrave; c&amp;aacute;c ch&amp;iacute;nh s&amp;aacute;ch &#273;i&#7873;u 