      &lt;td&gt;913808126 / 086.296 7378&lt;/td&gt;\r\n        &lt;/tr&gt;\r\n        &lt;tr&gt;\r\n            &lt;td&gt;&lt;strong&gt;&#272;&#7883;a ch&#7881;&lt;/strong&gt;&lt;/td&gt;\r\n            &lt;td&gt;122, Nguy&#7877;n H&#7891;ng &#272;&amp;agrave;o, Ph&#432;&#7901;ng 14, Qu&#7853;n T&amp;acirc;n B&amp;igrave;nh, TP H&#7891; Ch&amp;iacute; Minh&lt;/td&gt;\r\n        &lt;/tr&gt;\r\n        &lt;tr&gt;\r\n            &lt;td&gt;&lt;strong&gt;&#272;&#7883;a b&amp;agrave;n, th&#7883; tr&#432;&#7901;ng ph&amp;acirc;n ph&#7889;i&lt;/strong&gt;&lt;/td&gt;\r\n            &lt;td&gt;C&amp;aacute;c t&#7881;nh mi&#7873;n Nam&lt;/td&gt;\r\n        &lt;/tr&gt;\r\n        &lt;tr&gt;\r\n            &lt;td&gt;&lt;strong&gt;Th&#432;&#417;ng hi&#7879;u &#273;ang ph&amp;acirc;n ph&#7889;i&lt;/strong&gt;&lt;/td&gt;\r\n            &lt;td&gt;MIKADO &amp;amp; VINATILE&lt;/td&gt;\r\n        &lt;/tr&gt;\r\n        &lt;tr&gt;\r\n            &lt;td&gt;&lt;strong&gt;Ph&amp;acirc;n ph&#7889;i th&amp;ecirc;m c&#7911;a h&amp;atilde;ng kh&amp;aacute;c&lt;/strong&gt;&lt;/td&gt;\r\n            &lt;td&gt;Kh&amp;ocirc;ng&lt;/td&gt;\r\n        &lt;/tr&gt;\r\n        &lt;tr&gt;\r\n            &lt;td&gt;&lt;strong&gt;M&amp;atilde; s&#7889; thu&#7871;&lt;/strong&gt;&lt;/td&gt;\r\n            &lt;td&gt;0101272578004&lt;/td&gt;\r\n        &lt;/tr&gt;\r\n    &lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p&gt;&lt;font size=&quot;3&quot; class=&quot;Apple-style-span&quot;&gt;&amp;nbsp;&lt;/font&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;font size=&quot;3&quot; class=&quot;Apple-style-span&quot;&gt;&amp;nbsp;&lt;/font&gt;&lt;/p&gt;\r\n&lt;/div&gt;\r\n\r\n','26/10/2011','06/08/2013','',26,7,11978,'huyennt',1,'cn-cong-ty-cp-ky-thuong-thien-hoang-(tp-ha-noi)','&#273;&#7841;i l&yacute;, mikado ceramic, ph&acirc;n ph&#7889;i',NULL,NULL,NULL,'&#273;&#7841;i l&yacute;, mikado ceramic, ph&acirc;n ph&#7889;i',0,0,0,'','','admin',3,'MIKADO',1,0,0,0),(75,'Quy tr&igrave;nh s&#7843;n xu&#7845;t g&#7841;ch Ceramic','Quy tr&igrave;nh s&#7843;n xu&#7845;t g&#7841;ch men c&#7911;a MIKADO &#273;&#432;&#7907;c th&#7921;c hi&#7879;n theo 5 c&ocirc;ng &#273;o&#7841;n ch&iacute;nh, bao g&#7891;m c&ocirc;ng &#273;o&#7841;n chu&#7849;n b&#7883; b&#7897;t x&#432;&#417;ng, c&ocirc;ng &#273;o&#7841;n &eacute;p v&agrave; s&#7845;y g&#7841;ch, c&ocirc;ng &#273;o&#7841;n tr&aacute;ng men, c&ocirc;ng &#273;o&#7841;n nung g&#7841;ch, v&agrave; c&ocirc;ng &#273;o&#7841;n cu&#7889;i l&agrave; ph&acirc;n lo&#7841;i v&agrave; &#273;&oacute;ng g&oacute;i s&#7843;n ph&#7849;m.','&lt;p&gt;&lt;strong&gt;a. Chu&#7849;n b&#7883; b&#7897;t x&#432;&#417;ng:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguy&amp;ecirc;n li&#7879;u th&amp;ocirc; bao g&#7891;m &#273;&#7845;t s&amp;eacute;t v&amp;agrave; tr&amp;agrave;ng th&#7841;ch &#273;&#432;&#7907;c mua v&#7873; Nh&amp;agrave; m&amp;aacute;y theo k&#7871; ho&#7841;ch s&#7843;n xu&#7845;t v&amp;agrave; &#273;&#432;a v&amp;agrave;o kho d&#7921; tr&#7919; theo t&#7915;ng lo&#7841;i ri&amp;ecirc;ng bi&#7879;t tr&amp;ecirc;n c&#417; s&#7903; y&amp;ecirc;u c&#7847;u &#273;&#417;n ph&#7889;i li&#7879;u. Sau &#273;&amp;oacute;, C&amp;aacute;c nguy&amp;ecirc;n li&#7879;u &#273;&#432;&#7907;c c&amp;acirc;n &#273;&#7883;nh l&#432;&#7907;ng theo t&#7915;ng ch&#7911;ng lo&#7841;i, n&#7841;p v&amp;agrave;o ph&#7877;u &#273;&#7883;nh l&#432;&#7907;ng v&amp;agrave; &#273;&#432;&#7907;c chuy&#7875;n v&amp;agrave;o m&amp;aacute;y nghi&#7873;n bi b&#7857;ng h&#7879; th&#7889;ng b&#259;ng t&#7843;i ph&amp;acirc;n ph&#7889;i. T&#7841;i &#273;&amp;acirc;y, c&amp;aacute;c nguy&amp;ecirc;n li&#7879;u &#273;&#432;&#7907;c nghi&#7873;n m&#7883;n, tr&#7897;n l&#7851;n nhau v&amp;agrave; ho&amp;agrave; tr&#7897;n v&#7899;i n&#432;&#7899;c sao cho t&#7841;o n&amp;ecirc;n m&#7897;t h&#7895;n h&#7907;p h&#7891; c&amp;oacute; &#273;&#7897; &#7849;m kho&#7843;ng 36% &#273;&#7897; &#7849;m. R&#7891;i h&#7895;n h&#7907;p h&#7891; n&amp;agrave;y &#273;&#432;&#7907;c x&#7843; v&amp;agrave;o b&#7875; ch&#7913;a c&amp;oacute; m&amp;aacute;y khu&#7845;y. T&#7915; b&#7875; khu&#7845;y, h&#7891; &#273;&#432;&#7907;c l&amp;agrave;m &#273;&#7891;ng nh&#7845;t, qua c&amp;aacute;c c&amp;ocirc;ng &#273;o&#7841;n s&amp;agrave;ng l&#7885;c rung, l&#7885;c s&#7855;t t&#7915; r&#7891;i &#273;&#432;&#7907;c b&#417;m c&#7845;p v&amp;agrave;o l&amp;ograve; s&#7845;y phun. H&#7891; sau khi s&#7845;y phun s&#7869; &#273;&#7841;t &#273;&#432;&#7907;c d&#7841;ng b&#7897;t c&amp;oacute; &#273;&#7897; &#7849;m kho&#7843;ng 6% &#273;&#7897; &#7849;m &#273;&#432;&#7907;c b&#259;ng t&#7843;i v&amp;agrave; g&#7847;u n&amp;acirc;ng &#273;&#432;a v&amp;agrave;o d&#7921; tr&#7919; trong c&amp;aacute;c sil&amp;ocirc; ch&#7913;a.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;b. &amp;Eacute;p v&amp;agrave; s&#7845;y g&#7841;ch:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&#7902; c&amp;ocirc;ng &#273;o&#7841;n n&amp;agrave;y, b&#7897;t &amp;eacute;p &#273;&#432;&#7907;c th&amp;aacute;o ra kh&#7887;i sil&amp;ocirc; t&#7921; &#273;&#7897;ng, &#273;&#432;a qua b&#259;ng t&#7843;i v&amp;agrave; g&#7847;u n&amp;acirc;ng, chuy&#7875;n v&amp;agrave;o ph&#7877;u c&#7911;a m&amp;aacute;y &amp;eacute;p v&amp;agrave; c&#7845;p theo khu&amp;ocirc;n &amp;eacute;p. M&amp;aacute;y &amp;eacute;p v&#7899;i l&#7921;c &amp;eacute;p t&#7889;i &#273;a 2.500 t&#7845;n ho&#7841;t &#273;&#7897;ng t&#7921; &#273;&#7897;ng theo ch&#432;&#417;ng tr&amp;igrave;nh &#273;&amp;atilde; &#273;&#432;&#7907;c c&amp;agrave;i &#273;&#7863;t s&#7861;n. G&#7841;ch sau khi &amp;eacute;p &#273;&#432;&#7907;c n&#7841;y ra kh&#7887;i khu&amp;ocirc;n, r&#7891;i th&#7893;i s&#7841;ch b&#7909;i v&amp;agrave; &#273;&#432;&#7907;c chuy&#7875;n v&amp;agrave;o l&amp;ograve; s&#7845;y &#273;&#7913;ng. G&#7841;ch &#7903; c&amp;ocirc;ng &#273;o&#7841;n n&amp;agrave;y tr&#432;&#7899;c khi &#273;&#432;&#7907;c &#273;&#432;a v&amp;agrave;o s&#7845;y g&#7885;i l&amp;agrave; g&#7841;ch m&#7897;c. &#7902; l&amp;ograve; s&#7845;y &#273;&#7913;ng, g&#7841;ch &#273;&#432;&#7907;c s&#7845;y trung b&amp;igrave;nh kho&#7843;ng 75 ph&amp;uacute;t v&#7899;i nhi&#7879;t &#273;&#7897; s&#7845;y t&#7889;i &#273;a l&amp;agrave; 250oC.&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;&lt;strong&gt;c. Tr&amp;aacute;ng men:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Men &#273;&amp;atilde; &#273;&#432;&#7907;c gia c&amp;ocirc;ng s&#7861;n v&amp;agrave; d&#7921; tr&#7919; trong b&#7875; ch&#7913;a d&amp;ugrave;ng &#273;&#7875; c&#7845;p cho ph&amp;acirc;n x&#432;&#7903;ng tr&amp;aacute;ng men. Khi g&#7841;ch sau khi &#273;&#432;&#7907;c s&#7845;y &#7903; l&amp;ograve; s&#7845;y &#273;&#7913;ng xong, &#273;&#432;&#7907;c theo b&#259;ng chuy&#7873;n d&#7851;n &#273;&#432;a th&#7859;ng v&amp;agrave;o d&amp;acirc;y chuy&#7873;n tr&amp;aacute;ng men, l&amp;agrave;m s&#7841;ch, phun &#7849;m r&#7891;i ph&#7911; men v&amp;agrave; in hoa v&#259;n trang tr&amp;iacute; b&#7857;ng c&amp;aacute;c thi&#7871;t b&#7883; chuy&amp;ecirc;n d&amp;ugrave;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;d. Nung g&#7841;ch &#273;&amp;atilde; tr&amp;aacute;ng men:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;G&#7841;ch sau khi &#273;&amp;atilde; tr&amp;aacute;ng men trang tr&amp;iacute; &#273;&#432;&#7907;c &#273;&#432;a qua m&amp;aacute;y x&#7871;p t&#7843;i &#273;&#432;&#7907;c x&#7871;p l&amp;ecirc;n c&amp;aacute;c xe l&#432;u ch&#7913;a. Sau &#273;&amp;oacute; &#273;&#432;&#7907;c v&#7853;n chuy&#7875;n &#273;&#7871;n m&amp;aacute;y d&#7905; t&#7843;i v&amp;agrave; c&#7845;p v&amp;agrave;o l&amp;ograve; nung thanh l&#259;n. T&#7841;i &#273;&amp;acirc;y, l&amp;ograve; nung thanh l&#259;n, g&#7841;ch &#273;&#432;&#7907;c nung &#7903; nhi&#7879;t &#273;&#7897; t&#7915; 1.150o &amp;ndash; 1.180o C.&lt;/p&gt;\r\n\r\n\r\n\r\n&lt;p&gt;&lt;strong&gt;e. Ph&amp;acirc;n lo&#7841;i v&amp;agrave; &#273;&amp;oacute;ng g&amp;oacute;i s&#7843;n ph&#7849;m:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;G&#7841;ch sau khi nung, qua &#273;&#432;&#7901;ng thanh l&#259;n &#273;&#432;&#7907;c &#273;&#432;a v&amp;agrave;o b&#259;ng chuy&#7873;n ph&amp;acirc;n lo&#7841;i t&#7921; &#273;&#7897;ng, x&#7871;p ch&#7891;ng v&amp;agrave; &#273;&#432;&#7907;c &#273;&amp;oacute;ng g&amp;oacute;i h&#7897;p c&amp;aacute;c t&amp;ocirc;ng, d&amp;aacute;n keo, in nh&amp;atilde;n, b&#7885;c nilon v&amp;agrave; x&#7871;p l&amp;ecirc;n xe n&amp;acirc;ng h&amp;agrave;ng &#273;&#432;a v&amp;agrave;o kho th&amp;agrave;nh ph&#7849;m.&lt;br /&gt;\r\nSau khi d&amp;acirc;y chuy&#7873;n s&#7889; 3 &#273;i v&amp;agrave;o ho&#7841;t &#273;&#7897;ng, C&amp;ocirc;ng ty &#273;&amp;atilde; t&#7853;p trung &#273;i v&amp;agrave;o &#273;&#7847;u t&#432; chi&#7873;u s&amp;acirc;u v&#7873; m&amp;aacute;y m&amp;oacute;c, thi&#7871;t b&#7883;, nh&#7857;m t&#7841;o ra s&#7843;n ph&#7849;m &#273;&#7841;t ch&#7845;t l&#432;&#7907;ng cao, &#273;a d&#7841;ng ho&amp;aacute; v&#7873; k&amp;iacute;ch th&#432;&#7899;c v&amp;agrave; m&amp;agrave;u s&#7855;c s&#7843;n ph&#7849;m. Trong n&#259;m 2007 C&amp;ocirc;ng ty &#273;&amp;atilde; nhanh ch&amp;oacute;ng &#273;&#7847;u t&#432; m&amp;aacute;y in Rotocolor v&amp;agrave; &#273;&#432;a ra th&#7883; tr&#432;&#7901;ng 130 m&#7851;u m&#7899;i t&#259;ng 21% so v&#7899;i n&#259;m tr&#432;&#7899;c, &#273;&amp;aacute;p &#7913;ng k&#7883;p th&#7901;i v&#7873; nhu c&#7847;u th&#7849;m m&#7929; c&#7843; ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng. .&lt;/p&gt;\r\n','26/10/2011','26/10/2011','/home/uploads/image/news/10-2011/quytrinh.jpg',41,13,14627,'admin',1,'quy-trinh-san-xuat-gach-ceramic','quy tr&igrave;nh s&#7843;n xu&#7845;t, mikado ceramic',NULL,NULL,NULL,'quy tr&igrave;nh s&#7843;n xu&#7845;t, mikado ceramic',0,0,0,'','','admin',4,'MIKADO',1,0,0,0),(76,'C&aacute;ch ch&#7885;n g&#7841;ch l&aacute;t n&#7873;n','&#272;&#7889;i v&#7899;i nh&agrave; ph&#7889; c&oacute; m&#7863;t ti&#7873;n d&#432;&#7899;i 5 m, n&ecirc;n ch&#7885;n g&#7841;ch tr&#417;n nh&#7861;n &#273;&#7875; d&#7877; lau r&#7917;a. H&#417;n n&#7919;a, do th&#432;&#7901;ng &#273;i ch&acirc;n tr&#7847;n v&agrave;o nh&agrave;, th&#7853;m ch&iacute; ng&#7891;i, n&#7857;m ngay tr&ecirc;n n&#7873;n n&ecirc;n g&#7841;ch tr&#417;n s&#7869; thu&#7853;n l&#7907;i h&#417;n. M&agrave;u th&igrave; ch&#7885;n t&ocirc;ng s&aacute;ng l&agrave;m ch&#7911; &#273;&#7841;o. C&oacute; th&#7875; d&ugrave;ng m&agrave;u s&#7851;m, nh&#432;ng ch&#7881; &#7903; ph&#7847;n vi&#7873;n.','&lt;p&gt;Nh&amp;agrave; nh&#7887; ch&#7881; n&amp;ecirc;n d&amp;ugrave;ng g&#7841;ch nh&#7887;, t&#7889;i &#273;a 40x40 cm/vi&amp;ecirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Bi&#7879;t th&#7921; hay nh&amp;agrave; c&amp;oacute; di&#7879;n t&amp;iacute;ch r&#7897;ng r&amp;atilde;i, c&amp;oacute; th&#7875; d&amp;ugrave;ng g&#7841;ch &#273;a d&#7841;ng h&#417;n nh&#432; c&amp;aacute;c lo&#7841;i th&amp;ocirc; m&#7897;c, c&amp;oacute; b&#7873; m&#7863;t nh&amp;aacute;m s&#7847;n s&amp;ugrave;i, g&#7841;ch men m&amp;aacute;t, &#273;&amp;aacute; t&#7921; nhi&amp;ecirc;n... K&amp;iacute;ch c&#7905; vi&amp;ecirc;n g&#7841;ch c&#361;ng linh &#273;&#7897;ng t&#7915; nh&#7887; &#273;&#7871;n l&#7899;n, trang tr&#7885;ng h&#417;n th&amp;igrave; d&amp;ugrave;ng g&#7841;ch nh&#7861;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nh&amp;agrave; v&#432;&#7901;n, c&amp;oacute; th&#7875; l&amp;aacute;t nh&#7919;ng lo&#7841;i g&#7841;ch t&#7921; nhi&amp;ecirc;n, th&amp;ocirc; m&#7897;c h&#417;n d&#7841;ng nh&amp;agrave; bi&#7879;t th&#7921; nh&#432; &#273;&amp;aacute; ch&#7867;, g&#7841;ch &#273;inh x&#7871;p &#273;&#7913;ng, b&amp;ecirc; t&amp;ocirc;ng s&#7887;i hay m&#7843;nh g&#7889;m x&#7871;p t&#7841;o h&amp;igrave;nh tr&amp;ecirc;n n&#7873;n xi m&#259;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;L&amp;aacute;t g&#7841;ch theo c&amp;aacute;c khu v&#7921;c ch&#7913;c n&#259;ng&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;ograve;ng kh&amp;aacute;ch c&amp;oacute; th&#7875; l&amp;aacute;t nhi&#7873;u kh&#7893; g&#7841;ch, m&amp;agrave;u x&#7917; l&amp;yacute; phong ph&amp;uacute; gi&#7919;a s&#7855;c &#273;&#7853;m, nh&#7841;t v&amp;igrave; &#273;&amp;acirc;y l&amp;agrave; kh&amp;ocirc;ng gian t&#7841;o &#7845;n tu&#7907;ng. C&amp;oacute; th&#7875; &#273;i&#7875;m h&#7885;a ti&#7871;t b&#7857;ng g&#7841;ch vi&#7873;n, t&#7841;o h&amp;igrave;nh vu&amp;ocirc;ng, tr&amp;ograve;n, m&#7843;ng nh&#7845;n to&amp;agrave;n n&#7873;n ph&amp;ograve;ng hay &#7903; gi&#7919;a v&#7899;i g&#7841;ch c&amp;ugrave;ng ch&#7845;t li&#7879;u ho&#7863;c k&#7871;t h&#7907;p g&#7895;, g&#7889;m...&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;ograve;ng &#259;n, c&amp;oacute; th&#7875; x&amp;acirc;y gi&#7853;t c&#7845;p &#273;&#7875; t&#7841;o s&#7921; kh&amp;aacute;c bi&#7879;t gi&#7919;a hai kh&amp;ocirc;ng gian s&#7917; d&#7909;ng v&amp;agrave; c&amp;oacute; th&#7875; ch&#7885;n m&amp;agrave;u nh&#7841;t h&#417;n cho c&#7843;m gi&amp;aacute;c b&amp;igrave;nh y&amp;ecirc;n. N&#7871;u c&amp;oacute; &#273;i&#7875;m xuy&#7871;t hoa v&#259;n th&amp;igrave; n&amp;ecirc;n ch&#7885;n m&amp;agrave;u s&#7855;c &#273;&#417;n gi&#7843;n, d&#7883;u, kh&amp;ocirc;ng &#273;&#7875; t&#432;&#417;ng ph&#7843;n m&#7841;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;ograve;ng sinh ho&#7841;t chung x&#7917; l&amp;yacute; t&#432;&#417;ng &#273;&#7891;ng nh&#432; ph&amp;ograve;ng &#259;n nh&#432;ng c&amp;oacute; th&#7875; linh ho&#7841;t, bi&#7871;n c&amp;aacute;ch m&#7897;t ch&amp;uacute;t &#273;&#7875; l&amp;agrave;m t&#259;ng s&#7921; &#7845;m c&amp;uacute;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nh&amp;agrave; v&#7879; sinh c&amp;oacute; c&amp;aacute;c lo&#7841;i g&#7841;ch chuy&amp;ecirc;n d&#7909;ng v&#7899;i &#273;&#7897; nh&amp;aacute;m s&#7847;n cao ch&#7889;ng tr&#432;&#7907;t. T&#432;&#417;ng t&#7921;, s&amp;acirc;n th&#432;&#7907;ng, ban c&amp;ocirc;ng tr&amp;aacute;nh d&amp;ugrave;ng g&#7841;ch l&amp;aacute;ng. H&amp;igrave;nh th&#7913;c l&amp;aacute;t &#7903; c&amp;aacute;c v&#7883; tr&amp;iacute; n&amp;agrave;y c&amp;oacute; th&#7875; sinh &#273;&#7897;ng h&#417;n, t&#7841;o h&amp;igrave;nh sin, ch&#7919; U, ch&#7919; C... N&#7871;u d&amp;ugrave;ng g&#7841;ch nung, c&#7847;n &#273;&#7875; m&#7841;ch v&#7919;a to, &#273;&#7911; s&#7913;c b&amp;aacute;m ch&#7855;c v&amp;agrave; ch&#7845;t ch&#7889;ng th&#7845;m. K&amp;iacute;ch th&#432;&#7899;c vi&amp;ecirc;n g&#7841;ch &#273;a d&#7841;ng nh&#432; 30x20, 30x30,15x30 cm...&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;ograve;ng ng&#7911;, n&#7871;u c&amp;oacute; &#273;i&#7873;u ki&#7879;n, c&amp;oacute; th&#7875; s&#7917; d&#7909;ng nguy&amp;ecirc;n li&#7879;u g&#7895; s&amp;agrave;n, th&#7843;m. V&#7899;i g&#7841;ch th&amp;igrave; n&amp;ecirc;n d&amp;ugrave;ng kh&#7893; l&#7899;n h&#417;n 30 cm, h&#7841;n ch&#7871; trang tr&amp;iacute;, &#273;i&#7875;m xuy&#7871;t v&amp;igrave; qu&amp;aacute; nhi&#7873;u v&#7853;t d&#7909;ng trong ph&amp;ograve;ng s&#7869; l&amp;agrave;m r&#7889;i. C&#361;ng c&amp;oacute; th&#7875; l&amp;aacute;t xoay nghi&amp;ecirc;ng vi&amp;ecirc;n g&#7841;ch &#273;&#7875; t&#7841;o s&#7921; m&#7899;i l&#7841;, thay v&amp;igrave; l&amp;aacute;t xu&amp;ocirc;i theo chi&#7873;u vu&amp;ocirc;ng v&#7913;c c&#7911;a ph&amp;ograve;ng. T&amp;ocirc;ng m&amp;agrave;u ph&amp;ugrave; h&#7907;p l&amp;agrave; nh&#7841;t v&amp;agrave; h&#417;i &#7917;ng s&#7855;c n&amp;oacute;ng nh&#432; kem, h&#7891;ng hay v&amp;agrave;ng &#273;&#7845;t... Ch&#7845;t li&#7879;u g&#7841;ch nh&#7861;n b&amp;oacute;ng hay h&#417;i nh&amp;aacute;m &#273;&#7873;u c&amp;oacute; th&#7875; d&amp;ugrave;ng &#273;&#432;&#7907;c.&lt;/p&gt;\r\n','26/10/2011','26/10/2011','',41,13,9243,'admin',1,'cach-chon-gach-lat-nen','c&aacute;ch ch&#7885;n g&#7841;ch l&aacute;t n&#7873;n, mikado ceramic, g&#7841;ch l&aacute;t',NULL,NULL,NULL,'c&aacute;ch ch&#7885;n g&#7841;ch l&aacute;t n&#7873;n, mikado ceramic, g&#7841;ch l&aacute;t',0,0,0,'','','',4,'MIKADO',1,0,0,0),(77,'B&#7855;c Giang: Khuy&#7871;n kh&iacute;ch s&#7843;n xu&#7845;t v&agrave; s&#7917; d&#7909;ng v&#7853;t li&#7879;u x&acirc;y kh&ocirc;ng nung','&#272;&#7875; c&oacute; &#273;&#432;&#7907;c s&#7843;n ph&#7849;m g&#7841;ch ch&#7845;t l&#432;&#7907;ng cao, c&oacute; th&#7875; &#7913;ng d&#7909;ng &#273;&#432;&#7907;c trong c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y d&#7921;ng, nhi&#7873;u ch&#7911; &#273;&#7847;u t&#432; &#273;&atilde; nghi&ecirc;n c&#7913;u, t&igrave;m hi&#7875;u v&agrave; &#273;&#432;a v&agrave;o &#7913;ng d&#7909;ng h&#7879; th&#7889;ng d&acirc;y chuy&#7873;n m&aacute;y m&oacute;c thi&#7871;t b&#7883; hi&#7879;n &#273;&#7841;i, s&#7843;n xu&#7845;t v&#7899;i quy m&ocirc; l&#7899;n s&#7843;n xu&#7845;t GKN.','&lt;p&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\nTr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng VLXD t&#7841;i B&#7855;c Giang, g&#7841;ch kh&amp;ocirc;ng nung (GKN) &#273;&amp;atilde; xu&#7845;t hi&#7879;n t&#7915; kh&amp;aacute; l&amp;acirc;u. &#272;&#7875; c&amp;oacute; &#273;&#432;&#7907;c s&#7843;n ph&#7849;m g&#7841;ch ch&#7845;t l&#432;&#7907;ng cao, c&amp;oacute; th&#7875; &#7913;ng d&#7909;ng &#273;&#432;&#7907;c trong t&#7845;t c&#7843; c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh x&amp;acirc;y d&#7921;ng d&amp;acirc;n d&#7909;ng, c&amp;ocirc;ng nghi&#7879;p, giao th&amp;ocirc;ng, thu&#7927; l&#7907;i&amp;hellip; nhi&#7873;u ch&#7911; &#273;&#7847;u t&#432; &#273;&amp;atilde; nghi&amp;ecirc;n c&#7913;u, t&amp;igrave;m hi&#7875;u v&amp;agrave; &#273;&#432;a v&amp;agrave;o &#7913;ng d&#7909;ng h&#7879; th&#7889;ng d&amp;acirc;y chuy&#7873;n m&amp;aacute;y m&amp;oacute;c thi&#7871;t b&#7883; hi&#7879;n &#273;&#7841;i, s&#7843;n xu&#7845;t v&#7899;i quy m&amp;ocirc; l&#7899;n nh&#432; Cty TNHH B&amp;igrave;nh &#272;&#7883;nh &#273;&amp;atilde; s&#7843;n xu&#7845;t GKN t&#7915; n&#259;m 2007 (c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; t&#7915; 10 - 20 tri&#7879;u vi&amp;ecirc;n/n&#259;m). S&#7843;n ph&#7849;m c&#7911;a Cty &#273;&#432;&#7907;c ti&amp;ecirc;u th&#7909; tr&amp;ecirc;n &#273;&#7883;a b&amp;agrave;n t&#7881;nh v&amp;agrave; c&amp;aacute;c t&#7881;nh l&amp;acirc;n c&#7853;n nh&#432; B&#7855;c Ninh, L&#7841;ng S&#417;n, H&amp;agrave; N&#7897;i.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;386&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/khuyen_khich_sx_GKN.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;(&#7842;nh minh h&#7885;a)&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;Theo l&#7897; tr&amp;igrave;nh xo&amp;aacute; b&#7887; l&amp;ograve; g&#7841;ch th&#7911; c&amp;ocirc;ng quy &#273;&#7883;nh t&#7841;i Quy&#7871;t &#273;&#7883;nh s&#7889; 147/2009/Q&#272;-UBND ng&amp;agrave;y 31/12/2009 c&#7911;a UBND t&#7881;nh B&#7855;c Giang, &#273;&#7871;n th&amp;aacute;ng 3/2012 s&#7869; c&amp;oacute; 7/10 huy&#7879;n, TP ph&#7843;i ch&#7845;m d&#7913;t ho&#7841;t &#273;&#7897;ng s&#7843;n xu&#7845;t g&#7841;ch th&#7911; c&amp;ocirc;ng v&amp;agrave; &#273;&#7871;n h&#7871;t th&amp;aacute;ng 3/2013, tr&amp;ecirc;n to&amp;agrave;n t&#7881;nh kh&amp;ocirc;ng c&amp;ograve;n ho&#7841;t &#273;&#7897;ng s&#7843;n xu&#7845;t g&#7841;ch th&#7911; c&amp;ocirc;ng. Nh&#432; v&#7853;y s&#7869; gi&#7843;m kho&#7843;ng 50% s&#7843;n l&#432;&#7907;ng g&#7841;ch nung hi&#7879;n c&amp;oacute; tr&amp;ecirc;n &#273;&#7883;a b&amp;agrave;n t&#7881;nh, l&amp;agrave;m &#7843;nh h&#432;&#7903;ng nhi&#7873;u &#273;&#7871;n kh&#7843; n&#259;ng cung &#7913;ng g&#7841;ch x&amp;acirc;y cho c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh. V&amp;igrave; v&#7853;y, chi&#7871;n l&#432;&#7907;c ph&amp;aacute;t tri&#7875;n s&#7843;n xu&#7845;t GKN &#273;ang &#273;&#432;&#7907;c khuy&#7871;n kh&amp;iacute;ch &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t theo h&#432;&#7899;ng c&amp;ocirc;ng ngh&#7879; hi&#7879;n &#273;&#7841;i, quy m&amp;ocirc; l&#7899;n, k&amp;iacute;ch th&#432;&#7899;c l&#7899;n, nh&#7865; &#273;&#7875; thay th&#7871; d&#7847;n g&#7841;ch &#273;&#7845;t s&amp;eacute;t nung.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7875; c&amp;oacute; h&#432;&#7899;ng ph&amp;aacute;t tri&#7875;n t&#7889;t cho s&#7843;n ph&#7849;m GKN, S&#7903; X&amp;acirc;y d&#7921;ng B&#7855;c Giang &#273;ang ti&#7871;p t&#7909;c t&#7893; ch&#7913;c t&#7889;t c&amp;ocirc;ng t&amp;aacute;c tuy&amp;ecirc;n truy&#7873;n, qu&amp;aacute;n tri&#7879;t c&amp;aacute;c ch&#7911; th&#7875; trong ho&#7841;t &#273;&#7897;ng x&amp;acirc;y d&#7921;ng v&amp;agrave; ng&#432;&#7901;i d&amp;acirc;n th&#7845;y r&amp;otilde; nh&#7919;ng &#432;u &#273;i&#7875;m, l&#7907;i th&#7871; trong vi&#7879;c s&#7917; d&#7909;ng GKN; &#432;u ti&amp;ecirc;n s&#7917; d&#7909;ng GKN v&amp;agrave;o c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh x&amp;acirc;y d&#7921;ng; khuy&#7871;n kh&amp;iacute;ch c&amp;aacute;c th&amp;agrave;nh ph&#7847;n kinh t&#7871; tham gia v&amp;agrave;o s&#7843;n xu&#7845;t, s&#7917; d&#7909;ng v&amp;agrave; kinh doanh c&amp;aacute;c lo&#7841;i v&#7853;t li&#7879;u x&amp;acirc;y, l&#7907;p kh&amp;ocirc;ng nung&amp;hellip;&lt;/p&gt;','28/10/2011','28/10/2011','/home/uploads/image/news/10-2011/khuyen_khich_sx_GKN.jpg',23,2,2450,'huyennt',1,'bac-giang-khuyen-khich-san-xuat-va-su-dung-vat-lieu-xay-khong-nung','g&#7841;ch kh&ocirc;ng nung, khuy&#7871;n kh&iacute;ch, s&#7843;n xu&#7845;t, VLXD, mikado ceramic',NULL,NULL,NULL,'g&#7841;ch kh&ocirc;ng nung, khuy&#7871;n kh&iacute;ch, s&#7843;n xu&#7845;t, VLXD, mikado ceramic',0,0,0,'','','',3,'diaoconline.vn',1,0,0,0),(78,'H&agrave; N&#7897;i s&#7869; di d&#7901;i h&agrave;ng lo&#7841;t c&#417; s&#7903; s&#7843;n xu&#7845;t VLXD','Ng&agrave;y 28-10, S&#7903; X&acirc;y d&#7921;ng H&agrave; N&#7897;i &#273;&atilde; c&ocirc;ng b&#7889; Quy ho&#7841;ch ph&aacute;t tri&#7875;n v&#7853;t li&#7879;u x&acirc;y d&#7921;ng (VLXD) TP H&agrave; N&#7897;i &#273;&#7871;n n&#259;m 2020, &#273;&#7883;nh h&#432;&#7899;ng &#273;&#7871;n n&#259;m 2030.','\r\n\r\n&lt;p&gt;&#272;&amp;acirc;y l&amp;agrave; quy ho&#7841;ch chuy&amp;ecirc;n ng&amp;agrave;nh &#273;&#7847;u ti&amp;ecirc;n &#273;&#432;&#7907;c ph&amp;ecirc; duy&#7879;t v&amp;agrave; c&amp;ocirc;ng b&#7889; sau khi c&amp;oacute; Quy ho&#7841;ch chung x&amp;acirc;y d&#7921;ng Th&#7911; &#273;&amp;ocirc;.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nTheo &#273;&amp;oacute;, trong giai &#273;o&#7841;n &#273;&#7871;n n&#259;m 2020, s&#7843;n xu&#7845;t VLXD c&#7911;a H&amp;agrave; N&#7897;i s&#7869; l&amp;agrave; ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p th&amp;acirc;n thi&#7879;n m&amp;ocirc;i tr&#432;&#7901;ng. C&amp;ocirc;ng ngh&#7879; s&#7843;n xu&#7845;t theo ph&#432;&#417;ng ph&amp;aacute;p truy&#7873;n th&#7889;ng ti&amp;ecirc;u t&#7889;n nhi&amp;ecirc;u li&#7879;u, hi&#7879;u qu&#7843; kinh t&#7871; th&#7845;p, g&amp;acirc;y &amp;ocirc; nhi&#7877;m m&amp;ocirc;i tr&#432;&#7901;ng s&#7869; ph&#7843;i x&#7917; l&amp;yacute;, lo&#7841;i b&#7887; d&#7847;n. Ri&amp;ecirc;ng &#273;&#7889;i v&#7899;i g&#7841;ch th&#7911; c&amp;ocirc;ng, ng&#7915;ng s&#7843;n xu&#7845;t v&amp;agrave;o n&#259;m 2012. D&#7921; ki&#7871;n, t&#7893;ng v&#7889;n &#273;&#7847;u t&#432; cho c&amp;aacute;c d&#7921; &amp;aacute;n s&#7843;n xu&#7845;t VLXD trong giai &#273;o&#7841;n t&#7915; nay &#273;&#7871;n n&#259;m 2015 l&amp;agrave; h&#417;n 9.400 t&#7927; &#273;&#7891;ng, n&#259;m 2016-2020 l&amp;agrave; h&#417;n 3.000 t&#7927; &#273;&#7891;ng.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nQuy ho&#7841;ch &#273;&amp;atilde; &#273;&#432;a ra ph&#432;&#417;ng h&#432;&#7899;ng di d&#7901;i m&#7897;t s&#7889; c&#417; s&#7903; s&#7843;n xu&#7845;t trong giai &#273;o&#7841;n t&#7915; nay &#273;&#7871;n n&#259;m 2015 g&#7891;m 12 &#273;&#417;n v&#7883; trong &#273;&amp;oacute; c&amp;oacute; c&amp;aacute;c c&amp;ocirc;ng ty: CP g&#7841;ch ng&amp;oacute;i Th&#7841;ch B&amp;agrave;n, CP B&amp;ecirc; t&amp;ocirc;ng v&amp;agrave; x&amp;acirc;y d&#7921;ng Th&#7883;nh Li&#7879;t&amp;hellip; Giai &#273;o&#7841;n 2016-2020 di chuy&#7875;n 5 c&#417; s&#7903;, trong &#273;&amp;oacute; c&amp;oacute; c&amp;aacute;c c&amp;ocirc;ng ty: CP Vi&#7879;t M&#7929;, CP Viglacera &#272;&amp;ocirc;ng Anh&amp;hellip;&lt;/p&gt;\r\n','29/10/2011','29/10/2011','',23,2,1737,'huyennt',1,'ha-noi-se-di-doi-hang-loat-co-so-san-xuat-vlxd','VLXD, &#273;&#7883;nh h&#432;&#7899;ng. x&acirc;y d&#7921;ng, ph&aacute;t tri&#7875;n',NULL,NULL,NULL,'VLXD, &#273;&#7883;nh h&#432;&#7899;ng. x&acirc;y d&#7921;ng, ph&aacute;t tri&#7875;n',0,0,0,'','','',3,'hanoimoi.com.vn',1,0,0,1),(79,'H&agrave; N&#7897;i: C&ocirc;ng b&#7889; quy ho&#7841;ch ph&aacute;t tri&#7875;n VLXD &#273;&#7871;n 2030','Quy ho&#7841;ch chuy&ecirc;n ngh&agrave;nh &#273;&#7847;u ti&ecirc;n &#273;&#432;&#7907;c ph&ecirc; duy&#7879;t v&agrave; c&ocirc;ng b&#7889; sau khi c&oacute; Quy ho&#7841;ch x&acirc;y d&#7921;ng Th&#7911; &#273;&ocirc;.','\r\n&lt;p&gt;S&#7903; X&amp;acirc;y d&#7921;ng H&amp;agrave; N&#7897;i v&#7915;a c&amp;ocirc;ng b&#7889; Quy ho&#7841;ch ph&amp;aacute;t tri&#7875;n v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng (VLXD) th&amp;agrave;nh ph&#7889; &#273;&#7871;n n&#259;m 2020, &#273;&#7883;nh h&#432;&#7899;ng &#273;&#7871;n n&#259;m 2030. &#272;&amp;acirc;y c&#361;ng l&amp;agrave; quy ho&#7841;ch chuy&amp;ecirc;n ng&amp;agrave;nh &#273;&#7847;u ti&amp;ecirc;n &#273;&#432;&#7907;c ph&amp;ecirc; duy&#7879;t v&amp;agrave; c&amp;ocirc;ng b&#7889; sau khi c&amp;oacute; Quy ho&#7841;ch chung x&amp;acirc;y d&#7921;ng Th&#7911; &#273;&amp;ocirc;.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nTheo quy ho&#7841;ch, giai &#273;o&#7841;n &#273;&#7871;n n&#259;m 2020, c&amp;ocirc;ng nghi&#7879;p s&#7843;n xu&#7845;t VLXD c&#7911;a H&amp;agrave; N&#7897;i ph&#7843;i &#273;&#7843;m b&#7843;o l&amp;agrave; ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p th&amp;acirc;n thi&#7879;n m&amp;ocirc;i tr&#432;&#7901;ng, h&#432;&#7899;ng t&#7899;i n&#7873;n c&amp;ocirc;ng nghi&#7879;p xanh, trong &#273;&amp;oacute; &#273;&#7849;y m&#7841;nh s&#7843;n xu&#7845;t c&amp;aacute;c lo&#7841;i VLXD kh&amp;ocirc;ng nung, x&#7917; l&amp;yacute; v&amp;agrave; lo&#7841;i b&#7887; d&#7847;n c&amp;aacute;c c&amp;ocirc;ng ngh&#7879; s&#7843;n xu&#7845;t theo ph&#432;&#417;ng ph&amp;aacute;p truy&#7873;n th&#7889;ng ti&amp;ecirc;u t&#7889;n nhi&amp;ecirc;u li&#7879;u, hi&#7879;u qu&#7843; kinh t&#7871; th&#7845;p, g&amp;acirc;y &amp;ocirc; nhi&#7877;m m&amp;ocirc;i tr&#432;&#7901;ng. &#272;&#7889;i v&#7899;i g&#7841;ch th&#7911; c&amp;ocirc;ng, ng&#7915;ng s&#7843;n xu&#7845;t v&amp;agrave;o n&#259;m 2012. Quy ho&#7841;ch c&#361;ng &#273;&#7863;t m&#7909;c ti&amp;ecirc;u trong giai &#273;o&#7841;n &#273;&#7871;n 2015, t&#7889;c &#273;&#7897; t&#259;ng gia str&#7883; s&#7843;n xu&#7845;t c&amp;ocirc;ng nghi&#7879;p VLXD &#273;&#7841;t 17%/n&#259;m, giai &#273;o&#7841;n 2016-2020 &#273;&#7841;t 16%. &#272;&#7871;n 2020, ng&amp;agrave;nh n&amp;agrave;y s&#7869; &#273;&amp;oacute;ng g&amp;oacute;p cho ng&amp;acirc;n s&amp;aacute;ch TP kho&#7843;ng 3.800 t&#7927; &#273;&#7891;ng. &#272;&#7875; th&#7921;c hi&#7879;n nh&#7919;ng d&#7921; &amp;aacute;n &#273;&#7847;u t&#432; theo quy ho&#7841;ch, d&#7921; ki&#7871;n t&#7893;ng v&#7889;n &#273;&#7847;u t&#432; cho ph&amp;aacute;t tri&#7875;n c&amp;ocirc;ng nghi&#7879;p VLXD trong giai &#273;o&#7841;n t&#7915; nay &#273;&#7871;n n&#259;m 2015 l&amp;agrave; h&#417;n 9.400 t&#7927; &#273;&#7891;ng, t&#7915; 2016-2020 l&amp;agrave; h&#417;n 3.000 t&#7927; &#273;&#7891;ng.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;417&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/vat_lieu_xay_dung_khong_nung.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;V&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng kh&amp;ocirc;ng nung&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Quy ho&#7841;ch c&#361;ng &#273;&amp;atilde; &#273;&#432;a ra ph&#432;&#417;ng h&#432;&#7899;ng di d&#7901;i m&#7897;t s&#7889; c&#417; s&#7903; s&#7843;n xu&#7845;t trong giai &#273;o&#7841;n t&#7915; nay &#273;&#7871;n 2015 g&#7891;m 12 &#273;&#417;n v&#7883; trong &#273;&amp;oacute; c&amp;oacute; C&amp;ocirc;ng ty CP g&#7841;ch ng&amp;oacute;i Th&#7841;c B&amp;agrave;n, C&amp;ocirc;ng ty CP b&amp;ecirc; t&amp;ocirc;ng v&amp;agrave; x&amp;acirc;y d&#7921;ng Th&#7883;nh Li&#7879;t&amp;hellip; Trong giai &#273;o&#7841;n 2016-2020 di chuy&#7875;n 5 c&#417; s&#7903;, trong &#273;&amp;oacute; c&amp;oacute; C&amp;ocirc;ng ty CP Vi&#7879;t M&#7929;, C&amp;ocirc;ng ty CP Viglacera &#272;&amp;ocirc;ng Anh&amp;hellip;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nVi&#7879;c c&amp;ocirc;ng b&#7889; quy ho&#7841;ch s&#7869; l&amp;agrave; c&#259;n c&#7913; cho c&amp;aacute;c ng&amp;agrave;nh, c&amp;aacute;c nh&amp;agrave; &#273;&#7847;u t&#432;, c&amp;aacute;c DN s&#7843;n xu&#7845;t VLXD c&#7911;a TP l&#7853;p c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&#7847;u t&#432;, chu&#7849;n b&#7883; l&#7921;c l&#432;&#7907;ng, t&amp;igrave;m ngu&#7891;n v&#7889;n &#273;&#7875; tri&#7875;n khai c&amp;aacute;c d&#7921; &amp;aacute;n ph&amp;ugrave; h&#7907;p v&amp;agrave; hi&#7879;u qu&#7843;, &#273;&#7891;ng th&#7901;i n&amp;acirc;ng cao c&amp;ocirc;ng t&amp;aacute;c qu&#7843;n l&amp;yacute; Nh&amp;agrave; n&#432;&#7899;c trong l&#297;nh v&#7921;c n&amp;agrave;y.&lt;/p&gt;','29/10/2011','29/10/2011','/home/uploads/image/news/10-2011/vat_lieu_xay_dung_khong_nung.jpg',23,2,1750,'huyennt',1,'ha-noi-cong-bo-quy-hoach-phat-trien-vlxd-den-2030','g&#7841;ch kh&ocirc;ng nung, quy ho&#7841;ch, mikadoceramic, VLXD, ph&aacute;t tri&#7875;n',NULL,NULL,NULL,'g&#7841;ch kh&ocirc;ng nung, quy ho&#7841;ch, mikadoceramic, VLXD, ph&aacute;t tri&#7875;n',0,0,0,'','','',3,'',1,0,0,0),(80,'Th&#7883; tr&#432;&#7901;ng th&eacute;p &#273;ang &hellip; r&#7889;i lo&#7841;n','&ldquo;&#272;&atilde; c&oacute; hi&#7879;n t&#432;&#7907;ng c&aacute;c doanh nghi&#7879;p ch&#7883;u l&#7895;, h&#7841; gi&aacute; b&aacute;n b&#7857;ng c&aacute;ch t&#259;ng m&#7913;c chi&#7871;t kh&#7845;u, gi&#7843;m c&#432;&#7899;c v&#7853;n t&#7843;i, cho &#273;&#7841;i l&yacute; tr&#7843; ch&#7853;m &#273;&#7875; ti&ecirc;u th&#7909; &#273;&#432;&#7907;c h&agrave;ng&hellip; &#273;i&#7873;u n&agrave;y &#273;ang g&acirc;y r&#7889;i lo&#7841;n th&#7883; tr&#432;&#7901;ng.&rdquo; - Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i th&eacute;p Vi&#7879;t Nam l&ecirc;n ti&#7871;ng.','\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;500&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/thep_ton.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;Th&amp;eacute;p t&#7891;n kho &#273;ang ch&#7901; ti&amp;ecirc;u th&#7909;&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left; &quot;&gt;&lt;br /&gt;\r\n&lt;strong&gt;Doanh nghi&#7879;p ki&#7879;n nhau b&amp;aacute;n ph&amp;aacute; gi&amp;aacute;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Chi&#7873;u 27/10, B&#7897; C&amp;ocirc;ng th&#432;&#417;ng &#273;&amp;atilde; t&#7893; ch&#7913;c h&#7897;i ngh&#7883; t&amp;igrave;m gi&#7843;i ph&amp;aacute;p th&amp;aacute;o g&#7905; kh&amp;oacute; kh&#259;n cho ng&amp;agrave;nh th&amp;eacute;p. T&#7841;i h&#7897;i ngh&#7883;, &amp;ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng, Ch&#7911; t&#7883;ch Hi&#7879;p th&#7897;i Th&amp;eacute;p Vi&#7879;t Nam (VSA) cho bi&#7871;t, trong 10 th&amp;aacute;ng qua, s&#7843;n xu&#7845;t c&#7911;a ng&amp;agrave;nh th&amp;eacute;p tuy gi&#7919; &#7893;n &#273;&#7883;nh nh&#432;ng hi&#7879;u qu&#7843; th&#7845;p, t&#7891;n kho t&#259;ng cao, ti&amp;ecirc;u th&#7909; ng&amp;agrave;y c&amp;agrave;ng ch&#7853;m, &#7843;nh h&#432;&#7903;ng nghi&amp;ecirc;m tr&#7885;ng t&#7899;i &#273;&#7901;i s&#7889;ng c&amp;ocirc;ng nh&amp;acirc;n v&amp;agrave; hi&#7879;u qu&#7843; kinh doanh c&#7911;a c&amp;aacute;c doanh nghi&#7879;p ng&amp;agrave;nh th&amp;eacute;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&#7871;u nh&#432; nh&#7919;ng n&#259;m tr&#432;&#7899;c, m&#7913;c ti&amp;ecirc;u th&#7909; kh&amp;aacute;, t&#7891;n kho c&#7911;a DN th&amp;eacute;p ch&#7881; &#7903; m&#7913;c 250 &amp;ndash; 300 ngh&amp;igrave;n t&#7845;n, nay &#273;&amp;atilde; l&amp;ecirc;n t&#7899;i h&#417;n 500 ngh&amp;igrave;n t&#7845;n, m&#7913;c r&#7845;t &#273;&amp;aacute;ng lo ng&#7841;i trong b&#7889;i c&#7843;nh l&amp;atilde;i su&#7845;t &#7903; m&#7913;c cao v&amp;agrave; doanh nghi&#7879;p th&amp;eacute;p ph&#7843;i vay v&#7889;n ng&amp;acirc;n h&amp;agrave;ng r&#7845;t l&#7899;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng C&#432;&#7901;ng c&#361;ng &#273;&#432;a ra d&#7851;n ch&#7913;ng v&#7873; gi&amp;aacute; s&#7843;n xu&#7845;t th&amp;eacute;p &#273;ang &#7903; m&#7913;c b&amp;aacute;o &#273;&#7897;ng. C&#7909; th&#7875; ph&amp;ocirc;i th&amp;eacute;p hi&#7879;n &#7903; m&#7913;c 14 tri&#7879;u &#273;&#7891;ng/t&#7845;n, chi ph&amp;iacute; s&#7843;n xu&#7845;t (t&#7915; ph&amp;ocirc;i c&amp;aacute;n ra th&amp;agrave;nh ph&#7849;m) kho&#7843;ng 1,5 tri&#7879;u &#273;&#7891;ng/t&#7845;ng. Nh&#432;ng &#273;&#7875; b&amp;aacute;n &#273;&#432;&#7907;c tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng 15,5 tri&#7879;u &#273;&#7891;ng/t&#7845;n l&amp;agrave; r&#7845;t kh&amp;oacute; kh&#259;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&amp;ldquo;&#272;&amp;atilde; c&amp;oacute; hi&#7879;n t&#432;&#7907;ng c&amp;aacute;c doanh nghi&#7879;p ch&#7883;u l&#7895;, h&#7841; gi&amp;aacute; b&amp;aacute;n b&#7857;ng c&amp;aacute;ch t&#259;ng m&#7913;c chi&#7871;t kh&#7845;u, gi&#7843;m c&#432;&#7899;c v&#7853;n t&#7843;i, cho &#273;&#7841;i l&amp;yacute; tr&#7843; ch&#7853;m &#273;&#7875; ti&amp;ecirc;u th&#7909; &#273;&#432;&#7907;c h&amp;agrave;ng&amp;hellip; &#273;i&#7873;u n&amp;agrave;y &#273;ang r&amp;acirc;y r&#7889;i lo&#7841;n th&#7883; tr&#432;&#7901;ng&lt;/em&gt;.&amp;rdquo; - &amp;ocirc;ng C&#432;&#7901;ng n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nghi&amp;ecirc;m tr&#7885;ng h&#417;n, vi&#7879;c n&amp;agrave;y &#273;&#432;&#7907;c VSA nh&#7853;n &#273;&#7883;nh c&amp;oacute; th&#7875; s&#7869; l&amp;agrave; m&#7847;m m&#7889;ng cho c&amp;aacute;c v&#7909; ki&#7879;n ch&#7889;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; trong th&#7901;i gian t&#7899;i khi m&amp;agrave; ngay trong ch&amp;iacute;nh n&#7897;i b&#7897; Hi&#7879;p h&#7897;i &#273;&amp;atilde; c&amp;oacute; t&amp;igrave;nh tr&#7841;ng ki&#7879;n c&amp;aacute;o cho l&amp;agrave; b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; gi&#7919;a T&#7893;ng c&amp;ocirc;ng ty th&amp;eacute;p Vi&#7879;t Nam v&amp;agrave; Posco Vi&#7879;t Nam.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7873; ph&amp;iacute;a doanh nghi&#7879;p, &#273;&#7841;i di&#7879;n c&amp;ocirc;ng ty Posco Vi&#7879;t Nam cho r&#7857;ng, tr&#432;&#7899;c ch&#7911; tr&#432;&#417;ng gi&#7843;m thi&#7875;u c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&#7847;u t&#432; nh&#7857;m ki&#7873;m ch&#7871; l&#7841;m ph&amp;aacute;t c&#7911;a Ch&amp;iacute;nh ph&#7911;, c&amp;aacute;c doanh nghi&#7879;p c&#7889; g&#7855;ng xu&#7845;t kh&#7849;u ra c&amp;aacute;c th&#7883; tr&#432;&#7901;ng n&#432;&#7899;c ngo&amp;agrave;i l&amp;agrave; &#273;i&#7873;u t&#7845;t y&#7871;u.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, vi&#7879;c xu&#7845;t kh&#7849;u hi&#7879;n nay c&#7911;a c&amp;aacute;c doanh nghi&#7879;p c&#361;ng g&#7863;p kh&amp;ocirc;ng &amp;iacute;t kh&amp;oacute; kh&#259;n do ch&amp;iacute;nh s&amp;aacute;ch b&#7843;o h&#7897; ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p c&#7911;a c&amp;aacute;c th&#7883; tr&#432;&#7901;ng s&#7903; t&#7841;i. &#272;i&#7875;n h&amp;igrave;nh nh&#432; ngay trong khu v&#7921;c ASEAN l&amp;agrave; Th&amp;aacute;i Lan, Indonesia&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;Trong khi &#273;&amp;oacute;, t&#7841;i Vi&#7879;t Nam, Ch&amp;iacute;nh ph&#7911; l&#7841;i ch&#432;a xem x&amp;eacute;t v&#7873; thi&#7871;t l&#7853;p h&amp;agrave;ng r&amp;agrave;o k&#7929; thu&#7853;t &#273;&#7875; ng&#259;n ch&#7863;n t&amp;igrave;nh tr&#7841;ng nh&#7853;p kh&#7849;u th&amp;eacute;p ch&#7845;t l&#432;&#7907;ng k&amp;eacute;m v&amp;agrave;o Vi&#7879;t Nam.&amp;rdquo; &amp;ndash; v&#7883; &#273;&#7841;i di&#7879;n n&amp;agrave;y n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&amp;aacute;ng ch&amp;uacute; &amp;yacute;, &amp;ocirc;ng Tr&#7847;n Tu&#7845;n D&#432;&#417;ng, T&#7893;ng Gi&amp;aacute;m &#273;&#7889;c T&#7853;p &#273;o&amp;agrave;n H&amp;ograve;a Ph&amp;aacute;t c&amp;ograve;n c&#7843;nh b&amp;aacute;o, kh&amp;ocirc;ng ch&#7881; ti&amp;ecirc;u th&#7909; th&amp;eacute;p trong n&#432;&#7899;c gi&#7843;m, suy tho&amp;aacute;i kinh t&#7871; th&#7871; gi&#7899;i &#273;ang tr&#7903; l&#7841;i khi&#7871;n nhu c&#7847;u th&amp;eacute;p tr&amp;ecirc;n th&#7871; gi&#7899;i gi&#7843;m, k&amp;eacute;o gi&amp;aacute; th&amp;eacute;p gi&#7843;m. Nguy c&#417; nh&amp;igrave;n th&#7845;y l&amp;agrave; th&amp;eacute;p ngo&#7841;i c&amp;oacute; th&#7875; tr&amp;agrave;n v&amp;agrave;o &#273;&#7849;y c&amp;aacute;c DN th&amp;eacute;p trong n&#432;&#7899;c v&amp;agrave;o t&amp;igrave;nh th&#7871; kh&amp;oacute; kh&#259;n h&#417;n do ph&#7843;i c&#7841;nh tranh v&#7899;i th&amp;eacute;p ngo&#7841;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Doanh nghi&#7879;p ph&#7843;i t&#7921; c&#7913;u m&amp;igrave;nh&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo d&#7921; b&amp;aacute;o c&#7911;a c&#7843; l&amp;atilde;nh &#273;&#7841;o V&#7909; C&amp;ocirc;ng nghi&#7879;p n&#7863;ng v&amp;agrave; V&#7909; th&#7883; tr&#432;&#7901;ng trong n&#432;&#7899;c c&#7911;a B&#7897; C&amp;ocirc;ng th&#432;&#417;ng th&amp;igrave;, n&#259;m nay &#273;&amp;atilde; kh&amp;oacute; kh&#259;n nh&#432;ng n&#259;m sau c&amp;ograve;n ti&#7871;p t&#7909;c kh&amp;oacute; kh&#259;n h&#417;n v&amp;igrave; Ch&amp;iacute;nh ph&#7911; ti&#7871;p t&#7909;c th&#7855;t ch&#7863;t t&amp;iacute;n d&#7909;ng, t&#259;ng tr&#432;&#7903;ng GDP n&#259;m 2012 d&#7921; ki&#7871;n s&#7869; ch&#7881; kho&#7843;ng t&#7915; 6 - 6,5% &#273;&#7875; ki&#7875;m so&amp;aacute;t CPI v&#7873; d&#432;&#7899;i 1 con s&#7889;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong b&#7889;i c&#7843;nh s&#7913;c c&#7847;u chung s&#7909;t gi&#7843;m, ng&amp;agrave;nh th&amp;eacute;p kh&amp;ocirc;ng th&#7875; &#273;&#7913;ng ngo&amp;agrave;i l&#7873;. Ch&amp;iacute;nh v&amp;igrave; v&#7853;y, &#273;&#7875; &#273;&#7889;i m&#7863;t v&#7899;i kh&amp;oacute; kh&#259;n n&amp;agrave;y, c&amp;aacute;c doanh nghi&#7879;p ph&#7843;i n&#7895; l&#7921;c t&#7921; c&#7913;u m&amp;igrave;nh b&#7857;ng vi&#7879;c c&#7855;t gi&#7843;m chi ph&amp;iacute;, h&#7841; gi&amp;aacute; th&amp;agrave;nh &#273;&#7875; c&#7841;nh tranh.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7845;n &#273;&#7873; c&#361;ng &#273;&amp;atilde; &#273;&#432;&#7907;c c&amp;aacute;c doanh nghi&#7879;p x&amp;aacute;c &#273;&#7883;nh. L&amp;atilde;nh &#273;&#7841;o t&#7853;p &#273;o&amp;agrave;n H&amp;ograve;a Ph&amp;aacute;t cho bi&#7871;t, gi&#7843;i ph&amp;aacute;p &#7913;ng ph&amp;oacute; v&#7899;i kh&amp;oacute; kh&#259;n c&#7911;a doanh nghi&#7879;p th&amp;eacute;p l&amp;agrave; ti&#7871;t gi&#7843;m s&#7843;n l&#432;&#7907;ng, tr&amp;aacute;nh t&#7891;n kho &#273;&#7875; ph&#7843;i ch&#7883;u chi ph&amp;iacute; v&#7889;n vay; &#273;&#7891;ng th&#7901;i ti&#7871;t ki&#7879;m c&amp;aacute;c chi ph&amp;iacute; qu&#7843;n l&amp;yacute;, n&#259;ng l&#432;&#7907;ng &#273;&#7875; t&#7841;o ra s&#7843;n ph&#7849;m c&amp;oacute; gi&amp;aacute; th&amp;agrave;nh c&#7841;nh tranh.&lt;/p&gt;\r\n\r\n&lt;p&gt;T&#7893;ng C&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam (VNStell) c&#361;ng &#273;&#7873; ngh&#7883; Ch&amp;iacute;nh ph&#7911; &#432;u ti&amp;ecirc;n ngo&#7841;i t&#7879; cho ng&amp;agrave;nh th&amp;eacute;p b&#7903;i th&amp;eacute;p c&#361;ng l&amp;agrave; m&#7863;t h&amp;agrave;ng ph&#7843;i th&#7921;c hi&#7879;n b&amp;igrave;nh &#7893;n gi&amp;aacute;, l&#7841;i c&amp;oacute; vai tr&amp;ograve; quan tr&#7885;ng trong n&#7873;n kinh t&#7871;.&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, c&amp;aacute;c doanh nghi&#7879;p &#273;ang tr&amp;ocirc;ng ch&#7901; v&amp;agrave;o vi&#7879;c B&#7897; C&amp;ocirc;ng th&#432;&#417;ng c&#7847;n t&#259;ng c&#432;&#7901;ng c&amp;ocirc;ng t&amp;aacute;c gi&amp;aacute;m s&amp;aacute;t th&#7883; tr&#432;&#7901;ng, th&amp;ocirc;ng tin &#273;&amp;uacute;ng, k&#7883;p th&#7901;i, tr&amp;aacute;nh t&amp;aacute;c &#273;&#7897;ng ti&amp;ecirc;u c&#7921;c do nhi&#7877;u th&amp;ocirc;ng tin nh&#432; DN ph&amp;aacute; s&#7843;n, n&#7907; x&#7845;u&amp;hellip; d&#7851;n &#273;&#7871;n c&amp;oacute; d&#432; lu&#7853;n kh&amp;ocirc;ng t&#7889;t, &#7843;nh h&#432;&#7903;ng &#273;&#7871;n ho&#7841;t &#273;&#7897;ng s&#7843;n xu&#7845;t, kinh doanh c&#7911;a c&amp;aacute;c doanh nghi&#7879;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7873; d&amp;agrave;i h&#7841;n, doanh nghi&#7879;p c&#361;ng &#273;&#7873; xu&#7845;t B&#7897; C&amp;ocirc;ng th&#432;&#417;ng c&#7847;n xem x&amp;eacute;t kh&#7843; n&#259;ng x&amp;acirc;y d&#7921;ng ph&#432;&#417;ng &amp;aacute;n t&amp;aacute;i c&#7845;u tr&amp;uacute;c ng&amp;agrave;nh th&amp;eacute;p. &#272;&amp;acirc;y l&amp;agrave; c&#417; h&#7897;i &#273;&#7875; nh&amp;igrave;n l&#7841;i &#273;i&#7875;m m&#7841;nh, y&#7871;u &#273;&#7875; t&amp;aacute;i c&#7845;u tr&amp;uacute;c cho ng&amp;agrave;nh hi&#7879;u qu&#7843; h&#417;n.&lt;/p&gt;','29/10/2011','29/10/2011','/home/uploads/image/news/10-2011/thep_ton.jpg',23,2,2092,'huyennt',1,'thi-truong-thep-dang--roi-loan','th&eacute;p, th&#7883; tr&#432;&#7901;ng, r&#7889;i lo&#7841;n, VLXD',NULL,NULL,NULL,'th&eacute;p, th&#7883; tr&#432;&#7901;ng, r&#7889;i lo&#7841;n, VLXD',0,0,0,'','','',3,'dothi.net',1,1,0,0),(81,'Doanh nghi&#7879;p th&eacute;p hoang mang v&igrave; l&#7895;','Ng&agrave;nh th&eacute;p hoang mang v&igrave; ti&ecirc;u th&#7909; ch&#7853;m, l&#7895; cu&#7889;i n&#259;m &quot;&#259;n&quot; h&#7871;t l&atilde;i &#273;&#7847;u n&#259;m.','\r\n\r\n&lt;p&gt;&lt;strong&gt;B&amp;aacute;n ch&#7853;m, l&#7895; cu&#7889;i n&#259;m &#259;n h&#7871;t l&amp;atilde;i &#273;&#7847;u n&#259;m&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7899;i m&#7913;c t&#259;ng tr&#432;&#7903;ng d&#7921; ki&#7871;n &amp;acirc;m 7,69%, n&#259;m 2011 s&#7869; l&#7843; n&#259;m &#273;&#7863;c bi&#7879;t kh&amp;oacute; kh&#259;n c&#7911;a ng&amp;agrave;nh th&amp;eacute;p. T&#7841;i cu&#7897;c h&#7885;p m&#7899;i &#273;&amp;acirc;y t&#7841;i B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng, &amp;ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng, Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam cho bi&#7871;t, t&amp;iacute;nh &#273;&#7871;n nay, t&#7893;ng ti&amp;ecirc;u th&#7909; th&amp;eacute;p &#273;&amp;atilde; gi&#7843;m g&#7847;n 10%. Nh&#7919;ng ch&amp;iacute;nh s&amp;aacute;ch c&#7855;t gi&#7843;m &#273;&#7847;u t&#432; c&amp;ocirc;ng, c&#7855;t gi&#7843;m chi ti&amp;ecirc;u, ng&amp;acirc;n h&amp;agrave;ng si&#7871;t ch&#7863;t t&amp;iacute;n d&#7909;ng khi&#7871;n cho c&amp;aacute;c d&#7921; &amp;aacute;n b&#7845;t &#273;&#7897;ng s&#7843;n &#273;&amp;oacute;ng b&#259;ng, k&amp;eacute;o theo, ng&amp;agrave;nh v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng nh&#432; th&amp;eacute;p ch&#7883;u &#7843;nh h&#432;&#7903;ng r&#7845;t l&#7899;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7847;u n&#259;m, ng&amp;agrave;nh th&amp;eacute;p c&amp;ograve;n &#273;ang ti&amp;ecirc;u th&#7909; t&#7889;t, nh&#432;ng sau khi Ngh&#7883; quy&#7871;t 11 ra &#273;&#7901;i, ti&amp;ecirc;u th&#7909; s&#7843;n ph&#7849;m ng&amp;agrave;nh th&amp;eacute;p b&#7855;t &#273;&#7847;u gi&#7843;m d&#7847;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p t&amp;iacute;nh to&amp;aacute;n, hi&#7879;n nay, th&amp;eacute;p gi&#7843;m gi&amp;aacute; 200.000-300.000 &#273;&#7891;ng/t&#7845;n. Gi&amp;aacute; th&amp;eacute;p b&amp;aacute;n t&#7841;i nh&amp;agrave; m&amp;aacute;y kh&amp;ocirc;ng k&#7875; VAT &#432;&#7899;c kho&#7843;ng 15 tri&#7879;u &#273;&#7891;ng/t&#7845;n trong khi gia ph&amp;ocirc;i tr&amp;ecirc;n 14 tri&#7879;u &#273;&#7891;ng/t&#7845;n. &#272;&amp;aacute;ng ra, &#273;&#7875; h&amp;ograve;a v&#7889;n, s&#7843;n ph&#7849;m th&amp;eacute;p ph&#7843;i b&amp;aacute;n gi&amp;aacute; 15,5 tri&#7879;u &#273;&#7891;ng/t&#7845;n. Nh&#432; v&#7853;y, hi&#7879;n nay c&amp;aacute;c doanh nghi&#7879;p &#273;&#7873;u &#273;ang ph&#7843;i ch&#7883;u l&#7895;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;&lt;em&gt;B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, l&amp;atilde;i su&#7845;t vay cao nh&#432; hi&#7879;n nay th&amp;igrave; kh&amp;ocirc;ng doanh nghi&#7879;p n&amp;agrave;o c&amp;oacute; th&#7875; tr&#7843; n&#7907;, hay ti&#7871;p c&#7853;n &#273;&#432;&#7907;c v&#7889;n. Doanh nghi&#7879;p th&amp;eacute;p l&amp;atilde;i &#273;&#432;&#7907;c 10% l&amp;agrave; m&#7915;ng l&#7855;m r&#7891;i, nh&#432;ng hi&#7879;n t&#7841;i l&amp;atilde;i vay l&amp;agrave; 20%, th&#7853;m ch&amp;iacute; h&#417;n th&amp;igrave; ch&#7881; c&amp;oacute; l&#7895;&lt;/em&gt;&amp;quot;, &amp;ocirc;ng C&#432;&#7901;ng cho bi&#7871;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;&lt;em&gt;N&#7871;u 3 th&amp;aacute;ng n&#7919;a, t&amp;igrave;nh h&amp;igrave;nh th&#7883; tr&#432;&#7901;ng v&#7851;n th&#7871; n&amp;agrave;y to&amp;agrave;n b&#7897; l&amp;atilde;i c&#7911;a doanh nghi&#7879;p th&amp;eacute;p &#7903; giai &#273;o&#7841;n &#273;&#7847;u n&#259;m s&#7869; tr&#7903; v&#7873; con s&#7889; 0 ho&#7863;c s&#7869; &amp;quot;&amp;acirc;m&amp;quot; v&#7889;n. &#272;&amp;acirc;y l&amp;agrave; hi&#7879;n t&#432;&#7907;ng &#273;&amp;atilde; l&#7863;p l&#7841;i 2-3 n&#259;m nay trong ng&amp;agrave;nh, c&#7913; v&amp;agrave;i th&amp;aacute;ng &#273;&#7847;u n&#259;m c&amp;aacute;c doanh nghi&#7879;p l&amp;atilde;i l&#7899;n, &#273;&#7871;n cu&#7889;i n&#259;m l&#7841;i &amp;acirc;m v&#7889;n v&amp;agrave; c&amp;oacute; th&#7875; ch&#7883;u l&#7895; t&#7899;i c&#7843; n&#259;m sau&lt;/em&gt;&amp;quot;, &amp;ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng lo l&#7855;ng.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/doanh_nghiep_thep_hoang_mang_vi_lo1.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;&lt;em&gt;M&#7897;t s&#7889; doanh nghi&#7879;p th&amp;eacute;p hi&#7879;n kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng, d&#7915;ng s&#7843;n xu&#7845;t&amp;nbsp;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nCho &#273;&#7871;n nay, m&#7863;c d&amp;ugrave; ch&#432;a c&amp;oacute; doanh nghi&#7879;p th&amp;eacute;p n&amp;agrave;o c&amp;ocirc;ng b&#7889; ph&amp;aacute; s&#7843;n, nh&#432;ng th&#7921;c t&#7871; doanh nghi&#7879;p kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng, d&#7915;ng s&#7843;n xu&#7845;t &#273;&amp;atilde; c&amp;oacute;. V&amp;iacute; d&#7909;, c&amp;ocirc;ng ty th&amp;eacute;p V&#7841;n L&#7907;i trong cu&#7897;c h&#7885;p &#272;&#7841;i h&#7897;i &#273;&#7891;ng C&#7893; &#273;&amp;ocirc;ng g&#7847;n &#273;&amp;acirc;y &#273;&amp;atilde; tuy&amp;ecirc;n b&#7889; b&amp;aacute;n c&#417; s&#7903; c&#7911;a m&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Ph&amp;aacute; gi&amp;aacute; c&#7843; trong th&#7883; tr&#432;&#7901;ng n&#7897;i &#273;&#7883;a&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo c&amp;acirc;u chuy&#7879;n c&#7911;a &amp;ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng, th&#7883; tr&#432;&#7901;ng th&amp;eacute;p &#273;ang xu&#7889;ng d&#7889;c m&#7897;t c&amp;aacute;ch th&#7843;m h&#7841;i trong khi c&amp;aacute;c doanh nghi&#7879;p th&amp;igrave; hoang mang v&amp;agrave; th&#7853;m ch&amp;iacute;, &#273;&amp;atilde; c&#7841;nh tranh kh&amp;ocirc;ng l&amp;agrave;nh m&#7841;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;iacute;a xu&#7845;t kh&#7849;u, 2 s&#7843;n ph&#7849;m ch&amp;iacute;nh l&amp;agrave; th&amp;eacute;p cu&#7897;n c&amp;aacute;n ngu&#7897;i xu&#7845;t sang Indonesi &#273;&amp;atilde; b&#7883; ki&#7879;n b&amp;aacute;n ph&amp;aacute; gi&amp;aacute;, &#7889;ng th&amp;eacute;p m&#7899;i &#273;&amp;acirc;y nh&#7845;t c&#361;ng b&#7883; ki&#7879;n b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; t&#7841;i M&#7929;. Kh&amp;ocirc;ng d&#7915;ng l&#7841;i &#7903; &#273;&amp;oacute;, c&amp;oacute; th&#7875; s&#7843;n ph&#7849;m tr&amp;aacute;ng t&amp;ocirc;n, m&#7841; k&#7869;m c&#361;ng b&#7883; ki&#7879;n.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&#272;&amp;atilde; c&amp;oacute; chuy&#7879;n doanh nghi&#7879;p b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; nhau, d&#7851;n t&#7899;i ki&#7879;n c&amp;aacute;o n&#7897;i b&#7897;. Ch&#7845;p nh&#7853;n kh&amp;oacute; kh&#259;n c&amp;ograve;n k&amp;eacute;o d&amp;agrave;i, ph&#7843;i t&#7921; c&#7913;u m&amp;igrave;nh tr&#432;&#7899;c l&amp;agrave; th&amp;ocirc;ng &#273;i&#7879;p chung c&#7911;a c&#7897;ng &#273;&#7891;ng doanh nghi&#7879;p ng&amp;agrave;nh n&amp;agrave;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;T&#7841;i th&#7883; tr&#432;&#7901;ng trong n&#432;&#7899;c, &#273;&amp;atilde; xu&#7845;t hi&#7879;n hi&#7879;n t&#432;&#7907;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; &#7903; m&#7897;t v&amp;agrave;i doanh nghi&#7879;p n&amp;ecirc;n &#273;&amp;atilde; l&#7895; c&amp;ograve;n l&#7895; th&amp;ecirc;m. Nhu c&#7847;u chung v&#7873; x&amp;acirc;y d&#7921;ng c&#7911;a n&#7873;n kinh t&#7871; kh&amp;ocirc;ng cao n&amp;ecirc;n doanh nghi&#7879;p c&amp;oacute; gi&#7843;m gi&amp;aacute; b&amp;aacute;n c&#361;ng kh&amp;ocirc;ng th&#7875; t&#259;ng l&#432;&#7907;ng b&amp;aacute;n &#273;&#432;&#7907;c l&amp;agrave; bao. Khi kh&amp;ocirc;ng xu&#7845;t kh&#7849;u &#273;&#432;&#7907;c, doanh nghi&#7879;p trong n&#432;&#7899;c kh&amp;ocirc;ng c&amp;oacute; s&#7921; b&amp;agrave;n b&#7841;c chia s&#7867; kh&amp;oacute; kh&#259;n chung, chia s&#7867; th&#7883; ph&#7847;n, n&amp;ecirc;n gi&amp;aacute; c&#7843; ch&amp;agrave;o c&amp;aacute;c &#273;&#7889;i t&amp;aacute;c r&#7845;t kh&amp;aacute;c nhau, d&#7851;n t&#7899;i nh&#7919;ng ki&#7879;n c&amp;aacute;o trong n&#7897;i b&#7897; c&amp;aacute;c &#273;&#417;n v&#7883;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong ng&amp;agrave;nh, nh&#7919;ng &#273;&#417;n v&#7883; m&#7841;nh, s&#7903; h&#7919;u c&amp;ocirc;ng ngh&#7879; l&amp;ograve; cao nh&#432; T&#7893;ng c&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam, c&amp;ocirc;ng ty Th&amp;eacute;p H&amp;ograve;a Ph&amp;aacute;t th&amp;igrave; c&amp;ograve;n c&amp;oacute; th&#7875; v&#7853;n h&amp;agrave;nh t&#7889;t, do c&amp;oacute; &#273;&#7911; nguy&amp;ecirc;n li&#7879;u, v&#7889;n. C&amp;ograve;n c&amp;aacute;c c&amp;ocirc;ng ty kh&amp;aacute;c nh&#432; V&#7841;n L&#7907;i, &#272;&amp;igrave;nh V&#361; &#273;&#7873;u &#273;&amp;atilde; c&#7841;n ki&#7879;t nguy&amp;ecirc;n li&#7879;u &#273;&#7847;u v&amp;agrave;o, s&#7843;n xu&#7845;t c&#7847;m ch&#7915;ng. Khi ch&#7881; c&amp;oacute; v&amp;agrave;i doanh nghi&#7879;p c&amp;oacute; l&amp;ograve; cao mua qu&#7863;ng n&amp;ecirc;n ngay c&#7843; vi&#7879;c b&amp;aacute;n qu&#7863;ng s&#7855;t &#7903; c&amp;aacute;c &#273;&#7883;a ph&#432;&#417;ng c&#361;ng r&#7845;t kh&amp;oacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Nghi&amp;ecirc;m Xu&amp;acirc;n &#272;a, Ph&amp;oacute; T&#7893;ng gi&amp;aacute;m &#273;&#7889;c T&#7893;ng c&amp;ocirc;ng Th&amp;eacute;p Vi&#7879;t Nam cho hay, 10 th&amp;aacute;ng &#273;&#7847;u n&#259;m, l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; th&amp;eacute;p c&#7911;a t&#7893;ng c&amp;ocirc;ng ty &#273;&amp;atilde; gi&#7843;m 2,7% so v&#7899;i c&amp;ugrave;ng k&#7923;, l&amp;agrave; m&#7913;c gi&#7843;m s&amp;uacute;t &#273;&amp;aacute;ng k&#7875;. Trong &#273;&amp;oacute;, th&amp;eacute;p x&amp;acirc;y d&#7921;ng gi&#7843;m gi&#7843;m 6,7% so v&#7899;i c&amp;ugrave;ng k&#7923;. 10 th&amp;aacute;ng qua, 50% &#273;&#417;n v&#7883; trong T&#7893;ng c&amp;ocirc;ng ty Th&amp;eacute;p ch&#7881; &#273;&#7841;t 60% k&#7871; ho&#7841;ch s&#7843;n xu&#7845;t n&#259;m, nhi&#7873;u &#273;&#417;n v&#7883; ph&#7843;i &#273;i&#7873;u ch&#7881;nh k&#7871; ho&#7841;ch 2011 v&amp;agrave; hi&#7879;n &#273;&amp;atilde; c&amp;oacute; 3 &#273;&#417;n v&#7883; b&#7883; l&#7895;.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7883; Ph&amp;oacute; T&#7893;ng gi&amp;aacute;m &#273;&#7889;c n&amp;agrave;y b&#259;n kho&#259;n: &amp;quot;M&#7863;c d&amp;ugrave; ph&amp;iacute;a Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; n&#432;&#7899;c &#273;&amp;atilde; c&amp;oacute; ch&amp;iacute;nh s&amp;aacute;ch gi&#7843;m l&amp;atilde;i su&#7845;t huy &#273;&#7897;ng, cho vay nh&#432;ng th&#7921;c t&#7871; ch&amp;uacute;ng t&amp;ocirc;i kh&amp;ocirc;ng th&#7845;y gi&#7843;m. Hi&#7879;n, l&amp;atilde;i su&#7845;t &#273;i vay v&#7851;n h&#417;n 20%/n&#259;m ch&#7913; kh&amp;ocirc;ng c&amp;oacute; chuy&#7879;n &#273;&amp;atilde; xu&#7889;ng 17-19%&amp;quot;.&lt;/p&gt;\r\n\r\n\r\n\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;480&quot; height=&quot;270&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/doanh_nghiep_thep_hoang_mang_vi_lo2.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;C&amp;aacute;c DN th&amp;eacute;p ph&#7843;i t&#7921; c&#7913;u m&amp;igrave;nh l&amp;agrave; ch&amp;iacute;n&lt;/span&gt;&lt;/em&gt;h&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nKh&#7843; n&#259;ng ti&#7871;p c&#7853;n v&#7889;n n&amp;oacute;i chung v&#7851;n r&#7845;t h&#7841;n ch&#7871; v&amp;igrave; c&amp;aacute;c ng&amp;acirc;n h&amp;agrave;ng ch&#7885;n l&#7885;c kh&amp;aacute;ch &#273;&#7875; tr&amp;aacute;nh t&#259;ng tr&#432;&#7903;ng t&amp;iacute;n d&#7909;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;&lt;em&gt;B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute; l&amp;agrave; kh&amp;oacute; kh&#259;n ngo&#7841;i t&#7879; v&#7851;n c&amp;ograve;n l&amp;agrave; n&#7895;i lo l&#7899;n cho doanh nghi&#7879;p. Hai ng&amp;agrave;y h&amp;ocirc;m nay, th&#7883; tr&#432;&#7901;ng ngo&#7841;i t&#7879; l&#7841;i &#273;&amp;oacute;ng b&#259;ng do b&#7883; ki&#7875;m so&amp;aacute;t g&#7855;t gao. Hi&#7879;n, tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng &#273;&amp;atilde; xu&#7845;t hi&#7879;n t&amp;igrave;nh tr&#7841;ng hai t&#7927; gi&amp;aacute; khi&#7871;n cho chi ph&amp;iacute; v&#7889;n c&#7911;a c&amp;aacute;c &#273;&#417;n v&#7883; t&#259;ng cao l&amp;ecirc;n. Trong 10 th&amp;aacute;ng, ch&amp;uacute;ng t&amp;ocirc;i c&amp;oacute; l&amp;atilde;i nh&#432;ng c&amp;oacute; th&#7875; ch&amp;ecirc;nh l&#7879;ch t&#7927; gi&amp;aacute; trong qu&amp;yacute; 4 s&#7869; v&amp;eacute;t h&#7871;t kho&#7843;n l&amp;atilde;i n&amp;agrave;y&lt;/em&gt;&amp;quot;, &amp;ocirc;ng &#272;a n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Gi&#7843;m s&#7843;n xu&#7845;t &#273;&#7875; t&#7921; c&#7913;u m&amp;igrave;nh tr&#432;&#7899;c&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;R&#7845;t th&#7859;ng th&#7855;n v&amp;agrave; &#273;i&#7873;m t&#297;nh, &amp;ocirc;ng Tr&#7847;n Tu&#7845;n D&#432;&#417;ng, T&#7893;ng gi&amp;aacute;m &#273;&#7889;c T&#7853;p &#273;o&amp;agrave;n H&amp;ograve;a Ph&amp;aacute;t, b&amp;agrave;y t&#7887;: &amp;quot;Kh&amp;oacute; kh&#259;n cho ng&amp;agrave;nh th&amp;eacute;p s&#7869; c&amp;ograve;n k&amp;eacute;o d&amp;agrave;i n&#7919;a. V&amp;igrave; nh&#7919;ng ch&amp;iacute;nh s&amp;aacute;ch c&#7911;a Ch&amp;iacute;nh ph&#7911; theo Ngh&#7883; quy&#7871;t 11 l&amp;agrave; &#273;&amp;uacute;ng v&amp;agrave; s&#7869; c&amp;ograve;n duy tr&amp;igrave; trong th&#7901;i gian d&amp;agrave;i t&#7899;i, th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n &#273;i xu&#7889;ng l&amp;agrave; do v&amp;agrave;i n&#259;m tr&#432;&#7899;c t&#259;ng tr&#432;&#7903;ng qu&amp;aacute; n&amp;oacute;ng n&amp;ecirc;n c&#361;ng s&#7869; kh&amp;ocirc;ng th&#7875; ph&#7909;c h&#7891;i nhanh trong ng&#7855;n h&#7841;n. Nhu c&#7847;u gi&#7843;m n&amp;ecirc;n nguy c&#417; th&amp;eacute;p th&#7915;a l&amp;agrave; c&amp;oacute;&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo &amp;ocirc;ng D&#432;&#417;ng, ch&amp;uacute;ng ta ph&#7843;i h&#7885;c theo th&#7871; gi&#7899;i, ph&#7843;i ti&#7871;t gi&#7843;m s&#7843;n l&#432;&#7907;ng cho ph&amp;ugrave; h&#7907;p th&#7883; tr&#432;&#7901;ng &#273;ang b&#7883; thu h&#7865;p. Thep H&amp;ograve;a Ph&amp;aacute;t chi&#7871;m th&#7883; ph&#7847;n l&#7899;n th&#7913; hai &#7903; Vi&#7879;t Nam nh&#432;ng hi&#7879;n c&#361;ng ch&#7881; ch&#7841;y 80% c&amp;ocirc;ng su&#7845;t. Co h&#7865;p s&#7843;n xu&#7845;t l&amp;uacute;c n&amp;agrave;y v&#7915;a l&amp;agrave; &#273;&#7875; gi&#7843;m t&#7891;n kho, v&#7915;a gi&#7843;m &#273;&#432;&#7907;c chi ph&amp;iacute; n&#259;ng l&#432;&#7907;ng, t&amp;agrave;i ch&amp;iacute;nh...&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Nghi&amp;ecirc;m Xu&amp;acirc;n &#272;a c&#361;ng &#273;&#7891;ng t&amp;igrave;nh cho r&#7857;ng, c&amp;aacute;c doanh nghi&#7879;p c&#7847;n ti&#7871;p t&#7909;c kh&#7843;o s&amp;aacute;t m&#7903; r&#7897;ng th&#7883; tr&#432;&#7901;ng xu&amp;acirc;t kh&#7849;u &#273;&#7875; h&#7895; tr&#7907; cho ph&#7847;n n&#7897;i &#273;&#7883;a b&#7883; gi&#7843;m, &#273;&#7891;ng th&#7901;i, tranh th&#7911; t&amp;aacute;i c&#7845;u tr&amp;uacute;c ch&amp;iacute;nh m&amp;igrave;nh, s&#7855;p x&#7871;p l&#7841;i c&amp;aacute;c c&#417; s&#7903; l&#432;u th&amp;ocirc;ng th&amp;eacute;p &#273;&#7875; ti&#7871;t gi&#7843;m chi ph&amp;iacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong khi &#273;&amp;oacute;, &amp;ocirc;ng Ho&amp;agrave;ng V&#259;n T&amp;ograve;ng, Ch&#7911; t&#7883;ch C&amp;ocirc;ng ty Gang th&amp;eacute;p Th&amp;aacute;i Nguy&amp;ecirc;n c&#361;ng cho hay, ti&amp;ecirc;u th&#7909; th&#7901;i gian qua c&#7911;a &#273;&#417;n v&#7883; ch&#7881; b&#7857;ng 70-71%. Ch&amp;uacute;ng t&amp;ocirc;i b&#7855;t bu&#7897;c ph&#7843;i gi&#7843;m gi&amp;aacute; th&amp;agrave;nh, t&#7915; &#273;&#7847;u n&#259;m &#273;&#7871;n nay &#273;&amp;atilde; ti&#7871;t ki&#7879;m &#273;&#432;&#7907;c t&#7899;i 150 t&#7927; &#273;&#7891;ng so v&#7899;i gi&amp;aacute; th&amp;agrave;nh k&#7871; ho&#7841;ch &#273;&#7863;t ra.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng L&amp;ecirc; Minh H&#7843;i, Ch&#7911; t&#7883;ch C&amp;ocirc;ng ty th&amp;eacute;p Vi&#7879;t &#272;&#7913;c cho r&#7857;ng, v&#7845;n &#273;&#7873; b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; th&amp;eacute;p trong n&#432;&#7899;c ph&#7843;i &#273;&#432;&#7907;c b&amp;agrave;n s&amp;acirc;u, k&#7929;. Hi&#7879;n ch&amp;uacute;ng ta &#273;ang l&#7895; n&amp;ecirc;n ph&#7843;i &#273;i &#273;&#7871;n v&#7845;n &#273;&#7873; m&#7845;u ch&#7889;t l&amp;agrave; ph&#7843;i x&amp;acirc;y d&#7921;ng mang l&#432;&#7899;i b&amp;aacute;n h&amp;agrave;ng. C&amp;aacute;c doanh nghi&#7879;p th&amp;eacute;p l&#7899;n n&amp;ecirc;n th&amp;agrave;nh l&#7853;p nh&amp;oacute;m G &#273;&#7875; b&amp;agrave;n b&#7841;c v&amp;agrave; t&#7915; &#273;&amp;oacute; c&amp;aacute;c c&amp;ocirc;ng ty nh&#7887; s&#7869; theo nh&amp;oacute;m G...Do m&#7841;ng l&#432;&#7899;i ph&amp;acirc;n ph&#7889;i hi&#7879;n r&#7845;t l&#7911;ng c&#7911;ng, ai c&#361;ng ch&#7913;ng minh n&#259;ng l&#7921;c s&#7843;n xu&#7845;t cao v&amp;agrave; &#273;&#432;a ra gi&amp;aacute; kh&amp;ocirc;ng h&#7907;p l&amp;yacute;. Vi&#7879;t &#272;&#7913;c cho r&#7857;ng, n&amp;ecirc;n &#273;&#432;a ra m&#7913;c gi&amp;aacute; c&amp;oacute; l&#7907;i nhu&#7853;n nh&#7845;t &#273;&#7883;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&#432;&#7899;c nhi&#7873;u gi&amp;atilde;i b&amp;agrave;y c&#7911;a c&amp;aacute;c doanh nghi&#7879;p, &amp;ocirc;ng Nguy&#7877;n M&#7841;nh Qu&amp;acirc;n, V&#7909; tr&#432;&#7903;ng V&#7909; C&amp;ocirc;ng nghi&#7879;p n&#7863;ng chia s&#7867;, kh&amp;oacute; kh&#259;n v&#7873; l&amp;atilde;i su&#7845;t cao th&amp;igrave; Ch&amp;iacute;nh ph&#7911; &#273;&amp;atilde; c&amp;oacute; nhi&#7873;u bi&#7879;n ph&amp;aacute;p. Nh&#432;ng ph&amp;iacute;a ch&#7911; quan, ng&amp;agrave;nh th&amp;eacute;p ph&#7843;i t&amp;iacute;nh to&amp;aacute;n l&#7853;p h&amp;agrave;ng r&amp;agrave;o k&#7929; thu&#7853;t ph&amp;ugrave; h&#7907;p v&#7899;i &#273;&#7863;c th&amp;ugrave; trong n&#432;&#7899;c, &#273;&#7875; c&#7841;nh tranh &#273;&#432;&#7907;c v&#7899;i th&amp;eacute;p nh&#7853;p kh&#7849;u. N&#7871;u kh&amp;ocirc;ng l&amp;agrave; ng&amp;agrave;nh s&#7869; r&#417;i v&amp;agrave;o c&#7843;nh &amp;quot;ch&amp;uacute;ng ta ch&#7863;t ch&amp;acirc;n ch&amp;iacute;nh m&amp;igrave;nh&amp;quot;. C&amp;aacute;c doanh nghi&#7879;p ph&#7843;i c&amp;oacute; &#273;&#7873; xu&#7845;t c&#7909; th&#7875; &#273;&#7875; ch&amp;uacute;ng t&amp;ocirc;i c&amp;oacute; t&#7893;ng h&#7907;p hi&#7879;u qu&#7843;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;iacute;a B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng s&#7869; c&#7889; g&#7855;ng th&#432;&#417;ng l&#432;&#7907;ng ti&#7871;p v&#7899;i Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; n&#432;&#7899;c &#273;&#7875; th&amp;aacute;o g&#7905; cho c&amp;aacute;c doanh nghi&#7879;p m&#7863;c d&amp;ugrave;, th&amp;eacute;p kh&amp;ocirc;ng thu&#7897;c di&#7879;n &#432;u ti&amp;ecirc;n ngo&#7841;i t&#7879;.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&#259;m 2012, t&amp;igrave;nh h&amp;igrave;nh s&#7869; kh&amp;ocirc;ng kh&amp;aacute; h&#417;n ngay n&amp;ecirc;n c&amp;aacute;c doanh nghi&#7879;p th&amp;eacute;p ph&#7843;i c&amp;oacute; li&#7879;u li&#7879;u m&amp;agrave; &#7913;ng ph&amp;oacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&amp;quot;&#272;&#432;&#417;ng nhi&amp;ecirc;n, Ch&amp;iacute;nh ph&#7911; v&amp;agrave; b&#7897; ng&amp;agrave;nh s&#7869; ph&#7843;i th&amp;aacute;o g&#7905; kh&amp;oacute; kh&#259;n cho doanh nghi&#7879;p. T&#432;&#417;ng lai ng&amp;agrave;nh th&amp;eacute;p v&#7851;n s&amp;aacute;ng s&#7911;a ch&#7913; kh&amp;ocirc;ng ph&#7843;i qu&amp;aacute; &#273;en t&#7889;i v&amp;igrave; nhu c&#7847;u c&#7911;a s&#7843;n ph&#7849;m n&amp;agrave;y trong qu&amp;aacute; tr&amp;igrave;nh ph&amp;aacute;t tri&#7875;n &#273;&#7845;t n&#432;&#7899;c c&amp;ograve;n r&#7845;t l&#7899;n, c&amp;ograve;n nhi&#7873;u c&#417; h&#7897;i ph&amp;aacute;t tri&#7875;n. M&#7895;i doanh nghi&#7879;p c&#7847;n c&amp;oacute; gi&#7843;i ph&amp;aacute;p c&#7909; th&#7875; t&amp;ugrave;y &#273;&#7863;c th&amp;ugrave; c&#7911;a ri&amp;ecirc;ng &#273;&#417;n v&#7883; m&amp;igrave;nh, nh&#432;ng tr&amp;ecirc;n tinh th&#7847;n chung l&amp;agrave; ch&#7845;p nh&#7853;n s&#7855;p t&#7899;i c&amp;ograve;n kh&amp;oacute; kh&#259;n. Ch&amp;uacute;ng ta ph&#7843;i t&#7921; tr&amp;ocirc;ng mong v&amp;agrave;o s&#7913;c m&amp;igrave;nh l&amp;agrave; ch&amp;iacute;nh&lt;/em&gt;&amp;quot;, Th&#7913; tr&#432;&#7903;ng B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng L&amp;ecirc; D&#432;&#417;ng Quang nh&#7845;n m&#7841;nh.&lt;/p&gt;','31/10/2011','31/10/2011','/home/uploads/image/news/10-2011/doanh_nghiep_thep_hoang_mang_vi_lo2.jpg',23,2,1810,'huyennt',1,'doanh-nghiep-thep-hoang-mang-vi-lo','doanh nghi&#7879;p th&eacute;p, hoang mang,',NULL,NULL,NULL,'doanh nghi&#7879;p th&eacute;p, hoang mang,',0,0,0,'','','',3,'xaydungvietnam.vn',1,0,0,1),(82,'H&agrave; N&#7897;i: C&ocirc;ng b&#7889; quy ho&#7841;ch ph&aacute;t tri&#7875;n VLXD &#273;&#7871;n 2030','S&#7903; X&acirc;y d&#7921;ng H&agrave; N&#7897;i v&#7915;a c&ocirc;ng b&#7889; Quy ho&#7841;ch ph&aacute;t tri&#7875;n v&#7853;t li&#7879;u x&acirc;y d&#7921;ng (VLXD) th&agrave;nh ph&#7889; &#273;&#7871;n n&#259;m 2020, &#273;&#7883;nh h&#432;&#7899;ng &#273;&#7871;n n&#259;m 2030.','&lt;p&gt;&#272;&amp;acirc;y c&#361;ng l&amp;agrave; quy ho&#7841;ch chuy&amp;ecirc;n ng&amp;agrave;nh &#273;&#7847;u ti&amp;ecirc;n &#273;&#432;&#7907;c ph&amp;ecirc; duy&#7879;t v&amp;agrave; c&amp;ocirc;ng b&#7889; sau khi c&amp;oacute; Quy ho&#7841;ch chung x&amp;acirc;y d&#7921;ng Th&#7911; &#273;&amp;ocirc;.&lt;/p&gt;\r\n\r\n\r\n\r\n&lt;p&gt;Theo quy ho&#7841;ch, giai &#273;o&#7841;n &#273;&#7871;n n&#259;m 2020, c&amp;ocirc;ng nghi&#7879;p s&#7843;n xu&#7845;t VLXD c&#7911;a H&amp;agrave; N&#7897;i ph&#7843;i &#273;&#7843;m b&#7843;o l&amp;agrave; ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p th&amp;acirc;n thi&#7879;n m&amp;ocirc;i tr&#432;&#7901;ng, h&#432;&#7899;ng t&#7899;i n&#7873;n c&amp;ocirc;ng nghi&#7879;p xanh, trong &#273;&amp;oacute; &#273;&#7849;y m&#7841;nh s&#7843;n xu&#7845;t c&amp;aacute;c lo&#7841;i VLXD kh&amp;ocirc;ng nung, x&#7917; l&amp;yacute; v&amp;agrave; lo&#7841;i b&#7887; d&#7847;n c&amp;aacute;c c&amp;ocirc;ng ngh&#7879; s&#7843;n xu&#7845;t theo ph&#432;&#417;ng ph&amp;aacute;p truy&#7873;n th&#7889;ng ti&amp;ecirc;u t&#7889;n nhi&amp;ecirc;u li&#7879;u, hi&#7879;u qu&#7843; kinh t&#7871; th&#7845;p, g&amp;acirc;y &amp;ocirc; nhi&#7877;m m&amp;ocirc;i tr&#432;&#7901;ng. &#272;&#7889;i v&#7899;i g&#7841;ch th&#7911; c&amp;ocirc;ng, ng&#7915;ng s&#7843;n xu&#7845;t v&amp;agrave;o n&#259;m 2012. Quy ho&#7841;ch c&#361;ng &#273;&#7863;t m&#7909;c ti&amp;ecirc;u trong giai &#273;o&#7841;n &#273;&#7871;n 2015, t&#7889;c &#273;&#7897; t&#259;ng gia str&#7883; s&#7843;n xu&#7845;t c&amp;ocirc;ng nghi&#7879;p VLXD &#273;&#7841;t 17%/n&#259;m, giai &#273;o&#7841;n 2016-2020 &#273;&#7841;t 16%. &#272;&#7871;n 2020, ng&amp;agrave;nh n&amp;agrave;y s&#7869; &#273;&amp;oacute;ng g&amp;oacute;p cho ng&amp;acirc;n s&amp;aacute;ch TP kho&#7843;ng 3.800 t&#7927; &#273;&#7891;ng. &#272;&#7875; th&#7921;c hi&#7879;n nh&#7919;ng d&#7921; &amp;aacute;n &#273;&#7847;u t&#432; theo quy ho&#7841;ch, d&#7921; ki&#7871;n t&#7893;ng v&#7889;n &#273;&#7847;u t&#432; cho ph&amp;aacute;t tri&#7875;n c&amp;ocirc;ng nghi&#7879;p VLXD trong giai &#273;o&#7841;n t&#7915; nay &#273;&#7871;n n&#259;m 2015 l&amp;agrave; h&#417;n 9.400 t&#7927; &#273;&#7891;ng, t&#7915; 2016-2020 l&amp;agrave; h&#417;n 3.000 t&#7927; &#273;&#7891;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Quy ho&#7841;ch c&#361;ng &#273;&amp;atilde; &#273;&#432;a ra ph&#432;&#417;ng h&#432;&#7899;ng di d&#7901;i m&#7897;t s&#7889; c&#417; s&#7903; s&#7843;n xu&#7845;t trong giai &#273;o&#7841;n t&#7915; nay &#273;&#7871;n 2015 g&#7891;m 12 &#273;&#417;n v&#7883; trong &#273;&amp;oacute; c&amp;oacute; C&amp;ocirc;ng ty CP g&#7841;ch ng&amp;oacute;i Th&#7841;c B&amp;agrave;n, C&amp;ocirc;ng ty CP b&amp;ecirc; t&amp;ocirc;ng v&amp;agrave; x&amp;acirc;y d&#7921;ng Th&#7883;nh Li&#7879;t&amp;hellip; Trong giai &#273;o&#7841;n 2016-2020 di chuy&#7875;n 5 c&#417; s&#7903;, trong &#273;&amp;oacute; c&amp;oacute; C&amp;ocirc;ng ty CP Vi&#7879;t M&#7929;, C&amp;ocirc;ng ty CP Viglacera &#272;&amp;ocirc;ng Anh&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;334&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/ha-noi-cong-bo-pt-vlxd.JPG&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Vi&#7879;c c&amp;ocirc;ng b&#7889; quy ho&#7841;ch s&#7869; l&amp;agrave; c&#259;n c&#7913; cho c&amp;aacute;c ng&amp;agrave;nh, c&amp;aacute;c nh&amp;agrave; &#273;&#7847;u t&#432;, c&amp;aacute;c DN s&#7843;n xu&#7845;t VLXD c&#7911;a TP l&#7853;p c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&#7847;u t&#432;, chu&#7849;n b&#7883; l&#7921;c l&#432;&#7907;ng, t&amp;igrave;m ngu&#7891;n v&#7889;n &#273;&#7875; tri&#7875;n khai c&amp;aacute;c d&#7921; &amp;aacute;n ph&amp;ugrave; h&#7907;p v&amp;agrave; hi&#7879;u qu&#7843;, &#273;&#7891;ng th&#7901;i n&amp;acirc;ng cao c&amp;ocirc;ng t&amp;aacute;c qu&#7843;n l&amp;yacute; Nh&amp;agrave; n&#432;&#7899;c trong l&#297;nh v&#7921;c n&amp;agrave;y.&lt;/p&gt;\r\n','01/11/2011','01/11/2011','/home/uploads/image/news/10-2011/ha-noi-cong-bo-pt-vlxd.JPG',23,2,1877,'huyennt',1,'ha-noi-cong-bo-quy-hoach-phat-trien-vlxd-den-2030','VLXD, &#273;&#7883;nh h&#432;&#7899;ng, quy ho&#7841;ch,  ph&aacute;t tri&#7875;n',NULL,NULL,NULL,'VLXD, &#273;&#7883;nh h&#432;&#7899;ng, quy ho&#7841;ch,  ph&aacute;t tri&#7875;n',0,0,0,'','','',3,'http://kientrucvietnam.org.vn',1,0,0,0),(83,'Ng&agrave;nh th&eacute;p v&#7851;n l&#7841;c quan trong d&agrave;i h&#7841;n','M&#7863;c d&ugrave; c&aacute;c doanh nghi&#7879;p th&eacute;p trong n&#432;&#7899;c &#273;ang g&#7863;p nhi&#7873;u kh&oacute; kh&#259;n nh&#432;ng nh&#7919;ng &aacute;ch t&#7855;c v&#7873; s&#7843;n xu&#7845;t kinh doanh trong hi&#7879;n t&#7841;i s&#7869; kh&ocirc;ng &#7843;nh h&#432;&#7903;ng &#273;&#7871;n quy ho&#7841;ch chung c&#7911;a to&agrave;n ng&agrave;nh trong nh&#7919;ng n&#259;m t&#7899;i.','\r\n&lt;p style=&quot;text-align: left; &quot;&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\nNh&#7919;ng d&#7921; b&amp;aacute;o l&#7841;c quan &#273;&amp;atilde; &#273;&#432;&#7907;c &amp;ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng, Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i th&amp;eacute;p Vi&#7879;t Nam (VSA) &#273;&#432;a ra b&#7903;i l&#432;&#7907;ng th&amp;eacute;p ti&amp;ecirc;u th&#7909; d&#7921; b&amp;aacute;o s&#7869; c&amp;ograve;n t&#259;ng cao trong th&#7901;i gian t&#7899;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trao &#273;&#7893;i v&#7899;i Th&#7901;i b&amp;aacute;o Kinh t&#7871; S&amp;agrave;i G&amp;ograve;n Online ng&amp;agrave;y 1-11, &amp;ocirc;ng C&#432;&#7901;ng &#273;&#432;a ra d&#7851;n ch&#7913;ng l&amp;agrave; hi&#7879;n s&#7843;n l&#432;&#7907;ng th&amp;eacute;p b&amp;igrave;nh qu&amp;acirc;n &#273;&#7847;u ng&#432;&#7901;i c&#7911;a Vi&#7879;t Nam m&#7899;i &#7903; kho&#7843;ng 130 kg th&amp;eacute;p/ng&#432;&#7901;i v&amp;agrave; ph&#7843;i t&#259;ng l&amp;ecirc;n 200-300 kg th&amp;eacute;p/ng&#432;&#7901;i n&#7871;u Vi&#7879;t Nam mu&#7889;n tr&#7903; th&amp;agrave;nh n&#432;&#7899;c c&amp;ocirc;ng nghi&#7879;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo &amp;ocirc;ng C&#432;&#7901;ng, nh&#7919;ng kh&amp;oacute; kh&#259;n c&#7911;a doanh nghi&#7879;p th&amp;eacute;p hi&#7879;n nay ch&#7883;u t&amp;aacute;c &#273;&#7897;ng b&#7903;i ch&amp;iacute;nh s&amp;aacute;ch chung c&#7911;a k&amp;igrave;m ch&#7871; l&#7841;m ph&amp;aacute;t, c&#7855;t gi&#7843;m &#273;&#7847;u t&#432; c&amp;ocirc;ng&amp;hellip; Tuy nhi&amp;ecirc;n, nh&#7919;ng &#7843;nh h&#432;&#7903;ng &#273;&#7871;n ng&amp;agrave;nh th&amp;eacute;p c&#361;ng ch&#7881; l&amp;agrave; t&#7841;m th&#7901;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Vi&#7879;c &#273;&amp;oacute;ng c&#7917;a m&#7897;t s&#7889; doanh nghi&#7879;p th&amp;eacute;p th&#7901;i gian qua, theo &amp;ocirc;ng C&#432;&#7901;ng, m&#7897;t ph&#7847;n c&#361;ng do c&amp;aacute;c doanh nghi&#7879;p l&amp;agrave;m &#259;n manh m&amp;uacute;n, &#273;&#7847;u t&#432; kh&amp;ocirc;ng b&amp;agrave;i b&#7843;n d&#7851;n &#273;&#7871;n chuy&#7879;n d&#432; th&#7915;a c&amp;ocirc;ng su&#7845;t, ph&amp;aacute; s&#7843;n. &amp;Ocirc;ng C&#432;&#7901;ng nh&#7853;n &#273;&#7883;nh &#273;i&#7873;u nay c&#361;ng gi&amp;uacute;p ng&amp;agrave;nh th&amp;eacute;p t&amp;aacute;i c&#417; c&#7845;u theo h&#432;&#7899;ng ph&amp;aacute;t tri&#7875;n b&#7873;n v&#7919;ng h&#417;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng C&#432;&#7901;ng n&amp;oacute;i, &#273;&#7875; ng&amp;agrave;nh th&amp;eacute;p ph&amp;aacute;t tri&#7875;n b&#7873;n v&#7919;ng, tr&amp;aacute;nh l&#7863;p l&#7841;i t&amp;igrave;nh tr&#7841;ng s&#7843;n xu&#7845;t &amp;aacute;ch t&#7855;c khi th&#7883; tr&#432;&#7901;ng c&amp;oacute; bi&#7871;n &#273;&#7897;ng, doanh nghi&#7879;p n&amp;ecirc;n ti&#7871;p t&#7909;c x&amp;uacute;c ti&#7871;n &#273;&#7849;y m&#7841;nh xu&#7845;t kh&#7849;u. B&#7857;ng ch&#7913;ng cho th&#7845;y nh&#7919;ng kh&amp;oacute; kh&#259;n c&#7911;a ng&amp;agrave;nh th&amp;eacute;p trong th&#7901;i gian qua &#273;&#432;&#7907;c gi&#7843;m b&#7899;t m&#7897;t ph&#7847;n l&amp;agrave; nh&#7901; v&amp;agrave;o vi&#7879;c &#273;&#7849;y m&#7841;nh l&#432;&#7907;ng xu&#7845;t kh&#7849;u.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong 10 th&amp;aacute;ng &#273;&#7847;u n&#259;m nay, c&amp;aacute;c doanh nghi&#7879;p th&amp;eacute;p &#273;&amp;atilde; xu&#7845;t kh&#7849;u x&#7845;p x&#7881; 1,3 tri&#7879;u t&#7845;n, ch&#7911; y&#7871;u l&amp;agrave; c&amp;aacute;c lo&#7841;i th&amp;eacute;p x&amp;acirc;y d&#7921;ng, th&amp;eacute;p &#7889;ng, th&amp;eacute;p cu&#7897;n c&amp;aacute;n ngu&#7897;i, t&amp;ocirc;n m&#7841; k&#7869;m, t&amp;ocirc;n m&#7841; m&amp;agrave;u, &#273;&#7841;t kim ng&#7841;ch kho&#7843;ng 1,5 t&#7881; &#273;&amp;ocirc; la M&#7929;, t&#259;ng m&#7841;nh so v&#7899;i kim ng&#7841;ch 1,3 t&#7881; &#273;&amp;ocirc; la M&#7929; trong c&#7843; n&#259;m 2010.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng &#273;&amp;atilde; ho&amp;agrave;n ch&#7881;nh v&amp;agrave; &#273;ang tr&amp;igrave;nh Ch&amp;iacute;nh ph&#7911; xem x&amp;eacute;t, ph&amp;ecirc; duy&#7879;t &#273;&#7873; &amp;aacute;n &#273;i&#7873;u ch&#7881;nh &amp;ldquo;Quy ho&#7841;ch ph&amp;aacute;t tri&#7875;n h&#7879; th&#7889;ng s&#7843;n xu&#7845;t v&amp;agrave; h&#7879; th&#7889;ng ph&amp;acirc;n ph&#7889;i m&#7863;t h&amp;agrave;ng th&amp;eacute;p giai &#273;o&#7841;n 2011 - 2020, t&#7847;m nh&amp;igrave;n 2025&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng C&#432;&#7901;ng cho bi&#7871;t m&#7909;c ti&amp;ecirc;u trong &#273;i&#7873;u ch&#7881;nh quy ho&#7841;ch l&#7847;n n&amp;agrave;y l&amp;agrave; nh&#7857;m t&#7853;p trung h&#432;&#7899;ng &#273;&#7847;u t&#432; v&amp;agrave;o s&#7843;n xu&#7845;t c&amp;aacute;c lo&#7841;i th&amp;eacute;p thi&#7871;u nhi&#7873;u, ch&#432;a s&#7843;n xu&#7845;t trong n&#432;&#7899;c nh&#432; th&amp;eacute;p cu&#7897;n c&amp;aacute;n n&amp;oacute;ng, th&amp;eacute;p ch&#7871; t&#7841;o c&#417; kh&amp;iacute;, th&amp;eacute;p h&#7907;p kim &#273;&#7875; gi&#7843;m b&#7899;t nh&#7853;p kh&#7849;u, tr&amp;aacute;nh &#273;&#7847;u t&#432; tr&amp;agrave;n lan.&lt;/p&gt;\r\n','02/11/2011','02/11/2011','/home/uploads/image/news/10-2011/nganh-thep-van-lac-quan.jpg',23,2,1828,'huyennt',1,'nganh-thep-van-lac-quan-trong-dai-han','ngh&agrave;nh th&eacute;p, th&#7883; tr&#432;&#7901;ng, VLXD',NULL,NULL,NULL,'ngh&agrave;nh th&eacute;p, th&#7883; tr&#432;&#7901;ng, VLXD',0,0,0,'','','',3,'http://vatlieuxaydung.batdongsan.com.vn',1,0,0,0),(84,'Th&eacute;p: B&#7883; ki&#7879;n ch&#7889;ng b&aacute;n ph&aacute; gi&aacute;','V&#7915;a qua, theo ngu&#7891;n tin t&#7915; C&#7909;c qu&#7843;n l&yacute; c&#7841;nh tranh, c&aacute;c th&agrave;nh vi&ecirc;n c&#7911;a ng&agrave;nh &#7889;ng th&eacute;p cu&#7897;n cacbon c&#7911;a Hoa K&#7923; (CWP) &#273;&atilde; n&#7897;p &#273;&#417;n ki&#7879;n ch&#7889;ng b&aacute;n ph&aacute; gi&aacute; v&agrave; ch&#7889;ng tr&#7907; c&#7845;p &#273;&#7889;i v&#7899;i s&#7843;n ph&#7849;m CWP c&#7911;a &#7844;n &#272;&#7897;, Oman, C&aacute;c Ti&#7875;u v&#432;&#417;ng qu&#7889;c &#7842; R&#7853;p Th&#7889;ng nh&#7845;t v&agrave; Vi&#7879;t Nam.','\r\n&lt;p&gt;&lt;strong&gt;Th&amp;eacute;p: B&#7883; ki&#7879;n ch&#7889;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;0&quot; height=&quot;0&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/thepcuon.JPG&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;0&quot; height=&quot;0&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/thepcuon.JPG&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;400&quot; height=&quot;286&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/thepcuon.JPG&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&#7842;nh minh h&#7885;a.&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n&#272;&#432;&#7907;c bi&#7871;t, &#273;&amp;acirc;y l&amp;agrave; lo&#7841;i th&amp;eacute;p &#273;&#432;&#7907;c d&amp;ugrave;ng trong ng&amp;agrave;nh s&#7843;n xu&#7845;t, trang tr&amp;iacute; n&#7897;i th&#7845;t d&amp;acirc;n d&#7909;ng v&amp;agrave; c&amp;oacute; thu&#7871; nh&#7853;p kh&#7849;u sang M&#7929; &#7903; m&#7913;c 0%.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c doanh nghi&#7879;p Vi&#7879;t Nam trong danh s&amp;aacute;ch &#273;&#417;n ki&#7879;n l&amp;agrave; c&amp;aacute;c c&amp;ocirc;ng ty t&#432; nh&amp;acirc;n, li&amp;ecirc;n doanh nh&#432;: Hoa Sen Group, H&#7919;u Li&amp;ecirc;n &amp;Aacute; Ch&amp;acirc;u, H&amp;ograve;a Ph&amp;aacute;t, Vinapipe,&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n ch&#432;a c&amp;oacute; th&amp;ocirc;ng tin v&#7873; bi&amp;ecirc;n &#273;&#7897; ph&amp;aacute; gi&amp;aacute; v&amp;agrave; ch&#7889;ng tr&#7907; c&#7845;p b&#7883; c&amp;aacute;o bu&#7897;c. Tuy nhi&amp;ecirc;n, c&amp;oacute; kh&#7843; n&#259;ng M&#7929; s&#7869; y&amp;ecirc;u c&#7847;u &amp;aacute;p d&#7909;ng ph&#432;&#417;ng ph&amp;aacute;p ch&#7889;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; &#273;&#7889;i v&#7899;i n&#7873;n kinh t&#7871; phi th&#7883; tr&#432;&#7901;ng &#273;&#7889;i v&#7899;i Vi&#7879;t Nam. &#272;&amp;acirc;y l&amp;agrave; l&#7847;n th&#7913; 2 M&#7929; ki&#7879;n Vi&#7879;t Nam v&#7873; ch&#7889;ng tr&#7907; c&#7845;p v&amp;agrave; ti&#7871;p t&#7909;c s&#7869; g&amp;acirc;y tranh c&amp;atilde;i b&#7903;i c&amp;ugrave;ng l&amp;uacute;c ki&#7879;n ch&#7889;ng tr&#7907; c&#7845;p v&amp;agrave; ch&#7889;ng b&amp;aacute;n gi&amp;aacute; s&#7917; d&#7909;ng ph&#432;&#417;ng ph&amp;aacute;p &#273;&#7889;i v&#7899;i n&#7873;n kinh t&#7871; phi th&#7883; tr&#432;&#7901;ng.&lt;/p&gt;','02/11/2011','02/11/2011','/home/uploads/image/news/10-2011/thepcuon.JPG',23,2,1855,'huyennt',1,'thep-bi-kien-chong-ban-pha-gia','VLXD, th&eacute;p cu&#7889;n, ch&#7889;ng b&aacute;n ph&aacute; gi&aacute;',NULL,NULL,NULL,'VLXD, th&eacute;p cu&#7889;n, ch&#7889;ng b&aacute;n ph&aacute; gi&aacute;',0,0,0,'','','',3,'http://cafeland.vn',1,1,0,1),(85,'V&#7853;t li&#7879;u x&acirc;y d&#7921;ng &#7843;m &#273;&#7841;m theo b&#7845;t &#273;&#7897;ng s&#7843;n','&#272;&#7883;a &#7889;c &#7843;m &#273;&#7841;m &#273;&atilde; k&eacute;o th&#7883; tr&#432;&#7901;ng v&#7853;t li&#7879;u x&acirc;y d&#7921;ng &ldquo;l&#7841;nh&rdquo; theo, d&ugrave; &#273;ang v&agrave;o cao &#273;i&#7875;m x&acirc;y d&#7921;ng cu&#7889;i n&#259;m.','&lt;p&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Ch&#7883;u &#7843;nh h&#432;&#7903;ng n&#7863;ng n&#7873; nh&#7845;t l&amp;agrave; th&amp;eacute;p. &#7870; &#7849;m tri&#7873;n mi&amp;ecirc;n t&#7915; &#273;&#7847;u n&#259;m &#273;&#7871;n h&#7871;t th&amp;aacute;ng 10, l&#432;&#7907;ng ph&amp;ocirc;i th&amp;eacute;p v&amp;agrave; th&amp;eacute;p th&amp;agrave;nh ph&#7849;m t&#7891;n kho l&amp;ecirc;n &#273;&#7871;n 900.000 t&#7845;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Th&amp;eacute;p &amp;ldquo;lao&amp;rdquo; theo b&#7845;t &#273;&#7897;ng s&#7843;n&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;M&#7863;c d&amp;ugrave; &#273;&amp;atilde; gi&#7843;m gi&amp;aacute; b&amp;aacute;n nhi&#7873;u l&#7847;n nh&#432;ng th&#7883; tr&#432;&#7901;ng th&amp;eacute;p v&#7851;n ch&#432;a kh&#7903;i s&#7855;c. Theo l&amp;atilde;nh &#273;&#7841;o m&#7897;t c&amp;ocirc;ng ty th&amp;eacute;p t&#7841;i TP HCM, &#273;&#7875; b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng, doanh nghi&#7879;p n&amp;agrave;y, t&#7915; 3 th&amp;aacute;ng nay, li&amp;ecirc;n t&#7909;c gi&#7843;m gi&amp;aacute;. Hi&#7879;n nay, gi&amp;aacute; th&amp;eacute;p b&amp;aacute;n t&#7841;i nh&amp;agrave; m&amp;aacute;y kh&amp;ocirc;ng k&#7875; VAT kho&#7843;ng 15 tri&#7879;u &#273;&#7891;ng/t&#7845;n, trong khi gi&amp;aacute; ph&amp;ocirc;i &#273;&amp;atilde; 14 tri&#7879;u &#273;&#7891;ng/t&#7845;n. M&#7913;c gi&amp;aacute; tr&amp;ecirc;n &#273;ang ti&#7871;p t&#7909;c gi&#7843;m 200.000 - 300.000 &#273;&#7891;ng/t&#7845;n so v&#7899;i qu&amp;yacute; tr&#432;&#7899;c.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color: rgb(128, 0, 0);&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img width=&quot;500&quot; height=&quot;335&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/10-2011/vat-lieu-xay-dung-am-dam-theo-bat-dong-san.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0);&quot;&gt;&lt;em&gt;T&lt;/em&gt;&lt;em&gt;h&#7883; tr&#432;&#7901;ng B&#272;S &#7843;m &#273;&#7841;m &#273;&amp;atilde; k&amp;eacute;o ng&amp;agrave;nh VLXD &amp;quot;l&#7841;nh&amp;quot; theo.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;T&#7841;i bu&#7893;i h&#7885;p th&amp;aacute;o g&#7905; kh&amp;oacute; kh&#259;n cho ng&amp;agrave;nh th&amp;eacute;p do B&#7897; C&amp;ocirc;ng th&#432;&#417;ng t&#7893; ch&#7913;c, chi&#7873;u 27/10, &amp;ocirc;ng Tr&#7847;n Tu&#7845;n D&#432;&#417;ng, T&#7893;ng gi&amp;aacute;m &#273;&#7889;c T&#7853;p &#273;o&amp;agrave;n H&amp;ograve;a Ph&amp;aacute;t, cho bi&#7871;t, th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n tr&#7847;m l&#7855;ng, nhi&#7873;u d&#7921; &amp;aacute;n ng&#7915;ng thi c&amp;ocirc;ng t&amp;aacute;c &#273;&#7897;ng tr&#7921;c ti&#7871;p &#273;&#7871;n ng&amp;agrave;nh th&amp;eacute;p, nh&#7845;t l&amp;agrave; th&amp;eacute;p x&amp;acirc;y d&#7921;ng. Nhu c&#7847;u th&amp;eacute;p gi&#7843;m v&amp;agrave; gi&amp;aacute; gi&#7843;m m&#7841;nh d&#7851;n &#273;&#7871;n nhi&#7873;u nh&amp;agrave; m&amp;aacute;y th&#7915;a th&amp;eacute;p. &amp;Ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng, Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam, ph&amp;agrave;n n&amp;agrave;n: Trong 9 th&amp;aacute;ng qua, ti&amp;ecirc;u th&#7909; th&amp;eacute;p x&amp;acirc;y d&#7921;ng ch&#7881; g&#7847;n 6,1 tri&#7879;u t&#7845;n, b&#7857;ng 96,02% so v&#7899;i n&#259;m 2010. D&#7921; ki&#7871;n, ti&amp;ecirc;u th&#7909; th&amp;eacute;p c&#7843; n&#259;m 2011 gi&#7843;m 7,69% so v&#7899;i n&#259;m 2010. Ph&amp;oacute; t&#7893;ng gi&amp;aacute;m &#273;&#7889;c T&#7893;ng c&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam, Nghi&amp;ecirc;m Xu&amp;acirc;n &#272;a th&#7915;a nh&#7853;n, 10 th&amp;aacute;ng qua, s&#7843;n xu&#7845;t th&amp;eacute;p x&amp;acirc;y d&#7921;ng ch&#7881; &#273;&#7841;t 1,9 tri&#7879;u t&#7845;n, gi&#7843;m 2% so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m ngo&amp;aacute;i, nh&#432;ng l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; gi&#7843;m t&#7899;i 6,7%, t&#7891;n kho 185.000 t&#7845;n... &amp;ldquo;&lt;em&gt;C&amp;oacute; &#273;&#7871;n 10 ng&amp;agrave;y trong 1 th&amp;aacute;ng ch&amp;uacute;ng t&amp;ocirc;i kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c s&#7843;n ph&#7849;m n&amp;agrave;o&lt;/em&gt;&amp;rdquo;, l&amp;atilde;nh &#273;&#7841;o m&#7897;t c&amp;ocirc;ng ty th&amp;eacute;p than th&#7903;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng C&#432;&#7901;ng c&amp;ograve;n cho bi&#7871;t hi&#7879;n &#273;&amp;atilde; c&amp;oacute; m&#7897;t doanh nghi&#7879;p th&amp;eacute;p l&amp;ecirc;n ti&#7871;ng b&amp;aacute;n c&#417; s&#7903; s&#7843;n xu&#7845;t, c&amp;aacute;c c&amp;ocirc;ng ty kh&amp;aacute;c ph&#7843;i ch&#7841;y d&#432;&#7899;i c&amp;ocirc;ng su&#7845;t, kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng v&amp;agrave; c&amp;oacute; nguy c&#417; ng&#7915;ng s&#7843;n xu&#7845;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;L&#7895; v&#7851;n kh&amp;ocirc;ng &#273;&#7849;y &#273;&#432;&#7907;c h&amp;agrave;ng&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Xi m&#259;ng c&#361;ng &#273;&#432;&#7907;c xem l&amp;agrave; ng&amp;agrave;nh &amp;ldquo;n&amp;oacute;ng&amp;rdquo; khi l&#432;&#7907;ng t&#7891;n kho kh&amp;ocirc;ng thua k&amp;eacute;m th&amp;eacute;p. Ch&#7881; ri&amp;ecirc;ng t&#7841;i T&#7893;ng c&amp;ocirc;ng ty C&amp;ocirc;ng nghi&#7879;p Xi m&#259;ng Vi&#7879;t Nam (Vicem) &#273;ang t&#7891;n kho&#7843;ng 2 tri&#7879;u t&#7845;n xi m&#259;ng v&amp;agrave; clinker. Tuy nhi&amp;ecirc;n, tr&amp;aacute;i v&#7899;i di&#7877;n bi&#7871;n c&#7911;a th&amp;eacute;p l&amp;agrave; s&#7913;c ti&amp;ecirc;u th&#7909; v&amp;agrave; m&#7913;c gi&amp;aacute; &#273;&#7873;u gi&#7843;m th&amp;igrave; gi&amp;aacute; xi m&#259;ng l&#7841;i t&#259;ng kho&#7843;ng 5% so v&#7899;i qu&amp;yacute; tr&#432;&#7899;c. C&#7909; th&#7875;, gi&amp;aacute; xi m&#259;ng Holcim qu&amp;yacute; tr&#432;&#7899;c 86.000 &#273;&#7891;ng/bao th&amp;igrave; nay l&amp;ecirc;n 91.000 &#273;&#7891;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c lo&#7841;i v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng kh&amp;aacute;c nh&#432; g&#7841;ch, c&amp;aacute;t, &#273;&amp;aacute;&amp;hellip; c&#361;ng chung s&#7889; ph&#7853;n khi s&#7913;c ti&amp;ecirc;u th&#7909; kh&amp;aacute; ch&#7853;m. Theo s&#7889; li&#7879;u th&#7889;ng k&amp;ecirc; c&#7911;a ng&amp;agrave;nh g&#7889;m, l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t t&#7891;n kho &#273;&amp;atilde; l&amp;ecirc;n tr&amp;ecirc;n 30 tri&#7879;u m2. C&amp;ograve;n ng&amp;agrave;nh k&amp;iacute;nh m&#7863;c d&amp;ugrave; m&#7913;c gi&amp;aacute; gi&#7843;m h&#417;n 40% so v&#7899;i qu&amp;yacute; tr&#432;&#7899;c v&#7851;n kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng, d&#7921; &#273;o&amp;aacute;n l&#432;&#7907;ng h&amp;agrave;ng t&#7891;n kho s&#7869; t&#259;ng g&#7845;p 2 l&#7847;n so v&#7899;i n&#259;m 2010. Theo &amp;ocirc;ng Nguy&#7877;n Quang Vinh, T&#7893;ng gi&amp;aacute;m &#273;&#7889;c C&amp;ocirc;ng ty x&amp;acirc;y d&#7921;ng Th&amp;agrave;nh ph&#7889; (Citicons), &#273;&amp;uacute;ng ra gi&amp;aacute; ph&#7843;i t&#259;ng do gi&amp;aacute; &#273;&#7847;u v&amp;agrave;o t&#259;ng&amp;hellip; nh&#432;ng do h&amp;agrave;ng t&#7891;n nhi&#7873;u qu&amp;aacute;, ai c&#361;ng mu&#7889;n b&amp;aacute;n ra &#273;&#7875; thu h&#7891;i v&#7889;n v&#7873; ti&#7871;p t&#7909;c s&#7843;n xu&#7845;t n&amp;ecirc;n &#273;&amp;agrave;nh ph&#7843;i gi&#7843;m gi&amp;aacute; 5 - 7% &#273;&#7875; mong thu ti&#7873;n m&#7863;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7913; tr&#432;&#7903;ng B&#7897; X&amp;acirc;y d&#7921;ng Nguy&#7877;n Tr&#7847;n Nam c&#361;ng x&amp;aacute;c nh&#7853;n, ng&amp;agrave;nh s&#7843;n xu&#7845;t xi-m&#259;ng, b&amp;ecirc;-t&amp;ocirc;ng &#273;&amp;atilde; b&#7883; &#7843;nh h&#432;&#7903;ng n&#7863;ng. &amp;ldquo;L&#432;&#7907;ng ti&amp;ecirc;u th&#7909; xi m&#259;ng ch&#7853;m, t&#7891;n kho &#7903; h&#7847;u h&#7871;t c&amp;aacute;c nh&amp;agrave; s&#7843;n xu&#7845;t b&amp;ecirc; t&amp;ocirc;ng t&#7915; c&#7885;c ch&#7883;u &#7913;ng l&#7921;c cho &#273;&#7871;n k&#7871;t c&#7845;u b&amp;ecirc; t&amp;ocirc;ng l&#7855;p r&amp;aacute;p t&#259;ng g&#7845;p &#273;&amp;ocirc;i. C&amp;ocirc;ng nh&amp;acirc;n &#273;&amp;atilde; b&#7855;t &#273;&#7847;u ph&#7843;i ngh&#7881; vi&#7879;c&amp;rdquo;, &amp;ocirc;ng n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;&lt;em&gt;T&#7915; th&amp;aacute;ng 6/2011, c&amp;ocirc;ng ty t&amp;ocirc;i ch&#7881; k&amp;yacute; &#273;&#432;&#7907;c 1 c&amp;ocirc;ng tr&amp;igrave;nh x&amp;acirc;y d&#7921;ng v&amp;agrave; &#273;&#7871;n nay kh&amp;ocirc;ng k&amp;yacute; th&amp;ecirc;m &#273;&#432;&#7907;c h&#7907;p &#273;&#7891;ng n&amp;agrave;o. &#272;&amp;atilde; th&#7871;, m&#7897;t s&#7889; h&#7907;p &#273;&#7891;ng &#273;ang l&amp;agrave;m c&#361;ng ph&#7843;i ng&#432;ng do ch&#7911; &#273;&#7847;u t&#432; k&#7865;t v&#7889;n. Hi&#7879;n v&#7851;n c&amp;ograve;n m&#7897;t v&amp;agrave;i c&amp;ocirc;ng tr&amp;igrave;nh &#273;ang ho&#7841;t &#273;&#7897;ng nh&#432;ng ch&#7881; c&#7847;m ch&#7915;ng. Nhi&#7873;u c&amp;ocirc;ng ty x&amp;acirc;y d&#7921;ng kh&amp;aacute;c c&#361;ng r&#417;i v&amp;agrave;o t&amp;igrave;nh c&#7843;nh t&#432;&#417;ng t&#7921; khi kh&amp;ocirc;ng k&amp;yacute; &#273;&#432;&#7907;c h&#7907;p &#273;&#7891;ng b&#7903;i c&amp;aacute;c d&#7921; &amp;aacute;n b&#7845;t &#273;&#7897;ng s&#7843;n &#273;ang ng&#7915;ng thi c&amp;ocirc;ng. Kh&amp;ocirc;ng c&amp;oacute; h&#7907;p &#273;&#7891;ng m&#7899;i, nh&#432;ng h&#7907;p &#273;&#7891;ng c&#361; c&#361;ng t&#7855;t, v&amp;igrave; ng&amp;acirc;n h&amp;agrave;ng kh&amp;ocirc;ng gi&#7843;i ng&amp;acirc;n v&#7889;n &#273;&#7875; nh&amp;agrave; th&#7847;u c&amp;oacute; ti&#7873;n mua VLXD thi c&amp;ocirc;ng&lt;/em&gt;&amp;rdquo;. (&amp;Ocirc;ng Nguy&#7877;n Quang Vinh, T&#7893;ng gi&amp;aacute;m &#273;&#7889;c Citicons).&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;','03/11/2011','03/11/2011','/home/uploads/image/news/10-2011/vat-lieu-xay-dung-am-dam-theo-bat-dong-san.jpg',23,2,1839,'huyennt',1,'vat-lieu-xay-dung-am-dam-theo-bat-dong-san','th&eacute;p lao v&agrave;o B&#272;S, VLXD &#7843;m &#273;&#7841;m theo B&#272;S',NULL,NULL,NULL,'th&eacute;p lao v&agrave;o B&#272;S, VLXD &#7843;m &#273;&#7841;m theo B&#272;S',0,0,0,'','','huyennt',3,'http://www.diaoconline.vn',1,0,0,0),(86,'Doanh nghi&#7879;p th&eacute;p : Kh&oacute; kh&#259;n ch&#7891;ng ch&#7845;t','V&#7899;i th&#7921;c tr&#7841;ng t&#7891;n kho kho&#7843;ng 500.000 t&#7845;n v&agrave; vi&#7879;c ti&ecirc;u th&#7909; li&ecirc;n t&#7909;c &ldquo;xu&#7889;ng d&#7889;c kh&ocirc;ng phanh&rdquo;, t&#7915; m&#7913;c 480.000 t&#7845;n trong th&aacute;ng 8, &#273;&atilde; gi&#7843;m 100.000 t&#7845;n v&agrave;o th&aacute;ng 9 v&agrave; trong th&aacute;ng 10 ch&#7881; &#273;&#7841;t x&#7845;p x&#7881; 300.000 t&#7845;n, ng&agrave;nh th&eacute;p &#273;ang trong t&igrave;nh tr&#7841;ng &ldquo;ch&#7907; chi&#7873;u&rdquo;.','&lt;p&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;gt;&lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=54&quot;&gt; Th&amp;eacute;p: B&#7883; ki&#7879;n ch&#7889;ng ph&amp;aacute; gi&amp;aacute; &lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&amp;gt; &lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=53&quot;&gt;Ng&amp;agrave;nh th&amp;eacute;p v&#7851;n l&#7841;c quan trong d&amp;agrave;i h&#7841;n&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;gt;&lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=51&quot;&gt; Doanh nghi&#7879;p th&amp;eacute;p hoang mang v&amp;igrave; l&#7895;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng - Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p (VSA) cho bi&#7871;t t&#259;ng tr&#432;&#7903;ng ng&amp;agrave;nh th&amp;eacute;p &#273;ang &#7903; m&#7913;c... &amp;acirc;m 7,69%, 20% DN th&amp;eacute;p &#273;ang r&#7845;t kh&amp;oacute; kh&#259;n, c&#7847;n ph&#7843;i t&amp;aacute;i c&#417; c&#7845;u n&#7871;u kh&amp;ocirc;ng mu&#7889;n b&#7883; ph&amp;aacute; s&#7843;n v&amp;agrave;o n&#259;m 2012.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&amp;ldquo;B&amp;aacute;n m&amp;igrave;nh&amp;rdquo; &#273;&#7875;... tr&#7843; n&#7907;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;M&#7863;c d&amp;ugrave; cho t&#7899;i th&#7901;i &#273;i&#7875;m hi&#7879;n t&#7841;i ch&#432;a c&amp;oacute; DN n&amp;agrave;o ch&amp;iacute;nh th&#7913;c c&amp;ocirc;ng b&#7889; ph&amp;aacute; s&#7843;n, song v&#7899;i nh&#7919;ng kh&amp;oacute; kh&#259;n hi&#7879;n t&#7841;i, c&#7897;ng v&#7899;i kho&#7843;n l&amp;atilde;i ng&amp;acirc;n h&amp;agrave;ng &#273;&amp;atilde; khi&#7871;n nhi&#7873;u DN l&amp;acirc;m v&amp;agrave;o c&#7843;nh &amp;ldquo;&#273;&#432;&#7901;ng c&amp;ugrave;ng&amp;rdquo; l&amp;agrave; ph&#7843;i rao b&amp;aacute;n t&amp;agrave;i s&#7843;n &#273;&#7875; tr&#7843; n&#7907;. &#272;i&#7875;n h&amp;igrave;nh trong s&#7889; n&amp;agrave;y l&amp;agrave; Cty th&amp;eacute;p V&#7841;n L&#7907;i, m&#7899;i &#273;&amp;acirc;y ng&amp;agrave;y 18/10/2011, t&#7841;i &#272;&#7841;i h&#7897;i c&#7893; &#273;&amp;ocirc;ng b&#7845;t th&#432;&#7901;ng, DN n&amp;agrave;y &#273;&amp;atilde; th&amp;ocirc;ng qua vi&#7879;c b&amp;aacute;n t&amp;agrave;i s&#7843;n Cty &#273;&#7875; th&#7921;c hi&#7879;n vi&#7879;c tr&#7843; c&amp;aacute;c kho&#7843;n n&#7907; cho ng&amp;acirc;n h&amp;agrave;ng v&amp;agrave; c&amp;aacute;c ch&#7911; n&#7907;.&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img width=&quot;500&quot; height=&quot;335&quot; src=&quot;/home/uploads/image/news/10-2011/doanh-nghiep-thep--kho-khan-chong-chat.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color: rgb(128, 0, 0);&quot;&gt;&lt;em&gt;Ng&amp;agrave;nh th&amp;eacute;p &#273;ang &#273;&#7889;i m&#7863;t v&#7899;i l&#432;&#7907;ng h&amp;agrave;ng t&#7891;n kho &#7903; m&#7913;c cao,hi&#7879;n c&amp;oacute; kho&#7843;ng 500.000 t&#7845;n, g&#7845;p &#273;&amp;ocirc;i l&amp;uacute;c b&amp;igrave;nh th&#432;&#7901;ng &lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo &amp;ocirc;ng C&#432;&#7901;ng, v&#7899;i t&amp;igrave;nh h&amp;igrave;nh gi&amp;aacute; th&amp;eacute;p li&amp;ecirc;n ti&#7871;p gi&#7843;m nh&#432; hi&#7879;n nay c&#7897;ng v&#7899;i &amp;ldquo;b&amp;agrave;i ca&amp;rdquo; mu&amp;ocirc;n th&#7911;a: &amp;ldquo;&#272;&#7847;u n&#259;m l&amp;atilde;i l&#7899;n, cu&#7889;i n&#259;m &amp;acirc;m v&amp;agrave;o&amp;rdquo; c&#7911;a ng&amp;agrave;nh tr&#432;&#7899;c sau g&amp;igrave; c&amp;aacute;c DN c&#361;ng s&#7869; ph&#7843;i &amp;ldquo;ra &#273;i&amp;rdquo;. C&#7909; th&#7875; hi&#7879;n nay, ng&amp;agrave;nh th&amp;eacute;p &#273;ang &#273;&#7889;i m&#7863;t v&#7899;i l&#432;&#7907;ng h&amp;agrave;ng t&#7891;n kho &#7903; m&#7913;c cao, hi&#7879;n kho&#7843;ng 500.000 t&#7845;n, g&#7845;p &#273;&amp;ocirc;i l&amp;uacute;c b&amp;igrave;nh th&#432;&#7901;ng. T&amp;igrave;nh tr&#7841;ng n&amp;agrave;y &#273;ang di&#7877;n ra &#7903; nh&#7919;ng DN th&amp;eacute;p m&#7899;i ra &#273;&#7901;i, &#273;ang ch&#7883;u chi ph&amp;iacute; l&amp;atilde;i vay l&#7899;n, trong khi h&amp;agrave;ng l&amp;agrave;m ra kh&amp;ocirc;ng ti&amp;ecirc;u th&#7909; &#273;&#432;&#7907;c. Gi&amp;aacute; th&amp;eacute;p b&amp;aacute;n t&#7841;i nh&amp;agrave; m&amp;aacute;y kh&amp;ocirc;ng k&#7875; thu&#7871; gi&amp;aacute; tr&#7883; gia t&#259;ng kho&#7843;ng 15 tri&#7879;u &#273;&#7891;ng/t&#7845;n; gi&amp;aacute; ph&amp;ocirc;i tr&amp;ecirc;n 14 tri&#7879;u &#273;&#7891;ng/t&#7845;n. Trong khi &#273;&amp;oacute;, &#273;&#7875; h&amp;ograve;a v&#7889;n, DN ph&#7843;i b&amp;aacute;n &#273;&#432;&#7907;c 15,5 tri&#7879;u &#273;&#7891;ng/t&#7845;n. &amp;ldquo;&#272;i&#7873;u &#273;&amp;oacute; c&amp;oacute; ngh&#297;a l&amp;agrave; DN &#273;ang ch&#7883;u l&#7895;. N&#7871;u 3 th&amp;aacute;ng n&#7919;a, t&amp;igrave;nh h&amp;igrave;nh v&#7851;n kh&amp;ocirc;ng &#273;&#7893;i, to&amp;agrave;n b&#7897; s&#7889; l&amp;atilde;i t&#7915; &#273;&#7847;u n&#259;m s&#7869; tr&#7903; v&#7873; 0 ho&#7863;c &amp;acirc;m&amp;rdquo; - &amp;Ocirc;ng C&#432;&#7901;ng kh&#7859;ng &#273;&#7883;nh.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nL&#7869; th&#432;&#7901;ng t&amp;igrave;nh, khi l&amp;agrave;m &#259;n kinh doanh c&amp;oacute; l&amp;atilde;i th&amp;igrave; kh&amp;ocirc;ng sao, nh&#432;ng h&#7877; kinh doanh kh&amp;oacute; kh&#259;n th&amp;igrave; c&amp;aacute;c DN th&#432;&#7901;ng t&amp;igrave;m &#273;&#7911; m&#7885;i c&amp;aacute;ch &#273;&#7875; b&amp;aacute;n cho b&#7857;ng &#273;&#432;&#7907;c h&amp;agrave;ng ho&amp;aacute; c&#7911;a m&amp;igrave;nh. &#272;&amp;atilde; v&#7853;y &amp;ldquo;ho&#7841; v&amp;ocirc; &#273;&#417;n ch&amp;iacute;&amp;rdquo;, khi th&amp;eacute;p trong n&#432;&#7899;c &#273;ang g&#7863;p kh&amp;oacute; th&amp;igrave; ph&amp;iacute;a xu&#7845;t kh&#7849;u, 2 s&#7843;n ph&#7849;m ch&amp;iacute;nh l&amp;agrave; th&amp;eacute;p cu&#7897;n c&amp;aacute;n ngu&#7897;i xu&#7845;t sang Indonesia &#273;&amp;atilde; b&#7883; ki&#7879;n b&amp;aacute;n ph&amp;aacute; gi&amp;aacute;, &#7889;ng th&amp;eacute;p m&#7899;i &#273;&amp;acirc;y nh&#7845;t c&#361;ng b&#7883; ki&#7879;n b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; t&#7841;i M&#7929;. Kh&amp;ocirc;ng d&#7915;ng l&#7841;i &#7903; &#273;&amp;oacute;, c&amp;oacute; th&#7875; s&#7843;n ph&#7849;m tr&amp;aacute;ng t&amp;ocirc;n, m&#7841; k&#7869;m c&#361;ng b&#7883; ki&#7879;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;DN ph&#7843;i t&#7921; c&#7913;u m&amp;igrave;nh&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&#432;&#7899;c nh&#7919;ng kh&amp;oacute; kh&#259;n c&#7911;a ng&amp;agrave;nh th&amp;eacute;p, Th&#7913; tr&#432;&#7903;ng B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng L&amp;ecirc; D&#432;&#417;ng Quang cho r&#7857;ng, th&#7901;i gian t&#7899;i ch&#7855;c ch&#7855;n ng&amp;agrave;nh th&amp;eacute;p s&#7869; v&#7851;n c&amp;ograve;n g&#7863;p kh&amp;oacute; kh&#259;n, c&#7847;n h&#432;&#7899;ng th&amp;aacute;o g&#7905;, song DN ph&#7843;i ch&#7911; &#273;&#7897;ng th&amp;aacute;o g&#7905; ch&#7913; kh&amp;ocirc;ng ch&#7901; ai kh&amp;aacute;c. D&#297; nhi&amp;ecirc;n, Ch&amp;iacute;nh ph&#7911; v&amp;agrave; B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng c&amp;oacute; tr&amp;aacute;ch nhi&#7879;m gi&amp;uacute;p &#273;&#7905; v&#7873; m&#7863;t ch&amp;iacute;nh s&amp;aacute;ch, nh&#432;ng b&#7843;n th&amp;acirc;n c&amp;aacute;c DN ph&#7843;i t&#7921; th&amp;acirc;n v&#7853;n &#273;&#7897;ng l&amp;agrave; ch&amp;iacute;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7891;ng t&amp;igrave;nh v&#7899;i quan &#273;i&#7875;m n&amp;agrave;y c&#7911;a B&#7897; C&amp;ocirc;ng Th&#432;&#417;ng, nhi&#7873;u DN cho bi&#7871;t b&#7843;n th&amp;acirc;n h&#7885; c&#361;ng &#273;ang &amp;ldquo;g&#7891;ng m&amp;igrave;nh&amp;rdquo; &#273;&#7875; v&#432;&#7907;t kh&amp;oacute; trong giai &#273;o&#7841;n hi&#7879;n nay. B&#7857;ng ch&#7913;ng l&amp;agrave; nhi&#7873;u DN &#273;&amp;atilde; ph&#7843;i t&#7921; ti&#7871;t gi&amp;aacute;m s&#7843;n l&#432;&#7907;ng v&amp;agrave; t&#7921; t&amp;igrave;m c&amp;aacute;c gi&#7843;i ph&amp;aacute;p &#273;&#7889;i ph&amp;oacute; v&#7899;i t&amp;igrave;nh th&#7871;. &amp;Ocirc;ng Tr&#7847;n Tu&#7845;n D&#432;&#417;ng - T&#7893;ng gi&amp;aacute;m &#273;&#7889;c T&#7853;p &#273;o&amp;agrave;n H&amp;ograve;a Ph&amp;aacute;t, DN &#273;&#7913;ng th&#7913; hai v&#7873; s&#7843;n l&#432;&#7907;ng th&amp;eacute;p trong n&#432;&#7899;c cho bi&#7871;t, DN n&amp;agrave;y &#273;&amp;atilde; ph&#7843;i c&#7855;t gi&#7843;m 20% c&amp;ocirc;ng su&#7845;t do kh&amp;oacute; kh&#259;n th&#7883; tr&#432;&#7901;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nC&amp;ugrave;ng quan &#273;i&#7875;m n&amp;agrave;y, &amp;ocirc;ng Ho&amp;agrave;ng V&#259;n T&amp;ograve;ng - Ch&#7911; t&#7883;ch Cty Gang th&amp;eacute;p Th&amp;aacute;i Nguy&amp;ecirc;n c&#361;ng cho hay, ti&amp;ecirc;u th&#7909; th&#7901;i gian qua c&#7911;a &#273;&#417;n v&#7883; ch&#7881; b&#7857;ng 70-71%. DN bu&#7897;c ph&#7843;i gi&#7843;m gi&amp;aacute; th&amp;agrave;nh, t&#7915; &#273;&#7847;u n&#259;m &#273;&#7871;n nay &#273;&amp;atilde; ti&#7871;t ki&#7879;m &#273;&#432;&#7907;c t&#7899;i 150 t&#7881; &#273;&#7891;ng so v&#7899;i gi&amp;aacute; th&amp;agrave;nh k&#7871; ho&#7841;ch.&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;ecirc;n c&#7841;nh nh&#7919;ng vi&#7879;c t&#7921; th&amp;acirc;n v&#7853;n &#273;&#7897;ng c&#7911;a m&amp;igrave;nh, c&amp;aacute;c DN c&#361;ng mong mu&#7889;n Ch&amp;iacute;nh ph&#7911; c&#7847;n c&amp;oacute; nh&#7919;ng bi&#7879;n ph&amp;aacute;p ph&amp;ugrave; h&#7907;p v&#7899;i th&amp;ocirc;ng l&#7879; qu&#7889;c t&#7871; &#273;&#7875; gi&#7843;m l&#432;&#7907;ng nh&#7853;p kh&#7849;u, ch&#7889;ng gian l&#7853;n th&#432;&#417;ng m&#7841;i t&#7915; Trung Qu&#7889;c v&amp;agrave; h&#7841;n ch&#7871; xu&#7845;t qu&#7863;ng sang n&#432;&#7899;c n&amp;agrave;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c chuy&amp;ecirc;n gia cho r&#7857;ng, ng&amp;agrave;nh th&amp;eacute;p c&#7847;n t&amp;iacute;nh t&#7899;i vi&#7879;c l&#7853;p h&amp;agrave;ng r&amp;agrave;o k&#7929; thu&#7853;t ph&amp;ugrave; h&#7907;p v&#7899;i &#273;&#7863;c th&amp;ugrave; trong n&#432;&#7899;c, &#273;&#7875; c&#7841;nh tranh &#273;&#432;&#7907;c v&#7899;i th&amp;eacute;p nh&#7853;p kh&#7849;u. &#272;i&#7873;u n&amp;agrave;y &#7903; nhi&#7873;u n&#432;&#7899;c c&#361;ng &#273;&amp;atilde; th&#7921;c hi&#7879;n. Ngo&amp;agrave;i ra, tr&#432;&#7899;c nh&#7919;ng kh&amp;oacute; kh&#259;n v&#7873; ti&amp;ecirc;u th&#7909; c&#361;ng nh&#432; s&#7921; &#273;&#7847;u t&#432; &#7891; &#7841;t, ng&amp;agrave;nh th&amp;eacute;p c&#7847;n t&amp;iacute;nh t&#7899;i gi&#7843;i ph&amp;aacute;p t&amp;aacute;i c&#417; c&#7845;u, trong &#273;&amp;oacute; gi&#7843;i ph&amp;aacute;p mua b&amp;aacute;n, s&amp;aacute;p nh&#7853;p l&amp;agrave; m&#7897;t gi&#7843;i ph&amp;aacute;p t&#7889;t v&amp;igrave; ch&#7881; c&amp;oacute; nh&#7919;ng nh&amp;agrave; &#273;&#7847;u t&#432; c&amp;oacute; n&#259;ng l&#7921;c m&#7899;i c&amp;oacute; &#273;&#7911; kh&#7843; n&#259;ng t&amp;agrave;i ch&amp;iacute;nh v&#7921;c d&#7853;y c&amp;aacute;c DN l&amp;agrave;m &#259;n k&amp;eacute;m hi&#7879;u qu&#7843;, ch&#7859;ng h&#7841;n nh&#432; th&#432;&#417;ng v&#7909; Th&amp;eacute;p &#272;&amp;igrave;nh V&#361; b&amp;aacute;n 70% n&#7907; cho nh&amp;agrave; &#273;&#7847;u t&#432; c&#7911;a Australia l&amp;agrave; m&#7897;t v&amp;iacute; d&#7909;.&lt;/p&gt;\r\n\r\n','03/11/2011','03/11/2011','/home/uploads/image/news/10-2011/doanh-nghiep-thep--kho-khan-chong-chat.jpg',23,2,2007,'huyennt',1,'doanh-nghiep-thep-kho-khan-chong-chat','doanh nghi&#7879;p th&eacute;p, kh&oacute; kh&#259;n ch&#7891;ng ch&#7845;t, ph&aacute; s&#7843;n, VLXD',NULL,NULL,NULL,'doanh nghi&#7879;p th&eacute;p, kh&oacute; kh&#259;n ch&#7891;ng ch&#7845;t, ph&aacute; s&#7843;n, VLXD',0,0,0,'','','',3,'http://dothi.net',1,0,0,0),(87,'Doanh nghi&#7879;p th&eacute;p thu h&#7865;p s&#7843;n xu&#7845;t','C&aacute;c doanh nghi&#7879;p l&#7899;n ng&agrave;nh th&eacute;p &#273;ang n&#7895; l&#7921;c ti&#7871;t gi&#7843;m s&#7843;n l&#432;&#7907;ng, s&#7855;p x&#7871;p l&#7841;i s&#7843;n xu&#7845;t nh&#7857;m v&#432;&#7907;t qua giai &#273;o&#7841;n b&aacute;n h&agrave;ng kh&oacute; kh&#259;n hi&#7879;n nay.','&lt;p&gt;C&amp;aacute;c tin li&amp;ecirc;n quan:&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;gt; &lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=53&quot;&gt;Ng&amp;agrave;nh th&amp;eacute;p v&#7851;n l&#7841;c quan trong d&amp;agrave;i h&#7841;n&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&amp;gt; &lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=54&quot;&gt;Th&amp;eacute;p: B&#7883; ki&#7879;n ch&#7889;ng ph&amp;aacute; gi&amp;aacute; &lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;gt;&lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=51&quot;&gt; Doanh nghi&#7879;p th&amp;eacute;p hoang mang v&amp;igrave; l&#7895;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;gt; &lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=56&quot;&gt;Doanh nghi&#7879;p th&amp;eacute;p: kh&amp;oacute; kh&#259;n ch&#7891;ng ch&#7845;t&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;R&#7845;t th&#7859;ng th&#7855;n khi nh&amp;igrave;n nh&#7853;n v&#7873; c&amp;aacute;c t&amp;aacute;c &#273;&#7897;ng b&amp;ecirc;n ngo&amp;agrave;i &#7843;nh h&#432;&#7903;ng t&#7899;i s&#7843;n xu&#7845;t, kinh doanh c&#7911;a ng&amp;agrave;nh th&amp;eacute;p, tr&#7843; l&#7901;i ph&#7887;ng v&#7845;n c&#7911;a ph&amp;oacute;ng vi&amp;ecirc;n B&amp;aacute;o &#272;&#7847;u t&#432; v&amp;agrave;o &#273;&#7847;u tu&#7847;n n&amp;agrave;y, &amp;ocirc;ng Tr&#7847;n Tu&#7845;n D&#432;&#417;ng, T&#7893;ng gi&amp;aacute;m &#273;&#7889;c T&#7853;p &#273;o&amp;agrave;n H&amp;ograve;a Ph&amp;aacute;t (HPG) cho r&#7857;ng, kh&amp;oacute; kh&#259;n c&#7911;a ng&amp;agrave;nh th&amp;eacute;p do gi&#7843;m &#273;&#7847;u t&#432; c&amp;ocirc;ng l&amp;agrave; kh&amp;oacute; tr&amp;aacute;nh kh&#7887;i. B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n l&#7841;i &#273;ang ch&#7883;u nh&#7919;ng t&amp;aacute;c &#273;&#7897;ng x&#7845;u v&amp;agrave; d&#7921; &#273;o&amp;aacute;n ph&#7843;i v&amp;agrave;i n&#259;m n&#7919;a m&#7899;i c&amp;oacute; th&#7875; ph&#7909;c h&#7891;i.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0);&quot;&gt;&lt;em&gt;&lt;br /&gt;\r\n&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0);&quot;&gt;&lt;em&gt;&lt;img width=&quot;500&quot; height=&quot;365&quot; src=&quot;/home/uploads/image/news/10-2011/doanh-nghiep-thep-thu-hep-san-xuat.jpg&quot; alt=&quot;&quot; /&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0);&quot;&gt;&lt;em&gt;&lt;br /&gt;\r\nNhi&#7873;u doanh nghi&#7879;p c&amp;ograve;n t&#7891;n &#273;&#7885;ng h&amp;agrave;ng tr&#259;m t&#7845;n th&amp;eacute;p.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nTrong khi kinh t&#7871; trong n&#432;&#7899;c kh&amp;oacute; kh&#259;n, th&amp;igrave; t&amp;igrave;nh h&amp;igrave;nh th&#7871; gi&#7899;i c&#361;ng kh&amp;ocirc;ng s&amp;aacute;ng s&#7911;a g&amp;igrave;. &amp;ldquo;Gi&amp;aacute; th&amp;eacute;p th&#7871; gi&#7899;i gi&#7843;m, gi&amp;aacute; nguy&amp;ecirc;n li&#7879;u c&#361;ng gi&#7843;m, s&#7843;n l&#432;&#7907;ng v&amp;agrave; nhu c&#7847;u &#273;&#7873;u gi&#7843;m v&amp;agrave; nguy c&#417; th&#7915;a th&amp;eacute;p l&amp;agrave; c&amp;oacute; th&#7853;t&amp;rdquo;, &amp;ocirc;ng D&#432;&#417;ng n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&amp;iacute;nh b&#7903;i v&#7853;y, d&amp;ugrave; l&amp;agrave; nh&amp;agrave; s&#7843;n xu&#7845;t th&amp;eacute;p l&#7899;n t&#7841;i Vi&#7879;t Nam, v&#7899;i doanh thu t&#7915; th&amp;eacute;p &#273;ang chi&#7871;m kho&#7843;ng 50% doanh thu c&#7911;a to&amp;agrave;n T&#7853;p &#273;o&amp;agrave;n, nh&#432;ng &amp;ocirc;ng D&#432;&#417;ng th&#7915;a nh&#7853;n, &amp;ldquo;doanh nghi&#7879;p (DN) Vi&#7879;t Nam ph&#7843;i h&#7885;c c&amp;aacute;ch m&amp;agrave; th&#7871; gi&#7899;i &#273;ang l&amp;agrave;m&amp;rdquo;. Theo &#273;&amp;oacute;, HPG &#273;&amp;atilde; quy&#7871;t &#273;&#7883;nh ti&#7871;t gi&#7843;m s&#7843;n xu&#7845;t, ch&#7881; ho&#7841;t &#273;&#7897;ng 80% c&amp;ocirc;ng su&#7845;t &#273;&#7875; gi&#7843;m t&#7891;n kho, &#273;&#7891;ng th&#7901;i &#273;&#432;a ra c&amp;aacute;c gi&#7843;i ph&amp;aacute;p &#273;&#7875; gi&#7843;m ti&amp;ecirc;u hao n&#259;ng l&#432;&#7907;ng, nguy&amp;ecirc;n li&#7879;u nh&#7857;m ti&#7871;t ki&#7879;m t&amp;agrave;i ch&amp;iacute;nh trong &#273;i&#7873;u ki&#7879;n t&amp;iacute;n d&#7909;ng th&#7855;t ch&#7863;t, b&amp;aacute;n h&amp;agrave;ng gi&#7843;m s&amp;uacute;t hi&#7879;n nay.&lt;/p&gt;\r\n\r\n&lt;p&gt;Gi&#7843;m s&#7843;n xu&#7845;t c&#361;ng &#273;&#432;&#7907;c c&amp;aacute;c DN th&amp;eacute;p l&#7899;n kh&amp;aacute;c &amp;aacute;p d&#7909;ng. &amp;Ocirc;ng Nghi&amp;ecirc;m Xu&amp;acirc;n &#272;a, Ph&amp;oacute; t&#7893;ng gi&amp;aacute;m &#273;&#7889;c T&#7893;ng c&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam &amp;ndash; C&amp;ocirc;ng ty c&#7893; ph&#7847;n (VNSteel) cho hay, trong 10 th&amp;aacute;ng qua, VNSteel s&#7843;n xu&#7845;t &#273;&#432;&#7907;c kho&#7843;ng 1,9 tri&#7879;u t&#7845;n, gi&#7843;m 2% so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m 2010, c&amp;ograve;n ti&amp;ecirc;u th&#7909; gi&#7843;m g&#7847;n 4%. Trong h&#7879; th&#7889;ng c&#7911;a VNSteel, &#273;&amp;atilde; xu&#7845;t hi&#7879;n t&amp;igrave;nh tr&#7841;ng l&#7895; &#7903; m&#7897;t s&#7889; DN.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nTi&amp;ecirc;u th&#7909; th&amp;eacute;p x&amp;acirc;y d&#7921;ng gi&#7843;m m&#7841;nh, g&amp;acirc;y kh&amp;oacute; cho c&amp;aacute;c doanh nghi&#7879;p s&#7843;n xu&#7845;t. Th&#7915;a nh&#7853;n th&#7921;c tr&#7841;ng n&amp;agrave;y, &#273;&#7841;i di&#7879;n c&#7911;a Pomina Group, nh&amp;agrave; s&#7843;n xu&#7845;t th&amp;eacute;p x&amp;acirc;y d&#7921;ng l&#7899;n nh&#7845;t &#7903; mi&#7873;n Nam cho hay, nhu c&#7847;u c&#7911;a th&#7883; tr&#432;&#7901;ng ph&amp;iacute;a Nam hi&#7879;n ch&#7881; b&#7857;ng 75% so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m ngo&amp;aacute;i. &#272;&amp;atilde; c&amp;oacute; DN ch&#7881; ho&#7841;t &#273;&#7897;ng 20 ng&amp;agrave;y/th&amp;aacute;ng, do kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nB&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, vi&#7879;c h&amp;agrave;ng t&#7891;n kho qu&amp;aacute; l&#7899;n, nguy c&#417; ph&amp;aacute; s&#7843;n &#7903; nh&#7919;ng DN kh&amp;ocirc;ng c&amp;oacute; kh&#7843; n&#259;ng t&amp;igrave;m ki&#7871;m v&#7889;n trong &#273;i&#7873;u ki&#7879;n t&amp;iacute;n d&#7909;ng c&#259;ng th&#7859;ng, vay v&#7889;n kh&amp;oacute; kh&#259;n hi&#7879;n nay c&#361;ng &#273;&#432;&#7907;c nh&#7855;c t&#7899;i. &amp;ldquo;Ch&#432;a c&amp;oacute; DN th&amp;eacute;p n&amp;agrave;o ph&amp;aacute; s&#7843;n, nh&#432;ng &#273;&amp;atilde; c&amp;oacute; nhi&#7873;u DN th&amp;eacute;p &#273;&amp;igrave;nh &#273;&#7889;n s&#7843;n xu&#7845;t; n&#7871;u t&amp;igrave;nh h&amp;igrave;nh x&#7845;u ti&#7871;p di&#7877;n, s&#7869; c&amp;oacute; nhi&#7873;u DN ph&#7843;i &#273;&amp;oacute;ng c&#7917;a&amp;rdquo;, &amp;ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng, Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam (VSA) c&#7843;nh b&amp;aacute;o.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&amp;ecirc;n th&#7921;c t&#7871;, &#273;&amp;atilde; c&amp;oacute; nh&#7919;ng DN s&#7843;n xu&#7845;t ph&amp;ocirc;i th&amp;eacute;p ho&#7841;t &#273;&#7897;ng r&#7845;t c&#7847;m ch&#7915;ng nh&#432; l&amp;ograve; cao c&#7911;a C&amp;ocirc;ng ty Th&amp;eacute;p V&#7841;n L&#7907;i hay Nh&amp;agrave; m&amp;aacute;y Ph&amp;ocirc;i th&amp;eacute;p &#272;&amp;igrave;nh V&#361;, b&#7903;i thi&#7871;u nguy&amp;ecirc;n li&#7879;u v&amp;agrave; kh&amp;ocirc;ng &#273;&#7911; t&amp;agrave;i ch&amp;iacute;nh &#273;&#7875; mua qu&#7863;ng, ph&#7909;c v&#7909; s&#7843;n xu&#7845;t ph&amp;ocirc;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Vi&#7879;c t&amp;igrave;m th&#7883; tr&#432;&#7901;ng xu&#7845;t kh&#7849;u cho c&amp;aacute;c DN th&amp;eacute;p c&#361;ng kh&amp;ocirc;ng d&#7877; d&amp;agrave;ng. Hi&#7879;n t&#7841;i, s&#7843;n ph&#7849;m th&amp;eacute;p Vi&#7879;t Nam &#273;&amp;atilde; &#273;&#432;&#7907;c xu&#7845;t kh&#7849;u sang m&#7897;t s&#7889; n&#432;&#7899;c, nh&#432; Indonesia, M&#7929;, Canada, nh&#432;ng &#273;&#7875; tr&#7909; v&#7919;ng t&#7841;i c&amp;aacute;c th&#7883; tr&#432;&#7901;ng n&amp;agrave;y c&#361;ng kh&amp;ocirc;ng d&#7877;. &amp;Ocirc;ng L&amp;ecirc; Minh H&#7843;i, Ch&#7911; t&#7883;ch H&#272;QT C&amp;ocirc;ng ty &#7888;ng th&amp;eacute;p Vi&#7879;t &#272;&#7913;c cho hay, t&#7841;i th&#7883; tr&#432;&#7901;ng M&#7929;, s&#7843;n ph&#7849;m &#7889;ng th&amp;eacute;p c&#7911;a Vi&#7879;t Nam ph&#7843;i c&#7841;nh tranh gay g&#7855;t v&#7899;i s&#7843;n ph&#7849;m c&#7911;a c&amp;aacute;c n&#432;&#7899;c nh&#432; &#7844;n &#272;&#7897;, Oman...&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7841;i di&#7879;n C&amp;ocirc;ng ty TNHH Th&amp;eacute;p Posco Vi&#7879;t Nam cho bi&#7871;t, Posco Vi&#7879;t Nam c&amp;oacute; t&amp;iacute;nh &#273;&#7871;n vi&#7879;c xu&#7845;t kh&#7849;u th&amp;eacute;p sang c&amp;aacute;c th&#7883; tr&#432;&#7901;ng trong khu v&#7921;c, nh&#432;ng c&amp;aacute;c n&#432;&#7899;c n&amp;agrave;y &#273;&#432;a ra nhi&#7873;u h&#7841;n ch&#7871;, nh&#432; h&amp;agrave;ng r&amp;agrave;o ch&#7845;t l&#432;&#7907;ng, thu&#7871; nh&#7853;p kh&#7849;u, thu&#7871; ch&#7889;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; &#273;&#7875; b&#7843;o v&#7879; s&#7843;n xu&#7845;t trong n&#432;&#7899;c. Ch&#7859;ng h&#7841;n, ngo&amp;agrave;i c&amp;aacute;c y&amp;ecirc;u c&#7847;u v&#7873; ti&amp;ecirc;u chu&#7849;n ch&#7845;t l&#432;&#7907;ng ph&#7843;i &#273;&#7841;t theo quy &#273;&#7883;nh, Th&amp;aacute;i Lan, Indonesia v&amp;agrave; Malaysia c&amp;ograve;n &#273;&#432;a ra m&#7913;c thu&#7871; nh&#7853;p kh&#7849;u &#273;&#7889;i v&#7899;i th&amp;eacute;p c&amp;aacute;n ngu&#7897;i l&#7847;n l&#432;&#7907;t l&amp;agrave; 5%, 10% v&amp;agrave; 20-50%.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;Vi&#7879;t Nam c&#361;ng n&amp;ecirc;n x&amp;acirc;y d&#7921;ng c&amp;aacute;c ti&amp;ecirc;u chu&#7849;n k&#7929; thu&#7853;t &#273;&#7875; h&#7841;n ch&#7871; t&amp;igrave;nh tr&#7841;ng &#273;&#7893; b&#7897; c&#7911;a c&amp;aacute;c s&#7843;n ph&#7849;m th&amp;eacute;p t&#7915; c&amp;aacute;c n&#432;&#7899;c kh&amp;aacute;c, g&amp;acirc;y kh&amp;oacute; kh&#259;n cho s&#7843;n xu&#7845;t trong n&#432;&#7899;c trong &#273;i&#7873;u ki&#7879;n hi&#7879;n nay&amp;rdquo;, &#273;&#7841;i di&#7879;n C&amp;ocirc;ng ty Posco Vi&#7879;t Nam ki&#7871;n ngh&#7883;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Li&amp;ecirc;n quan &#273;&#7871;n vi&#7879;c th&amp;aacute;o g&#7905; kh&amp;oacute; kh&#259;n cho ng&amp;agrave;nh th&amp;eacute;p, &#273;&#7841;i di&#7879;n V&#7909; Ch&amp;iacute;nh s&amp;aacute;ch thu&#7871; (B&#7897; T&amp;agrave;i ch&amp;iacute;nh) cho r&#7857;ng, kh&amp;ocirc;ng th&#7875; t&#259;ng gi&#7843;m thu&#7871; li&amp;ecirc;n t&#7909;c, b&#7903;i nh&#432; v&#7853;y s&#7869; c&amp;agrave;ng l&amp;agrave;m r&#7889;i &#273;&#7883;nh h&#432;&#7899;ng ho&#7841;t &#273;&#7897;ng c&#7911;a c&amp;aacute;c DN. Tr&amp;ecirc;n th&#7921;c t&#7871;, t&#259;ng thu&#7871; nh&#7853;p kh&#7849;u th&amp;eacute;p t&#7915; c&amp;aacute;c n&#432;&#7899;c ASEAN s&#7869; kh&amp;ocirc;ng c&amp;oacute; nhi&#7873;u t&amp;aacute;c d&#7909;ng, b&#7903;i khung thu&#7871; n&amp;agrave;y theo cam k&#7871;t &#273;&amp;atilde; &#7903; m&#7913;c th&#7845;p. H&#417;n n&#7919;a, vi&#7879;c t&#259;ng thu&#7871; ph&#7843;i ch&#7881; ra r&amp;otilde; r&amp;agrave;ng l&amp;agrave; v&#7899;i m&#7863;t h&amp;agrave;ng n&amp;agrave;o, t&#259;ng bao nhi&amp;ecirc;u, ch&#7913; kh&amp;ocirc;ng n&amp;ecirc;n n&amp;oacute;i chung chung. Ngo&amp;agrave;i ra, &#273;&#7875; h&#7895; tr&#7907; DN &#273;&#7889;i ph&amp;oacute; v&#7899;i c&amp;aacute;c v&#7909; ki&#7879;n th&#432;&#417;ng m&#7841;i, V&#7909; Ch&amp;iacute;nh s&amp;aacute;ch thu&#7871; c&#361;ng khuy&#7871;n ngh&#7883; DN n&amp;ecirc;n h&#7907;p t&amp;aacute;c t&#7889;t v&#7899;i c&amp;aacute;c c&#417; quan h&#7919;u tr&amp;aacute;ch &#273;&#7875; xem x&amp;eacute;t c&amp;aacute;c y&amp;ecirc;u c&#7847;u, ki&#7879;n ch&#7889;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; c&amp;oacute; vi ph&#7841;m cam k&#7871;t c&#7911;a T&#7893; ch&#7913;c Th&#432;&#417;ng m&#7841;i th&#7871; gi&#7899;i (WTO) hay kh&amp;ocirc;ng, thay v&amp;igrave; t&#7921; m&amp;igrave;nh ch&#7889;ng &#273;&#7905; v&amp;igrave; s&#7907; b&#7883; &#7843;nh h&#432;&#7903;ng t&#7899;i b&amp;aacute;n h&amp;agrave;ng.&lt;/p&gt;\r\n','03/11/2011','03/11/2011','/home/uploads/image/news/10-2011/doanh-nghiep-thep-thu-hep-san-xuat.jpg',23,2,2110,'huyennt',1,'doanh-nghiep-thep-thu-hep-san-xuat','doanh nghi&#7879;p th&eacute;p, VLXD, s&#7843;n xu&#7845;t, thu h&#7865;p',NULL,NULL,NULL,'doanh nghi&#7879;p th&eacute;p, VLXD, s&#7843;n xu&#7845;t, thu h&#7865;p',0,0,0,'','','',3,'http://dothi.net',1,0,0,0),(88,'S&#7841;ch 1 t&#7845;n n&#432;&#7899;c nhi&#7877;m ph&oacute;ng x&#7841; b&#7857;ng 1 gram v&#7853;t li&#7879;u m&#7899;i','M&#7899;i &#273;&acirc;y c&aacute;c nh&agrave; khoa h&#7885;c t&#7841;i &#272;H C&ocirc;ng ngh&#7879; Queensland (&Uacute;c) cho bi&#7871;t &#273;&atilde; ph&aacute;t tri&#7875;n th&agrave;nh c&ocirc;ng v&#7853;t li&#7879;u th&ocirc;ng minh &#273;&#7847;u ti&ecirc;n tr&ecirc;n th&#7871; gi&#7899;i c&oacute; kh&#7843; n&#259;ng h&#7845;p th&#7909; v&agrave; lo&#7841;i b&#7887; ch&#7845;t ph&oacute;ng x&#7841; trong n&#432;&#7899;c r&#7845;t hi&#7879;u qu&#7843; gi&uacute;p ti&#7871;t ki&#7879;m l&#432;&#7907;ng n&#432;&#7899;c l&#7899;n.','\r\n&lt;p&gt;C&amp;aacute;c nh&amp;agrave; m&amp;aacute;y &#273;i&#7879;n h&#7841;t nh&amp;acirc;n th&#432;&#7901;ng &#273;&#432;&#7907;c x&amp;acirc;y d&#7921;ng g&#7847;n ngu&#7891;n n&#432;&#7899;c nh&#7857;m m&#7909;c &#273;&amp;iacute;ch l&amp;agrave;m m&amp;aacute;t nhi&#7879;t th&#7843;i c&#7911;a nh&amp;agrave; m&amp;aacute;y. &#272;i&#7873;u n&amp;agrave;y c&amp;oacute; ngh&#297;a l&amp;agrave; n&#432;&#7899;c s&#7869; b&#7883; nhi&#7877;m ph&amp;oacute;ng x&#7841; n&#7871;u x&#7843;y ra th&#7843;m ho&#7841; h&#7841;t nh&amp;acirc;n.&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img width=&quot;500&quot; height=&quot;283&quot; src=&quot;/home/uploads/image/news/10-2011/pxa.jpg&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0);&quot;&gt;&lt;em&gt;GS Zhu Huai-Yong v&#7899;i c&amp;aacute;c s&#7907;i nano titanate c&amp;oacute; th&#7875; lo&#7841;i b&#7887; ch&#7845;t ph&amp;oacute;ng x&#7841; t&#7915; n&#432;&#7899;c b&#7883; &amp;ocirc; nhi&#7877;m&lt;/em&gt;&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;V&#7853;t li&#7879;u th&amp;ocirc;ng minh do GS Zhu Huai-Yong t&#7841;i &#272;H C&amp;ocirc;ng ngh&#7879; Queensland h&#7907;p t&amp;aacute;c v&#7899;i T&#7893; ch&#7913;c C&amp;ocirc;ng ngh&#7879; v&amp;agrave; Khoa h&#7885;c H&#7841;t nh&amp;acirc;n &amp;Uacute;c v&amp;agrave; &#272;H Pennsylvania (M&#7929;) ph&amp;aacute;t tri&#7875;n &#273;&#432;&#7907;c c&#7845;u t&#7841;o b&#7903;i c&amp;aacute;c s&#7907;i v&amp;agrave; &#7889;ng nano Titanate. Kh&amp;ocirc;ng gi&#7889;ng nh&#432; c&amp;aacute;c ph&#432;&#417;ng ph&amp;aacute;p l&amp;agrave;m s&#7841;ch hi&#7879;n t&#7841;i, v&#7853;t li&#7879;u m&#7899;i c&amp;oacute; kh&#7843; n&#259;ng h&#7845;p th&#7909; c&amp;aacute;c ch&#7845;t ph&amp;oacute;ng x&#7841; ch&#7871;t ng&#432;&#7901;i trong ngu&#7891;n n&#432;&#7899;c &amp;ocirc; nhi&#7877;m. Ch&#7845;t ph&amp;oacute;ng x&#7841; sau &#273;&amp;oacute; s&#7869; &#273;&#432;&#7907;c x&#7917; l&amp;yacute; an to&amp;agrave;n kh&amp;ocirc;ng g&amp;acirc;y ra nguy c&#417; r&amp;ograve; r&#7881; cho d&amp;ugrave; v&#7853;t li&#7879;u c&amp;oacute; b&#7883; &#432;&#7899;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ho&#7841;t &#273;&#7897;ng c&#7911;a thi&#7871;t b&#7883; nh&#432; sau: khi n&#432;&#7899;c &amp;ocirc; nhi&#7877;m ch&#7843;y qua c&amp;aacute;c &#7889;ng v&amp;agrave; c&amp;aacute;c s&#7907;i nano, ion c&#7911;a c&#7911;a ph&amp;oacute;ng x&#7841; Cesium (Cs +) b&#7883; m&#7855;c k&#7865;t t&#7841;i &#273;&amp;acirc;y do s&#7921; thay &#273;&#7893;i c&#7845;u tr&amp;uacute;c c&#7911;a v&#7853;t li&#7879;u. Ngo&amp;agrave;i ra, b&#7857;ng c&amp;aacute;ch th&amp;ecirc;m c&amp;aacute;c tinh th&#7875; nano oxit b&#7841;c tr&amp;ecirc;n b&#7873; m&#7863;t, c&#7845;u tr&amp;uacute;c nano c&amp;oacute; th&#7875; gi&#7919; l&#7841;i c&amp;aacute;c ion i-&#7889;t ph&amp;oacute;ng x&#7841; (I-). Nh&#7919;ng ion n&amp;agrave;y sau &#273;&amp;oacute; s&#7869; &#273;&#432;&#7907;c s&#7917; d&#7909;ng &#273;&#7875; &#273;i&#7873;u tr&#7883; b&#7879;nh ung th&#432; tuy&#7871;n gi&amp;aacute;p, hay d&amp;ugrave;ng l&amp;agrave;m c&amp;aacute;c thi&#7871;t b&#7883; th&#259;m d&amp;ograve; v&amp;agrave; ch&#7849;n &#273;o&amp;aacute;n y t&#7871;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;&lt;em&gt;M&#7897;t gam s&#7907;i nano c&amp;oacute; th&#7875; l&amp;agrave;m s&#7841;ch &amp;iacute;t nh&#7845;t 1 t&#7845;n n&#432;&#7899;c nhi&#7877;m x&#7841;&amp;rdquo;, Gi&amp;aacute;o s&#432; Zhu cho bi&#7871;t. &amp;ldquo;V&#7853;t li&#7879;u s&#7869; gi&amp;uacute;p ti&#7871;t ki&#7879;m m&#7897;t l&#432;&#7907;ng n&#432;&#7899;c l&#7899;n v&amp;agrave; ng&#259;n ch&#7863;n nguy c&#417; ph&amp;oacute;ng x&#7841; ng&#7845;m v&amp;agrave;o &#273;&#7845;t&lt;/em&gt;&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n nay &amp;Uacute;c l&amp;agrave; qu&#7889;c gia s&#7843;n xu&#7845;t Titania, v&#7853;t li&#7879;u th&amp;ocirc; d&amp;ugrave;ng &#273;&#7875; ch&#7871; t&#7841;o s&#7907;i v&amp;agrave; &#7889;ng nano Titanate, l&#7899;n nh&#7845;t th&#7871; gi&#7899;i.&lt;/p&gt;','03/11/2011','03/11/2011','/home/uploads/image/news/10-2011/pxa.jpg',23,2,1705,'huyennt',1,'sach-1-tan-nuoc-nhiem-phong-xa-bang-1-gram-vat-lieu-moi','V&#7853;t li&#7879;u th&ocirc;ng minh, ch&#7845;t ph&oacute;ng x&#7841;, VLXD',NULL,NULL,NULL,'V&#7853;t li&#7879;u th&ocirc;ng minh, ch&#7845;t ph&oacute;ng x&#7841;, VLXD',0,0,0,'','','',3,'http://khoahoc.baodatviet.vn',1,0,0,0),(89,'G&#7841;ch &#7889;p l&aacute;t: h&agrave;ng l&#7853;u ho&agrave;nh h&agrave;nh, h&agrave;ng t&#7891;n b&#7845;t &#273;&#7897;ng','C&ugrave;ng ch&#7883;u kh&oacute; kh&#259;n v&#7899;i ng&agrave;nh s&#7843;n xu&#7845;t VLXD trong th&#7901;i &#273;i&#7875;m hi&#7879;n t&#7841;i, g&#7841;ch &#7889;p l&aacute;t trong n&#432;&#7899;c &#273;ang r&#417;i v&agrave;o t&igrave;nh c&#7843;nh &#7843;m &#273;&#7841;m ch&#432;a t&#7915;ng c&oacute;.','&lt;p&gt;&amp;nbsp;Nh&amp;agrave; m&amp;aacute;y s&#7843;n xu&#7845;t th&amp;igrave; &#273;&amp;igrave;nh tr&#7879;, h&amp;agrave;ng b&amp;aacute;n &#7871; &#7849;m l&#7841;i th&amp;ecirc;m vi&#7879;c ph&#7843;i g&#7891;ng m&amp;igrave;nh c&#7841;nh tranh v&#7899;i h&amp;agrave;ng ngo&#7841;i, h&amp;agrave;ng nh&#7853;p l&#7853;u&amp;hellip; khi&#7871;n cho th&#7883; tr&#432;&#7901;ng n&amp;agrave;y c&amp;agrave;ng th&amp;ecirc;m hiu h&#7855;t.&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;337&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/vat-lieu-xay-dung.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;C&amp;aacute;c c&#417; s&#7903; s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&amp;aacute;t &#273;&#7873;u g&#7863;p kh&amp;oacute;&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;strong&gt;Ch&#432;a khi n&amp;agrave;o kh&amp;oacute; kh&#259;n nh&#432; b&amp;acirc;y gi&#7901;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;ang l&amp;agrave; m&amp;ugrave;a x&amp;acirc;y d&#7921;ng, nh&#432;ng thay v&amp;igrave; kh&amp;ocirc;ng kh&amp;iacute; t&#7845;p n&#7853;p mua b&amp;aacute;n nh&#432; nh&#7919;ng n&#259;m tr&#432;&#7899;c th&amp;igrave; t&#7841;i c&amp;aacute;c c&#7917;a h&amp;agrave;ng, &#273;&#7841;i l&amp;yacute; kinh doanh VLXD b&amp;acirc;y gi&#7901; &#273;&#7873;u ch&#7883;u chung m&#7897;t t&amp;igrave;nh c&#7843;nh ng&#432;&#7901;i mua th&#432;a th&#7899;t, &#273;&#417;n &#273;&#7863;t h&amp;agrave;ng &amp;iacute;t &#7887;i. &#272;&#7889;i v&#7899;i kh&amp;aacute;ch mua h&amp;agrave;ng, h&#7885; th&#432;&#7901;ng ch&#7885;n nh&#7919;ng v&#7853;t li&#7879;u c&amp;oacute; ph&amp;acirc;n kh&amp;uacute;c gi&amp;aacute; trung b&amp;igrave;nh &#273;&#7875; ph&amp;ugrave; h&#7907;p v&#7899;i kh&#7843; n&#259;ng chi tr&#7843;. Ch&amp;iacute;nh v&amp;igrave; th&#7871;, ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng th&#432;&#7901;ng &#432;u ti&amp;ecirc;n h&#417;n v&#7899;i nh&#7919;ng s&#7843;n ph&#7849;m &#273;&#7865;p, ch&#7845;t l&#432;&#7907;ng kh&amp;aacute; v&#7899;i gi&amp;aacute; trung b&amp;igrave;nh b&#7845;t k&#7875; h&amp;agrave;ng n&#7897;i hay h&amp;agrave;ng ngo&#7841;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Anh Tr&#7847;n Nguy&#7877;n Chi&#7871;n (Thanh Nh&amp;agrave;n - H&amp;agrave; N&#7897;i) cho bi&#7871;t: T&amp;ocirc;i &#273;ang ho&amp;agrave;n thi&#7879;n c&#259;n h&#7897; m&#7899;i, n&amp;ecirc;n c&#361;ng qua c&amp;aacute;c c&#7917;a h&amp;agrave;ng VLXD &#273;&#7875; tham kh&#7843;o gi&amp;aacute; c&#361;ng nh&#432; t&amp;igrave;m s&#7843;n ph&#7849;m ph&amp;ugrave; h&#7907;p. Th&#7921;c t&#7871; th&amp;igrave; ng&#432;&#7901;i mua c&#361;ng ch&#7859;ng th&#7875; bi&#7871;t c&#7909; th&#7875; ch&#7845;t l&#432;&#7907;ng &#273;&#432;&#7907;c ki&#7875;m &#273;&#7883;nh th&#7871; n&amp;agrave;o, ch&#7881; quan s&amp;aacute;t b&#7857;ng m&#7855;t th&#432;&#7901;ng, h&#7887;i qua kinh nghi&#7879;m c&#7911;a ng&#432;&#7901;i kh&amp;aacute;c &#273;&amp;atilde; s&#7917; d&#7909;ng c&#7897;ng th&amp;ecirc;m t&#432; v&#7845;n c&#7911;a ch&#7911; c&#7917;a h&amp;agrave;ng&amp;hellip; th&amp;igrave; quy&#7871;t &#273;&#7883;nh mua th&amp;ocirc;i. N&amp;ecirc;n cho d&amp;ugrave; h&amp;agrave;ng n&#7897;i, hay h&amp;agrave;ng ngo&#7841;i (&#273;&#7889;i v&#7899;i c&#7843; h&amp;agrave;ng Trung Qu&#7889;c), ch&#7881; c&#7847;n gi&amp;aacute; trung b&amp;igrave;nh v&amp;agrave; ch&#7845;t l&#432;&#7907;ng t&#7889;t th&amp;igrave; t&amp;ocirc;i ch&#7885;n th&amp;ocirc;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;S&#7913;c mua tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng s&#7909;t gi&#7843;m kh&amp;ocirc;ng ch&#7881; th&#7875; hi&#7879;n qua th&#7883; tr&#432;&#7901;ng mua l&#7867; c&#7911;a ng&#432;&#7901;i d&amp;acirc;n, m&amp;agrave; ngay c&#7843; &#273;&#417;n &#273;&#7863;t h&amp;agrave;ng c&#7911;a c&amp;aacute;c Cty x&amp;acirc;y d&#7921;ng c&#361;ng kh&amp;aacute; th&#432;a th&#7899;t. Nguy&amp;ecirc;n nh&amp;acirc;n &#273;&#432;&#7907;c x&amp;aacute;c &#273;&#7883;nh v&#7851;n l&amp;agrave; B&#272;S &amp;ldquo;&#273;&amp;oacute;ng b&#259;ng&amp;rdquo; n&amp;ecirc;n vi&#7879;c &#273;&#7847;u t&#432; mua v&#7853;t li&#7879;u ho&amp;agrave;n thi&#7879;n cho c&#259;n h&#7897; v&#7851;n h&#7841;n ch&#7871;. B&amp;ecirc;n c&#7841;nh kh&amp;oacute; kh&#259;n do &#7843;nh h&#432;&#7903;ng kinh t&#7871; trong n&#432;&#7899;c, th&amp;igrave; g&#7841;ch &#7889;p l&amp;aacute;t Vi&#7879;t Nam l&#7841;i &#273;ang ph&#7843;i &amp;ldquo;m&#7879;t m&#7887;i&amp;rdquo; v&#7899;i s&#7921; c&#7841;nh tranh c&#7911;a h&amp;agrave;ng nh&#7853;p l&#7853;u, gian l&#7853;n th&#432;&#417;ng m&#7841;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo th&amp;ocirc;ng tin m&#7899;i &#273;&amp;acirc;y c&#7911;a Hi&#7879;p h&#7897;i G&#7889;m s&#7913; x&amp;acirc;y d&#7921;ng Vi&#7879;t Nam (VIBCA) th&amp;igrave;: Hi&#7879;n s&#7843;n ph&#7849;m g&#7841;ch &#7889;p l&amp;aacute;t t&#7891;n kho t&#7841;i c&amp;aacute;c Cty trong n&#432;&#7899;c &#432;&#7899;c kho&#7843;ng 30 tri&#7879;u m2, t&#432;&#417;ng &#7913;ng 2 ngh&amp;igrave;n t&#7927; &#273;&#7891;ng. So v&#7899;i s&#7913;c ti&amp;ecirc;u th&#7909; c&#7911;a n&#259;m 2010 l&amp;agrave; 330 tri&#7879;u m2 (b&#7857;ng 88% l&#432;&#7907;ng s&#7843;n xu&#7845;t v&amp;agrave; 79% n&#259;ng l&#7921;c s&#7843;n xu&#7845;t) th&amp;igrave; d&#7921; ki&#7871;n n&#259;m 2011, l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; g&#7841;ch &#7889;p l&amp;aacute;t c&#7911;a Vi&#7879;t Nam ch&#7881; kho&#7843;ng 290 tri&#7879;u m2/n&#259;m (b&#7857;ng 70% n&#259;ng l&#7921;c s&#7843;n xu&#7845;t theo c&amp;ocirc;ng su&#7845;t l&#7855;p &#273;&#7863;t). M&#7897;t trong nh&#7919;ng nguy&amp;ecirc;n nh&amp;acirc;n quan tr&#7885;ng d&#7851;n &#273;&#7871;n t&amp;igrave;nh tr&#7841;ng ng&amp;agrave;nh g&#7841;ch &#7889;p l&amp;aacute;t l&amp;acirc;m v&amp;agrave;o t&amp;igrave;nh tr&#7841;ng tr&amp;ecirc;n &#273;&#432;&#7907;c VIBCA nh&#7853;n &#273;&#7883;nh l&amp;agrave; do bu&amp;ocirc;n l&#7853;u v&amp;agrave; gian l&#7853;n th&#432;&#417;ng m&#7841;i g&#7841;ch &#7889;p l&amp;aacute;t t&#7915; Trung Qu&#7889;c. C&amp;aacute;c m&#7863;t h&amp;agrave;ng gian l&#7853;n v&#7873; gi&amp;aacute;, thu&#7871; su&#7845;t v&amp;agrave; s&#7889; l&#432;&#7907;ng &#273;&#432;&#7907;c t&#7853;p trung &#273;&#7889;i v&#7899;i g&#7841;ch ceramic, &#273;&amp;aacute; x&#7867; granit (&#273;&amp;aacute; hoa c&#432;&#417;ng) v&amp;agrave; &#273;&amp;aacute; x&#7867; marble (&#273;&amp;aacute; c&#7849;m th&#7841;ch) nh&#7853;p kh&#7849;u (NK) c&amp;oacute; xu&#7845;t x&#7913; t&#7915; Trung Qu&#7889;c.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&#259;m 2011, l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t nh&#7853;p v&amp;agrave;o ng&amp;agrave;y c&amp;agrave;ng t&#259;ng, trong &#273;&amp;oacute; h&amp;agrave;ng l&#7853;u v&amp;agrave; gian l&#7853;n th&#432;&#417;ng m&#7841;i di&#7877;n ra r&#7845;t ph&#7913;c t&#7841;p, tinh vi &#273;&amp;atilde; g&amp;acirc;y nhi&#7873;u kh&amp;oacute; kh&#259;n cho c&amp;aacute;c DN s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&amp;aacute;t Vi&#7879;t Nam. T&amp;igrave;nh tr&#7841;nggian l&#7853;n th&#432;&#417;ng m&#7841;i m&#7863;t h&amp;agrave;ng g&#7841;ch &#7889;p l&amp;aacute;t di&#7877;n ra kh&amp;aacute; &#273;a d&#7841;ng nh&#432;: Gian l&#7853;n v&#7873; s&#7889; l&#432;&#7907;ng khai b&amp;aacute;o khi NK; xu&#7845;t tr&amp;igrave;nh h&amp;oacute;a &#273;&#417;n mua h&amp;agrave;ng v&#7899;i gi&amp;aacute; th&#7845;p h&#417;n nhi&#7873;u so v&#7899;i gi&amp;aacute; th&#7921;c&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&#272;&#7889;i m&#7863;t v&#7899;i h&amp;agrave;ng l&#7853;u, gian l&#7853;n th&#432;&#417;ng m&#7841;i&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo th&#7889;ng k&amp;ecirc; c&#7911;a VIBCA th&amp;igrave; n&#259;m 2010, l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t NK l&amp;ecirc;n t&#7899;i 95,5 tri&#7879;u USD, trong &#273;&amp;oacute; l&#432;&#7907;ng g&#7841;ch NK t&#7915; Trung Qu&#7889;c l&amp;agrave; 76,119 tri&#7879;u USD. &amp;ldquo;&#272;&amp;acirc;y l&amp;agrave; con s&#7889; th&#7845;p h&#417;n r&#7845;t nhi&#7873;u so v&#7899;i l&#432;&#7907;ng NK kh&amp;ocirc;ng ki&#7875;m so&amp;aacute;t &#273;&#432;&#7907;c t&#7915; Trung Qu&#7889;c&amp;rdquo;- l&amp;agrave; nh&#7853;n &#273;&#7883;nh c&#7911;a Ch&#7911; t&#7883;ch VIBCA - &amp;ocirc;ng &#272;inh Quang Huy. Hi&#7879;n nay, Trung Qu&#7889;c c&amp;oacute; th&#7875; s&#7843;n xu&#7845;t kho&#7843;ng 6 t&#7927; m&amp;sup2;/n&#259;m g&#7841;ch &#7889;p l&amp;aacute;t, chi&#7871;m 2/3 s&#7843;n l&#432;&#7907;ng to&amp;agrave;n th&#7871; gi&#7899;i. C&#7841;nh tranh b&#7857;ng gi&amp;aacute; r&#7867;, m&#7851;u m&amp;atilde; &#273;a d&#7841;ng, hi&#7879;n Trung Qu&#7889;c &#273;ang th&#7889;ng l&#297;nh th&#7883; tr&#432;&#7901;ng g&#7841;ch &#7889;p l&amp;aacute;t to&amp;agrave;n c&#7847;u v&amp;agrave; &amp;ldquo;l&#7845;n &amp;aacute;t&amp;rdquo; ngay c&#7843; nh&#7919;ng n&#432;&#7899;c c&amp;oacute; b&#7873; d&amp;agrave;y truy&#7873;n th&#7889;ng v&amp;agrave; n&#259;ng l&#7921;c s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&amp;aacute;t l&#7899;n nh&#432; Italy, T&amp;acirc;y Ban Nha.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Huy ph&amp;acirc;n t&amp;iacute;ch: H&amp;igrave;nh th&#7913;c gian l&#7853;n th&#432;&#417;ng m&#7841;i hi&#7879;n nay &#273;&#432;&#7907;c t&#7893; ch&#7913;c d&#432;&#7899;i d&#7841;ng ng&#432;&#7901;i NK xu&#7845;t tr&amp;igrave;nh h&amp;oacute;a &#273;&#417;n mua h&amp;agrave;ng t&#7915; Trung Qu&#7889;c v&#7899;i gi&amp;aacute; th&#7845;p h&#417;n nhi&#7873;u so v&#7899;i gi&amp;aacute; th&#7921;c ph&#7843;i mua. Th&#7921;c ch&#7845;t c&amp;oacute; th&#7875; coi &#273;&amp;acirc;y l&amp;agrave; h&amp;oacute;a &#273;&#417;n c&#7911;a h&amp;agrave;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute;. Do &#273;&amp;oacute;, m&#7913;c thu&#7871; NK &#273;&#432;&#7907;c &#273;&amp;aacute;nh gi&amp;aacute; tr&amp;ecirc;n m&#7913;c gi&amp;aacute; th&#7845;p h&#417;n gi&amp;aacute; th&#7921;c d&#7851;n &#273;&#7871;n t&amp;igrave;nh tr&#7841;ng g&#7841;ch &#7889;p l&amp;aacute;t c&amp;oacute; xu&#7845;t x&#7913; t&#7915; Trung Qu&#7889;c &#273;&#432;&#7907;c &#273;&#432;a v&amp;agrave;o Vi&#7879;t Nam c&#7841;nh tranh kh&amp;ocirc;ng l&amp;agrave;nh m&#7841;nh v&#7899;i s&#7843;n ph&#7849;m c&amp;ugrave;ng lo&#7841;i s&#7843;n xu&#7845;t trong n&#432;&#7899;c. Vi&#7879;c gian l&#7853;n th&#432;&#417;ng m&#7841;i n&amp;agrave;y &#273;&amp;atilde; di&#7877;n ra nhi&#7873;u n&#259;m v&amp;agrave; ng&amp;agrave;y c&amp;agrave;ng nghi&amp;ecirc;m tr&#7885;ng l&amp;agrave;m &#7843;nh h&#432;&#7903;ng kh&amp;ocirc;ng nh&#7887; &#273;&#7871;n ng&amp;agrave;nh g&#7841;ch &#7889;p l&amp;aacute;t trong n&#432;&#7899;c.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/gach-op-lat-ton-kho.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;0&quot; height=&quot;0&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/gach-op-lat-ton-kho.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;G&#7841;ch &#7889;p &#273;ang l&amp;aacute;t t&#7891;n kho cao do th&#7883; tr&#432;&#7901;ng &#7843;m &#273;&#7841;m v&amp;agrave; n&#7841;n h&amp;agrave;ng l&#7853;u ho&amp;agrave;nh h&amp;agrave;nh&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nTr&#432;&#7899;c t&amp;igrave;nh tr&#7841;ng n&amp;agrave;y, v&#7915;a qua, VIBCA c&amp;ugrave;ng c&amp;aacute;c DN &#273;&amp;atilde; t&#7893; ch&#7913;c h&#7897;i ngh&#7883; v&#7899;i C&#7909;c &#272;i&#7873;u tra ch&#7889;ng bu&amp;ocirc;n l&#7853;u - T&#7893;ng c&#7909;c H&#7843;i quan &#273;&#7875; ph&#7843;n &amp;aacute;nh s&#7921; vi&#7879;c gian l&#7853;n th&#432;&#417;ng m&#7841;i n&amp;agrave;y. Hi&#7879;p h&#7897;i G&#7889;m s&#7913; x&amp;acirc;y d&#7921;ng c&#361;ng &#273;&#7873; ngh&#7883; T&#7893;ng c&#7909;c H&#7843;i quan ch&#7881; &#273;&#7841;o c&amp;aacute;c &#273;&#417;n v&#7883; tr&#7921;c thu&#7897;c ki&#7875;m so&amp;aacute;t ch&#7863;t ch&#7869; vi&#7879;c NK m&#7863;t h&amp;agrave;ng g&#7841;ch &#7889;p l&amp;aacute;t qua c&#7917;a kh&#7849;u ch&amp;iacute;nh; t&#259;ng c&#432;&#7901;ng ki&#7875;m so&amp;aacute;t ho&#7841;t &#273;&#7897;ng bu&amp;ocirc;n l&#7853;u qua &#273;&#432;&#7901;ng m&amp;ograve;n, l&#7889;i m&#7903; bi&amp;ecirc;n gi&#7899;i &#273;&#7875; h&#7841;n ch&#7871; l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t v&amp;agrave;o n&#7897;i &#273;&#7883;a Vi&#7879;t Nam. &#272;&#7891;ng th&#7901;i, &#273;&#7873; ngh&#7883; T&#7893;ng c&#7909;c H&#7843;i quan v&amp;agrave; c&amp;aacute;c c&#417; quan h&#7919;u quan th&#7921;c hi&#7879;n nghi&amp;ecirc;m t&amp;uacute;c, ch&#7863;t ch&#7869; Th&amp;ocirc;ng t&#432; 14/2010/TT-BXD Quy &#273;&#7883;nh v&#7873; qu&#7843;n l&amp;yacute; ch&#7845;t l&#432;&#7907;ng s&#7843;n ph&#7849;m, h&amp;agrave;ng h&amp;oacute;a VLXD g&#7841;ch &#7889;p l&amp;aacute;t nh&#7857;m &#273;&#7843;m b&#7843;o ch&#7845;t l&#432;&#7907;ng h&amp;agrave;ng h&amp;oacute;a v&amp;igrave; quy&#7873;n l&#7907;i c&#7911;a ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng v&amp;agrave; ki&#7875;m so&amp;aacute;t s&#7889; l&#432;&#7907;ng h&amp;agrave;ng NK.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7875; c&#7913;u ng&amp;agrave;nh s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&amp;aacute;t Vi&#7879;t Nam ra kh&#7887;i kh&amp;oacute; kh&#259;n trong th&#7901;i &#273;i&#7875;m hi&#7879;n t&#7841;i, VIBCA g&#7917;i B&#7897; T&amp;agrave;i ch&amp;iacute;nh v&#7873; vi&#7879;c &#273;&#432;a m&#7863;t h&amp;agrave;ng g&#7841;ch &#7889;p l&amp;aacute;t v&amp;agrave;o danh m&#7909;c r&#7911;i ro, tr&amp;ecirc;n c&#417; s&#7903; &#273;&amp;oacute; &amp;aacute;p gi&amp;aacute; t&amp;iacute;nh thu&#7871; tr&amp;ecirc;n m&amp;eacute;t vu&amp;ocirc;ng theo t&#7915;ng lo&#7841;i k&amp;iacute;ch th&#432;&#7899;c, ch&#7913; kh&amp;ocirc;ng t&amp;iacute;nh thu&#7871; theo h&amp;oacute;a &#273;&#417;n mua h&amp;agrave;ng t&#7915; Trung Qu&#7889;c do DN nh&#7853;p kh&#7849;u xu&#7845;t tr&amp;igrave;nh th&amp;ocirc;ng quan. Tuy nhi&amp;ecirc;n, &#273;&#7875; th&amp;uacute;c &#273;&#7849;y ng&amp;agrave;nh s&#7843;n xu&#7845;t VLXD trong n&#432;&#7899;c ph&amp;aacute;t tri&#7875;n, l&#7845;y l&#7841;i th&#7883; ph&#7847;n, c&#7847;n c&amp;oacute; &#273;&#7883;nh h&#432;&#7899;ng &#273;&#7875; ph&amp;aacute;t tri&#7875;n l&amp;agrave;nh m&#7841;nh th&#7883; tr&#432;&#7901;ng B&#272;S c&amp;ugrave;ng s&#7921; n&#7895; l&#7921;c m&#7841;nh h&#417;n c&#7911;a c&amp;aacute;c DN trong vi&#7879;c &#273;&#7847;u t&#432; m&amp;aacute;y m&amp;oacute;c, thi&#7871;t b&#7883; hi&#7879;n &#273;&#7841;i, n&amp;acirc;ng cao ch&#7845;t l&#432;&#7907;ng, gi&#7843;m gi&amp;aacute; th&amp;agrave;nh s&#7843;n ph&#7849;m.&lt;/p&gt;\r\n','04/11/2011','07/11/2011','/home/uploads/image/news/11-2011/vat-lieu-xay-dung.jpg',23,2,2675,'huyennt',1,'gach-op-lat-hang-lau-hoanh-hanh-hang-ton-bat-dong','g&#7841;ch &#7889;p l&aacute;t, VLXD, h&agrave;ng l&#7853;u, h&agrave;ng t&#7891;n b&#7845;t &#273;&#7897;ng',NULL,NULL,NULL,'g&#7841;ch &#7889;p l&aacute;t, VLXD, h&agrave;ng l&#7853;u, h&agrave;ng t&#7891;n b&#7845;t &#273;&#7897;ng',0,0,0,'','','huyennt',3,'http://dothi.net',1,0,0,0),(90,'C&#7843; n&#432;&#7899;c ti&ecirc;u th&#7909; 39,1 tri&#7879;u t&#7845;n xi m&#259;ng','M&#7863;c d&ugrave; v&#7851;n c&ograve;n nhi&#7873;u kh&oacute; kh&#259;n trong s&#7843;n xu&#7845;t kinh doanh do l&#7841;m ph&aacute;t cao, c&aacute;c ng&acirc;n h&agrave;ng si&#7871;t ch&#7863;t ho&#7841;t &#273;&#7897;ng cho vay t&iacute;n d&#7909;ng, th&#7883; tr&#432;&#7901;ng B&#272;S tr&#7847;m l&#7855;ng... nh&#432;ng v&#7899;i s&#7921; n&#7895; l&#7921;c c&#7889; g&#7855;ng c&#7911;a m&igrave;nh, c&aacute;c DN thu&#7897;c B&#7897; v&#7851;n c&oacute; m&#7913;c t&#259;ng so v&#7899;i v&#7899;i c&ugrave;ng k&#7923;.','&lt;p&gt;&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;415&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/luong-xi-mang.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;(&#7842;nh minh h&#7885;a)&lt;/p&gt;\r\n\r\n&lt;p&gt;C&#7909; th&#7875;, gi&amp;aacute; tr&#7883; s&#7843;n xu&#7845;t kinh doanh th&amp;aacute;ng 10 &#273;&#7841;t 12.416,4 t&#7927; &#273;&#7891;ng, 10 th&amp;aacute;ng &#432;&#7899;c &#273;&#7841;t 124.834,9 t&#7927; &#273;&#7891;ng, b&#7857;ng 81,1% so v&#7899;i k&#7871; ho&#7841;ch n&#259;m, b&#7857;ng 112,3% so v&#7899;i c&amp;ugrave;ng k&#7923;. Trong &#273;&amp;oacute;:&lt;/p&gt;\r\n\r\n&lt;p&gt;Gi&amp;aacute; tr&#7883; x&amp;acirc;y l&#7855;p c&#7911;a c&amp;aacute;c &#273;&#417;n v&#7883; &#432;&#7899;c th&#7921;c hi&#7879;n th&amp;aacute;ng 10 &#273;&#7841;t 5.150,2 t&#7927; &#273;&#7891;ng, 10 th&amp;aacute;ng &#432;&#7899;c &#273;&#7841;t 50.828,9 t&#7927; &#273;&#7891;ng, b&#7857;ng 80,6% so v&#7899;i k&#7871; ho&#7841;ch n&#259;m, b&#7857;ng 107,6% so v&#7899;i c&amp;ugrave;ng k&#7923;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Gi&amp;aacute; tr&#7883; s&#7843;n xu&#7845;t c&amp;ocirc;ng nghi&#7879;p v&amp;agrave; VLXD c&#7911;a c&amp;aacute;c &#273;&#417;n v&#7883; (k&#7875; c&#7843; TCty C&amp;ocirc;ng nghi&#7879;p Xi m&#259;ng Vi&#7879;t Nam) &#432;&#7899;c th&#7921;c hi&#7879;n th&amp;aacute;ng 10 &#273;&#7841;t kho&#7843;ng 4.794,5 t&#7927; &#273;&#7891;ng, 10 th&amp;aacute;ng &#432;&#7899;c &#273;&#7841;t 46.437,1 t&#7927; &#273;&#7891;ng, b&#7857;ng 83% so v&#7899;i k&#7871; ho&#7841;ch n&#259;m, b&#7857;ng 110,4% so v&#7899;i c&amp;ugrave;ng k&#7923;.&lt;/p&gt;\r\n\r\n&lt;p&gt;S&#7843;n xu&#7845;t xi m&#259;ng to&amp;agrave;n ng&amp;agrave;nh th&amp;aacute;ng 10 &#432;&#7899;t &#273;&#7841;t 3,63 tri&#7879;u t&#7845;n, 10 th&amp;aacute;ng &#273;&#7841;t 39,4 tri&#7879;u t&#7845;n, b&#7857;ng 72,3% so k&#7871; ho&#7841;ch n&#259;m. Th&amp;aacute;ng 10 s&#7843;n l&#432;&#7907;ng xi m&#259;ng ti&amp;ecirc;u th&#7909; &#432;&#7899;c &#273;&#7841;t 3,54 tri&#7879;u t&#7845;n, gi&#7843;m kh&amp;aacute; m&#7841;nh so v&#7899;i c&amp;aacute;c th&amp;aacute;ng qu&amp;yacute; II do &#7843;nh h&#432;&#7903;ng c&#7911;a th&#7901;i ti&#7871;t m&#432;a nhi&#7873;u v&amp;agrave; s&#7921; tr&#7847;m l&#7855;ng c&#7911;a th&#7883; tr&#432;&#7901;ng B&#272;S, 10 th&amp;aacute;ng n&#259;m &#432;&#7899;c ti&amp;ecirc;u th&#7909; &#273;&#7841;t 39,1 tri&#7879;u t&#7845;n, b&#7857;ng 71% k&#7871; ho&#7841;ch n&#259;m.&lt;/p&gt;','08/11/2011','08/11/2011','/home/uploads/image/news/11-2011/luong-xi-mang.jpg',23,2,1758,'huyennt',1,'ca-nuoc-tieu-thu-39-1-trieu-tan-xi-mang','VLXD, xi m&#259;ng, s&#7843;n xu&#7845;t',NULL,NULL,NULL,'VLXD, xi m&#259;ng, s&#7843;n xu&#7845;t',0,0,0,'','','',3,'http://www.xaydungvietnam.vn',1,0,0,0),(91,'Th&eacute;p &#7871;, DN ki&#7879;n nhau b&aacute;n ph&aacute; gi&aacute;','C&aacute;c doanh nghi&#7879;p (DN) s&#7843;n xu&#7845;t th&eacute;p &#273;ang v&ocirc; c&ugrave;ng ch&#7853;t v&#7853;t. N&#7871;u t&igrave;nh tr&#7841;ng n&agrave;y ti&#7871;p t&#7909;c duy tr&igrave; th&ecirc;m v&agrave;i ba th&aacute;ng n&#7919;a, ch&#7855;c ch&#7855;n m&#7885;i kho&#7843;n l&atilde;i c&aacute;c DN c&oacute; &#273;&#432;&#7907;c t&#7915; &#273;&#7847;u n&#259;m s&#7869; b&#7857;ng kh&ocirc;ng, th&#7853;m ch&iacute; l&agrave; &acirc;m.','&lt;p&gt;C&amp;aacute;c tin li&amp;ecirc;n quan:&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;gt;&lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=53&quot;&gt; Ng&amp;agrave;nh th&amp;eacute;p v&#7851;n l&#7841;c quan trong d&amp;agrave;i h&#7841;n&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;gt;&lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=54&quot;&gt; Th&amp;eacute;p: B&#7883; ki&#7879;n ch&#7889;ng ph&amp;aacute; gi&amp;aacute;&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;gt;&lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=51&quot;&gt; Doanh nghi&#7879;p th&amp;eacute;p hoang mang v&amp;igrave; l&#7895;&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;gt; &lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=56&quot;&gt;Doanh nghi&#7879;p th&amp;eacute;p: kh&amp;oacute; kh&#259;n ch&#7891;ng ch&#7845;t&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color: rgb(0, 0, 0); &quot;&gt;&amp;gt; &lt;a href=&quot;http://mikadoceramic.vn/home/Tin-tuc/?aid=57&quot;&gt;Doanh nghi&#7879;p th&amp;eacute;p thu h&#7865;p s&#7843;n xu&#7845;t&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;580&quot; height=&quot;439&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/doanh-nghiep-thep-kho-khan.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;&lt;em&gt;Qu&amp;aacute; kh&amp;oacute; kh&#259;n &#273;&amp;atilde; c&amp;oacute; DN th&amp;eacute;p ph&#7843;i d&#7915;ng s&#7843;n xu&#7845;t&amp;nbsp;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n&#272;&amp;oacute; l&amp;agrave; t&amp;acirc;m tr&#7841;ng chung c&#7911;a c&amp;aacute;c &#273;&#7841;i di&#7879;n c&amp;aacute;c DN s&#7843;n xu&#7845;t th&amp;eacute;p trong n&#432;&#7899;c &amp;quot;than&amp;quot; v&#7899;i B&#7897; C&amp;ocirc;ng th&#432;&#417;ng t&#7841;i cu&#7897;c h&#7885;p chi&#7873;u 27.10, v&amp;agrave; mong b&#7897; ch&#7911; qu&#7843;n c&amp;oacute; h&#432;&#7899;ng th&amp;aacute;o g&#7905; kh&amp;oacute; kh&#259;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Kh&amp;oacute; ti&amp;ecirc;u th&#7909;, DN b&amp;aacute;n ph&amp;aacute; gi&amp;aacute;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo &amp;ocirc;ng Ph&#7841;m Ch&amp;iacute; C&#432;&#7901;ng, Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam (VSA), l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; th&amp;eacute;p 2 th&amp;aacute;ng g&#7847;n &#273;&amp;acirc;y s&#7909;t gi&#7843;m nghi&amp;ecirc;m tr&#7885;ng. Ti&amp;ecirc;u th&#7909; th&amp;eacute;p trong th&amp;aacute;ng 9 v&#7915;a qua ch&#7881; &#273;&#7841;t 381.000 t&#7845;n, gi&#7843;m h&#417;n 100.000 t&#7845;n so v&#7899;i th&amp;aacute;ng 8. D&#7921; b&amp;aacute;o s&#7913;c ti&amp;ecirc;u th&#7909; th&amp;eacute;p trong th&amp;aacute;ng 10 n&amp;agrave;y c&amp;oacute; th&#7875; s&#7869; c&amp;ograve;n th&#7845;p h&#417;n c&#7843; th&amp;aacute;ng 9, &#432;&#7899;c kho&#7843;ng 300.000 t&#7845;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngay DN ch&#7911; l&#7921;c nh&#432; T&#7893;ng c&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam, 10 th&amp;aacute;ng &#273;&#7847;u n&#259;m l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; &#273;&#7841;t 1,79 tri&#7879;u t&#7845;n, gi&#7843;m 6,7% so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m 2010. Hay nh&#432; T&#7853;p &#273;o&amp;agrave;n Th&amp;eacute;p H&amp;ograve;a Ph&amp;aacute;t &amp;ndash; &#273;&#7913;ng th&#7913; 2 v&#7873; c&amp;ocirc;ng su&#7845;t s&#7843;n xu&#7845;t nh&#432;ng hai th&amp;aacute;ng nay c&#361;ng ch&#7881; ch&#7841;y &#273;&#432;&#7907;c 80% c&amp;ocirc;ng su&#7845;t, &#273;&#7875; tr&amp;aacute;nh d&#432; th&#7915;a cung, l&#432;&#7907;ng h&amp;agrave;ng t&#7891;n kho.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7883; tr&#432;&#7901;ng b&#7883; thu h&#7865;p, l&#432;&#7907;ng h&amp;agrave;ng t&#7891;n kho l&#7899;n, trong khi &#273;&amp;oacute;, d&amp;ugrave; ng&amp;acirc;n h&amp;agrave;ng &#273;&amp;atilde; &#273;&#432;a ra ch&#432;&#417;ng tr&amp;igrave;nh &#432;u &#273;&amp;atilde;i l&amp;atilde;i su&#7845;t vay cho DN t&#7915; 17-19%/n&#259;m, nh&#432;ng th&#7921;c t&#7871; DN ng&amp;agrave;nh th&amp;eacute;p v&#7851;n ch&#432;a th&#7875; ti&#7871;p c&#7853;n &#273;&#432;&#7907;c ngu&#7891;n v&#7889;n n&amp;agrave;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&#361;ng theo Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam: &lt;em&gt;&amp;quot;Hi&#7879;n t&#7841;i ch&#432;a c&amp;oacute; DN th&amp;eacute;p c&amp;ocirc;ng b&#7889; ph&amp;aacute; s&#7843;n, nh&#432;ng DN th&amp;eacute;p &#273;ang thua l&#7895; n&#7863;ng l&#7855;m r&#7891;i, n&#7871;u 2 th&amp;aacute;ng cu&#7889;i n&#259;m v&#7851;n ti&#7871;p t&#7909;c kinh doanh &#7843;m &#273;&#7841;m, th&#7883; tr&#432;&#7901;ng b&#7883; thu h&#7865;p th&amp;igrave; ph&#7847;n l&amp;atilde;i c&#7911;a 10 th&amp;aacute;ng &#273;&#7847;u n&#259;m coi nh&#432; &#273;i tong&lt;/em&gt;&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&amp;acirc;y l&amp;agrave; hi&#7879;n t&#432;&#7907;ng l&#7863;p l&#7841;i 2-3 n&#259;m nay. V&amp;agrave;i th&amp;aacute;ng &#273;&#7847;u n&#259;m l&amp;atilde;i l&#7899;n, cu&#7889;i n&#259;m &amp;acirc;m, l&#7895; t&#7899;i c&#7843; n&#259;m sau. Th&#7921;c t&#7871; doanh nghi&#7879;p kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng, &#273;&amp;atilde; d&#7915;ng s&#7843;n xu&#7845;t. &#272;&#417;n c&#7917;, C&amp;ocirc;ng ty th&amp;eacute;p V&#7841;n L&#7907;i trong cu&#7897;c h&#7885;p &#272;&#7841;i h&#7897;i c&#7893; &#273;&amp;ocirc;ng g&#7847;n &#273;&amp;acirc;y &#273;&amp;atilde; tuy&amp;ecirc;n b&#7889; ng&#7915;ng s&#7843;n xu&#7845;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Quan ng&#7841;i h&#417;n, &amp;ocirc;ng C&#432;&#7901;ng th&amp;ocirc;ng tin, &#273;ang c&amp;oacute; hi&#7879;n t&#432;&#7907;ng c&#7841;nh tranh kh&amp;ocirc;ng l&amp;agrave;nh m&#7841;nh gi&#7919;a c&amp;aacute;c DN th&amp;eacute;p v&#7899;i nhau. Nhi&#7873;u DN b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; ngay t&#7841;i th&#7883; tr&#432;&#7901;ng trong n&#432;&#7899;c &#273;&#7875; &#273;&#7849;y m&#7841;nh l&#432;&#7907;ng h&amp;agrave;ng ti&amp;ecirc;u th&#7909;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;n nhi&#7873;u DN &#273;ang ch&amp;agrave;o gi&amp;aacute; th&#7845;p h&#417;n gi&amp;aacute; th&#7883; tr&#432;&#7901;ng t&#7915; 200.000 -300.000 &#273;&#7891;ng/t&#7845;n &#273;&#7875; &#273;&#7849;y m&#7841;nh ti&amp;ecirc;u th&#7909; v&amp;agrave; gi&#7843;m l&#432;&#7907;ng h&amp;agrave;ng t&#7891;n kho.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;&lt;em&gt;Khi ti&amp;ecirc;u th&#7909; trong n&#432;&#7899;c v&amp;agrave; c&#7843; xu&#7845;t kh&#7849;u kh&amp;oacute; kh&#259;n, c&amp;aacute;c DN trong n&#432;&#7899;c kh&amp;ocirc;ng c&amp;oacute; s&#7921; b&amp;agrave;n b&#7841;c chia s&#7867; kh&amp;oacute; kh&#259;n chung, chia s&#7867; th&#7883; ph&#7847;n m&amp;agrave; l&#7841;i ch&amp;agrave;o gi&amp;aacute; th&#7845;p h&#417;n &#273;&#7889;i t&amp;aacute;c nh&#7857;m gi&amp;agrave;nh b&#7841;n h&amp;agrave;ng n&amp;ecirc;n &#273;&amp;atilde; c&amp;oacute; hi&#7879;n t&#432;&#7907;ng DN ki&#7879;n c&amp;aacute;o nhau trong n&#7897;i b&#7897; Hi&#7879;p h&#7897;&lt;/em&gt;i&amp;quot;- &amp;ocirc;ng C&#432;&#7901;ng b&amp;igrave;nh lu&#7853;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7921;c t&#7871;, trong n&#7897;i b&#7897; Hi&#7879;p h&#7897;i &#273;&amp;atilde; c&amp;oacute; t&amp;igrave;nh tr&#7841;ng ki&#7879;n c&amp;aacute;o cho l&amp;agrave; b&amp;aacute;n ph&amp;aacute; gi&amp;aacute; gi&#7919;a T&#7893;ng c&amp;ocirc;ng ty th&amp;eacute;p Vi&#7879;t Nam v&amp;agrave; Posco Vi&#7879;t Nam.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Ph&#7843;i t&#7921; c&#7913;u m&amp;igrave;nh&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo d&#7921; b&amp;aacute;o c&#7911;a B&#7897; C&amp;ocirc;ng th&#432;&#417;ng, ch&#7911; tr&#432;&#417;ng th&#7855;t ch&#7863;t &#273;&#7847;u t&#432; c&amp;ocirc;ng, t&amp;iacute;n d&#7909;ng c&#7911;a Ch&amp;iacute;nh ph&#7911; ch&#7855;c ch&#7855;n s&#7869; c&amp;ograve;n k&amp;eacute;o d&amp;agrave;i, v&#7899;i &#273;&amp;agrave; n&amp;agrave;y kh&amp;oacute; kh&#259;n c&#7911;a ng&amp;agrave;nh th&amp;eacute;p c&amp;oacute; th&#7875; c&amp;ograve;n k&amp;eacute;o d&amp;agrave;i sang n&#259;m 2012.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong b&#7889;i c&#7843;nh s&#7913;c mua s&#7909;t gi&#7843;m, ng&amp;agrave;nh th&amp;eacute;p kh&amp;ocirc;ng th&#7875; &#273;&#7913;ng ngo&amp;agrave;i l&#7873;. Suy tho&amp;aacute;i kinh t&#7871; th&#7871; gi&#7899;i &#273;ang tr&#7903; l&#7841;i khi&#7871;n nhu c&#7847;u th&amp;eacute;p tr&amp;ecirc;n th&#7871; gi&#7899;i gi&#7843;m, k&amp;eacute;o gi&amp;aacute; th&amp;eacute;p gi&#7843;m.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nguy c&#417; nh&amp;igrave;n th&#7845;y l&amp;agrave; th&amp;eacute;p ngo&#7841;i c&amp;oacute; th&#7875; tr&amp;agrave;n v&amp;agrave;o &#273;&#7849;y c&amp;aacute;c DN th&amp;eacute;p trong n&#432;&#7899;c v&amp;agrave;o t&amp;igrave;nh th&#7871; kh&amp;oacute; kh&#259;n h&#417;n do ph&#7843;i c&#7841;nh tranh v&#7899;i th&amp;eacute;p ngo&#7841;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7913; tr&#432;&#7903;ng B&#7897; C&amp;ocirc;ng th&#432;&#417;ng L&amp;ecirc; D&#432;&#417;ng Quang cho r&#7857;ng, t&#432;&#417;ng lai c&#7911;a ng&amp;agrave;nh th&amp;eacute;p l&amp;agrave; r&#7845;t s&amp;aacute;ng s&#7911;a, nhu c&#7847;u th&amp;eacute;p trong nh&#7919;ng n&#259;m t&#7899;i c&amp;ograve;n nhi&#7873;u. V&amp;igrave; v&#7853;y, c&amp;aacute;c DN ph&#7843;i t&#7921; c&#7913;u m&amp;igrave;nh tr&#432;&#7899;c khi &#273;&#432;&#7907;c c&#7913;u. B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute; ph&#7843;i ti&#7871;t ki&#7879;m n&#259;ng l&#432;&#7907;ng, v&#7853;t t&#432;, nguy&amp;ecirc;n v&#7853;t li&#7879;u, c&amp;aacute;c DN c&#7847;n h&#7907;p t&amp;aacute;c ch&#7863;t ch&#7869; &#273;&#7875; &#273;&#7889;i ph&amp;oacute; v&#7899;i v&#7845;n &#273;&#7873; ch&#7889;ng b&amp;aacute;n ph&amp;aacute; gi&amp;aacute;,&amp;hellip;&lt;/p&gt;','08/11/2011','08/11/2011','/home/uploads/image/news/11-2011/doanh-nghiep-thep-kho-khan.jpg',23,2,1964,'huyennt',1,'thep-e-dn-kien-nhau-ban-pha-gia','doanh nghi&#7879;p th&eacute;p, kh&oacute; kh&#259;n, b&aacute;n ph&aacute; gi&aacute;',NULL,NULL,NULL,'doanh nghi&#7879;p th&eacute;p, kh&oacute; kh&#259;n, b&aacute;n ph&aacute; gi&aacute;',0,0,0,'','','huyennt',3,'http://dothi.net',1,0,0,0),(92,'Ng&agrave;nh nh&#7921;a x&acirc;y d&#7921;ng: &ldquo;V&#432;&#7907;t kh&oacute;&rdquo; b&#7903;i &#273;&#7849;y m&#7841;nh b&aacute;n h&agrave;ng v&agrave; tr&#7919; nguy&ecirc;n v&#7853;t li&#7879;u','V&#7889;n vay ng&acirc;n h&agrave;ng c&oacute; t&aacute;c &#273;&#7897;ng &#273;&#7871;n k&#7871;t qu&#7843; kinh doanh c&#7911;a 6 doanh nghi&#7879;p, tuy nhi&ecirc;n kh&ocirc;ng l&#7899;n ngo&#7841;i tr&#7915; DAG.','&lt;p&gt;&amp;nbsp;6 doanh nghi&#7879;p thu&#7897;c nh&amp;oacute;m nh&#7921;a x&amp;acirc;y d&#7921;ng - bao b&amp;igrave; nh&#7921;a x&amp;acirc;y d&#7921;ng g&#7891;m: CTCP Nh&#7921;a B&amp;igrave;nh Minh (MCK: BMP), CTCP Nh&#7921;a Thi&#7871;u Ni&amp;ecirc;n Ti&#7873;n Phong (MCK: NTP), CTCP T&#7853;p &#273;o&amp;agrave;n Nh&#7921;a &#272;&amp;ocirc;ng &amp;Aacute; (MCK: DAG), CTCP Nh&#7921;a &#272;&#7891;ng Nai (MCK: DNP), CTCP Nh&#7921;a &#272;&amp;agrave; N&#7861;ng (MCK: DPC), CTCP Nh&#7921;a R&#7841;ng &#272;&amp;ocirc;ng (MCK: RDP) &#273;&amp;atilde; c&amp;ocirc;ng b&#7889; B&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2011.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong 6 doanh nghi&#7879;p, ch&#7881; c&amp;oacute; 2 doanh nghi&#7879;p c&amp;oacute; l&#7907;i nhu&#7853;n tr&#432;&#7899;c thu&#7871; gi&#7843;m so v&#7899;i 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2010 l&amp;agrave; DPC v&amp;agrave; DAG.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;5/6 doanh nghi&#7879;p c&amp;oacute; bi&amp;ecirc;n l&#7907;i nhu&#7853;n g&#7897;p gi&#7843;m&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;Sau 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2011 ho&#7841;t &#273;&#7897;ng, b&amp;aacute;o c&amp;aacute;o k&#7871;t qu&#7843; kinh doanh c&#7911;a 6 doanh nghi&#7879;p ng&amp;agrave;nh nh&#7921;a x&amp;acirc;y d&#7921;ng ni&amp;ecirc;m y&#7871;t tr&amp;ecirc;n 2 S&#7903; giao d&#7883;ch ch&#7913;ng kho&amp;aacute;n cho th&#7845;y l&#7907;i th&#7871; &#273;&#7847;u ti&amp;ecirc;n thu&#7897;c v&#7873; doanh nghi&#7879;p c&amp;oacute; k&#7871; ho&#7841;ch d&#7921; tr&#7919; nguy&amp;ecirc;n v&#7853;t li&#7879;u t&#7889;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;5/6 doanh nghi&#7879;p c&amp;oacute; bi&amp;ecirc;n l&#7907;i nhu&#7853;n g&#7897;p gi&#7843;m, ngo&#7841;i tr&#7915; RDP. Theo gi&#7843;i tr&amp;igrave;nh c&#7911;a m&#7897;t s&#7889; doanh nghi&#7879;p trong ng&amp;agrave;nh, trong k&#7923; gi&amp;aacute; v&#7889;n h&amp;agrave;ng b&amp;aacute;n t&#259;ng cao do gi&amp;aacute; nguy&amp;ecirc;n v&#7853;t li&#7879;u t&#259;ng, chi ph&amp;iacute; &#273;&#7847;u v&amp;agrave;o nh&#7853;p kh&#7849;u t&#259;ng do &#7843;nh h&#432;&#7903;ng c&#7911;a t&#7927; gi&amp;aacute;, c&#361;ng nh&#432; chi ph&amp;iacute; v&#7853;n chuy&#7875;n, &#273;i&#7879;n t&#259;ng, nh&amp;acirc;n c&amp;ocirc;ng t&#259;ng. &#272;&amp;acirc;y l&amp;agrave; nh&#7919;ng nguy&amp;ecirc;n nh&amp;acirc;n ch&amp;iacute;nh l&amp;agrave;m cho bi&amp;ecirc;n l&#7907;i nhu&#7853;n g&#7897;p c&#7911;a h&#7847;u h&#7871;t c&amp;aacute;c doanh nghi&#7879;p gi&#7843;m ngo&#7841;i tr&#7915; RDP.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;img width=&quot;550&quot; height=&quot;550&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/nganhnhua1.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;Theo gi&#7843;i tr&amp;igrave;nh c&#7911;a RDP, trong qu&amp;yacute; II/2011 s&#7843;n l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; bao b&amp;igrave;, m&amp;agrave;ng &#273;i m&#432;a t&#259;ng &#273;&amp;atilde; l&amp;agrave;m cho doanh thu t&#259;ng; qu&amp;yacute; III/2011, s&#7843;n l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; t&#259;ng so v&#7899;i c&amp;ugrave;ng k&#7923;, b&amp;ecirc;n c&#7841;nh &#273;&amp;oacute; C&amp;ocirc;ng ty c&amp;oacute; k&#7871; ho&#7841;ch d&#7921; tr&#7919; nguy&amp;ecirc;n v&#7853;t li&#7879;u t&#7889;t. &#272;&amp;acirc;y c&amp;oacute; th&#7875; xem l&amp;agrave; nguy&amp;ecirc;n nh&amp;acirc;n ch&amp;iacute;nh gi&amp;uacute;p RDP c&#7843;i thi&#7879;n &#273;&#432;&#7907;c bi&amp;ecirc;n l&#7907;i nhu&#7853;n g&#7897;p c&#7911;a m&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&#272;&#7849;y m&#7841;nh b&amp;aacute;n h&amp;agrave;ng&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;N&#432;&#7899;c l&amp;ecirc;n thuy&#7873;n l&amp;ecirc;n&amp;rdquo;, gi&amp;aacute; v&#7889;n h&amp;agrave;ng b&amp;aacute;n t&#259;ng v&#7851;n ch&#432;a ph&#7843;i l&amp;agrave; nguy&amp;ecirc;n nh&amp;acirc;n ch&amp;iacute;nh t&amp;aacute;c &#273;&#7897;ng &#273;&#7871;n k&#7871;t qu&#7843; kinh doanh cu&#7889;i c&amp;ugrave;ng c&#7911;a c&amp;aacute;c doanh nghi&#7879;p ng&amp;agrave;nh nh&#7921;a. B&#7903;i tr&amp;ecirc;n th&#7921;c t&#7871;, c&amp;aacute;c doanh nghi&#7879;p nh&#432; BMP, NTP bi&amp;ecirc;n l&#7907;i nhu&#7853;n g&#7897;p gi&#7843;m nh&#7865;, l&#7907;i nhu&#7853;n tr&#432;&#7899;c thu&#7871; v&#7851;n t&#259;ng d&amp;ugrave; cho c&amp;aacute;c chi ph&amp;iacute; b&amp;aacute;n h&amp;agrave;ng, chi ph&amp;iacute; qu&#7843;n l&amp;yacute; doanh nghi&#7879;p, chi ph&amp;iacute; l&amp;atilde;i vay t&#259;ng. &#272;i&#7875;n h&amp;igrave;nh DNP c&amp;oacute; bi&amp;ecirc;n l&#7907;i nhu&#7853;n g&#7897;p gi&#7843;m h&#417;n 25% nh&#432;ng l&#7907;i nhu&#7853;n tr&#432;&#7899;c thu&#7871; t&#259;ng 38,4% so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m tr&#432;&#7899;c.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;img width=&quot;550&quot; height=&quot;550&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/nganhnhua2.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&amp;nbsp;S&#7843;n l&#432;&#7907;ng ti&amp;ecirc;u th&#7909; t&#259;ng so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m tr&#432;&#7899;c l&amp;agrave; m&#7845;u ch&#7889;t gi&amp;uacute;p c&amp;aacute;c doanh nghi&#7879;p v&#432;&#7907;t qua r&amp;agrave;o c&#7843;n gi&amp;aacute; v&#7889;n cao. Doanh thu thu&#7847;n b&amp;aacute;n h&amp;agrave;ng v&amp;agrave; cung c&#7845;p d&#7883;ch v&#7909; 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m ngo&#7841;i tr&#7915; DPC gi&#7843;m 0,9%, c&amp;ograve;n l&#7841;i 5 doanh nghi&#7879;p &#273;&#7873;u c&amp;oacute; m&#7913;c t&#259;ng t&#7915; 27% &#273;&#7871;n 62,1% so v&#7899;i 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2010.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;G&#7863;p kh&amp;oacute; v&#7899;i l&amp;atilde;i su&#7845;t cao?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong 6 doanh nghi&#7879;p, c&amp;oacute; 2 doanh nghi&#7879;p c&amp;oacute; l&#7907;i nhu&#7853;n tr&#432;&#7899;c thu&#7871; gi&#7843;m so v&#7899;i 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2010 l&amp;agrave; DPC v&amp;agrave; DAG. &#272;&amp;acirc;y l&amp;agrave; c&#7843; 2 doanh nghi&#7879;p c&amp;oacute; chi ph&amp;iacute; l&amp;atilde;i vay 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m t&#259;ng so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m tr&#432;&#7899;c cao nh&#7845;t trong nh&amp;oacute;m.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;img width=&quot;550&quot; height=&quot;550&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/nganhnhua3.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&amp;nbsp;Tuy nhi&amp;ecirc;n, x&amp;eacute;t v&#7873; t&#7927; tr&#7885;ng chi ph&amp;iacute; l&amp;atilde;i vay/doanh thu thu&#7847;n 9 th&amp;aacute;ng DAG cao nh&#7845;t nh&amp;oacute;m kho&#7843;ng 6%, DPC ch&#7881; 1,3% th&#7845;p h&#417;n DNP v&amp;agrave; RDP.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;img width=&quot;550&quot; height=&quot;550&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/nganhnhua4.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;Theo DAG, l&amp;atilde;i su&#7845;t t&#259;ng cao n&#259;m 2011 n&amp;ecirc;n chi ph&amp;iacute; l&amp;atilde;i vay c&#7911;a DAG lu&amp;ocirc;n duy tr&amp;igrave; &#7903; m&#7913;c cao&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;img width=&quot;550&quot; height=&quot;550&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/nganhnhua5.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;V&amp;igrave; v&#7853;y l&amp;atilde;i su&#7845;t cao th&#7921;c s&#7921; c&amp;oacute; g&amp;acirc;y kh&amp;oacute; cho c&amp;aacute;c doanh nghi&#7879;p, nh&#432;ng c&amp;aacute;c doanh nghi&#7879;p ch&#7911; &#273;&#7897;ng trong qu&#7843;n l&amp;yacute; gi&amp;aacute; th&amp;agrave;nh hi&#7879;u qu&#7843; v&amp;agrave; &#273;&#7849;y m&#7841;nh b&amp;aacute;n h&amp;agrave;ng, k&#7871;t qu&#7843; kinh doanh s&#7869; kh&#7843; quan h&#417;n. B&#7903;i th&#7921;c t&#7871; cho th&#7845;y, DPC do doanh thu thu&#7847;n gi&#7843;m 0,9%, bi&amp;ecirc;n l&#7907;i nhu&#7853;n g&#7897;p gi&#7843;m 6% so v&#7899;i 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2010, d&#7851;n &#273;&#7871;n l&#7907;i nhu&#7853;n tr&#432;&#7899;c thu&#7871; gi&#7843;m 30,5%; c&amp;ograve;n l&#7841;i  3 doanh nghi&#7879;p kh&amp;aacute;c d&amp;ugrave; c&amp;oacute; l&amp;atilde;i vay t&#259;ng nh&#432;ng doanh thu thu&#7847;n t&#259;ng m&#7841;nh, l&#7907;i nhu&#7853;n tr&#432;&#7899;c thu&#7871; t&#259;ng.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&amp;nbsp;&lt;img width=&quot;550&quot; height=&quot;550&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/nganhnhua6.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;Ngo&#7841;i tr&#7915; BMP kh&amp;ocirc;ng c&amp;ocirc;ng b&#7889; l&amp;atilde;i c&#417; b&#7843;n tr&amp;ecirc;n m&#7895;i c&#7893; phi&#7871;u (EPS) c&amp;ograve;n l&#7841;i trong 5 doanh nghi&#7879;p ch&#7881; c&amp;oacute; 2 doanh nghi&#7879;p DNP v&amp;agrave; RDP c&amp;oacute; EPS 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2011 t&#259;ng so v&#7899;i 9 th&amp;aacute;ng &#273;&#7847;u n&#259;m 2010. &#272;&amp;acirc;y c&#361;ng l&amp;agrave; 2 doanh nghi&#7879;p c&amp;oacute; m&#7913;c t&#259;ng tr&#432;&#7903;ng l&#7907;i nhu&#7853;n tr&#432;&#7899;c thu&#7871; cao nh&#7845;t nh&amp;oacute;m.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&lt;strong&gt;M&#7897;t s&#7889; ch&#7881; ti&amp;ecirc;u kh&amp;aacute;c:&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;img width=&quot;550&quot; height=&quot;550&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/nganhnhua7.jpg&quot; /&gt;&lt;/p&gt;\r\n','09/11/2011','09/11/2011','/home/uploads/image/news/11-2011/nganhnhua1.jpg',23,2,1868,'huyennt',1,'nganh-nhua-xay-dung-vuot-kho-boi-day-manh-ban-hang-va-tru-nguyen-vat-lieu','Nh&#7921;a, doanh thu, x&acirc;y d&#7921;ng, VLXD, b&aacute;n h&agrave;ng, tr&#7919; nguy&ecirc;n',NULL,NULL,NULL,'Nh&#7921;a, doanh thu, x&acirc;y d&#7921;ng, VLXD, b&aacute;n h&agrave;ng, tr&#7919; nguy&ecirc;n',0,0,0,'','','huyennt',3,'http://cafef.vn',1,0,0,0),(93,'C&#7845;m tri&#7879;t &#273;&#7875; vi&#7879;c d&ugrave;ng &#273;&#7845;t s&#7843;n xu&#7845;t n&ocirc;ng nghi&#7879;p s&#7843;n xu&#7845;t g&#7841;ch ng&oacute;i &#273;&#7845;t s&eacute;t nung','B&#7897; X&acirc;y d&#7921;ng v&#7915;a c&oacute; v&#259;n b&#7843;n &#273;&#7873; ngh&#7883; UBND c&aacute;c T&#7881;nh,Th&agrave;nh ph&#7889; t&#259;ng c&#432;&#7901;ng c&ocirc;ng t&aacute;c qu&#7843;n l&yacute; nh&agrave; n&#432;&#7899;c v&#7873; s&#7843;n xu&#7845;t v&#7853;t li&#7879;u x&acirc;y d&#7921;ng, &#273;&#7863;c bi&#7879;t l&agrave; qu&#7843;n l&yacute; ch&#7863;t ch&#7869; c&aacute;c d&#7921; &aacute;n &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t g&#7841;ch ng&oacute;i &#273;&#7845;t s&eacute;t nung.','\r\n\r\n\r\n&lt;p&gt;&#272;&#7875; t&#7893;ng h&#7907;p, b&amp;aacute;o c&amp;aacute;o Th&#7911; t&#432;&#7899;ng Ch&amp;iacute;nh ph&#7911; v&amp;agrave;o cu&#7889;i n&#259;m 2011 v&#7873; t&amp;igrave;nh h&amp;igrave;nh th&#7921;c hi&#7879;n Quy&#7871;t &#273;&#7883;nh s&#7889; 567/2010/Q&#272;- TTg c&#7911;a Th&#7911; t&#432;&#7899;ng Ch&amp;iacute;nh ph&#7911;, Quy&#7871;t &#273;&#7883;nh s&#7889; 15/2000/Q&#272;-BXD ng&amp;agrave;y 24/7/2000 v&amp;agrave; c&amp;aacute;c Th&amp;ocirc;ng t&#432; c&#7911;a B&#7897; X&amp;acirc;y d&#7921;ng; B&#7897; X&amp;acirc;y d&#7921;ng v&#7915;a c&amp;oacute; v&#259;n b&#7843;n &#273;&#7873; ngh&#7883; UBND c&amp;aacute;c T&#7881;nh, Th&amp;agrave;nh ph&#7889; tr&#7921;c thu&#7897;c Trung &#432;&#417;ng t&#259;ng c&#432;&#7901;ng c&amp;ocirc;ng t&amp;aacute;c qu&#7843;n l&amp;yacute; nh&amp;agrave; n&#432;&#7899;c v&#7873; s&#7843;n xu&#7845;t v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng, &#273;&#7863;c bi&#7879;t l&amp;agrave; qu&#7843;n l&amp;yacute; ch&#7863;t ch&#7869; c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i &#273;&#7845;t s&amp;eacute;t nung. C&#7845;m tri&#7879;t &#273;&#7875; vi&#7879;c d&amp;ugrave;ng &#273;&#7845;t s&#7843;n xu&#7845;t n&amp;ocirc;ng nghi&#7879;p s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i &#273;&#7845;t s&amp;eacute;t nung; c&#7845;m nung g&#7841;ch b&#7857;ng l&amp;ograve; th&#7911; c&amp;ocirc;ng; t&#259;ng c&#432;&#7901;ng tri&#7875;n khai Ch&#432;&#417;ng tr&amp;igrave;nh ph&amp;aacute;t tri&#7875;n VLXKN t&#7915;ng b&#432;&#7899;c thay th&#7871; v&#7853;t li&#7879;u nung. Ch&#7881; &#273;&#7841;o ki&#7875;m tra, t&#7893;ng h&#7907;p, b&amp;aacute;o c&amp;aacute;o k&#7871;t qu&#7843; tri&#7875;n khai, th&#7921;c hi&#7879;n c&amp;aacute;c V&#259;n b&#7843;n n&amp;oacute;i tr&amp;ecirc;n theo c&amp;aacute;c n&#7897;i dung sau:&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nT&amp;igrave;nh h&amp;igrave;nh l&#7853;p quy ho&#7841;ch v&amp;agrave; th&#7921;c hi&#7879;n quy ho&#7841;ch ph&amp;aacute;t tri&#7875;n v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng giai &#273;o&#7841;n 2005-2010; 2011-2015 t&#7847;m nh&amp;igrave;n 2020. T&amp;igrave;nh h&amp;igrave;nh nguy&amp;ecirc;n li&#7879;u v&amp;agrave; quy ho&#7841;ch v&amp;ugrave;ng nguy&amp;ecirc;n li&#7879;u &#273;&#7875; s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i &#273;&#7845;t s&amp;eacute;t nung tr&amp;ecirc;n &#273;&#7883;a b&amp;agrave;n. T&amp;igrave;nh h&amp;igrave;nh &#273;&#7847;u t&#432; v&amp;agrave; s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i nung v&amp;agrave; v&#7853;t li&#7879;u x&amp;acirc;y kh&amp;ocirc;ng nung tr&amp;ecirc;n &#273;&#7883;a b&amp;agrave;n (s&#7889; li&#7879;u l&#7853;p theo bi&#7875;u m&#7851;u). K&#7871;t qu&#7843; th&#7921;c hi&#7879;n l&#7897; tr&amp;igrave;nh xo&amp;aacute; b&#7887;, chuy&#7875;n &#273;&#7893;i c&amp;aacute;c l&amp;ograve; g&#7841;ch th&#7911; c&amp;ocirc;ng sang l&amp;ograve; c&amp;oacute; c&amp;ocirc;ng ngh&#7879; ti&amp;ecirc;n ti&#7871;n. T&amp;igrave;nh h&amp;igrave;nh tri&#7875;n khai c&amp;ocirc;ng t&amp;aacute;c qu&#7843;n l&amp;yacute; nh&amp;agrave; n&#432;&#7899;c v&#7873; ch&#7845;t l&#432;&#7907;ng VLXD, vi&#7879;c th&#7921;c hi&#7879;n ch&#7913;ng nh&#7853;n h&#7907;p qui v&amp;agrave; c&amp;ocirc;ng b&#7889; h&#7907;p qui, &#273;&#432;&#7907;c qui &#273;&#7883;nh t&#7841;i 04 Th&amp;ocirc;ng t&#432; n&amp;oacute;i tr&amp;ecirc;n. C&amp;aacute;c kh&amp;oacute; kh&#259;n v&#432;&#7899;ng m&#7855;c v&amp;agrave; ki&#7871;n ngh&#7883; .&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nTh&#7913; tr&#432;&#7903;ng Nguy&#7877;n Tr&#7847;n Nam cho bi&#7871;t: Theo s&#7889; li&#7879;u b&amp;aacute;o c&amp;aacute;o c&#7911;a c&amp;aacute;c t&#7881;nh, th&amp;agrave;nh ph&#7889; v&amp;agrave; k&#7871;t qu&#7843; kh&#7843;o s&amp;aacute;t t&#7841;i m&#7897;t s&#7889; &#273;&#7883;a ph&#432;&#417;ng cho th&#7845;y, nhi&#7873;u &#273;&#7883;a ph&#432;&#417;ng &#273;&amp;atilde; t&#7893; ch&#7913;c tri&#7875;n khai, th&#7921;c hi&#7879;n nghi&amp;ecirc;m t&amp;uacute;c c&amp;aacute;c Quy&#7871;t &#273;&#7883;nh tr&amp;ecirc;n. &#272;&#7871;n th&#7901;i &#273;i&#7875;m gi&#7919;a n&#259;m 2011, m&#7897;t s&#7889; &#273;&#7883;a ph&#432;&#417;ng &#273;&amp;atilde; lo&#7841;i b&#7887; &#273;&#432;&#7907;c h&#7847;u h&#7871;t c&amp;aacute;c l&amp;ograve; th&#7911; c&amp;ocirc;ng v&amp;agrave; ph&#7845;n &#273;&#7845;u lo&#7841;i b&#7887; ho&amp;agrave;n to&amp;agrave;n c&amp;aacute;c l&amp;ograve; th&#7911; c&amp;ocirc;ng s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i &#273;&#7845;t s&amp;eacute;t nung, khuy&#7871;n kh&amp;iacute;ch &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t v&#7853;t li&#7879;u x&amp;acirc;y kh&amp;ocirc;ng nung (VLXKN) nh&#432;: t&#7881;nh H&#7843;i D&#432;&#417;ng, t&#7881;nh &#272;&#7891;ng Nai, t&#7881;nh B&#7855;c Ninh&amp;hellip; v&amp;agrave; th&amp;agrave;nh ph&#7889; H&#7891; Ch&amp;iacute; Minh. M&#7897;t s&#7889; t&#7881;nh &#273;&amp;atilde; x&amp;acirc;y d&#7921;ng l&#7897; tr&amp;igrave;nh &#273;&#7875; tri&#7875;n khai th&#7921;c hi&#7879;n, song nhi&#7873;u t&#7881;nh th&#7921;c hi&#7879;n c&amp;aacute;c Quy&#7871;t &#273;&#7883;nh tr&amp;ecirc;n c&amp;ograve;n h&#7841;n ch&#7871;, c&amp;oacute; m&#7897;t s&#7889; t&#7881;nh v&#7851;n t&#7891;n t&#7841;i l&amp;ograve; th&#7911; c&amp;ocirc;ng ngay trong, ven th&amp;agrave;nh ph&#7889;, th&#7883; x&amp;atilde;, th&#7883; tr&#7845;n g&amp;acirc;y &amp;ocirc; nhi&#7877;m m&amp;ocirc;i tr&#432;&#7901;ng, l&amp;agrave;m &#7843;nh h&#432;&#7903;ng t&#7899;i &#273;&#7901;i s&#7889;ng d&amp;acirc;n sinh, hu&#7927; ho&#7841;i c&amp;acirc;y tr&#7891;ng v&#7853;t nu&amp;ocirc;i, g&amp;acirc;y b&#7913;c x&amp;uacute;c trong x&amp;atilde; h&#7897;i nh&#432;: V&#297;nh Long, An Giang... &#7903; m&#7897;t s&#7889; t&#7881;nh, t&#7893;ng s&#7889; l&amp;ograve; th&#7911; c&amp;ocirc;ng s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i c&amp;ograve;n r&#7845;t l&#7899;n nh&#432;: An Giang, B&#7855;c Giang, H&amp;agrave; N&#7897;i, V&#297;nh Ph&amp;uacute;c, Ph&amp;uacute; Th&#7885;, &#272;&#7891;ng Th&amp;aacute;p, V&#297;nh Long... . &#272;&#7863;c bi&#7879;t c&amp;oacute; t&#7881;nh tr&#432;&#7899;c &#273;&amp;acirc;y &#273;&amp;atilde; xo&amp;aacute; b&#7887; l&amp;ograve; nung g&#7841;ch th&#7911; c&amp;ocirc;ng m&#7897;t c&amp;aacute;ch tri&#7879;t &#273;&#7875;, song m&#7845;y n&#259;m g&#7847;n &#273;&amp;acirc;y l&#7841;i ph&amp;aacute;t tri&#7875;n nhi&#7873;u tr&#7903; l&#7841;i nh&#432; t&#7881;nh Th&amp;aacute;i B&amp;igrave;nh.&lt;/p&gt;\r\n','13/11/2011','13/11/2011','',23,2,1977,'huyennt',1,'cam-triet-de-viec-dung-dat-san-xuat-nong-nghiep-san-xuat-gach-ngoi-dat-set-nung','g&#7841;ch ng&oacute;i, s&#7843;n xu&#7845;t, &#273;&#7845;t s&eacute;t nung, c&#7845;m tri&#7879;t, VLXD ',NULL,NULL,NULL,'g&#7841;ch ng&oacute;i, s&#7843;n xu&#7845;t, &#273;&#7845;t s&eacute;t nung, c&#7845;m tri&#7879;t, VLXD ',0,0,0,'','','huyennt',3,'http://dothi.net',1,0,0,0),(94,'S&#7917; d&#7909;ng r&#7897;ng r&atilde;i g&#7841;ch kh&ocirc;ng nung','Tuy nhi&ecirc;n, th&#7921;c t&#7871; cho th&#7845;y, v&#7851;n c&ograve;n nhi&#7873;u r&agrave;o c&#7843;n &#273;&#7889;i v&#7899;i s&#7921; ph&aacute;t tri&#7875;n c&#7911;a ng&agrave;nh v&#7853;t li&#7879;u x&acirc;y d&#7921;ng m&#7899;i n&agrave;y.','&lt;p&gt;&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\nNhi&#7873;u n&#259;m qua, c&amp;aacute;c ch&amp;iacute;nh s&amp;aacute;ch th&amp;uacute;c &#273;&#7849;y s&#7843;n xu&#7845;t, ti&amp;ecirc;u th&#7909; v&#7853;t li&#7879;u x&amp;acirc;y kh&amp;ocirc;ng nung (VLXKN), ch&#7911; y&#7871;u l&amp;agrave; g&#7841;ch kh&amp;ocirc;ng nung (GKN) &#273;&amp;atilde; &#273;&#432;&#7907;c Nh&amp;agrave; n&#432;&#7899;c ban h&amp;agrave;nh. Tuy nhi&amp;ecirc;n, th&#7921;c t&#7871; cho th&#7845;y, v&#7851;n c&amp;ograve;n nhi&#7873;u r&amp;agrave;o c&#7843;n &#273;&#7889;i v&#7899;i s&#7921; ph&amp;aacute;t tri&#7875;n c&#7911;a ng&amp;agrave;nh v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng m&#7899;i n&amp;agrave;y.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;strong&gt;Th&#7883; tr&#432;&#7901;ng kh&amp;oacute; ti&amp;ecirc;u th&#7909;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;S&#7843;n ph&#7849;m GKN c&amp;oacute; nhi&#7873;u lo&#7841;i nh&#432;: g&#7841;ch xi-m&#259;ng - c&#7889;t li&#7879;u (g&#7841;ch Block), g&#7841;ch b&amp;ecirc;-t&amp;ocirc;ng b&#7885;t, g&#7841;ch b&amp;ecirc;-t&amp;ocirc;ng kh&amp;iacute; ch&#432;ng &amp;aacute;p..., nh&#432;ng t&#7921;u trung &#273;&#7873;u c&amp;oacute; nh&#7919;ng &#273;&#7863;c t&amp;iacute;nh &#432;u vi&#7879;t: nh&#7865;, th&amp;acirc;n thi&#7879;n v&amp;agrave; ti&#7871;t ki&#7879;m. Song, tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng, t&#7927; l&#7879; s&#7917; d&#7909;ng v&#7853;t li&#7879;u n&amp;agrave;y hi&#7879;n c&amp;ograve;n r&#7845;t th&#7845;p. Nguy&amp;ecirc;n nh&amp;acirc;n ch&#7911; y&#7871;u l&amp;agrave; do th&amp;oacute;i quen s&#7917; d&#7909;ng g&#7841;ch &#273;&#7845;t nung, x&amp;acirc;y d&#7921;ng theo l&#7889;i th&#7911; c&amp;ocirc;ng truy&#7873;n th&#7889;ng c&#7911;a ng&#432;&#7901;i d&amp;acirc;n. V&#7873; ch&#7911; quan, &#273;&amp;oacute; l&amp;agrave; do vi&#7879;c ch&#7853;m ban h&amp;agrave;nh c&amp;aacute;c &#273;&#7883;nh m&#7913;c, ti&amp;ecirc;u chu&#7849;n, h&#432;&#7899;ng d&#7851;n thi c&amp;ocirc;ng, nghi&#7879;m thu c&amp;ocirc;ng tr&amp;igrave;nh... d&#7851;n t&#7899;i kh&amp;oacute; kh&#259;n v&#7873; d&#7921; to&amp;aacute;n, thanh quy&#7871;t to&amp;aacute;n c&amp;ocirc;ng tr&amp;igrave;nh c&amp;oacute; s&#7917; d&#7909;ng VLXKN. B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, t&amp;igrave;nh h&amp;igrave;nh kinh t&#7871; kh&amp;oacute; kh&#259;n t&#7915; &#273;&#7847;u n&#259;m c&#361;ng t&amp;aacute;c &#273;&#7897;ng m&#7841;nh &#273;&#7871;n vi&#7879;c ti&amp;ecirc;u th&#7909; d&amp;ograve;ng s&#7843;n ph&#7849;m m&#7899;i n&amp;agrave;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ph&amp;oacute; V&#7909; tr&#432;&#7903;ng V&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng (B&#7897; X&amp;acirc;y d&#7921;ng) Ph&#7841;m V&#259;n B&#7855;c cho bi&#7871;t, n&#7871;u c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&#7847;u t&#432; x&amp;acirc;y d&#7921;ng GKN &#273;i v&amp;agrave;o ho&#7841;t &#273;&#7897;ng theo &#273;&amp;uacute;ng ti&#7871;n &#273;&#7897; th&amp;igrave; t&#7927; tr&#7885;ng VLXKN s&#7869; &#273;&amp;aacute;p &#7913;ng &#273;&#432;&#7907;c kho&#7843;ng 18% t&#7893;ng kh&#7889;i l&#432;&#7907;ng v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng. C&#7843; n&#432;&#7899;c hi&#7879;n c&amp;oacute; g&#7847;n 30 doanh nghi&#7879;p (DN) l&#7853;p d&#7921; &amp;aacute;n &#273;&#7847;u t&#432;, nh&#432;ng m&#7899;i c&amp;oacute; b&#7843;y d&#7921; &amp;aacute;n &#273;i v&amp;agrave;o s&#7843;n xu&#7845;t v&#7899;i c&amp;ocirc;ng su&#7845;t m&#7897;t tri&#7879;u m3/n&#259;m, hai d&#7921; &amp;aacute;n &#273;&amp;atilde; l&#7855;p &#273;&#7863;t thi&#7871;t b&#7883; xong nh&#432;ng ch&#432;a &#273;&#432;a v&amp;agrave;o s&#7843;n xu&#7845;t th&#7917; v&amp;agrave; h&#417;n 12 d&#7921; &amp;aacute;n &#273;ang trong giai &#273;o&#7841;n chu&#7849;n b&#7883; &#273;&#7847;u t&#432;. Do &#7843;nh h&#432;&#7903;ng t&#7915; th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n &#7843;m &#273;&#7841;m, m&#7897;t s&#7889; d&#7921; &amp;aacute;n ph&#7843;i ho&amp;atilde;n, gi&amp;atilde;n ti&#7871;n &#273;&#7897;. Nhi&#7873;u DN &#273;&amp;atilde; &#273;i v&amp;agrave;o ho&#7841;t &#273;&#7897;ng c&amp;oacute; &amp;yacute; &#273;&#7883;nh qu&#7843;ng b&amp;aacute; r&#7897;ng r&amp;atilde;i d&amp;ograve;ng s&#7843;n ph&#7849;m m&#7899;i n&amp;agrave;y nh&#432;ng &#273;&amp;agrave;nh &amp;quot;ng&#7853;m ng&amp;ugrave;i&amp;quot; g&amp;aacute;c l&#7841;i do kh&#7843; n&#259;ng ti&amp;ecirc;u th&#7909; kh&amp;ocirc;ng &#273;&#432;&#7907;c c&#7843;i thi&#7879;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Gi&amp;aacute;m &#273;&#7889;c C&amp;ocirc;ng ty c&#7893; ph&#7847;n b&amp;ecirc;-t&amp;ocirc;ng kh&amp;iacute; ch&#432;ng &amp;aacute;p Viglacera Nguy&#7877;n &amp;ETH;&#7913;c H&#432;ng chia s&#7867;, c&amp;ocirc;ng ty m&#7899;i &#273;i v&amp;agrave;o s&#7843;n xu&#7845;t t&#7915; cu&#7889;i n&#259;m 2010, nh&#432;ng hi&#7879;n nay l&#432;&#7907;ng t&#7891;n kho &#273;&amp;atilde; l&amp;ecirc;n t&#7899;i v&amp;agrave;i tr&#259;m ngh&amp;igrave;n m3, tr&#7883; gi&amp;aacute; t&amp;aacute;m t&#7927; &#273;&#7891;ng, trong khi, c&amp;ocirc;ng ty v&#7851;n &#273;ang ph&#7843;i g&amp;aacute;nh l&amp;atilde;i vay ng&amp;acirc;n h&amp;agrave;ng t&#7915; 22,5% &#273;&#7871;n 23%/n&#259;m. H&#417;n n&#7919;a, d&amp;acirc;y chuy&#7873;n c&#7911;a nh&amp;agrave; m&amp;aacute;y l&amp;agrave; s&#7921; pha tr&#7897;n gi&#7919;a c&amp;ocirc;ng ngh&#7879; &amp;ETH;&#7913;c v&amp;agrave; thi&#7871;t b&#7883; nh&#7853;p kh&#7849;u t&#7915; Trung Qu&#7889;c n&amp;ecirc;n khi &amp;aacute;p d&#7909;ng t&#7841;i Vi&#7879;t Nam c&#361;ng ch&#432;a c&amp;oacute; b&#7897; quy chu&#7849;n t&#432;&#417;ng &#7913;ng. Hi&#7879;n nay, b&#7897; ti&amp;ecirc;u chu&#7849;n trong n&#432;&#7899;c &#273;ang &amp;aacute;p d&#7909;ng l&amp;agrave; b&#7897; ti&amp;ecirc;u chu&#7849;n c&#7911;a ch&amp;acirc;u &amp;Acirc;u, khi&#7871;n c&amp;aacute;c s&#7843;n ph&#7849;m s&#7843;n xu&#7845;t trong n&#432;&#7899;c kh&amp;oacute; &#273;&amp;aacute;p &#7913;ng &#273;&#432;&#7907;c. Ngo&amp;agrave;i ra, vi&#7879;c tuy&amp;ecirc;n truy&#7873;n s&#7917; d&#7909;ng lo&#7841;i g&#7841;ch m&#7899;i n&amp;agrave;y ch&#432;a &#273;&#432;&#7907;c quan t&amp;acirc;m v&amp;agrave; ch&#7853;m ban h&amp;agrave;nh b&#7897; ti&amp;ecirc;u chu&#7849;n &#273;&#7883;nh m&#7913;c &#273;&#7847;u t&#432; &#273;&#7889;i v&#7899;i lo&#7841;i GKN, d&#7851;n &#273;&#7871;n c&amp;aacute;c ch&#7911; &#273;&#7847;u t&#432; kh&amp;ocirc;ng th&#7875; &#273;&#432;a v&amp;agrave;o d&#7921; to&amp;aacute;n &#273;&#7875; quy&#7871;t to&amp;aacute;n c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh. Hi&#7879;n nay, c&amp;ocirc;ng ty m&#7899;i ch&#7881; b&amp;aacute;n &#273;&#432;&#7907;c kh&#7889;i l&#432;&#7907;ng s&#7843;n ph&#7849;m khi&amp;ecirc;m t&#7889;n cho m&#7897;t c&amp;ocirc;ng tr&amp;igrave;nh s&#7917; d&#7909;ng v&#7889;n ng&amp;acirc;n s&amp;aacute;ch Nh&amp;agrave; n&#432;&#7899;c, c&amp;ograve;n l&#7841;i ch&#7911; y&#7871;u ti&amp;ecirc;u th&#7909; trong c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh c&amp;oacute; v&#7889;n n&#432;&#7899;c ngo&amp;agrave;i v&amp;agrave; &#273;&amp;atilde; b&#432;&#7899;c &#273;&#7847;u m&#7903; r&#7897;ng sang khu v&#7921;c nh&amp;agrave; d&amp;acirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Tuy&amp;ecirc;n truy&#7873;n, qu&#7843;ng b&amp;aacute; g&#7855;n v&#7899;i c&#417; ch&#7871; th&amp;iacute;ch h&#7907;p&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Kh&amp;ocirc;ng th&#7875; ph&#7911; nh&#7853;n nh&#7919;ng &#432;u th&#7871; c&#7911;a GKN so v&#7899;i g&#7841;ch nung th&amp;ocirc;ng th&#432;&#7901;ng. GKN c&amp;oacute; t&#7927; tr&#7885;ng g&#7847;n b&#7857;ng m&#7897;t n&#7917;a &#273;&#7871;n hai ph&#7847;n ba so v&#7899;i g&#7841;ch &#273;&#7863;c &#273;&#7845;t s&amp;eacute;t nung, c&amp;oacute; k&#7871;t c&#7845;u nhi&#7873;u l&#7895; kh&amp;iacute;, &#273;&#432;&#7907;c ph&amp;acirc;n b&#7889; &#273;&#7873;u v&#7899;i m&#7853;t &#273;&#7897; cao n&amp;ecirc;n t&amp;iacute;nh n&#259;ng c&amp;aacute;ch &amp;acirc;m t&#7889;t g&#7845;p hai l&#7847;n so v&#7899;i g&#7841;ch x&amp;acirc;y th&amp;ocirc;ng th&#432;&#7901;ng, &#273;&#7891;ng th&#7901;i gi&#7919; &#7845;m cho ng&amp;ocirc;i nh&amp;agrave; v&#7873; m&amp;ugrave;a &#273;&amp;ocirc;ng, m&amp;aacute;t v&#7873; m&amp;ugrave;a h&amp;egrave;, ti&#7871;t ki&#7879;m &#273;i&#7879;n n&#259;ng (c&amp;oacute; th&#7875; ti&#7871;t gi&#7843;m &#273;&#7871;n 40% chi ph&amp;iacute; &#273;i&#7879;n n&#259;ng ti&amp;ecirc;u th&#7909; c&#7911;a m&amp;aacute;y &#273;i&#7873;u h&amp;ograve;a), gi&#7843;i ph&amp;oacute;ng &#273;&#7897; &#7849;m v&amp;agrave; lo&#7841;i tr&#7915; c&amp;aacute;c v&#7845;n &#273;&#7873; li&amp;ecirc;n quan &#273;&#7871;n n&#7845;m m&#7889;c. G&#7841;ch s&#7843;n xu&#7845;t theo nhi&#7873;u k&amp;iacute;ch th&#432;&#7899;c ph&amp;ugrave; h&#7907;p v&amp;agrave; ch&amp;iacute;nh x&amp;aacute;c, gi&amp;uacute;p r&amp;uacute;t ng&#7855;n th&#7901;i gian thi c&amp;ocirc;ng v&amp;agrave; ho&amp;agrave;n thi&#7879;n... &amp;ETH;&#7863;c bi&#7879;t, v&#7899;i th&amp;agrave;nh ph&#7847;n c&#7845;u t&#7841;o l&amp;agrave; v&#7853;t li&#7879;u tr&#417; v&amp;agrave; c&amp;aacute;c ch&#7845;t v&amp;ocirc; c&#417;, c&amp;aacute;c lo&#7841;i GKN c&amp;oacute; &#273;&#7897; c&#7913;ng, &#273;&#7897; b&#7873;n, kh&#7843; n&#259;ng ch&#7889;ng ch&amp;aacute;y cao, &amp;iacute;t ti&amp;ecirc;u t&#7889;n nhi&amp;ecirc;n li&#7879;u v&amp;agrave; r&#7845;t th&amp;acirc;n thi&#7879;n v&#7899;i m&amp;ocirc;i tr&#432;&#7901;ng. M&#7863;c d&amp;ugrave; gi&amp;aacute; GKN c&amp;ograve;n cao h&#417;n so v&#7899;i g&#7841;ch nung, nh&#432;ng n&#7871;u s&#7917; d&#7909;ng ho&amp;agrave;n to&amp;agrave;n GKN th&amp;igrave; t&#7893;ng m&#7913;c &#273;&#7847;u t&#432; x&amp;acirc;y d&#7921;ng c&amp;ocirc;ng tr&amp;igrave;nh c&amp;oacute; th&#7875; gi&#7843;m t&#7915; 7 &#273;&#7871;n 10% so v&#7899;i g&#7841;ch nung th&amp;ocirc;ng th&#432;&#7901;ng, trong khi ti&#7871;n &#273;&#7897; x&amp;acirc;y nhanh g&#7845;p hai &#273;&#7871;n n&#259;m l&#7847;n. V&#7853;y l&amp;agrave;m th&#7871; n&amp;agrave;o &#273;&#7875; GKN c&amp;oacute; ch&#7895; &#273;&#7913;ng tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng?&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7873; ph&#432;&#417;ng di&#7879;n qu&#7843;n l&amp;yacute; Nh&amp;agrave; n&#432;&#7899;c, c&#7847;n c&amp;oacute; nh&#7919;ng gi&#7843;i ph&amp;aacute;p quy&#7871;t li&#7879;t v&amp;agrave; &#273;&#7891;ng b&#7897;, tr&#432;&#7899;c m&#7855;t s&#7899;m ban h&amp;agrave;nh ti&amp;ecirc;u chu&#7849;n li&amp;ecirc;n quan &#273;&#7871;n thi&#7871;t k&#7871;, thi c&amp;ocirc;ng, nghi&#7879;m thu... c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh s&#7917; d&#7909;ng VLXKN. Song song v&#7899;i &#273;&amp;oacute; l&amp;agrave; gi&#7843;i ph&amp;aacute;p t&amp;igrave;m l&#7897; tr&amp;igrave;nh th&amp;iacute;ch h&#7907;p x&amp;oacute;a b&#7887; ho&amp;agrave;n to&amp;agrave;n c&amp;aacute;c l&amp;ograve; g&#7841;ch th&#7911; c&amp;ocirc;ng c&amp;ocirc;ng ngh&#7879; l&#7841;c h&#7853;u, &amp;ocirc; nhi&#7877;m m&amp;ocirc;i tr&#432;&#7901;ng. Theo Ph&amp;oacute; V&#7909; tr&#432;&#7903;ng Ph&#7841;m V&#259;n B&#7855;c, do ch&#432;a c&amp;oacute; b&#7897; quy chu&#7849;n n&amp;ecirc;n nhi&#7873;u DN &#273;&amp;atilde; m&#432;&#7907;n c&#7899; &#273;&#7875; kh&amp;ocirc;ng s&#7917; d&#7909;ng VLXKN v&amp;agrave;o x&amp;acirc;y d&#7921;ng. H&#417;n n&#7919;a, quy &#273;&#7883;nh b&#7855;t bu&#7897;c s&#7917; d&#7909;ng 30% GKN cho nh&amp;agrave; ch&amp;iacute;n t&#7847;ng m&#7899;i ch&#7881; &amp;aacute;p d&#7909;ng cho c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh s&#7917; d&#7909;ng v&#7889;n Nh&amp;agrave; n&#432;&#7899;c v&amp;agrave; hi&#7879;n v&#7851;n ch&#432;a c&amp;oacute; ch&#7871; t&amp;agrave;i x&#7917; ph&#7841;t. V&amp;igrave; v&#7853;y, gi&#7843;i ph&amp;aacute;p hi&#7879;u qu&#7843; l&amp;agrave; n&amp;ecirc;n g&#7855;n vi&#7879;c s&#7917; d&#7909;ng GKN v&#7899;i vi&#7879;c c&#7845;p ph&amp;eacute;p x&amp;acirc;y d&#7921;ng, v&#7915;a &#273;&#417;n gi&#7843;n, m&amp;agrave; v&#7851;n &#273;&amp;uacute;ng ch&#7913;c n&#259;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, c&#7847;n t&#259;ng c&#432;&#7901;ng tuy&amp;ecirc;n truy&#7873;n, qu&#7843;ng b&amp;aacute; r&#7897;ng r&amp;atilde;i &#273;&#7875; ng&#432;&#7901;i d&amp;acirc;n, DN nh&#7853;n th&#7913;c &#273;&amp;uacute;ng &#273;&#7855;n v&#7873; VLXKN, nh&#7845;t l&amp;agrave; GKN, t&#7915; &#273;&amp;oacute; thay &#273;&#7893;i th&amp;oacute;i quen s&#7917; d&#7909;ng c&amp;aacute;c lo&#7841;i v&#7853;t li&#7879;u truy&#7873;n th&#7889;ng. M&#7863;c d&amp;ugrave; l&amp;agrave; d&amp;ograve;ng s&#7843;n ph&#7849;m m&#7899;i, l&#7841;i &#273;&#432;a ra th&#7883; tr&#432;&#7901;ng v&amp;agrave;o th&#7901;i &#273;i&#7875;m kh&amp;oacute; kh&#259;n, nh&#432;ng kh&amp;ocirc;ng v&amp;igrave; th&#7871; m&amp;agrave; c&amp;aacute;c nh&amp;agrave; s&#7843;n xu&#7845;t VLXKN &#273;&#432;&#7907;c ph&amp;eacute;p coi nh&#7865; c&amp;ocirc;ng t&amp;aacute;c truy&#7873;n th&amp;ocirc;ng. &amp;ETH;&amp;atilde; c&amp;oacute; th&#7901;i &#273;i&#7875;m GKN &#273;&#432;&#7907;c tuy&amp;ecirc;n truy&#7873;n kh&amp;aacute; m&#7841;nh m&#7869;, nh&#432;ng khi th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n c&amp;oacute; d&#7845;u hi&#7879;u ch&#7919;ng l&#7841;i th&amp;igrave; th&amp;ocirc;ng tin v&#7873; d&amp;ograve;ng s&#7843;n ph&#7849;m m&#7899;i n&amp;agrave;y c&#361;ng &amp;quot;v&#7855;ng b&amp;oacute;ng&amp;quot; d&#7847;n, khi&#7871;n ng&#432;&#7901;i d&amp;acirc;n ch&#432;a &#273;&#7911; th&amp;ocirc;ng tin, l&amp;ograve;ng tin &#273;&#7875; thay &#273;&#7893;i th&amp;oacute;i quen.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ETH;&#7875; VLXKN kh&#7859;ng &#273;&#7883;nh &#273;&#432;&#7907;c vai tr&amp;ograve; tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng, n&amp;acirc;ng cao t&#7927; l&#7879; s&#7917; d&#7909;ng t&#7915; 30% &#273;&#7871;n 40% theo &#273;&#7883;nh h&#432;&#7899;ng c&#7911;a Ch&#432;&#417;ng tr&amp;igrave;nh ph&amp;aacute;t tri&#7875;n VLXKN c&#7911;a Ch&amp;iacute;nh ph&#7911;, c&amp;aacute;c b&#7897;, ng&amp;agrave;nh li&amp;ecirc;n quan c&#7847;n ban h&amp;agrave;nh nh&#7919;ng ch&amp;iacute;nh s&amp;aacute;ch &#273;&#7891;ng b&#7897;, &#273;&#7911; m&#7841;nh, trong &#273;&amp;oacute; t&#7853;p trung s&#7899;m x&amp;oacute;a b&#7887; c&amp;aacute;c c&#417; s&#7903; s&#7843;n xu&#7845;t g&#7841;ch nung c&amp;ocirc;ng ngh&#7879; l&#7841;c h&#7853;u, &amp;ocirc; nhi&#7877;m m&amp;ocirc;i tr&#432;&#7901;ng, h&#7895; tr&#7907; DN s&#7843;n xu&#7845;t GKN h&#7841; gi&amp;aacute; th&amp;agrave;nh s&#7843;n ph&#7849;m, &#273;&#7891;ng th&#7901;i khuy&#7871;n kh&amp;iacute;ch v&amp;agrave; b&#7855;t bu&#7897;c s&#7917; d&#7909;ng lo&#7841;i v&#7853;t li&#7879;u n&amp;agrave;y trong c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh x&amp;acirc;y d&#7921;ng.&lt;/p&gt;\r\n','14/11/2011','14/11/2011','',23,2,1906,'huyennt',1,'su-dung-rong-rai-gach-khong-nung','g&#7841;ch kh&ocirc;ng nung, s&#7843;n xu&#7845;t, x&acirc;y d&#7921;ng, VLXD, r&agrave;o c&#7843;n, c&#417; ch&#7871;, c&#7843;i thi&#7879;n, th&#7883; tr&#432;&#7901;ng, ti&ecirc;u th&#7909;',NULL,NULL,NULL,'g&#7841;ch kh&ocirc;ng nung, s&#7843;n xu&#7845;t, x&acirc;y d&#7921;ng, VLXD, r&agrave;o c&#7843;n, c&#417; ch&#7871;, c&#7843;i thi&#7879;n, th&#7883; tr&#432;&#7901;ng, ti&ecirc;u th&#7909;',0,0,0,'','','',3,'http://www.nhandan.com.vn',1,0,0,0),(95,'V&#7853;t li&#7879;u x&acirc;y d&#7921;ng &ldquo;nh&iacute;ch&rdquo; gi&aacute;','Tham kh&#7843;o nhi&#7873;u c&#7917;a h&agrave;ng v&#7853;t li&#7879;u x&acirc;y d&#7921;ng t&#7841;i TP.HCM cho th&#7845;y, nhi&#7873;u lo&#7841;i v&#7853;t li&#7879;u x&acirc;y d&#7921;ng( VLXD) &#273;ang t&#259;ng gi&aacute; nh&#7865;.','\r\n&lt;p&gt;Gi&amp;aacute; th&amp;eacute;p x&amp;acirc;y d&#7921;ng t&#7841;i th&#7883; tr&#432;&#7901;ng t&#259;ng gi&amp;aacute; nh&#7865;, trong &#273;&amp;oacute; t&#259;ng m&#7841;nh nh&#7845;t l&amp;agrave; c&amp;aacute;c lo&#7841;i th&amp;eacute;p cu&#7897;n. Ch&#7859;ng h&#7841;n, lo&#7841;i th&amp;eacute;p cu&#7897;n c&amp;oacute; gi&amp;aacute; b&amp;aacute;n l&#7867; t&#7915; 16.800&#273;/kg &#273;&#7871;n 17.000&#273;/kg, &#273;&#7873;u t&#259;ng 200 &#273;/kg so v&#7899;i tu&#7847;n tr&#432;&#7899;c. C&amp;aacute;c lo&#7841;i th&amp;eacute;p c&amp;acirc;y c&#361;ng t&#259;ng gi&amp;aacute; v&amp;agrave;i ng&amp;agrave;n &#273;&#7891;ng, t&#7915; 105.000 &#273;/c&amp;acirc;y &#273;&#7871;n 175.000&#273;/c&amp;acirc;y t&amp;ugrave;y theo th&#432;&#417;ng hi&#7879;u.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;373&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/vat-lieu-xay-dung-nhich-gia.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;em&gt;Gi&amp;aacute; th&amp;eacute;p x&amp;acirc;y d&#7921;ng t&#259;ng nh&#7865;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c m&#7863;t h&amp;agrave;ng x&amp;acirc;y d&#7921;ng kh&amp;aacute;c c&#361;ng &#273;ang t&#259;ng gi&amp;aacute; nh&#432; g&#7841;ch &#7889;ng Long Th&amp;agrave;nh-&#272;&#7891;ng Nai 670&#273;/vi&amp;ecirc;n, g&#7841;ch t&#432;&#7901;ng 09 (09x19x39 lo&#7841;i 3 l&#7895;)gi&amp;aacute; 3.200&#273;/ vi&amp;ecirc;n, c&amp;aacute;t t&#7915; 27.500-33.000&#273;/m3, &#273;&amp;aacute; 1x2 l&amp;agrave; 326.000-330.000&#273;/kh&#7889;i, &#273;&amp;aacute; 4x6 t&#7915; 230.000-280.000&#273;/kh&#7889;i t&amp;ugrave;y lo&#7841;i.&lt;br /&gt;\r\nNhi&#7873;u ch&#7911; c&#7917;a h&amp;agrave;ng chuy&amp;ecirc;n doanh VLXD cho bi&#7871;t, gi&amp;aacute; th&amp;eacute;p v&amp;agrave; c&amp;aacute;c lo&#7841;i v&#7853;t li&#7879;u kh&amp;aacute;c s&#7855;p t&#7899;i s&#7869; c&amp;ograve;n ti&#7871;p t&#7909;c t&#259;ng, do v&amp;agrave;o th&#7901;i &#273;i&#7875;m cu&#7889;i n&#259;m nhu c&#7847;u s&#7917;a ch&#7919;a nh&amp;agrave; m&#7899;i &#273;&amp;oacute;n t&#7871;t Nh&amp;acirc;m Th&amp;igrave;n t&#259;ng cao n&amp;ecirc;n h&amp;uacute;t h&amp;agrave;ng.&lt;/p&gt;','14/11/2011','14/11/2011','/home/uploads/image/news/11-2011/vat-lieu-xay-dung-nhich-gia.jpg',23,2,1740,'huyennt',1,'vat-lieu-xay-dung-nhich-gia','VLXD, nh&iacute;ch gi&aacute;, gi&aacute; th&eacute;p, x&acirc;y d&#7921;ng, c&#7917;a h&agrave;ng',NULL,NULL,NULL,'VLXD, nh&iacute;ch gi&aacute;, gi&aacute; th&eacute;p, x&acirc;y d&#7921;ng, c&#7917;a h&agrave;ng',0,0,0,'','','',3,'http://dothi.net',1,0,0,0),(96,'B&#7897; X&acirc;y d&#7921;ng &#273;&#7873; ngh&#7883; c&#7845;m s&#7917; d&#7909;ng &#273;&#7845;t n&ocirc;ng nghi&#7879;p s&#7843;n xu&#7845;t g&#7841;ch ng&oacute;i','B&#7897; X&acirc;y d&#7921;ng v&#7915;a c&oacute; v&#259;n b&#7843;n &#273;&#7873; ngh&#7883; UBND c&aacute;c t&#7881;nh, th&agrave;nh ph&#7889; t&#259;ng c&#432;&#7901;ng c&ocirc;ng t&aacute;c qu&#7843;n l&yacute; nh&agrave; n&#432;&#7899;c v&#7873; s&#7843;n xu&#7845;t v&#7853;t li&#7879;u x&acirc;y d&#7921;ng, &#273;&#7863;c bi&#7879;t l&agrave; qu&#7843;n l&yacute; ch&#7863;t ch&#7869; c&aacute;c d&#7921; &aacute;n &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t g&#7841;ch ng&oacute;i &#273;&#7845;t s&eacute;t nung.','\r\n&lt;p&gt;Theo &#273;&amp;oacute;, B&#7897; &#273;&#7873; ngh&#7883; c&#7845;m tri&#7879;t &#273;&#7875; vi&#7879;c d&amp;ugrave;ng &#273;&#7845;t s&#7843;n xu&#7845;t n&amp;ocirc;ng nghi&#7879;p s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i &#273;&#7845;t s&amp;eacute;t nung; c&#7845;m nung g&#7841;ch b&#7857;ng l&amp;ograve; th&#7911; c&amp;ocirc;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;B&#7897; X&amp;acirc;y d&#7921;ng c&#361;ng ch&#7881; &#273;&#7841;o c&amp;aacute;c &#273;&#7883;a ph&#432;&#417;ng ki&#7875;m tra, t&#7893;ng h&#7907;p, b&amp;aacute;o c&amp;aacute;o k&#7871;t qu&#7843; tri&#7875;n khai v&#7873; t&amp;igrave;nh h&amp;igrave;nh l&#7853;p quy ho&#7841;ch v&amp;agrave; th&#7921;c hi&#7879;n quy ho&#7841;ch ph&amp;aacute;t tri&#7875;n v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng giai &#273;o&#7841;n 2005-2010, 2011-2015 t&#7847;m nh&amp;igrave;n &#273;&#7873;n n&#259;m 2020; t&amp;igrave;nh h&amp;igrave;nh nguy&amp;ecirc;n li&#7879;u v&amp;agrave; quy ho&#7841;ch v&amp;ugrave;ng nguy&amp;ecirc;n li&#7879;u &#273;&#7875; s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i &#273;&#7845;t s&amp;eacute;t nung tr&amp;ecirc;n &#273;&#7883;a b&amp;agrave;n; &#273;&#7847;u t&#432; v&amp;agrave; s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i nung v&amp;agrave; v&#7853;t li&#7879;u x&amp;acirc;y kh&amp;ocirc;ng nung tr&amp;ecirc;n &#273;&#7883;a b&amp;agrave;n (s&#7889; li&#7879;u l&#7853;p theo bi&#7875;u m&#7851;u); k&#7871;t qu&#7843; th&#7921;c hi&#7879;n l&#7897; tr&amp;igrave;nh xo&amp;aacute; b&#7887;, chuy&#7875;n &#273;&#7893;i c&amp;aacute;c l&amp;ograve; g&#7841;ch th&#7911; c&amp;ocirc;ng sang l&amp;ograve; c&amp;oacute; c&amp;ocirc;ng ngh&#7879; ti&amp;ecirc;n ti&#7871;n; tri&#7875;n khai c&amp;ocirc;ng t&amp;aacute;c qu&#7843;n l&amp;yacute; nh&amp;agrave; n&#432;&#7899;c v&#7873; ch&#7845;t l&#432;&#7907;ng v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo s&#7889; li&#7879;u b&amp;aacute;o c&amp;aacute;o c&#7911;a c&amp;aacute;c t&#7881;nh, th&amp;agrave;nh ph&#7889; v&amp;agrave; k&#7871;t qu&#7843; kh&#7843;o s&amp;aacute;t t&#7841;i m&#7897;t s&#7889; &#273;&#7883;a ph&#432;&#417;ng, &#273;&#7871;n th&#7901;i &#273;i&#7875;m gi&#7919;a n&#259;m 2011, m&#7897;t s&#7889; &#273;&#7883;a ph&#432;&#417;ng &#273;&amp;atilde; lo&#7841;i b&#7887; &#273;&#432;&#7907;c h&#7847;u h&#7871;t c&amp;aacute;c l&amp;ograve; th&#7911; c&amp;ocirc;ng v&amp;agrave; ph&#7845;n &#273;&#7845;u lo&#7841;i b&#7887; ho&amp;agrave;n to&amp;agrave;n c&amp;aacute;c l&amp;ograve; th&#7911; c&amp;ocirc;ng s&#7843;n xu&#7845;t g&#7841;ch ng&amp;oacute;i &#273;&#7845;t s&amp;eacute;t nung, khuy&#7871;n kh&amp;iacute;ch &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t v&#7853;t li&#7879;u x&amp;acirc;y kh&amp;ocirc;ng nung (VLXKN). Tuy nhi&amp;ecirc;n, c&#361;ng c&amp;oacute; m&#7897;t s&#7889; &#273;&#7883;a ph&#432;&#417;ng tr&#432;&#7899;c &#273;&amp;acirc;y &#273;&amp;atilde; lo&#7841;i b&#7887; l&amp;ograve; th&#7911; c&amp;ocirc;ng, nh&#432;ng th&#7901;i gian g&#7847;n &#273;&amp;acirc;y l&#7841;i c&amp;oacute; t&amp;igrave;nh cho ho&#7841;t &#273;&#7897;ng tr&#7903; l&#7841;i. &lt;/p&gt;','14/11/2011','14/11/2011','',23,2,1777,'huyennt',1,'bo-xay-dung-de-nghi-cam-su-dung-dat-nong-nghiep-san-xuat-gach-ngoi','b&#7897; x&acirc;y d&#7921;ng, c&#7845;m tri&#7879;t, s&#7843;n xu&#7845;t g&#7841;ch ng&oacute;i, l&ograve; th&#7911; c&ocirc;ng, &#273;&#7845;t s&eacute;t nung',NULL,NULL,NULL,'b&#7897; x&acirc;y d&#7921;ng, c&#7845;m tri&#7879;t, s&#7843;n xu&#7845;t g&#7841;ch ng&oacute;i, l&ograve; th&#7911; c&ocirc;ng, &#273;&#7845;t s&eacute;t nung',0,0,0,'','','',3,'http://www.diaoconline.vn',1,0,0,0),(97,'Doanh nghi&#7879;p xi m&#259;ng Vi&#7879;t Nam: Ph&#432;&#417;ng &aacute;n ph&aacute; s&#7843;n &#273;&#432;&#7907;c t&iacute;nh &#273;&#7871;n','S&#7921; s&#7909;t gi&#7843;m v&#7873; m&#7913;c ti&ecirc;u th&#7909; trong th&#7901;i gian qua &#273;&atilde; th&#7875; hi&#7879;n kh&aacute; r&otilde; t&igrave;nh tr&#7841;ng &ldquo;suy nh&#432;&#7907;c&rdquo; c&#7911;a kh&ocirc;ng &iacute;t doanh nghi&#7879;p xi m&#259;ng.','&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/xi-mang-2.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;0&quot; height=&quot;0&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/xi-mang-2.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;&lt;em&gt;&lt;br /&gt;\r\nQu&amp;aacute; nhi&#7873;u x&amp;iacute; nghi&#7879;p s&#7843;n xu&#7845;t xi m&#259;ng c&#361;ng l&amp;agrave; nguy&amp;ecirc;n nh&amp;acirc;n g&amp;acirc;y kh&amp;oacute; th&#7883; tr&#432;&#7901;ng&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left; &quot;&gt;&lt;br /&gt;\r\nNgay c&#7843; doanh nghi&#7879;p v&#7889;n &#273;&#432;&#7907;c coi l&amp;agrave; m&#7841;nh v&#7873; th&#7883; tr&#432;&#7901;ng, th&#432;&#417;ng hi&#7879;u, v&#7899;i chi&#7871;n l&#432;&#7907;c ph&amp;aacute;t tri&#7875;n b&amp;agrave;i b&#7843;n nh&#432; H&amp;agrave; Ti&amp;ecirc;n, Ho&amp;agrave;ng Th&#7841;ch, B&#7881;m S&#417;n c&#7911;a T&#7893;ng c&amp;ocirc;ng ty C&amp;ocirc;ng nghi&#7879;p Xi m&#259;ng Vi&#7879;t Nam (Vicem), hay m&#7897;t s&#7889; c&amp;ocirc;ng ty li&amp;ecirc;n doanh c&#361;ng r&#417;i v&amp;agrave;o c&#7843;nh b&#7883; thu h&#7865;p th&#7883; tr&#432;&#7901;ng. T&amp;iacute;nh chung, sau 10 th&amp;aacute;ng ho&#7841;t &#273;&#7897;ng, th&#7883; ph&#7847;n c&#7911;a Vicem b&#7883; gi&#7843;m g&#7847;n 3% v&amp;agrave; song h&amp;agrave;nh v&#7899;i s&#7921; suy gi&#7843;m &#273;&amp;oacute; l&amp;agrave; kho&#7843;n l&#7895; tr&amp;ecirc;n 220 t&#7927; &#273;&#7891;ng.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Theo Vicem, chi ph&amp;iacute; t&amp;agrave;i ch&amp;iacute;nh t&#259;ng cao, nhi&#7873;u d&#7921; &amp;aacute;n m&#7899;i &#273;&#432;&#7907;c &#273;&#432;a v&amp;agrave;o ho&#7841;t &#273;&#7897;ng, ph&#7843;i tr&#7843; n&#7907; &#273;&#7847;u t&#432;, chi ph&amp;iacute; v&#7853;t t&#432; &#273;&#7847;u v&amp;agrave;o nh&#432; gi&amp;aacute; x&#259;ng t&#259;ng 32 - 43%, &#273;i&#7879;n t&#259;ng 15,28%, &#273;&#7863;c bi&#7879;t l&amp;agrave; gi&amp;aacute; than t&#259;ng 88%, c&#7897;ng v&#7899;i s&#7921; tr&#7847;m l&#7855;ng c&#7911;a th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n &#273;&amp;atilde; k&amp;eacute;o t&amp;igrave;nh h&amp;igrave;nh kinh doanh c&#7911;a ng&amp;agrave;nh xi m&#259;ng &#273;i xu&#7889;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong b&#7889;i c&#7843;nh kh&#7911;ng ho&#7843;ng, c&#7855;t gi&#7843;m &#273;&#7847;u t&#432;, doanh nghi&#7879;p l&amp;acirc;u n&#259;m trong ng&amp;agrave;nh, c&amp;oacute; s&#7861;n th&#7883; tr&#432;&#7901;ng, s&#7861;n kh&amp;aacute;ch h&amp;agrave;ng c&amp;ograve;n ph&#7843;i ch&#7853;t v&#7853;t duy tr&amp;igrave; ho&#7841;t &#273;&#7897;ng kinh doanh, th&amp;igrave; v&#7899;i doanh nghi&#7879;p ra &#273;&#7901;i sau, n&#7871;u kh&amp;ocirc;ng th&#7875; hi&#7879;n &#273;&#432;&#7907;c s&#7921; v&#432;&#7907;t tr&#7897;i v&#7873; c&amp;ocirc;ng ngh&#7879;, gi&amp;aacute; th&amp;agrave;nh v&amp;agrave; nh&#7919;ng &#273;&#7897;c chi&amp;ecirc;u &#273;&#7875; chi&#7871;m l&#297;nh th&#7883; tr&#432;&#7901;ng&amp;hellip;, kh&amp;oacute; kh&#259;n l&amp;agrave; &#273;i&#7873;u c&amp;oacute; th&#7875; nh&amp;igrave;n th&#7845;y tr&#432;&#7899;c.&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;413&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/xi-mang.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;Nh&amp;agrave; m&amp;aacute;y xi m&#259;ng &#272;&#7891;ng B&amp;agrave;nh&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;ocirc;ng ty Xi m&#259;ng &#272;&#7891;ng B&amp;agrave;nh (L&#7841;ng S&#417;n) gia nh&#7853;p th&#7883; tr&#432;&#7901;ng xi m&#259;ng cu&#7889;i n&#259;m 2010 sau 4 n&#259;m x&amp;acirc;y d&#7921;ng, nh&#432;ng sau ch&#432;a &#273;&#7847;y 1 n&#259;m ho&#7841;t &#273;&#7897;ng, &#273;&amp;atilde; l&#7895; 141 t&#7927; &#273;&#7891;ng v&amp;agrave; theo b&amp;aacute;o c&amp;aacute;o m&#7899;i nh&#7845;t, doanh nghi&#7879;p n&amp;agrave;y kh&amp;ocirc;ng c&amp;oacute; kh&#7843; n&#259;ng tr&#7843; n&#7907;.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&#259;m 2006, D&#7921; &amp;aacute;n xi m&#259;ng &#272;&#7891;ng B&amp;agrave;nh, c&amp;ocirc;ng su&#7845;t 910.000 t&#7845;n xi m&#259;ng/n&#259;m &#273;&#432;&#7907;c kh&#7903;i c&amp;ocirc;ng x&amp;acirc;y d&#7921;ng, c&amp;oacute; t&#7893;ng v&#7889;n 1.298,2 t&#7927; &#273;&#7891;ng, nh&#432;ng do ti&#7871;n &#273;&#7897; thi c&amp;ocirc;ng ch&#7853;m 2 n&#259;m, m&#7913;c &#273;&#7847;u t&#432; v&amp;igrave; th&#7871; &#273;&amp;atilde; &#273;&#7897;i l&amp;ecirc;n 1.505 t&#7927; &#273;&#7891;ng. &#272;i&#7873;u &#273;&amp;aacute;ng n&amp;oacute;i l&amp;agrave;, ngay t&#7915; th&#7901;i &#273;i&#7875;m &#273;&#7847;u t&#432; d&#7921; &amp;aacute;n n&amp;agrave;y, ng&amp;agrave;nh ch&#7913;c n&#259;ng &#273;&amp;atilde; c&amp;oacute; nh&#7919;ng c&#7843;nh b&amp;aacute;o v&#7873; d&#432; th&#7915;a ngu&#7891;n cung xi m&#259;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&#7847;n n&amp;oacute;i th&amp;ecirc;m r&#7857;ng, D&#7921; &amp;aacute;n Xi m&#259;ng &#272;&#7891;ng B&amp;agrave;nh &#273;&#432;&#7907;c &#273;&#7847;u t&#432; b&#7903;i c&amp;aacute;c c&#7893; &#273;&amp;ocirc;ng s&amp;aacute;ng l&#7853;p l&amp;agrave; T&#7893;ng c&amp;ocirc;ng ty C&#417; kh&amp;iacute; x&amp;acirc;y d&#7921;ng (Coma), T&#7893;ng c&amp;ocirc;ng ty M&amp;aacute;y v&amp;agrave; Thi&#7871;t b&#7883; c&amp;ocirc;ng nghi&#7879;p (Mie), C&amp;ocirc;ng ty Xi m&#259;ng L&#7841;ng S&#417;n. Th&#7921;c t&#7871;, ch&#7911; &#273;&#7847;u t&#432; ch&#7881; g&amp;oacute;p 52,485 t&#7927; &#273;&#7891;ng, chi&#7871;m kho&#7843;ng 3,49% t&#7893;ng m&#7913;c &#273;&#7847;u t&#432;, ph&#7847;n v&#7889;n thi&#7871;u &#273;&#432;&#7907;c vay t&#7915; c&amp;aacute;c t&#7893; ch&#7913;c t&amp;agrave;i ch&amp;iacute;nh g&#7891;m Ng&amp;acirc;n h&amp;agrave;ng &#272;&#7847;u t&#432; v&amp;agrave; Ph&amp;aacute;t tri&#7875;n Vi&#7879;t Nam (272,142 t&#7927; &#273;&#7891;ng), Ng&amp;acirc;n h&amp;agrave;ng N&amp;ocirc;ng nghi&#7879;p v&amp;agrave; Ph&amp;aacute;t tri&#7875;n n&amp;ocirc;ng th&amp;ocirc;n Vi&#7879;t Nam (183,467 t&#7927; &#273;&#7891;ng), Ng&amp;acirc;n h&amp;agrave;ng ANZ (747,850 t&#7927; &#273;&#7891;ng).&lt;/p&gt;\r\n\r\n&lt;p&gt;Gi&#7843; thi&#7871;t, n&#7871;u th&#7883; tr&#432;&#7901;ng xi m&#259;ng ph&amp;aacute;t tri&#7875;n b&amp;igrave;nh th&#432;&#7901;ng, kh&amp;ocirc;ng b&#7883; t&amp;aacute;c &#273;&#7897;ng b&#7903;i kh&#7911;ng ho&#7843;ng v&amp;agrave; ch&#432;&#417;ng tr&amp;igrave;nh c&#7855;t gi&#7843;m &#273;&#7847;u t&#432; c&amp;ocirc;ng, khi&#7871;n c&amp;aacute;c d&#7921; &amp;aacute;n l&#7899;n ho&#7863;c ph&#7843;i gi&amp;atilde;n ti&#7871;n &#273;&#7897;, ho&#7863;c d&#7915;ng &#273;&#7847;u t&#432;, th&amp;igrave; ch&#7855;c h&#7859;n c&amp;aacute;c d&#7921; &amp;aacute;n s&#7843;n xu&#7845;t xi m&#259;ng m&#7899;i tham gia th&#7883; tr&#432;&#7901;ng nh&#432; Xi m&#259;ng &#272;&#7891;ng B&amp;agrave;nh s&#7869; d&#7877; th&#7903; h&#417;n &#273;&amp;ocirc;i ch&amp;uacute;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, Xi m&#259;ng &#272;&#7891;ng B&amp;agrave;nh l&#7841;i l&amp;agrave; d&#7921; &amp;aacute;n &#273;&#432;&#7907;c &#273;&#7847;u t&#432; v&#7899;i 80% t&#7915; ngu&#7891;n v&#7889;n vay, th&#7901;i gian &#273;&#7847;u t&#432; k&amp;eacute;o d&amp;agrave;i th&amp;ecirc;m 2 n&#259;m, chi ph&amp;iacute; &#273;&#7847;u t&#432; b&#7883; &#273;&#7897;i l&amp;ecirc;n nhi&#7873;u, c&#7897;ng th&amp;ecirc;m v&#7889;n vay l&#432;u &#273;&#7897;ng &#273;&#7875; duy tr&amp;igrave; ho&#7841;t &#273;&#7897;ng, n&amp;ecirc;n kh&amp;oacute; kh&#259;n c&amp;agrave;ng ch&#7891;ng ch&#7845;t. Theo t&amp;iacute;nh to&amp;aacute;n c&#7911;a B&#7897; X&amp;acirc;y d&#7921;ng, t&#7915; n&#259;m 2011 &#273;&#7871;n 2015, doanh nghi&#7879;p n&amp;agrave;y s&#7869; thi&#7871;u kho&#7843;ng 600 t&#7927; &#273;&#7891;ng &#273;&#7875; tr&#7843; n&#7907; c&amp;aacute;c t&#7893; ch&#7913;c t&amp;iacute;n d&#7909;ng v&amp;agrave; b&amp;ugrave; &#273;&#7855;p ngu&#7891;n ti&#7873;n m&#7845;t c&amp;acirc;n &#273;&#7889;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&#7911; t&#7883;ch H&#272;QT Vicem, &amp;ocirc;ng L&amp;ecirc; V&#259;n Chung cho r&#7857;ng, c&amp;aacute;c d&#7921; &amp;aacute;n xi m&#259;ng th&#432;&#7901;ng &#273;&#432;&#7907;c &#273;&#7847;u t&#432; b&#7857;ng ngo&#7841;i t&#7879;. Sau th&#7901;i gian kh&#7911;ng ho&#7843;ng t&amp;agrave;i ch&amp;iacute;nh, l&amp;atilde;i su&#7845;t ng&amp;acirc;n h&amp;agrave;ng t&#259;ng, c&#7897;ng th&amp;ecirc;m tr&#432;&#7907;t gi&amp;aacute;, c&amp;ocirc;ng t&amp;aacute;c thu h&#7891;i v&#7889;n v&amp;agrave; tr&#7843; n&#7907; l&amp;agrave; g&amp;aacute;nh n&#7863;ng l&#7899;n &#273;&#7889;i v&#7899;i doanh nghi&#7879;p. Ri&amp;ecirc;ng t&#7841;i Vicem, v&#7899;i 7 d&#7921; &amp;aacute;n &#273;&#432;a v&amp;agrave;o s&#7843;n xu&#7845;t trong n&#259;m 2011, d&#7921; ki&#7871;n c&amp;aacute;c doanh nghi&#7879;p s&#7869; ph&#7843;i tr&#7843; kho&#7843;n n&#7907; kho&#7843;ng 3.200 t&#7927; &#273;&#7891;ng, t&#432;&#417;ng &#273;&#432;&#417;ng m&#7913;c v&#7889;n &#273;&#7847;u t&#432; m&#7899;i m&#7897;t nh&amp;agrave; m&amp;aacute;y xi m&#259;ng l&#7899;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&#361;ng theo &#273;&#7841;i di&#7879;n Vicem, vi&#7879;c tuy&amp;ecirc;n b&#7889; ph&amp;aacute; s&#7843;n &#273;ang l&amp;agrave; ph&#432;&#417;ng &amp;aacute;n c&#7847;n &#273;&#432;&#7907;c t&amp;iacute;nh &#273;&#7871;n v&#7899;i m&#7897;t s&#7889; d&#7921; &amp;aacute;n xi m&#259;ng qu&amp;aacute; y&#7871;u. M&#7863;c d&amp;ugrave; vi&#7879;c ph&amp;aacute; s&#7843;n c&amp;oacute; th&#7875; s&#7869; khi&#7871;n m&#7897;t b&#7897; ph&#7853;n lao &#273;&#7897;ng g&#7863;p kh&amp;oacute; kh&#259;n, nh&#432;ng &#273;&amp;acirc;y l&amp;agrave; m&#7897;t trong nh&#7919;ng &#273;&#7897;ng th&amp;aacute;i c&#7847;n thi&#7871;t &#273;&#7875; t&amp;aacute;i c&#417; c&#7845;u ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p quan tr&#7885;ng n&amp;agrave;y. &#272;&#7891;ng th&#7901;i, vi&#7879;c &#273;&amp;agrave;o th&#7843;i nh&#7919;ng nh&amp;acirc;n t&#7889; k&amp;eacute;m n&#259;ng l&#7921;c c&#7841;nh tranh c&#361;ng l&amp;agrave; m&#7897;t gi&#7843;i ph&amp;aacute;p c&#7847;n thi&#7871;t &#273;&#7875; l&amp;agrave;nh m&#7841;nh h&amp;oacute;a ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p xi m&#259;ng.&lt;/p&gt;','17/11/2011','17/11/2011','/home/uploads/image/news/11-2011/xi-mang-2.jpg',23,2,1721,'huyennt',1,'doanh-nghiep-xi-mang-viet-nam-phuong-an-pha-san-duoc-tinh-den','xi m&#259;ng, nh&agrave; m&aacute;y, VLXD, d&#7921; &aacute;n, doanh nghi&#7879;p, ph&aacute; s&#7843;n',NULL,NULL,NULL,'xi m&#259;ng, nh&agrave; m&aacute;y, VLXD, d&#7921; &aacute;n, doanh nghi&#7879;p, ph&aacute; s&#7843;n',0,0,0,'','','',3,'http://dothi.net',1,0,0,0),(98,'Ph&aacute;t tri&#7875;n g&#7841;ch AAC: H&#432;&#7899;ng t&#7899;i &ldquo;v&#7853;t li&#7879;u xanh&rdquo;','H&#432;&#7903;ng &#7913;ng v&agrave; g&#7845;p r&uacute;t &#273;&#7847;u t&#432; nh&agrave; m&aacute;y, thi&#7871;t b&#7883; d&acirc;y chuy&#7873;n s&#7843;n xu&#7845;t v&#7853;t li&#7879;u kh&ocirc;ng nung Nh&#432;ng &#273;&#7871;n nay, sau g&#7847;n hai n&#259;m, c&aacute;c DN s&#7843;n xu&#7845;t v&#7851;n c&ograve;n loay hoay t&igrave;m &#273;&#7847;u ra cho s&#7843;n ph&#7849;m.','\r\n\r\n&lt;p&gt;&lt;strong&gt;Ng&#432;&#7901;i d&amp;acirc;n ch&#432;a hi&#7875;u&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7875;u &#273;&#432;&#7907;c t&amp;iacute;nh &#432;u &#273;i&#7875;m v&#432;&#7907;t tr&#7897;i c&#7911;a &amp;ldquo;s&#7843;n ph&#7849;m xanh&amp;rdquo; n&amp;ecirc;n c&amp;aacute;c DN h&#432;&#7903;ng &#7913;ng nhi&#7879;t t&amp;igrave;nh ch&#432;&#417;ng tr&amp;igrave;nh ph&amp;aacute;t tri&#7875;n v&#7853;t li&#7879;u x&amp;acirc;y kh&amp;ocirc;ng nung (VLXKN), trong &#273;&amp;oacute; c&amp;oacute; th&#7875; k&#7875; &#273;&#7871;n s&#7921; ph&amp;aacute;t tri&#7875;n m&#7841;nh m&#7869; c&#7911;a b&amp;ecirc; t&amp;ocirc;ng nh&#7865;. &#272;&#7871;n nay &#273;&amp;atilde; c&amp;oacute; 8 nh&amp;agrave; m&amp;aacute;y b&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; ch&#432;ng &amp;aacute;p AAC &#273;i v&amp;agrave;o s&#7843;n xu&#7845;t v&amp;agrave; 1 nh&amp;agrave; m&amp;aacute;y chu&#7849;n b&#7883; &#273;i v&amp;agrave;o s&#7843;n xu&#7845;t v&#7899;i t&#7893;ng c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; 1,5 tri&#7879;u m3/n&#259;m, 17 c&#417; s&#7903; s&#7843;n xu&#7845;t b&amp;ecirc; t&amp;ocirc;ng b&#7885;t v&#7899;i t&#7893;ng c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; g&#7847;n 0,2 tri&#7879;u m3. T&#7893;ng c&amp;ocirc;ng su&#7845;t b&amp;ecirc; t&amp;ocirc;ng nh&#7865; hi&#7879;n c&amp;oacute; l&amp;agrave; 1,7 tri&#7879;u m3/n&#259;m. B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute; c&amp;oacute; 13 nh&amp;agrave; m&amp;aacute;y b&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; ch&#432;ng &amp;aacute;p v&#7899;i t&#7893;ng c&amp;ocirc;ng su&#7845;t 2,3 tri&#7879;u m3 &#273;ang &#273;&#432;&#7907;c &#273;&#7847;u t&#432; x&amp;acirc;y d&#7921;ng, s&#7869; &#273;i v&amp;agrave;o s&#7843;n xu&#7845;t trong n&#259;m 2012. Do &#273;&amp;oacute;, theo t&amp;iacute;nh to&amp;aacute;n c&#7911;a H&#7897;i VLXD th&amp;igrave; &#273;&#7871;n h&#7871;t n&#259;m 2012 s&#7869; c&amp;oacute; t&#7893;ng c&amp;ocirc;ng su&#7845;t 3,8 tri&#7879;u m3 b&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute;, ch&#432;a k&#7875; 13 DN &#273;ang l&#7853;p d&#7921; &amp;aacute;n ho&#7863;c c&amp;oacute; k&#7871; ho&#7841;ch &#273;&#7847;u t&#432; s&#7843;n xu&#7845;t b&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; v&#7899;i t&#7893;ng c&amp;ocirc;ng su&#7845;t 2,25 tri&#7879;u m3.&lt;/p&gt;\r\n\r\n&lt;p&gt;S&#7921; ph&amp;aacute;t tri&#7875;n nhanh &#273;&#7871;n m&#7913;c ch&amp;oacute;ng m&#7863;t c&#7911;a c&amp;aacute;c &#273;&#417;n v&#7883; s&#7843;n xu&#7845;t b&amp;ecirc; t&amp;ocirc;ng nh&#7865; &#273;&amp;atilde; &#273;&#7863;t ra m&#7897;t c&amp;acirc;u h&#7887;i l&#7899;n v&#7873; l&#7889;i &#273;i c&#7911;a s&#7843;n ph&#7849;m khi m&amp;agrave; kh&#7855;p c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh x&amp;acirc;y d&#7921;ng c&#361;ng nh&#432; ng&#432;&#7901;i d&amp;acirc;n v&#7851;n c&amp;ograve;n &amp;ldquo;l&#417; m&#417;&amp;rdquo; v&#7873; s&#7843;n ph&#7849;m n&amp;agrave;y. Kh&amp;ocirc;n ngoan v&amp;agrave; nh&#7841;y b&amp;eacute;n, nhi&#7873;u DN &#273;&amp;atilde; t&amp;igrave;m v&amp;agrave; thuy&#7871;t ph&#7909;c &#273;&#432;&#7907;c b&#7841;n h&amp;agrave;ng s&#7917; d&#7909;ng s&#7843;n ph&#7849;m c&#7911;a m&amp;igrave;nh, tuy nhi&amp;ecirc;n, kh&amp;ocirc;ng ph&#7843;i &#273;&#417;n v&#7883; n&amp;agrave;o c&#361;ng l&amp;agrave;m &#273;&#432;&#7907;c nh&#432; th&#7871; n&amp;ecirc;n s&#7843;n ph&#7849;m v&#7851;n b&#7883; &amp;ldquo;&#7871;&amp;rdquo; v&amp;agrave; t&#7891;n kho ho&#7863;c ho&#7841;t &#273;&#7897;ng kh&amp;ocirc;ng h&#7871;t c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871;. Nhi&#7873;u c&#417; s&#7903; ho&#7841;t &#273;&#7897;ng cao nh&#7845;t ch&#432;a &#273;&#432;&#7907;c 50% c&amp;ocirc;ng su&#7845;t, th&#7845;p nh&#7845;t ch&#7881; b&#7857;ng 20% c&amp;ocirc;ng su&#7845;t. C&amp;oacute; c&#417; s&#7903; &#273;&amp;atilde; t&#7891;n kho t&#7841;i b&amp;atilde;i l&amp;ecirc;n &#273;&#7871;n tr&amp;ecirc;n 10 ngh&amp;igrave;n m3 s&#7843;n ph&#7849;m (b&#7857;ng s&#7843;n l&#432;&#7907;ng 1 th&amp;aacute;ng s&#7843;n xu&#7845;t). M&#7897;t s&#7889; nh&amp;agrave; m&amp;aacute;y ph&#7843;i t&#7841;m d&#7915;ng s&#7843;n xu&#7845;t. Kh&amp;ocirc;ng &amp;iacute;t c&amp;aacute;c h&#7897;i ngh&#7883; m&#7903;, h&#7897;i &amp;yacute; k&amp;iacute;n gi&#7919;a c&amp;aacute;c DN s&#7843;n xu&#7845;t b&amp;ecirc; t&amp;ocirc;ng nh&#7865; &#273;&amp;atilde; di&#7877;n ra &#273;&#7875; c&amp;ugrave;ng h&#7907;p l&#7921;c t&amp;igrave;m l&#7889;i &#273;i cho s&#7843;n ph&#7849;m.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Nguy&#7877;n &#272;&#7913;c H&#432;ng - Gi&amp;aacute;m &#273;&#7889;c Cty CP B&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; VIGLACERA (B&#7855;c Ninh) cho bi&#7871;t: Hi&#7879;n t&#7841;i, ch&amp;uacute;ng t&amp;ocirc;i &#273;ang &#273;&#7913;ng tr&#432;&#7899;c r&#7845;t nhi&#7873;u th&amp;aacute;ch th&#7913;c &#273;&#7875; ph&amp;aacute;t tri&#7875;n s&#7843;n xu&#7845;t cho s&#7843;n ph&#7849;m g&#7841;ch AAC. T&amp;iacute;nh ph&amp;aacute;p l&amp;yacute; cho s&#7843;n ph&#7849;m AAC ch&#432;a &#273;&#7847;y &#273;&#7911; khi&#7871;n c&amp;aacute;c DN ch&amp;uacute;ng t&amp;ocirc;i r&#7845;t kh&amp;oacute; b&amp;aacute;n s&#7843;n ph&#7849;m v&amp;agrave;o c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh x&amp;acirc;y d&#7921;ng. C&#361;ng c&amp;ugrave;ng &amp;yacute; ki&#7871;n, &amp;ocirc;ng Ki&#7873;u V&#259;n M&amp;aacute;t - T&#7893;ng gi&amp;aacute;m &#273;&#7889;c Cty CP S&amp;ocirc;ng &#272;&amp;agrave; Cao C&#432;&#7901;ng (H&#7843;i D&#432;&#417;ng) cho r&#7857;ng: V&#7845;n &#273;&#7873; m&amp;agrave; c&amp;aacute;c DN ch&amp;uacute;ng t&amp;ocirc;i quan t&amp;acirc;m ch&amp;iacute;nh l&amp;agrave; &#273;&#7847;u ra cho s&#7843;n ph&#7849;m. T&amp;ocirc;i ngh&#297; &#273;&amp;atilde; &#273;&#7871;n l&amp;uacute;c c&#7847;n quy &#273;&#7883;nh c&amp;aacute;c nh&amp;agrave; cao t&#7847;ng s&#7917; d&#7909;ng t&#7915; 50 - 70% g&#7841;ch nh&#7865; t&#7915; n&#259;m 2012 ch&#7913; kh&amp;ocirc;ng ph&#7843;i khuy&#7871;n kh&amp;iacute;ch s&#7917; d&#7909;ng 30% g&#7841;ch nh&#7865; nh&#432; hi&#7879;n nay.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;T&amp;igrave;m c&amp;aacute;ch th&amp;aacute;o g&#7905;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng hi&#7879;n t&#7841;i, kh&amp;ocirc;ng ch&#7881; b&amp;ecirc; t&amp;ocirc;ng nh&#7865; nh&#432; g&#7841;ch AAC m&amp;agrave; t&#7845;t c&#7843; c&amp;aacute;c lo&#7841;i VLXD c&#361;ng &#273;ang &#7871; h&amp;agrave;ng do kinh t&#7871; n&#432;&#7899;c ta &#273;ang g&#7863;p kh&amp;oacute; kh&#259;n, l&#7841;m ph&amp;aacute;t cao, &#273;&#7847;u t&#432; c&amp;ocirc;ng b&#7883; c&#7855;t gi&#7843;m, th&#7883; tr&#432;&#7901;ng B&#272;S tr&#7847;m l&#7855;ng, l&amp;atilde;i vay t&#259;ng cao&amp;hellip; Trong v&#259;n b&#7843;n g&#7917;i Ch&amp;iacute;nh ph&#7911; c&#361;ng nh&#432; B&#7897; X&amp;acirc;y d&#7921;ng, H&#7897;i VLXD c&amp;oacute; &#273;&#432;a ra nh&#7919;ng kh&amp;oacute; kh&#259;n m&amp;agrave; c&amp;aacute;c &#273;&#417;n v&#7883; s&#7843;n xu&#7845;t g&#7841;ch AAC &#273;ang g&#7863;p ph&#7843;i, trong &#273;&amp;oacute; c&amp;oacute; nh&#7845;n m&#7841;nh &#273;&#7871;n vi&#7879;c: &#272;&#7871;n nay ch&#432;a c&amp;oacute; b&#7845;t c&#7913; m&#7897;t Th&amp;ocirc;ng t&#432; h&#432;&#7899;ng d&#7851;n th&#7921;c hi&#7879;n Quy&#7871;t &#273;&#7883;nh 567/Q&#272;-TTg k&amp;egrave;m theo; c&amp;aacute;c v&#259;n b&#7843;n li&amp;ecirc;n quan nh&#432;: Ti&amp;ecirc;u chu&#7849;n, &#273;&#7883;nh m&#7913;c v&#7853;t t&#432;, nh&amp;acirc;n c&amp;ocirc;ng, quy ph&#7841;m thi&#7871;t k&#7871;, thi c&amp;ocirc;ng v&amp;agrave; nghi&#7879;m thu c&amp;ocirc;ng tr&amp;igrave;nh s&#7917; d&#7909;ng s&#7843;n ph&#7849;m b&amp;ecirc; t&amp;ocirc;ng nh&#7865; c&#361;ng ch&#432;a &#273;&#432;&#7907;c ban h&amp;agrave;nh, n&amp;ecirc;n kh&amp;ocirc;ng c&amp;oacute; c&#417; s&#7903; &#273;&#7875; thi&#7871;t k&#7871;, x&amp;acirc;y d&#7921;ng, ki&#7875;m tra, gi&amp;aacute;m s&amp;aacute;t, nghi&#7879;m thu, thanh quy&#7871;t to&amp;aacute;n c&amp;ocirc;ng tr&amp;igrave;nh&amp;hellip; &#273;&#7863;c bi&#7879;t l&amp;agrave; c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh s&#7917; d&#7909;ng v&#7889;n Nh&amp;agrave; n&#432;&#7899;c, g&#7847;n nh&#432; 100% s&#7889; c&amp;ocirc;ng tr&amp;igrave;nh kh&amp;ocirc;ng s&#7917; d&#7909;ng b&amp;ecirc; t&amp;ocirc;ng nh&#7865;. V&amp;igrave; v&#7853;y c&amp;aacute;c ch&#7911; &#273;&#7847;u t&#432;, c&amp;aacute;c Cty t&#432; v&#7845;n thi&#7871;t k&#7871;, c&amp;aacute;c nh&amp;agrave; th&#7847;u x&amp;acirc;y d&#7921;ng, ng&#432;&#7901;i d&amp;acirc;n c&amp;ograve;n nhi&#7873;u nghi ng&#7841;i khi chuy&#7875;n qua s&#7917; d&#7909;ng s&#7843;n ph&#7849;m m&#7899;i v&amp;agrave; ch&#432;a c&amp;oacute; c&#417; s&#7903; &#273;&#7875; &#273;&#432;a c&amp;aacute;c s&#7843;n ph&#7849;m b&amp;ecirc; t&amp;ocirc;ng nh&#7865; v&amp;agrave;o thi&#7871;t k&#7871; c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, c&amp;oacute; m&#7897;t th&#7921;c t&#7871; c&#361;ng c&#7847;n ph&#7843;i &#273;&#432;&#7907;c nh&amp;igrave;n nh&#7853;n nghi&amp;ecirc;m t&amp;uacute;c, &#273;&amp;oacute; l&amp;agrave; trong qu&amp;aacute; tr&amp;igrave;nh th&#7921;c hi&#7879;n, c&amp;aacute;c nh&amp;agrave; &#273;&#7847;u t&#432; g&#7841;ch AAC c&amp;ograve;n thi&#7871;u kinh nghi&#7879;m v&amp;agrave; ngu&#7891;n v&#7889;n c&amp;ograve;n h&#7841;n ch&#7871;, n&amp;ecirc;n nh&#7853;p c&amp;aacute;c d&amp;acirc;y chuy&#7873;n c&amp;ocirc;ng ngh&#7879; v&#7899;i tr&amp;igrave;nh &#273;&#7897; trung b&amp;igrave;nh ho&#7863;c th&#7845;p, thi&#7871;u &#273;&#7891;ng b&#7897;, ch&#7845;t l&#432;&#7907;ng thi&#7871;t b&#7883; kh&amp;ocirc;ng t&#7889;t, c&amp;ocirc;ng t&amp;aacute;c chuy&#7875;n giao c&amp;ocirc;ng ngh&#7879;, k&#7929; thu&#7853;t s&#7843;n xu&#7845;t v&amp;agrave; ti&#7871;p thu c&amp;ocirc;ng ngh&#7879; ch&#432;a t&#7889;t. &#272;i&#7873;u n&amp;agrave;y &#7843;nh h&#432;&#7903;ng kh&amp;ocirc;ng nh&#7887; &#273;&#7871;n ch&#7845;t l&#432;&#7907;ng s&#7843;n ph&#7849;m. H&#417;n n&#7919;a, g&#7841;ch AAC &#273;&#432;a ra th&#7883; tr&#432;&#7901;ng l&#7841;i c&amp;oacute; gi&amp;aacute; cao h&#417;n so v&#7899;i g&#7841;ch nung truy&#7873;n th&#7889;ng n&amp;ecirc;n c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh t&#7887; ra kh&amp;ocirc;ng m&#7863;n m&amp;agrave; v&#7899;i &amp;ldquo;&lt;em&gt;s&#7843;n ph&#7849;m xanh&lt;/em&gt;&amp;rdquo; n&amp;agrave;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng L&amp;ecirc; V&#259;n T&#7899;i - V&#7909; tr&#432;&#7903;ng V&#7909; VLXD (B&#7897; X&amp;acirc;y d&#7921;ng) cho bi&#7871;t: Nh&amp;igrave;n ra th&#7871; gi&#7899;i, th&amp;igrave; c&amp;aacute;c n&#432;&#7899;c s&#7917; d&#7909;ng g&#7841;ch kh&amp;ocirc;ng nung h&#7885; kh&amp;ocirc;ng c&amp;oacute; ti&amp;ecirc;u chu&#7849;n v&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; h&#432;&#7899;ng d&#7851;n s&#7917; d&#7909;ng g&#7841;ch AAC. Tuy nhi&amp;ecirc;n, &#7903; Vi&#7879;t Nam, g&#7841;ch AAC l&amp;agrave; v&#7853;t li&#7879;u m&#7899;i v&#7899;i c&#7843; ng&#432;&#7901;i s&#7843;n xu&#7845;t, s&#7917; d&#7909;ng v&amp;agrave; nh&amp;agrave; qu&#7843;n l&amp;yacute;. Nh&#432;ng v&#7899;i nh&#7919;ng &#432;u &#273;i&#7875;m v&#432;&#7907;t tr&#7897;i n&amp;ecirc;n ngo&amp;agrave;i b&#7897; s&#7843;n ph&#7849;m TCVN 7959:2008 &#273;&#7889;i v&#7899;i block g&#7841;ch b&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; ch&#432;ng &amp;aacute;p &#273;&amp;atilde; &#273;&#432;&#7907;c ban h&amp;agrave;nh n&#259;m 2008 th&amp;igrave; B&#7897; X&amp;acirc;y d&#7921;ng th&#7845;y nh&#7845;t thi&#7871;t c&#7847;n ph&#7843;i ban h&amp;agrave;nh nh&#7919;ng b&#7897; t&amp;agrave;i li&#7879;u ti&amp;ecirc;u chu&#7849;n thi c&amp;ocirc;ng v&amp;agrave; nghi&#7879;m thu; t&amp;agrave;i li&#7879;u &#273;&#7883;nh m&#7913;c kinh t&#7871; k&#7929; thu&#7853;t&amp;hellip; &#273;&#7875; h&#7895; tr&#7907; cho vi&#7879;c ph&amp;aacute;t tri&#7875;n g&#7841;ch AAC &#7903; Vi&#7879;t Nam. Do &#273;&amp;oacute;, B&#7897; X&amp;acirc;y d&#7921;ng &#273;&amp;atilde; giao nhi&#7879;m v&#7909; cho Vi&#7879;n VLXD, Vi&#7879;n Kinh t&#7871; x&amp;acirc;y d&#7921;ng, Vi&#7879;n Khoa h&#7885;c c&amp;ocirc;ng ngh&#7879; x&amp;acirc;y d&#7921;ng nghi&amp;ecirc;n c&#7913;u, b&#7893; sung v&amp;agrave; ti&#7871;p t&#7909;c ho&amp;agrave;n thi&#7879;n c&amp;aacute;c ti&amp;ecirc;u chu&#7849;n &#273;&amp;atilde; ban h&amp;agrave;nh cho &#273;&#7847;y &#273;&#7911; c&amp;aacute;c ch&#7881; ti&amp;ecirc;u v&#7873; s&#7843;n ph&#7849;m. C&amp;aacute;c &#273;&#417;n v&#7883; c&#361;ng &#273;&amp;atilde; t&amp;iacute;ch c&#7921;c tri&#7875;n khai ho&amp;agrave;n ch&#7881;nh, trong th&#7901;i gian t&#7899;i s&#7869; ban h&amp;agrave;nh. C&amp;aacute;c DN c&#361;ng v&amp;igrave; th&#7871; c&#7847;n kh&#7855;c ph&#7909;c nh&#7919;ng nh&#432;&#7907;c &#273;i&#7875;m c&#7911;a s&#7843;n ph&#7849;m &#273;&#7875; ti&#7871;p c&#7853;n th&#7883; tr&#432;&#7901;ng, m&#7903; ra th&#7901;i &amp;ldquo;v&#7853;t li&#7879;u xanh&amp;rdquo; &#273;&#432;&#7907;c ng&#432;&#7901;i d&amp;acirc;n, c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh s&#7917; d&#7909;ng m&#7897;t c&amp;aacute;ch ph&#7893; bi&#7871;n, r&#7897;ng r&amp;atilde;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;ldquo;&lt;em&gt;B&#7897; X&amp;acirc;y d&#7921;ng th&#7845;y nh&#7845;t thi&#7871;t c&#7847;n ph&#7843;i ban h&amp;agrave;nh nh&#7919;ng b&#7897; t&amp;agrave;i li&#7879;u ti&amp;ecirc;u chu&#7849;n thi c&amp;ocirc;ng v&amp;agrave; nghi&#7879;m thu; t&amp;agrave;i li&#7879;u &#273;&#7883;nh m&#7913;c kinh t&#7871; k&#7929; thu&#7853;t&amp;hellip; &#273;&#7875; h&#7895; tr&#7907; cho vi&#7879;c ph&amp;aacute;t tri&#7875;n g&#7841;ch AAC &#7903; Vi&#7879;t Nam&lt;/em&gt;&amp;quot;&lt;/p&gt;','18/11/2011','18/11/2011','',23,2,1802,'huyennt',1,'phat-trien-gach-aac-huong-toi-vat-lieu-xanh','g&#7841;ch, &#273;&#7847;u t&#432;, v&#7853;t li&#7879;u, kh&ocirc;ng nung',NULL,NULL,NULL,'g&#7841;ch, &#273;&#7847;u t&#432;, v&#7853;t li&#7879;u, kh&ocirc;ng nung',0,0,0,'','','',3,'http://www.xaydungvietnam.vn',1,0,0,0),(99,'Th&eacute;p Vi&#7879;t Nam b&#7883; &#273;i&#7873;u tra ch&#7889;ng b&aacute;n ph&aacute; gi&aacute; t&#7841;i M&#7929;','C&#7909;c Qu&#7843;n l&yacute; c&#7841;nh tranh (B&#7897; C&ocirc;ng Th&#432;&#417;ng) cho bi&#7871;t B&#7897; Th&#432;&#417;ng m&#7841;i M&#7929; (DOC) v&#7915;a ch&iacute;nh th&#7913;c &#273;i&#7873;u tra ch&#7889;ng b&aacute;n ph&aacute; gi&aacute; v&agrave; ch&#7889;ng tr&#7907; c&#7845;p &#273;&#7889;i v&#7899;i s&#7843;n ph&#7849;m th&eacute;p c&#7911;a &#7844;n &#272;&#7897;, Oman, C&aacute;c Ti&#7875;u V&#432;&#417;ng qu&#7889;c &#7842; R&#7853;p Th&#7889;ng nh&#7845;t (UAE) v&agrave; Vi&#7879;t Nam.','\r\n&lt;p&gt;&lt;img width=&quot;0&quot; height=&quot;0&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/thep.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/thep.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&#7842;nh minh h&#7885;a&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Trong &#273;&amp;oacute;, Vi&#7879;t Nam c&amp;oacute; 10 doanh nghi&#7879;p s&#7843;n xu&#7845;t th&amp;eacute;p b&#7883; &#273;i&#7873;u tra. &#272;&#7889;i v&#7899;i Vi&#7879;t Nam, giai &#273;o&#7841;n &#273;i&#7873;u tra &#273;&#432;&#7907;c x&amp;aacute;c &#273;&#7883;nh t&#7915; ng&amp;agrave;y 1-4 &#273;&#7871;n 30-9-2011.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam cho bi&#7871;t n&#259;m 2010, c&#7843; n&#432;&#7899;c xu&#7845;t kh&#7849;u &#273;&#432;&#7907;c h&#417;n 1 tri&#7879;u t&#7845;n th&amp;eacute;p c&amp;aacute;c lo&#7841;i, d&#7921; ki&#7871;n n&#259;m nay, xu&#7845;t kh&#7849;u c&amp;oacute; t&#259;ng nh&#432;ng kh&amp;ocirc;ng &#273;&amp;aacute;ng k&#7875;.&lt;/p&gt;','19/11/2011','19/11/2011','/home/uploads/image/news/11-2011/thep.jpg',23,2,1818,'huyennt',1,'thep-viet-nam-bi-dieu-tra-chong-ban-pha-gia-tai-my','th&eacute;p, VLXD, b&aacute;n ph&aacute; gi&aacute;, xu&#7845;t kh&#7849;u, &#273;i&#7873;u tra',NULL,NULL,NULL,'th&eacute;p, VLXD, b&aacute;n ph&aacute; gi&aacute;, xu&#7845;t kh&#7849;u, &#273;i&#7873;u tra',0,0,0,'','','',3,'http://www.diaoconline.vn',1,0,0,0),(100,'V&#7853;t li&#7879;u m&#7899;i nh&#7865; h&#417;n x&#7889;p 100 l&#7847;n','C&aacute;c nh&agrave; khoa h&#7885;c M&#7929; v&#7915;a ch&#7871; t&#7841;o th&agrave;nh c&ocirc;ng v&#7853;t li&#7879;u nh&#7865; h&#417;n x&#7889;p t&#7899;i 100 l&#7847;n, si&ecirc;u r&#7895;ng v&agrave; c&oacute; c&#7845;u tr&uacute;c tr&#7853;t t&#7921;.','\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;313&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/vat-lieu-nhe-nhat-the-gioi.jpeg&quot; /&gt;&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;V&#7853;t li&#7879;u n&amp;agrave;y ch&#7913;a t&#7899;i 99,99% kh&amp;ocirc;ng kh&amp;iacute;, n&amp;oacute; nh&#7865; &#273;&#7871;n m&#7913;c c&amp;oacute; th&#7875; &#273;&#7863;t l&amp;ecirc;n &#273;&#7881;nh b&amp;ocirc;ng hoa b&#7891; c&amp;acirc;u anh m&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; b&#7845;t k&#7923; s&#7921; dao &#273;&#7897;ng n&amp;agrave;o. &#7842;nh: Ph&amp;ograve;ng th&amp;iacute; nghi&#7879;m HRL.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nNghi&amp;ecirc;n c&#7913;u th&#7921;c hi&#7879;n b&#7903;i nh&amp;oacute;m c&amp;aacute;c nh&amp;agrave; khoa h&#7885;c &#273;&#7871;n t&#7915; ph&amp;ograve;ng th&amp;iacute; nghi&#7879;m LHR, Trung t&amp;acirc;m Composites, &#272;&#7841;i h&#7885;c California v&amp;agrave; Vi&#7879;n c&amp;ocirc;ng ngh&#7879; California. C&amp;aacute;c nh&amp;agrave; khoa h&#7885;c &#273;&amp;atilde; t&#7841;o ra v&#7853;t li&#7879;u m&#7899;i c&amp;oacute; c&#7845;u tr&amp;uacute;c h&amp;igrave;nh l&#432;&#7899;i bao g&#7891;m c&amp;aacute;c &#7889;ng niken r&#7895;ng si&amp;ecirc;u nh&#7887;, c&amp;oacute; th&amp;agrave;nh ph&#7847;n ch&#7913;a t&#7899;i 99,99% l&amp;agrave; kh&amp;ocirc;ng kh&amp;iacute; v&amp;agrave; ch&#7881; 1% l&amp;agrave; ch&#7845;t r&#7855;n v&#7899;i k&amp;iacute;ch c&#7905; nanomet, micron v&amp;agrave; mm.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&#432;&#7899;c &#273;&amp;oacute;, lo&#7841;i v&#7853;t li&#7879;u nh&#7865; nh&#7845;t tr&amp;ecirc;n th&#7871; gi&#7899;i tr&#432;&#7899;c &#273;&amp;acirc;y l&amp;agrave; Aerogel, c&amp;ograve;n g&#7885;i l&amp;agrave; &amp;quot;kh&amp;oacute;i &#273;&amp;oacute;ng b&#259;ng&amp;quot;. Lo&#7841;i v&#7853;t li&#7879;u n&amp;agrave;y c&amp;oacute; t&#7927; tr&#7885;ng 1 mg m&#7895;i cm2, nh&#7865; h&#417;n c&#7843; kh&amp;ocirc;ng kh&amp;iacute; v&#7899;i nhi&#7879;t &#273;&#7897; trong ph&amp;ograve;ng. Trong khi &#273;&amp;oacute;, v&#7853;t li&#7879;u m&#7899;i &#273;&#432;&#7907;c c&amp;aacute;c nh&amp;agrave; khoa h&#7885;c t&#7841;o ra ch&#7881; n&#7863;ng 0,9 mg m&#7895;i cm2.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;quot;B&amp;iacute; quy&#7871;t l&amp;agrave; k&#7871;t n&#7889;i c&amp;aacute;c &#7889;ng r&#7895;ng v&#7899;i nhau t&#7841;o th&amp;agrave;nh m&#7897;t t&#7845;m l&#432;&#7899;i c&amp;oacute; &#273;&#7897; d&amp;agrave;y b&#7857;ng m&#7897;t ph&#7847;n ngh&amp;igrave;n s&#7907;i t&amp;oacute;c c&#7911;a con ng&#432;&#7901;i&amp;quot;, Science Daily d&#7851;n l&#7901;i ti&#7871;n s&#297; Tobias Schaedler, ph&amp;ograve;ng th&amp;iacute; nghi&#7879;m LHR.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c nh&amp;agrave; khoa h&#7885;c cho bi&#7871;t, v&#7853;t li&#7879;u m&#7899;i c&amp;oacute; th&#7875; &#273;&#432;&#7907;c &#7913;ng d&#7909;ng trong s&#7843;n xu&#7845;t c&amp;aacute;c &#273;i&#7879;n c&#7921;c pin gi&amp;uacute;p t&#259;ng l&#432;&#7907;ng &#273;i&#7879;n n&#259;ng v&amp;agrave; gi&#7843;m chi ph&amp;iacute; s&#7843;n xu&#7845;t cho b&#7897; pin th&#7871; h&#7879; m&#7899;i. N&amp;oacute; c&amp;ograve;n &#273;&#432;&#7907;c &#7913;ng d&#7909;ng cho qu&amp;aacute; tr&amp;igrave;nh h&#7845;p thu &amp;acirc;m thanh, ch&#7889;ng rung v&amp;agrave; s&#7889;c.&lt;/p&gt;\r\n\r\n&lt;p&gt;William Carter, m&#7897;t th&amp;agrave;nh vi&amp;ecirc;n trong nh&amp;oacute;m nghi&amp;ecirc;n c&#7913;u, mi&amp;ecirc;u t&#7843; thi&#7871;t k&#7871; c&#7911;a lo&#7841;i v&#7853;t li&#7879;u m&#7899;i nh&#432; m&#7897;t phi&amp;ecirc;n b&#7843;n thu nh&#7887; c&#7911;a th&amp;aacute;p Eiffel, t&#7913;c l&amp;agrave; n&amp;oacute; c&amp;oacute; &#273;&#7897; b&#7873;n cao, nh&#432;ng b&amp;ecirc;n trong ch&#7911; y&#7871;u l&amp;agrave; kh&amp;ocirc;ng kh&amp;iacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&amp;ldquo;Ch&amp;uacute;ng t&amp;ocirc;i &#273;&amp;atilde; t&#7841;o ra cu&#7897;c c&amp;aacute;ch m&#7841;ng trong l&#297;nh v&#7921;c nghi&amp;ecirc;n c&#7913;u v&#7873; v&#7853;t li&#7879;u si&amp;ecirc;u nh&#7865;&lt;/em&gt;&amp;rdquo;, &amp;ocirc;ng William Carter n&amp;oacute;i th&amp;ecirc;m.&lt;/p&gt;\r\n\r\n&lt;p&gt;K&#7871;t qu&#7843; nghi&amp;ecirc;n c&#7913;u &#273;&#259;ng tr&amp;ecirc;n t&#7841;p ch&amp;iacute; Science s&#7889; ra cu&#7889;i tu&#7847;n tr&#432;&#7899;c.&lt;/p&gt;','22/11/2011','22/11/2011','/home/uploads/image/news/11-2011/vat-lieu-nhe-nhat-the-gioi.jpeg',23,2,1885,'huyennt',1,'vat-lieu-moi-nhe-hon-xop-100-lan','th&#7883; tr&#432;&#7901;ng, g&#7841;ch, v&#7853;t li&#7879;u nh&#7865;, v&#7853;t li&#7879;u, v&#7853;t li&#7879;u m&#7899;i, c&#7845;u tr&uacute;c',NULL,NULL,NULL,'th&#7883; tr&#432;&#7901;ng, g&#7841;ch, v&#7853;t li&#7879;u nh&#7865;, v&#7853;t li&#7879;u, v&#7853;t li&#7879;u m&#7899;i, c&#7845;u tr&uacute;c',0,0,0,'','','',3,'http://dothi.net',1,0,0,0),(101,'Th&#7883; tr&#432;&#7901;ng g&#7841;ch n&#7897;i &#273;ang k&ecirc;u c&#7913;u','V&#7915;a qua, theo c&ocirc;ng v&#259;n c&#7911;a Hi&#7879;p h&#7897;i G&#7889;m s&#7913; x&acirc;y d&#7921;ng VN (VIBCA), ng&agrave;nh g&#7841;ch &#7889;p l&aacute;t trong n&#432;&#7899;c &#273;ang l&acirc;m v&agrave;o t&igrave;nh tr&#7841;ng kh&oacute; kh&#259;n do g&#7841;ch nh&#7853;p kh&#7849;u t&#7915; Trung Qu&#7889;c (TQ) s&#7917; d&#7909;ng c&aacute;c &ldquo;chi&ecirc;u&rdquo;: gian l&#7853;n th&#432;&#417;ng m&#7841;i, tr&#7889;n thu&#7871;, nh&#7853;p l&#7853;u...','\r\n&lt;p&gt;Ph&#7847;n l&#7899;n c&amp;aacute;c lo&#7841;i g&#7841;ch &#7889;p l&amp;aacute;t tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng &#273;&#7873;u c&amp;oacute; ngu&#7891;n g&#7889;c xu&#7845;t x&#7913; t&#7915; Trung Qu&#7889;c.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;G&#7841;ch &#7889;p l&amp;aacute;t c&amp;oacute; xu&#7845;t x&#7913; t&#7915; TQ hi&#7879;n nay tr&amp;agrave;n ng&#7853;p kh&amp;ocirc;ng ch&#7881; &#7903; c&amp;aacute;c th&amp;agrave;nh ph&#7889; l&#7899;n nh&#432; Tp.HCM, H&amp;agrave; N&#7897;i... m&amp;agrave; c&amp;ograve;n th&amp;acirc;m nh&#7853;p c&#7843; c&amp;aacute;c v&amp;ugrave;ng th&amp;ocirc;n qu&amp;ecirc;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Gi&amp;aacute; n&amp;agrave;o c&#361;ng c&amp;oacute;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Kh&#7843;os&amp;aacute;t tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng H&amp;agrave; N&#7897;i hi&#7879;n nay, c&amp;aacute;c lo&#7841;i g&#7841;ch &#7889;p l&amp;aacute;t c&#7911;a TQ ch&#7911; y&#7871;u l&amp;agrave; g&#7841;ch granite, ceramic l&amp;aacute;t n&#7873;n lo&#7841;i l&#7899;n k&amp;iacute;ch th&#432;&#7899;c 60x60cm, 80x80cm ho&#7863;c g&#7841;ch &#7889;p t&#432;&#7901;ng, nh&amp;agrave; v&#7879; sinh k&amp;iacute;ch c&#7905; 30x45cm, 30x60cm. Ch&#7881; c&#7847;n nh&amp;igrave;n v&amp;agrave;o ch&#7911;ng lo&#7841;i g&#7841;ch TQ &#273;ang b&amp;agrave;y b&amp;aacute;n, kh&amp;aacute;ch h&amp;agrave;ng s&#7869; ph&#7843;i... ch&amp;oacute;ng m&#7863;t v&amp;igrave; ki&#7875;u d&amp;aacute;ng v&amp;agrave; m&amp;agrave;u s&#7855;c c&#7911;a g&#7841;ch TQ qu&amp;aacute; phong ph&amp;uacute;, &#273;a d&#7841;ng.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nNh&#7853;p kh&#7849;u ch&amp;iacute;nh ng&#7841;ch s&#7869; v&#432;&#7907;t 100 tri&#7879;u USD&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nTheo VIBCA, n&#259;m 2009 t&#7893;ng kim ng&#7841;ch nh&#7853;p kh&#7849;u g&#7841;ch &#7889;p l&amp;aacute;t c&amp;aacute;c lo&#7841;i kho&#7843;ng 70 tri&#7879;u USD, trong &#273;&amp;oacute; nh&#7853;p t&#7915; TQ &#273;&#7871;n 51 tri&#7879;u USD. N&#259;m 2010, kim ng&#7841;ch nh&#7853;p kh&#7849;u g&#7841;ch &#7889;p l&amp;aacute;t v&amp;agrave;o th&#7883; tr&#432;&#7901;ng VN l&amp;ecirc;n t&#7899;i 95,5 tri&#7879;u USD, trong &#273;&amp;oacute; 76 tri&#7879;u USD l&amp;agrave; nh&#7853;p t&#7915; TQ. N&#259;m 2011, VIBCA cho r&#7857;ng l&#432;&#7907;ng g&#7841;ch &#7889;p l&amp;aacute;t nh&#7853;p kh&#7849;u t&#7915; TQ c&amp;oacute; kh&#7843; n&#259;ng v&#432;&#7907;t 100 tri&#7879;u USD.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo VIBCA, c&amp;aacute;c s&#7889; li&#7879;u n&amp;oacute;i tr&amp;ecirc;n ch&#432;a t&amp;iacute;nh nh&#7853;p kh&#7849;u qua &#273;&#432;&#7901;ng ti&#7875;u ng&#7841;ch v&amp;agrave; h&amp;agrave;ng l&#7853;u bi&amp;ecirc;n gi&#7899;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nTh&#7883;nh h&amp;agrave;nh nh&#7845;t hi&#7879;n nay l&amp;agrave; lo&#7841;i g&#7841;ch granite v&amp;acirc;n loang s&amp;aacute;ng m&amp;agrave;u, v&amp;acirc;n m&amp;agrave;u &#273;&amp;aacute;, m&amp;agrave;u t&#7889;i, v&amp;acirc;n gi&#7843; c&#7893;, lo&#7841;i c&amp;oacute; hoa v&#259;n nh&amp;acirc;n t&#7841;o (nh&#432; v&amp;acirc;n gi&#7843; g&#7895;), v&amp;agrave;ng kem nh&#432; m&amp;agrave;u l&amp;ocirc;ng g&amp;agrave; con ho&#7863;c h&#417;i n&amp;acirc;u... Ch&#7883; Nguy&#7879;t, nh&amp;acirc;n vi&amp;ecirc;n b&amp;aacute;n h&amp;agrave;ng t&#7841;i c&#7917;a h&amp;agrave;ng Kh&#432;&#417;ng Tu&#7845;n (&#273;&#432;&#7901;ng Ho&amp;agrave;ng Qu&#7889;c Vi&#7879;t, H&amp;agrave; N&#7897;i), cho bi&#7871;t gi&amp;aacute; g&#7841;ch &#7889;p l&amp;aacute;t TQ b&amp;igrave;nh th&#432;&#7901;ng t&#7915; 180.000-260.000 &#273;&#7891;ng/m2, lo&#7841;i &#273;&#7855;t h&#417;n kho&#7843;ng 400.000-500.000 &#273;&#7891;ng/m2... t&amp;ugrave;y lo&#7841;i v&amp;agrave; m&#7851;u m&amp;atilde;.&lt;/p&gt;\r\n\r\n&lt;p&gt;So v&#7899;i g&#7841;ch s&#7843;n xu&#7845;t trong n&#432;&#7899;c, gi&amp;aacute; g&#7841;ch TQ r&#7867; h&#417;n 20.000-50.000 &#273;&#7891;ng/m2 (t&amp;ugrave;y lo&#7841;i, k&amp;iacute;ch th&#432;&#7899;c).&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo m&#7897;t s&#7889; ng&#432;&#7901;i kinh doanh &#7903; H&amp;agrave; N&#7897;i, m&#7851;u m&amp;atilde; &#273;&#7865;p, ch&#7911;ng lo&#7841;i phong ph&amp;uacute;, ch&#7845;t l&#432;&#7907;ng ch&#7845;p nh&#7853;n v&amp;agrave; gi&amp;aacute; r&#7867; h&#417;n h&amp;agrave;ng trong n&#432;&#7899;c l&amp;agrave; nh&#7919;ng l&amp;yacute; do ch&amp;iacute;nh &#273;&#7875; ng&#432;&#7901;i ti&amp;ecirc;u d&amp;ugrave;ng ch&#7885;n mua c&amp;aacute;c lo&#7841;i g&#7841;ch xu&#7845;t x&#7913; t&#7915; TQ. &#272;&amp;acirc;y c&#361;ng l&amp;agrave; nguy&amp;ecirc;n nh&amp;acirc;n khi&#7871;n nhi&#7873;u doanh nghi&#7879;p nh&#7853;p g&#7841;ch TQ v&amp;agrave;o VN nhi&#7873;u h&#417;n &#273;&#7875; kinh doanh, trong &#273;&amp;oacute; kh&amp;ocirc;ng &amp;iacute;t &#273;&#432;&#7907;c nh&#7853;p kh&#7849;u b&#7857;ng &#273;&#432;&#7901;ng ti&#7875;u ng&#7841;ch &#7903; c&#7917;a kh&#7849;u M&amp;oacute;ng C&amp;aacute;i (Qu&#7843;ng Ninh)... tr&#7889;n thu&#7871; n&amp;ecirc;n gi&amp;aacute; c&amp;agrave;ng r&#7867; h&#417;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;G&#7841;ch &#7889;p l&amp;aacute;t TQ t&#7841;i th&#7883; tr&#432;&#7901;ng Tp.HCM c&#361;ng phong ph&amp;uacute; kh&amp;ocirc;ng k&amp;eacute;m H&amp;agrave; N&#7897;i. Ch&#7883; Nguy&#7879;t H&#7891;ng, ch&#7911; c&#7917;a h&amp;agrave;ng H&#7891;ng Anh (&#273;&#432;&#7901;ng T&amp;ocirc; Hi&#7871;n Th&amp;agrave;nh, Q.10), cho hay n&#7871;u c&amp;oacute; m&#432;&#7901;i ng&#432;&#7901;i kh&amp;aacute;ch v&amp;agrave;o ti&#7879;m h&#7887;i mua g&#7841;ch th&amp;igrave; c&amp;oacute; &#273;&#7871;n t&amp;aacute;m ng&#432;&#7901;i ch&#7885;n mua g&#7841;ch c&#7911;a TQ ch&#7881; v&amp;igrave; &amp;ldquo;gi&amp;aacute; m&#7873;m, l&#7841;i nhi&#7873;u ki&#7875;u, tha h&#7891; ch&#7885;n&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Nguy&#7877;n Long, ph&#7909; tr&amp;aacute;ch v&#7853;t t&#432; C&amp;ocirc;ng ty TNHH x&amp;acirc;y d&#7921;ng Minh Khoa (Q.T&amp;acirc;n B&amp;igrave;nh), cho r&#7857;ng n&#7871;u so s&amp;aacute;nh tr&#7921;c ti&#7871;p m&#7851;u g&#7841;ch granite lo&#7841;i 60x60cm c&#7911;a h&amp;atilde;ng T. v&#7899;i g&#7841;ch TQ th&amp;igrave; &amp;ldquo;m&amp;agrave;u h&amp;agrave;ng s&#7843;n xu&#7845;t trong n&#432;&#7899;c kh&amp;ocirc;ng &#273;&#7865;p, kh&amp;ocirc;ng b&amp;oacute;ng c&#361;ng kh&amp;ocirc;ng s&amp;aacute;ng, trong khi g&#7841;ch c&#7911;a TQ nh&amp;igrave;n b&#7855;t m&#7855;t v&amp;agrave; n&#432;&#7899;c m&amp;agrave;u r&#7845;t s&#7889;ng &#273;&#7897;ng&amp;rdquo;. Ch&#432;a k&#7875; m&#7913;c gi&amp;aacute; c&#7911;a g&#7841;ch TQ kh&amp;aacute; m&#7873;m khi c&amp;aacute;c lo&#7841;i g&#7841;ch ph&#7893; bi&#7871;n ch&#7881; dao &#273;&#7897;ng 160.000-170.000 &#273;&#7891;ng/m2, trong khi g&#7841;ch trong n&#432;&#7899;c t&#7915; tr&amp;ecirc;n 180.000 &#273;&#7891;ng/m2.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng V., m&#7897;t nh&amp;agrave; th&#7847;u x&amp;acirc;y d&#7921;ng, cho bi&#7871;t ngay c&#7843; khi ch&#7885;n g&#7841;ch cho c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh t&#432;&#417;ng &#273;&#7889;i l&#7899;n, nhi&#7873;u ch&#7911; &#273;&#7847;u t&#432; khi &#273;&#432;&#7907;c gi&#7899;i thi&#7879;u c&#361;ng &#273;&#7873;u ch&#7885;n h&amp;agrave;ng t&#7915; TQ ch&#7881; v&amp;igrave; y&#7871;u t&#7889; th&#7849;m m&#7929;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong khi &#273;&amp;oacute;, gi&#7899;i &amp;ldquo;&#273;&amp;aacute;nh&amp;rdquo; g&#7841;ch TQ nh&#7853;p kh&#7849;u cho bi&#7871;t khi h&amp;agrave;ng v&#7873; &#273;&#7871;n VN, bao b&amp;igrave; b&amp;ecirc;n ngo&amp;agrave;i c&#7911;a g&#7841;ch TQ &#273;&#7873;u b&#7883; l&#7897;t h&#7871;t. Thay v&amp;agrave;o &#273;&amp;oacute;, t&amp;ecirc;n c&#7911;a &#273;&#417;n v&#7883; nh&#7853;p h&amp;agrave;ng s&#7869; &#273;&#432;&#7907;c vi&#7871;t t&#7855;t v&amp;agrave; d&amp;aacute;n tr&amp;ecirc;n g&amp;oacute;c c&#7911;a th&amp;ugrave;ng h&amp;agrave;ng &#273;&#7875; c&amp;aacute;c c&#7917;a h&amp;agrave;ng l&#7845;y b&amp;aacute;n d&#7877; d&amp;agrave;ng ph&amp;acirc;n bi&#7879;t ngu&#7891;n cung.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhi&#7873;u nh&amp;agrave; th&#7847;u c&amp;ograve;n kh&#7859;ng &#273;&#7883;nh n&#7871;u bi&#7871;t &amp;ldquo;t&#7853;n l&amp;ograve;&amp;rdquo; c&#7911;a c&amp;aacute;c &#273;&#7847;u m&#7889;i nh&#7853;p g&#7841;ch TQ, c&amp;oacute; th&#7875; l&#7845;y &#273;&#432;&#7907;c gi&amp;aacute; r&#7867; h&#417;n gi&amp;aacute; b&amp;aacute;n l&#7867; ngo&amp;agrave;i th&#7883; tr&#432;&#7901;ng 20.000-30.000 &#273;&#7891;ng/m2 v&amp;igrave; kh&amp;ocirc;ng ph&#7843;i qua nhi&#7873;u kh&amp;acirc;u b&amp;aacute;n l&#7867; trung gian.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;550&quot; height=&quot;309&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/thi-truong-gach.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;G&#7841;ch Trung Qu&#7889;c &#273;&#432;&#7907;c b&amp;agrave;y b&amp;aacute;n tr&amp;agrave;n ng&#7853;p tr&amp;ecirc;n &#273;&#432;&#7901;ng Ho&amp;agrave;ng Qu&#7889;c Vi&#7879;t, H&amp;agrave; N&#7897;i&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;strong&gt;G&#7841;ch n&#7897;i... ng&#7855;c ngo&#7843;i&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo &amp;ocirc;ng &#272;inh Quang Huy - ch&#7911; t&#7883;ch VIBCA, ngo&amp;agrave;i vi&#7879;c &#273;&#7847;u ra th&#7883; tr&#432;&#7901;ng v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng b&#7883; &#7843;nh h&#432;&#7903;ng b&#7903;i th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n &#273;&amp;oacute;ng b&#259;ng, t&amp;igrave;nh tr&#7841;ng g&#7841;ch &#7889;p l&amp;aacute;t TQ nh&#7853;p l&#7853;u, gian l&#7853;n th&#432;&#417;ng m&#7841;i, tr&#7889;n thu&#7871; qua &#273;&#432;&#7901;ng bi&#7875;n, bi&amp;ecirc;n m&#7853;u l&amp;agrave; nguy&amp;ecirc;n nh&amp;acirc;n &lt;em&gt;&amp;ldquo;g&amp;oacute;p ph&#7847;n l&amp;agrave;m th&#7883; tr&#432;&#7901;ng ti&amp;ecirc;u th&#7909; l&#7851;n c&amp;aacute;c nh&amp;agrave; s&#7843;n xu&#7845;t v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng trong n&#432;&#7899;c l&amp;acirc;m v&amp;agrave;o t&amp;igrave;nh th&#7871; kh&amp;oacute; kh&#259;n nh&#7845;t t&#7915; tr&#432;&#7899;c &#273;&#7871;n nay&lt;/em&gt;&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngo&amp;agrave;i m&#7913;c t&#7891;n kho c&amp;aacute;c s&#7843;n ph&#7849;m g&#7841;ch &#7889;p l&amp;aacute;t &#273;&#432;&#7907;c VIBCA ghi nh&#7853;n x&#7845;p x&#7881; g&#7847;n 30 tri&#7879;u m2 (t&amp;iacute;nh &#273;&#7871;n cu&#7889;i th&amp;aacute;ng 10/2011), &amp;ocirc;ng Huy cho bi&#7871;t l&#432;&#7907;ng g&#7841;ch granite l&#7851;n ceramic ti&amp;ecirc;u th&#7909; trong n&#259;m 2011 &#432;&#7899;c ch&#7881; b&#7857;ng 70% l&#432;&#7907;ng g&#7841;ch n&amp;agrave;y &#273;&amp;atilde; s&#7843;n xu&#7845;t trong n&#259;m 2010, t&#432;&#417;ng &#7913;ng kho&#7843;ng 217 tri&#7879;u m2. N&#7871;u so v&#7899;i n&#259;ng l&#7921;c s&#7843;n xu&#7845;t c&#7911;a c&amp;aacute;c doanh nghi&#7879;p s&#7843;n xu&#7845;t g&#7841;ch &#7889;p l&amp;aacute;t c&amp;aacute;c lo&#7841;i trong c&#7843; n&#432;&#7899;c l&amp;ecirc;n &#273;&#7871;n h&#417;n 400 tri&#7879;u m2/n&#259;m, con s&#7889; ti&amp;ecirc;u th&#7909; d&#7921; ki&#7871;n n&amp;oacute;i tr&amp;ecirc;n qu&#7843; l&amp;agrave; r&#7845;t th&#7845;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo nh&#7853;n &#273;&#7883;nh c&#7911;a &amp;ocirc;ng Huy, gian l&#7853;n th&#432;&#417;ng m&#7841;i ph&#7893; bi&#7871;n nh&#7845;t hi&#7879;n nay xu&#7845;t hi&#7879;n d&#432;&#7899;i h&amp;igrave;nh th&#7913;c doanh nghi&#7879;p nh&#7853;p kh&#7849;u xu&#7845;t tr&amp;igrave;nh h&amp;oacute;a &#273;&#417;n mua h&amp;agrave;ng t&#7915; TQ v&#7899;i gi&amp;aacute; th&#7845;p h&#417;n nhi&#7873;u so v&#7899;i gi&amp;aacute; tr&#7883; th&#7921;c t&#7871; t&#7915; 50-70%, k&amp;ecirc; khai s&#7889; l&#432;&#7907;ng nh&#7853;p kh&#7849;u &amp;iacute;t h&#417;n s&#7889; l&#432;&#7907;ng th&#7921;c t&#7871;, s&#7917; d&#7909;ng h&amp;oacute;a &#273;&#417;n quay v&amp;ograve;ng v&amp;agrave; h&#7907;p th&#7913;c h&amp;oacute;a vi&#7879;c th&amp;ocirc;ng quan n&#7845;p d&#432;&#7899;i h&amp;igrave;nh th&#7913;c &amp;ldquo;cho nhu c&#7847;u s&#7917; d&#7909;ng&amp;rdquo;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&amp;iacute;nh v&amp;igrave; v&#7853;y, d&amp;ugrave; thu&#7871; su&#7845;t nh&#7853;p kh&#7849;u g&#7841;ch t&#7915; TQ theo &#273;&#432;&#7901;ng ch&amp;iacute;nh ng&#7841;ch &#273;ang &#7903; m&#7913;c 25% (ho&#7863;c h&#417;n, t&amp;ugrave;y m&amp;atilde; h&amp;agrave;ng) nh&#432;ng do khai b&amp;aacute;o qu&amp;aacute; th&#7845;p, thu&#7871; nh&#7853;p kh&#7849;u m&amp;agrave; Nh&amp;agrave; n&#432;&#7899;c thu v&#7873; ch&#7859;ng &#273;&amp;aacute;ng l&amp;agrave; bao tr&amp;ecirc;n gi&amp;aacute; tr&#7883;... khai kh&#7889;ng c&#7911;a doanh nghi&#7879;p nh&#7853;p kh&#7849;u. &amp;ldquo;Vi&#7879;c gian l&#7853;n th&#432;&#417;ng m&#7841;i n&amp;agrave;y di&#7877;n ra li&amp;ecirc;n t&#7909;c nhi&#7873;u n&#259;m qua v&amp;agrave; ng&amp;agrave;y c&amp;agrave;ng nghi&amp;ecirc;m tr&#7885;ng. &#272;i&#7873;u n&amp;agrave;y l&amp;agrave;m ng&amp;agrave;nh g&#7841;ch &#7889;p l&amp;aacute;t trong n&#432;&#7899;c ng&amp;agrave;y c&amp;agrave;ng l&amp;acirc;m v&amp;agrave;o t&amp;igrave;nh tr&#7841;ng kh&amp;oacute; kh&#259;n, nhi&#7873;u doanh nghi&#7879;p &#273;ang &#273;&#7913;ng tr&#432;&#7899;c nguy c&#417; ph&amp;aacute; s&#7843;n&amp;rdquo; - &amp;ocirc;ng Huy b&#7913;c x&amp;uacute;c.&lt;/p&gt;\r\n\r\n&lt;p&gt;Trong c&amp;ocirc;ng v&#259;n kh&#7849;n g&#7917;i Th&#7911; t&#432;&#7899;ng Ch&amp;iacute;nh ph&#7911; m&#7899;i &#273;&amp;acirc;y, VIBCA ki&#7871;n ngh&#7883; &#273;&#432;a m&#7863;t h&amp;agrave;ng g&#7841;ch &#7889;p l&amp;aacute;t nh&#7853;p kh&#7849;u t&#7915; TQ v&amp;agrave;o danh m&#7909;c r&#7911;i ro v&amp;agrave; &amp;aacute;p gi&amp;aacute; thu&#7871; t&amp;iacute;nh tr&amp;ecirc;n m&amp;eacute;t vu&amp;ocirc;ng, theo t&#7915;ng lo&#7841;i k&amp;iacute;ch th&#432;&#7899;c. C&#7909; th&#7875; &amp;aacute;p &#7903; m&#7913;c t&#7915; 5-12 USD/m2 &#273;&#7889;i v&#7899;i g&#7841;ch ceramic tr&amp;aacute;ng men m&amp;atilde; s&#7889; HS 6908, t&#7915; 5-13 USD/m2 &#273;&#7889;i v&#7899;i g&#7841;ch ceramic m&amp;agrave;i b&amp;oacute;ng ho&#7863;c gi&#7843; c&#7893; m&amp;atilde; s&#7889; HS 6907.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;ograve;n hi&#7879;n nay, vi&#7879;c t&amp;iacute;nh thu&#7871; ch&#7881; d&#7921;a theo h&amp;oacute;a &#273;&#417;n mua h&amp;agrave;ng c&#7911;a doanh nghi&#7879;p nh&#7853;p kh&#7849;u n&amp;ecirc;n ph&#7847;n l&#7899;n doanh nghi&#7879;p &#273;&#7873;u k&amp;ecirc; khai gi&amp;aacute; th&#7845;p h&#417;n r&#7845;t nhi&#7873;u so v&#7899;i gi&amp;aacute; tr&#7883; th&#7921;c t&#7871; &#273;&#7875; &amp;ldquo;n&amp;eacute;&amp;rdquo; thu&#7871;.&lt;/p&gt;','22/11/2011','22/11/2011','/home/uploads/image/news/11-2011/thi-truong-gach.jpg',23,2,2651,'huyennt',1,'thi-truong-gach-noi-dang-keu-cuu','g&#7841;ch, v&#7853;t li&#7879;u, th&#7883; tr&#432;&#7901;ng, th&#432;&#417;ng m&#7841;i, k&ecirc;u c&#7913;u',NULL,NULL,NULL,'g&#7841;ch, v&#7853;t li&#7879;u, th&#7883; tr&#432;&#7901;ng, th&#432;&#417;ng m&#7841;i, k&ecirc;u c&#7913;u',0,0,0,'','','',3,'http://www.xaydungvietnam.vn',1,0,0,1),(102,'Ra m&#7855;t s&#7843;n ph&#7849;m B&ecirc; t&ocirc;ng kh&iacute; c&#7845;p c&#432;&#7901;ng &#273;&#7897; 6','C&ocirc;ng ty CP B&ecirc; t&ocirc;ng kh&iacute; Viglacera v&#7915;a cho ra m&#7855;t d&ograve;ng s&#7843;n ph&#7849;m m&#7899;i B&ecirc; t&ocirc;ng kh&iacute; c&#7845;p c&#432;&#7901;ng &#273;&#7897; 6 d&agrave;nh cho c&aacute;c d&#7921; &aacute;n x&acirc;y d&#7921;ng cao c&#7845;p.','&lt;p style=&quot;text-align: center; &quot;&gt;&lt;br /&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/san-pham-betong.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&#7842;nh minh h&#7885;a&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;S&#7843;n ph&#7849;m B&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; c&#7845;p c&#432;&#7901;ng &#273;&#7897; 6 &#273;&#7841;t &#273;&#432;&#7907;c c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; k&#7929; thu&#7853;t v&#432;&#7907;t tr&#7897;i so v&#7899;i B&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; th&amp;ocirc;ng th&#432;&#7901;ng hi&#7879;n &#273;ang &#273;&#432;&#7907;c s&#7917; d&#7909;ng r&#7897;ng r&amp;atilde;i cho c&amp;aacute;c cho c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh cao t&#7847;ng. C&#432;&#7901;ng &#273;&#7897; n&amp;eacute;n c&#7911;a s&#7843;n ph&#7849;m &#273;&#7841;t cao 6 ~ 7,5 MPA (b&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; th&#432;&#7901;ng &#273;&#7841;t: 4 ~ 5 MPA). Kh&#7889;i l&#432;&#7907;ng th&#7875; t&amp;iacute;ch nh&#7865;: 700 ~ 850kg/m3&lt;/p&gt;\r\n\r\n&lt;p&gt;C&#432;&#7901;ng &#273;&#7897; n&amp;eacute;n cao cho ph&amp;eacute;p &#273;&#7843;m b&#7843;o t&amp;iacute;nh v&#7919;ng ch&#7855;c cho c&amp;aacute;c kh&#7889;i t&#432;&#7901;ng nh&amp;agrave; bi&#7879;t th&#7921; v&amp;agrave; li&#7873;n k&#7873;, &#273;&#7843;m b&#7843;o v&#7873; m&#7863;t an ninh cho ng&amp;ocirc;i nh&amp;agrave;. C&#432;&#7901;ng &#273;&#7897; b&#7913;c t&#432;&#7901;ng c&amp;ograve;n c&amp;oacute; th&#7875; &#273;&amp;aacute;p &#7913;ng t&#7889;t vi&#7879;c khoan, c&#7855;t, b&#7855;t v&amp;iacute;t v&amp;agrave; treo c&amp;aacute;c v&#7853;t d&#7909;ng trong nh&amp;agrave; nh&#432;: t&#7911; b&#7871;p, l&#7855;p &#273;&#7863;t &#273;i&#7879;n n&#432;&#7899;c, tivi, tranh &#7843;nh trang tr&amp;iacute; c&#7905; l&#7899;n&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngo&amp;agrave;i ra, s&#7843;n ph&#7849;m B&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; Viglacera c&#432;&#7901;ng &#273;&#7897; 6 c&amp;oacute; k&amp;iacute;ch th&#432;&#7899;c 150 x 200 x 600mm gi&amp;uacute;p thi c&amp;ocirc;ng d&#7877; d&amp;agrave;ng, ti&#7871;n &#273;&#7897; thi c&amp;ocirc;ng nhanh, ti&#7871;t ki&#7879;m nh&amp;acirc;n c&amp;ocirc;ng v&amp;agrave; v&#7853;t t&#432; x&amp;acirc;y d&#7921;ng cho ch&#7911; &#273;&#7847;u t&#432;. V&#7899;i chi&#7873;u d&amp;agrave;y 150mm, gi&amp;uacute;p gi&#7843;m &#273;&#7897; d&amp;agrave;y c&#7911;a t&#432;&#7901;ng so v&#7899;i t&#432;&#7901;ng 220mm truy&#7873;n th&#7889;ng, t&#259;ng di&#7879;n t&amp;iacute;ch s&#7917; d&#7909;ng cho ng&amp;ocirc;i nh&amp;agrave;.&lt;/p&gt;\r\n\r\n&lt;p&gt;S&#7843;n ph&#7849;m B&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; c&#7845;p c&#432;&#7901;ng &#273;&#7897; 6 l&#7847;n &#273;&#7847;u ti&amp;ecirc;n xu&#7845;t hi&#7879;n tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng nh&#432;ng &#273;&amp;atilde; &#273;&#7841;t &#273;&#432;&#7907;c th&#7887;a thu&#7853;n v&#7899;i Ch&#7911; &#273;&#7847;u t&#432; D&#7921; &amp;aacute;n Khu ch&#7913;c n&#259;ng &#273;&amp;ocirc; th&#7883; Xu&amp;acirc;n Ph&#432;&#417;ng v&amp;agrave; m&#7897;t s&#7889; d&#7921; &amp;aacute;n nh&amp;agrave; bi&#7879;t th&#7921;, li&#7873;n k&#7873; &#273;&#432;a v&amp;agrave;o s&#7917; d&#7909;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Vi&#7879;c &#273;&#432;a ra th&#7883; tr&#432;&#7901;ng s&#7843;n ph&#7849;m B&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; c&#7845;p &#273;&#7897; 6 m&#7897;t l&#7847;n n&#7919;a kh&#7859;ng &#273;&#7883;nh vai tr&amp;ograve; ti&amp;ecirc;n phong c&#7911;a T&#7893;ng c&amp;ocirc;ng ty Viglacera n&amp;oacute;i chung v&amp;agrave; C&amp;ocirc;ng ty CP B&amp;ecirc; t&amp;ocirc;ng kh&amp;iacute; Viglacera n&amp;oacute;i ri&amp;ecirc;ng trong l&#297;nh v&#7921;c s&#7843;n xu&#7845;t lo&#7841;i v&#7853;t li&#7879;u m&#7899;i cho th&#7883; tr&#432;&#7901;ng x&amp;acirc;y d&#7921;ng. &lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: right; &quot;&gt;&lt;em&gt;&lt;br /&gt;\r\n&lt;/em&gt;&lt;/p&gt;','23/11/2011','23/11/2011','/home/uploads/image/news/11-2011/san-pham-betong.jpg',23,2,1781,'huyennt',1,'ra-mat-san-pham-be-tong-khi-cap-cuong-do-6','b&ecirc; t&ocirc;ng, v&#7853;t li&#7879;u, s&#7843;n ph&#7849;m',NULL,NULL,NULL,'b&ecirc; t&ocirc;ng, v&#7853;t li&#7879;u, s&#7843;n ph&#7849;m',0,0,0,'','','',3,'http://www.diaoconline.vn',1,0,0,0),(103,'D&ugrave;ng th&#7843;m &#273;&uacute;ng c&aacute;ch','Th&#7901;i ti&#7871;t chuy&#7875;n m&ugrave;a c&#361;ng l&agrave; l&uacute;c nhi&#7873;u gia &#273;&igrave;nh mang c&aacute;c lo&#7841;i th&#7843;m ra s&#7917; d&#7909;ng. Kh&ocirc;ng ch&#7881; gi&uacute;p gi&#7919; nhi&#7879;t, gi&#7843;m ti&#7871;ng &#7891;n, gi&#7843;m &#273;&#7897; nguy hi&#7875;m khi tr&#7867; ho&#7841;t &#273;&#7897;ng, th&#7843;m c&ograve;n gi&uacute;p t&#7841;o phong c&aacute;ch, l&agrave;m &#273;i&#7875;m nh&#7845;n cho kh&ocirc;ng gian n&#7897;i th&#7845;t.','\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;324&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/dung-tham.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&#7842;nh minh h&#7885;a&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&#272;a s&#7889; th&#7843;m tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng hi&#7879;n &#273;&#432;&#7907;c nh&#7853;p t&#7915; Th&amp;aacute;i Lan, Indonesia, Malaysia, Trung Qu&#7889;c, Singapore&amp;hellip; Th&#7843;m &#273;&#432;&#7907;c k&#7871;t c&#7845;u theo d&#7841;ng cu&#7897;n ho&#7863;c t&#7845;m. Th&#7843;m cu&#7897;n c&amp;oacute; kh&#7893; 3,66m ho&#7863;c 4m, ph&amp;ugrave; h&#7907;p &#273;&#7875; l&amp;oacute;t to&amp;agrave;n b&#7897; di&#7879;n t&amp;iacute;ch s&amp;agrave;n. D&#7841;ng t&#7845;m r&#7901;i v&#7899;i k&amp;iacute;ch c&#7905; 1,6m x 2,3m ho&#7863;c 2m x 3m th&amp;iacute;ch h&#7907;p &#273;&#7875; trang tr&amp;iacute; ph&amp;ograve;ng kh&amp;aacute;ch ho&#7863;c ph&amp;ograve;ng ng&#7911;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&#7845;t li&#7879;u th&#7843;m th&#432;&#7901;ng l&amp;agrave; l&amp;ocirc;ng c&#7915;u, len, s&#7907;i nh&amp;acirc;n t&#7841;o, ni l&amp;ocirc;ng, olefin&amp;hellip; &#272;&#7855;t ti&#7873;n nh&#7845;t l&amp;agrave; nh&#7919;ng t&#7845;m th&#7843;m b&#7857;ng l&amp;ocirc;ng c&#7915;u, gi&amp;aacute; kho&#7843;ng 10 tri&#7879;u &#273;/t&#7845;m tr&#7903; l&amp;ecirc;n, v&#7899;i k&amp;iacute;ch c&#7905; 1,6m x 2,3m ho&#7863;c 2m x 3m, ho&#7863;c 2 tri&#7879;u &#273;/m2. Th&#7843;m len &#273;&#432;&#7907;c &#432;a chu&#7897;ng h&#417;n c&#7843; v&amp;igrave; &#273;a d&#7841;ng m&amp;agrave;u s&#7855;c, m&#7851;u m&amp;atilde; v&amp;agrave; c&amp;oacute; &#273;&#7897; b&#7873;n cao. Th&#7843;m len d&#7841;ng t&#7845;m dao &#273;&#7897;ng t&#7915; 4 - 20 tri&#7879;u &#273;&#7891;ng, d&#7841;ng cu&#7897;n t&#7915; 0,5 - 1,2 tri&#7879;u &#273;/m2. Th&#7843;m b&#7857;ng l&#7909;c b&amp;igrave;nh hay c&amp;oacute;i c&#361;ng &#273;&#432;&#7907;c ch&#7885;n l&#7921;a cho c&amp;aacute;c kh&amp;ocirc;ng gian nh&amp;agrave; c&amp;oacute; n&#7897;i th&#7845;t b&#7857;ng g&#7895;. Gi&amp;aacute; lo&#7841;i th&#7843;m n&amp;agrave;y kh&amp;aacute; m&#7873;m, kho&#7843;ng 20 ngh&amp;igrave;n &#273;/m2&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7843;m ph&amp;ugrave; h&#7907;p cho nh&#7919;ng kh&amp;ocirc;ng gian kh&amp;eacute;p k&amp;iacute;n, &amp;iacute;t b&#7909;i. Kh&amp;ocirc;ng n&amp;ecirc;n s&#7917; d&#7909;ng th&#7843;m trong c&amp;aacute;c kh&amp;ocirc;ng gian &#7849;m th&#7845;p nh&#432; nh&amp;agrave; b&#7871;p, g&amp;aacute;c x&amp;eacute;p ho&#7863;c trong ph&amp;ograve;ng &#259;n v&amp;igrave; th&#7843;m d&#7877; &#7849;m m&#7889;c v&amp;agrave; kh&amp;oacute; v&#7879; sinh. M&#7863;c d&amp;ugrave; nhi&#7873;u lo&#7841;i th&#7843;m c&amp;oacute; t&amp;iacute;nh n&#259;ng ch&#7889;ng &#7849;m, ch&#7889;ng ch&amp;aacute;y, nh&#432;ng c&#361;ng c&#7847;n tr&amp;aacute;nh n&#432;&#7899;c, tr&amp;aacute;nh l&#7917;a &#7903; khu v&#7921;c tr&#7843;i th&#7843;m.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&amp;ecirc;n ch&#7885;n th&#7843;m ch&#7845;t li&#7879;u ni l&amp;ocirc;ng cho nh&#7919;ng n&#417;i c&amp;oacute; s&#7921; &#273;i l&#7841;i v&amp;agrave; c&#7885; x&amp;aacute;t nhi&#7873;u. Khi mua th&#7843;m ch&#7845;t li&#7879;u m&amp;acirc;y tre l&amp;aacute;, n&amp;ecirc;n ch&#7885;n lo&#7841;i &#273;&amp;atilde; t&#7849;m s&#7845;y v&amp;agrave; x&#7917; l&amp;yacute; m&#7889;i m&#7885;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7843;m nhanh b&#7883; h&#432;, xu&#7889;ng c&#7845;p l&amp;agrave; do s&#7921; c&#7885; x&amp;aacute;t ho&#7863;c b&#7883; &#7849;m m&#7889;c. Do &#273;&amp;oacute;, c&#7847;n h&#7841;n ch&#7871; t&#7889;i &#273;a vi&#7879;c di chuy&#7875;n &#273;&#7891; v&#7853;t tr&amp;ecirc;n th&#7843;m, &#273;&#7863;c bi&#7879;t l&amp;agrave; nh&#7919;ng &#273;&#7891; v&#7853;t c&amp;oacute; g&amp;oacute;c c&#7841;nh, &#273;&#7891;ng th&#7901;i tr&amp;aacute;nh &#273;&#7893; n&#432;&#7899;c l&amp;ecirc;n th&#7843;m. N&#7871;u l&#7905; b&#7883; &#273;&#7893; n&#432;&#7899;c hay nh&#7919;ng lo&#7841;i ch&#7845;t l&#7887;ng c&amp;oacute; m&amp;agrave;u l&amp;ecirc;n th&#7843;m, c&#7847;n ph&#7843;i kh&#7855;c ph&#7909;c ngay b&#7857;ng c&amp;aacute;ch s&#7845;y, gi&#7863;t kh&amp;ocirc; b&#7857;ng ch&#7845;t t&#7849;y r&#7917;a chuy&amp;ecirc;n d&#7909;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7843;m l&amp;agrave; v&#7853;t r&#7845;t b&amp;aacute;m b&#7909;i n&amp;ecirc;n c&#7847;n th&#432;&#7901;ng xuy&amp;ecirc;n h&amp;uacute;t b&#7909;i, &amp;iacute;t nh&#7845;t l&amp;agrave; m&#7895;i tu&#7847;n. Sau 3 - 6 th&amp;aacute;ng, n&amp;ecirc;n gi&#7863;t th&#7843;m m&#7897;t l&#7847;n, kh&amp;ocirc;ng gi&#7863;t b&#7857;ng b&#7897;t gi&#7863;t th&amp;ocirc;ng th&#432;&#7901;ng. &#272;&#7889;i v&#7899;i th&#7843;m l&amp;ocirc;ng c&#7915;u, ch&#7881; n&amp;ecirc;n gi&#7863;t tay b&#7857;ng d&#7847;u g&#7897;i &#273;&#7847;u ho&#7863;c s&#7919;a t&#7855;m.&lt;/p&gt;\r\n','24/11/2011','24/11/2011','/home/uploads/image/news/11-2011/dung-tham.jpg',23,2,1836,'huyennt',1,'dung-tham-dung-cach','th&#7843;m, v&#7853;t li&#7879;u, t&#7845;m th&#7843;m, c&aacute;ch d&ugrave;ng',NULL,NULL,NULL,'th&#7843;m, v&#7853;t li&#7879;u, t&#7845;m th&#7843;m, c&aacute;ch d&ugrave;ng',0,0,0,'','','',3,'http://www.xaydungvietnam.vn',1,0,0,0),(104,'Tri&#7875;n l&atilde;m v&agrave; h&#7897;i ngh&#7883; qu&#7889;c t&#7871; v&#7873; thi&#7871;t b&#7883; v&agrave; c&ocirc;ng ngh&#7879; chi&#7871;u s&aacute;ng t&#7841;i Vi&#7879;t Nam 2011','C&ugrave;ng v&#7899;i c&aacute;c ch&iacute;nh s&aacute;ch li&ecirc;n quan &#273;&#7871;n ph&aacute;t tri&#7875;n ng&agrave;nh chi&#7871;u s&aacute;ng t&#7841;i Vi&#7879;t Nam, m&#7897;t s&#7889; d&#7921; &aacute;n c&#7911;a Ch&iacute;nh ph&#7911; trong l&#297;nh v&#7921;c n&agrave;y c&#361;ng s&#7869; &#273;&#432;&#7907;c gi&#7899;i thi&#7879;u t&#7841;i Tri&#7875;n l&atilde;m v&agrave; H&#7897;i ngh&#7883; qu&#7889;c t&#7871; v&#7873; Thi&#7871;t b&#7883; v&agrave; C&ocirc;ng ngh&#7879; chi&#7871;u s&aacute;ng t&#7841;i Vi&#7879;t Nam.','\r\n&lt;p&gt;Tri&#7875;n l&amp;atilde;m v&amp;agrave; H&#7897;i ngh&#7883; qu&#7889;c t&#7871; v&#7873; Thi&#7871;t b&#7883; v&amp;agrave; C&amp;ocirc;ng ngh&#7879; chi&#7871;u s&amp;aacute;ng t&#7841;i Vi&#7879;t Nam - 2011 LIGHTING WORLD IN VIETNAM, m&#7897;t s&#7921; ki&#7879;n l&#7899;n c&#7911;a ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p chi&#7871;u s&amp;aacute;ng, ch&amp;iacute;nh th&#7913;c khai m&#7841;c s&amp;aacute;ng nay ng&amp;agrave;y 24/11 t&#7841;i Trung t&amp;acirc;m H&#7897;i ch&#7907; v&amp;agrave; Tri&#7875;n l&amp;atilde;m T&amp;acirc;n B&amp;igrave;nh (TBECC - 446, Ho&amp;agrave;ng V&#259;n Th&#7909;, qu&#7853;n T&amp;acirc;n B&amp;igrave;nh, TP.HCM) v&amp;agrave; k&amp;eacute;o d&amp;agrave;i &#273;&#7871;n ng&amp;agrave;y 26/11.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/ong-tran-trong-hue.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img width=&quot;0&quot; height=&quot;0&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/ong-tran-trong-hue.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;&amp;Ocirc;ng Tr&#7847;n Tr&#7885;ng Hu&#7879;- Gi&amp;aacute;m &#273;&#7889;c C&amp;ocirc;ng ty chi&#7871;u s&amp;aacute;ng c&amp;ocirc;ng c&#7897;ng TP ph&amp;aacute;t bi&#7875;u t&#7841;i L&#7877; khai m&#7841;c.&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;T&#7841;i h&#417;n 200 gian h&amp;agrave;ng c&#7911;a Tri&#7875;n l&amp;atilde;m, kh&amp;aacute;ch tham quan &#273;&#432;&#7907;c ti&#7871;p c&#7853;n 5 nh&amp;oacute;m s&#7843;n ph&#7849;m tr&#432;ng b&amp;agrave;y ch&amp;iacute;nh, g&#7891;m thi&#7871;t b&#7883; chi&#7871;u s&amp;aacute;ng; chi&#7871;u s&amp;aacute;ng c&#7889; &#273;&#7883;nh; chi&#7871;u s&amp;aacute;ng LED/OLED, linh ph&#7909; ki&#7879;n chi&#7871;u s&amp;aacute;ng; thi&#7871;t b&#7883; (ch&#7871; t&#7841;o, ki&#7875;m tra, &#273;o l&#432;&#7901;ng, th&#7917; nghi&#7879;m, &#273;&amp;aacute;nh gi&amp;aacute;); c&amp;aacute;c s&#7843;n ph&#7849;m kh&amp;aacute;c nh&#432; k&#7929; thu&#7853;t chi&#7871;u s&amp;aacute;ng/thi&#7871;t k&#7871;/d&#7883;ch v&#7909;&amp;hellip; C&amp;aacute;c s&#7843;n ph&#7849;m n&amp;agrave;y do c&amp;aacute;c doanh nghi&#7879;p Vi&#7879;t Nam v&amp;agrave; c&amp;aacute;c doanh nghi&#7879;p &#273;&#7871;n t&#7915; c&amp;aacute;c qu&#7889;c gia v&amp;agrave; v&amp;ugrave;ng l&amp;atilde;nh th&#7893; c&amp;oacute; c&amp;ocirc;ng ngh&#7879; v&amp;agrave; thi&#7871;t b&#7883; chi&#7871;u s&amp;aacute;ng ti&amp;ecirc;n ti&#7871;n nh&#432; Italy, T&amp;acirc;y Ban Nha, B&#7881;, &amp;Aacute;o, H&amp;agrave;n Qu&#7889;c, Nh&#7853;t B&#7843;n, Trung Qu&#7889;c, &#7844;n &#272;&#7897;&amp;hellip; gi&#7899;i thi&#7879;u, tr&#432;ng b&amp;agrave;y.&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&#272;&#7863;c bi&#7879;t, t&#7841;i Tri&#7875;n l&amp;atilde;m, c&amp;ugrave;ng v&#7899;i c&amp;aacute;c ch&amp;iacute;nh s&amp;aacute;ch li&amp;ecirc;n quan &#273;&#7871;n ph&amp;aacute;t tri&#7875;n ng&amp;agrave;nh chi&#7871;u s&amp;aacute;ng t&#7841;i Vi&#7879;t Nam, m&#7897;t s&#7889; d&#7921; &amp;aacute;n c&#7911;a Ch&amp;iacute;nh ph&#7911; Vi&#7879;t Nam trong l&#297;nh v&#7921;c n&amp;agrave;y c&#361;ng s&#7869; &#273;&#432;&#7907;c gi&#7899;i thi&#7879;u, trong &#273;&amp;oacute; c&amp;oacute; D&#7921; &amp;aacute;n Chi&#7871;u s&amp;aacute;ng c&amp;ocirc;ng c&#7897;ng hi&#7879;u su&#7845;t cao t&#7841;i Vi&#7879;t Nam (VEEPL), D&#7921; &amp;aacute;n N&amp;acirc;ng cao hi&#7879;u qu&#7843; s&#7917; d&#7909;ng n&#259;ng l&#432;&#7907;ng trong c&amp;aacute;c doanh nghi&#7879;p nh&#7887; v&amp;agrave; v&#7915;a t&#7841;i Vi&#7879;t Nam (PECSME 2006-2010)&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p&gt;Lu&#7853;t S&#7917; d&#7909;ng n&#259;ng l&#432;&#7907;ng ti&#7871;t ki&#7879;m v&amp;agrave; hi&#7879;u qu&#7843; &#273;&amp;atilde; ch&amp;iacute;nh th&#7913;c c&amp;oacute; hi&#7879;u l&#7921;c c&amp;ugrave;ng v&#7899;i Ngh&#7883; &#273;&#7883;nh s&#7889; 21/2011/N&#272;-CP quy ho&#7841;ch chi ti&#7871;t v&amp;agrave; bi&#7879;n ph&amp;aacute;p thi h&amp;agrave;nh Lu&#7853;t S&#7917; d&#7909;ng n&#259;ng l&#432;&#7907;ng ti&#7871;t ki&#7879;m v&amp;agrave; hi&#7879;u qu&#7843;. Theo &#273;&amp;oacute;, vi&#7879;c &amp;aacute;p d&#7909;ng t&#7889;t c&amp;aacute;c thi&#7871;t b&#7883; c&amp;ocirc;ng ngh&#7879; gi&amp;uacute;p n&amp;acirc;ng cao hi&#7879;u qu&#7843; chi&#7871;u s&amp;aacute;ng v&amp;agrave; ti&#7871;t ki&#7879;m &#273;i&#7879;n n&#259;ng l&amp;agrave; v&#7845;n &#273;&#7873; b&#7855;t bu&#7897;c v&#7899;i c&amp;aacute;c doanh nghi&#7879;p, m&#7903; ra th&#7883; tr&#432;&#7901;ng r&#7845;t l&#7899;n cho ng&amp;agrave;nh thi&#7871;t b&#7883; chi&#7871;u s&amp;aacute;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ngo&amp;agrave;i ra, Vi&#7879;t Nam &#273;ang c&amp;oacute; nhi&#7873;u d&#7921; &amp;aacute;n &#273;&#432;&#7907;c &#273;&#7847;u t&#432;, tri&#7875;n khai nh&#432; c&amp;aacute;c khu &#273;&amp;ocirc; th&#7883; cao c&#7845;p, c&#417; s&#7903; h&#7841; t&#7847;ng quan tr&#7885;ng c&#7845;p qu&#7889;c gia, c&amp;aacute;c trung t&amp;acirc;m th&#432;&#417;ng m&#7841;i, khu c&amp;ocirc;ng nghi&#7879;p, kh&amp;aacute;ch s&#7841;n, cao &#7889;c v&#259;n ph&amp;ograve;ng, h&#7879; th&#7889;ng ng&amp;acirc;n h&amp;agrave;ng, b&#7843;o hi&#7875;m... S&#7921; ph&amp;aacute;t tri&#7875;n m&#7841;nh m&#7869; n&amp;agrave;y &#273;&amp;atilde; t&#7841;o nhu c&#7847;u l&#7899;n v&#7873; c&amp;ocirc;ng ngh&#7879; v&amp;agrave; c&amp;aacute;c s&#7843;n ph&#7849;m chi&#7871;u s&amp;aacute;ng, Do v&#7853;y, Tri&#7875;n l&amp;atilde;m c&amp;ograve;n t&#7841;o c&#417; h&#7897;i giao l&#432;u, t&amp;igrave;m hi&#7875;u &#273;&#7889;i t&amp;aacute;c v&amp;agrave; h&#7907;p t&amp;aacute;c &#273;&#7847;u t&#432;, kinh doanh gi&#7919;a c&amp;aacute;c doanh nghi&#7879;p trong ng&amp;agrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tri&#7875;n l&amp;atilde;m v&amp;agrave; H&#7897;i ngh&#7883; qu&#7889;c t&#7871; v&#7873; Thi&#7871;t b&#7883; v&amp;agrave; C&amp;ocirc;ng ngh&#7879; chi&#7871;u s&amp;aacute;ng t&#7841;i Vi&#7879;t Nam - 2011 LIGHTING WORLD IN VIETNAM do C&amp;ocirc;ng ty c&#7893; ph&#7847;n H&#7897;i ch&#7907; tri&#7875;n l&amp;atilde;m v&amp;agrave; qu&#7843;ng c&amp;aacute;o Vi&#7879;t Nam (VIETFAIR) v&amp;agrave; C&amp;ocirc;ng ty Stavis H&amp;agrave;n Qu&#7889;c t&#7893; ch&#7913;c, v&#7899;i s&#7921; &#7911;ng h&#7897; v&amp;agrave; b&#7843;o tr&#7907; c&#7911;a B&#7897; C&amp;ocirc;ng th&#432;&#417;ng, B&#7897; Khoa h&#7885;c v&amp;agrave; C&amp;ocirc;ng ngh&#7879;, Vi&#7879;n Khoa h&#7885;c v&amp;agrave; C&amp;ocirc;ng ngh&#7879;, UBND TP.HCM, D&#7921; &amp;aacute;n Chi&#7871;u s&amp;aacute;ng c&amp;ocirc;ng c&#7897;ng hi&#7879;u su&#7845;t cao t&#7841;i Vi&#7879;t Nam (VEEPL), H&#7897;i Chi&#7871;u s&amp;aacute;ng Vi&#7879;t Nam, Hi&#7879;p h&#7897;i LED H&amp;agrave;n Qu&#7889;c. OSRAM, Philips, FAWOO-KIDI, SBC l&amp;agrave; c&amp;aacute;c nh&amp;agrave; t&amp;agrave;i tr&#7907; ch&amp;iacute;nh c&#7911;a s&#7921; ki&#7879;n n&amp;agrave;y.&lt;/p&gt;','25/11/2011','25/11/2011','',23,2,1933,'huyennt',1,'trien-lam-va-hoi-nghi-quoc-te-ve-thiet-bi-va-cong-nghe-chieu-sang-tai-viet-nam-2011','vlxd, tri&#7875;n l&atilde;m, d&#7921; &aacute;n, H&#7897;i ngh&#7883;, qu&#7889;c t&#7871;, Thi&#7871;t b&#7883;, C&ocirc;ng ngh&#7879; ,chi&#7871;u s&aacute;ng',NULL,NULL,NULL,'vlxd, tri&#7875;n l&atilde;m, d&#7921; &aacute;n, H&#7897;i ngh&#7883;, qu&#7889;c t&#7871;, Thi&#7871;t b&#7883;, C&ocirc;ng ngh&#7879; ,chi&#7871;u s&aacute;ng',0,0,0,'','','',3,'http://dothi.net',1,0,0,0),(105,'Th&eacute;p x&acirc;y d&#7921;ng l&#7841;i b&#7883; l&agrave;m gi&aacute;','S&#7913;c ti&ecirc;u th&#7909; r&#7845;t th&#7845;p, gi&aacute; ph&ocirc;i th&eacute;p, th&eacute;p ph&#7871; li&#7879;u gi&#7843;m m&#7841;nh nh&#432;ng c&aacute;c &#273;&#417;n v&#7883; s&#7843;n xu&#7845;t th&eacute;p v&#7851;n &ldquo;neo&rdquo; gi&aacute; &#7903; m&#7913;c cao','\r\n&lt;p&gt;Th&#7901;i gian qua c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y th&amp;eacute;p &#273;&#7849;y gi&amp;aacute; b&amp;aacute;n th&amp;eacute;p x&amp;acirc;y d&#7921;ng l&amp;ecirc;n kh&amp;aacute; cao, trong khi s&#7913;c ti&amp;ecirc;u th&#7909; c&#7911;a th&#7883; tr&#432;&#7901;ng y&#7871;u. &#272;&#7875; ti&amp;ecirc;u th&#7909; &#273;&#432;&#7907;c h&amp;agrave;ng, c&amp;aacute;c h&amp;atilde;ng th&amp;eacute;p s&#7861;n s&amp;agrave;ng tr&amp;iacute;ch m&#7913;c hoa h&#7891;ng cho &#273;&#7841;i l&amp;yacute;, m&amp;ocirc;i gi&#7899;i l&amp;ecirc;n t&#7899;i 1 tri&#7879;u - 1,5 tri&#7879;u &#273;&#7891;ng/t&#7845;n, c&amp;ograve;n gi&amp;aacute; b&amp;aacute;n l&#7867; tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng cao ng&#7845;t ng&#432;&#7903;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;T&#259;ng gi&amp;aacute; k&amp;iacute;ch th&#7883; tr&#432;&#7901;ng&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo gi&#7899;i kinh doanh th&amp;eacute;p x&amp;acirc;y d&#7921;ng, s&#7913;c ti&amp;ecirc;u th&#7909; th&amp;eacute;p &#273;&amp;atilde; gi&#7843;m m&#7841;nh t&#7915; gi&#7919;a n&#259;m 2011 do suy tho&amp;aacute;i kinh t&#7871; v&amp;agrave; Nh&amp;agrave; n&#432;&#7899;c t&#259;ng c&#432;&#7901;ng si&#7871;t ch&#7863;t &#273;&#7847;u t&#432; c&amp;ocirc;ng trong l&#297;nh v&#7921;c x&amp;acirc;y d&#7921;ng. &amp;Ocirc;ng &#272;&#7895; Duy Th&amp;aacute;i, T&#7893;ng Gi&amp;aacute;m &#273;&#7889;c C&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t, cho bi&#7871;t: S&#7913;c ti&amp;ecirc;u th&#7909; &#273;ang gi&#7843;m m&#7841;nh ngay trong m&amp;ugrave;a x&amp;acirc;y d&#7921;ng l&amp;agrave; do &#273;&#7847;u t&#432; c&amp;ocirc;ng &#273;&amp;igrave;nh tr&#7879; v&amp;agrave; t&amp;igrave;nh tr&#7841;ng c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh nh&amp;agrave; &#7903; ri&amp;ecirc;ng l&#7867;, d&#7921; &amp;aacute;n c&#259;n h&#7897; b&#7883; &#273;&amp;oacute;ng b&#259;ng k&amp;eacute;o d&amp;agrave;i. C&#361;ng theo &amp;ocirc;ng Th&amp;aacute;i, c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y th&amp;eacute;p c&#7911;a Th&amp;eacute;p Vi&#7879;t &#273;&#7873;u &#273;&amp;atilde; ph&#7843;i gi&#7843;m c&amp;ocirc;ng su&#7845;t &#273;&#7871;n 50%. Nhi&#7873;u nh&amp;agrave; m&amp;aacute;y th&amp;eacute;p c&#7911;a c&amp;aacute;c h&amp;atilde;ng kh&amp;aacute;c c&#361;ng gi&#7843;m ph&amp;acirc;n n&#7917;a c&amp;ocirc;ng su&#7845;t ho&#7863;c ch&#7881; s&#7843;n xu&#7845;t c&#7847;m ch&#7915;ng, th&#7853;m ch&amp;iacute; c&amp;oacute; doanh nghi&#7879;p ph&#7843;i rao b&amp;aacute;n nh&amp;agrave; x&#432;&#7903;ng &#273;&#7875; tr&#7843; n&#7907; ng&amp;acirc;n h&amp;agrave;ng v&amp;igrave; l&amp;atilde;i su&#7845;t qu&amp;aacute; cao.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/Thep-xay-dung-lai-bi-lam-gia.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;&lt;em&gt;Gi&amp;aacute; ph&amp;ocirc;i th&amp;eacute;p gi&#7843;m m&#7841;nh nh&#432;ng gi&amp;aacute; th&amp;eacute;p v&#7851;n r&#7845;t cao.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\n&#272;&#7875; k&amp;iacute;ch c&#7847;u, c&amp;aacute;c h&amp;atilde;ng s&#7843;n xu&#7845;t &#273;&amp;atilde; s&#7917; d&#7909;ng &amp;ldquo;chi&amp;ecirc;u&amp;rdquo; k&amp;iacute;ch gi&amp;aacute; b&#7857;ng c&amp;aacute;ch &#273;&#7891;ng lo&#7841;t &#273;&#7849;y gi&amp;aacute; t&#259;ng 2 &#273;&#7907;t trong th&amp;aacute;ng 8-2011 v&#7899;i m&#7913;c t&#259;ng t&#7893;ng c&#7897;ng kho&#7843;ng 1 tri&#7879;u &#273;&#7891;ng/t&#7845;n. Nhi&#7873;u h&amp;atilde;ng c&amp;ograve;n phao tin l&amp;agrave; s&#7869; ti&#7871;p t&#7909;c t&#259;ng gi&amp;aacute; &#273;&#7875; &#273;&amp;aacute;nh v&amp;agrave;o t&amp;acirc;m l&amp;yacute; gi&#7899;i kinh doanh s&#7855;t th&amp;eacute;p c&#361;ng nh&#432; nh&amp;agrave; &#273;&#7847;u t&#432; b&#7845;t &#273;&#7897;ng s&#7843;n ph&#7843;i mua th&amp;eacute;p d&#7921; tr&#7919; v&amp;igrave; lo ng&#7841;i gi&amp;aacute; t&#259;ng. &amp;ldquo;Chi&amp;ecirc;u&amp;rdquo; n&amp;agrave;y &#273;&amp;atilde; mang l&#7841;i hi&#7879;u qu&#7843;  kh&amp;aacute; r&amp;otilde; khi s&#7913;c ti&amp;ecirc;u th&#7909; th&amp;eacute;p trong th&amp;aacute;ng 8 t&#259;ng kh&amp;aacute; cao, l&amp;ecirc;n 483.000 t&#7845;n. Gi&amp;aacute; th&amp;eacute;p ni&amp;ecirc;m y&#7871;t t&#7841;i c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y th&#7901;i &#273;i&#7875;m n&amp;agrave;y l&amp;ecirc;n &#273;&#7871;n 18,5 tri&#7879;u &#273;&#7891;ng/t&#7845;n...&lt;/p&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, sang th&amp;aacute;ng 9, s&#7913;c ti&amp;ecirc;u th&#7909; th&amp;eacute;p x&amp;acirc;y d&#7921;ng b&#7845;t ng&#7901; gi&#7843;m m&#7841;nh tr&#7903; l&#7841;i, ch&#7881; c&amp;ograve;n 381.000 t&#7845;n v&amp;agrave; th&amp;aacute;ng 10 gi&#7843;m c&amp;ograve;n 320.000 t&#7845;n. Nguy&amp;ecirc;n nh&amp;acirc;n l&amp;agrave; do gi&#7899;i kinh doanh tr&#7919; h&amp;agrave;ng qu&amp;aacute; nhi&#7873;u trong th&amp;aacute;ng 8, nh&#432;ng sau &#273;&amp;oacute; kh&amp;ocirc;ng ti&amp;ecirc;u th&#7909; &#273;&#432;&#7907;c h&amp;agrave;ng nh&#432; d&#7921; ki&#7871;n n&amp;ecirc;n h&#7885; kh&amp;ocirc;ng c&amp;ograve;n m&#7863;n m&amp;agrave; l&#7845;y th&amp;ecirc;m h&amp;agrave;ng. Nhi&#7873;u ng&#432;&#7901;i am hi&#7875;u th&#7883; tr&#432;&#7901;ng th&amp;eacute;p x&amp;acirc;y d&#7921;ng ti&#7871;t l&#7897;: Khi kh&amp;ocirc;ng b&amp;aacute;n &#273;&#432;&#7907;c h&amp;agrave;ng, l&#7869; ra c&amp;aacute;c h&amp;atilde;ng th&amp;eacute;p s&#7869; ph&#7843;i gi&#7843;m gi&amp;aacute; tr&#7903; l&#7841;i &#273;&#7875; k&amp;iacute;ch th&amp;iacute;ch th&#7883; tr&#432;&#7901;ng x&amp;acirc;y d&#7921;ng. Th&#7871; nh&#432;ng h&#7885; &#273;&amp;atilde; kh&amp;ocirc;ng ch&#7885;n c&amp;aacute;ch n&amp;agrave;y m&amp;agrave; l&#7841;i h&#7907;p s&#7913;c &#273;&#7875; &amp;ldquo;neo&amp;rdquo; gi&amp;aacute; b&#7857;ng c&amp;aacute;ch m&amp;oacute;c n&#7889;i v&#7899;i c&amp;aacute;c m&#7889;i l&amp;agrave;m &#259;n quen bi&#7871;t chi chi&#7871;t kh&#7845;u v&#7899;i m&#7913;c cao, t&#7915; 1 tri&#7879;u - 1,5 tri&#7879;u &#273;&#7891;ng/t&#7845;n &#273;&#7875; gi&#7919; gi&amp;aacute; th&amp;eacute;p tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng &#7903; m&#7913;c 18,5 tri&#7879;u - 18,6 tri&#7879;u &#273;&#7891;ng/t&#7845;n. Do gi&amp;aacute; b&amp;aacute;n l&#7867; qu&amp;aacute; cao n&amp;ecirc;n th&#7883; tr&#432;&#7901;ng th&amp;eacute;p c&amp;agrave;ng th&amp;ecirc;m &#7871; &#7849;m. Theo th&#7889;ng k&amp;ecirc; s&#417; b&#7897;, trong th&amp;aacute;ng 10, m&#7913;c ti&amp;ecirc;u th&#7909; th&amp;eacute;p x&amp;acirc;y d&#7921;ng c&#7911;a T&#7893;ng C&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t Nam (v&#259;n ph&amp;ograve;ng Tp.HCM), C&amp;ocirc;ng ty Th&amp;eacute;p Vina Kyoei, C&amp;ocirc;ng ty Th&amp;eacute;p Pomina&amp;hellip; gi&#7843;m &#273;&#7871;n 50%.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Ch&amp;ecirc;nh l&#7879;ch gi&amp;aacute; qu&amp;aacute; l&#7899;n&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;Ocirc;ng Nguy&#7877;n Ti&#7871;n Nghi, Ph&amp;oacute; Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i Th&amp;eacute;p Vi&#7879;t Nam, cho bi&#7871;t t&#7915; th&amp;aacute;ng 10 &#273;&#7871;n nay, gi&amp;aacute; qu&#7863;ng s&#7855;t, ph&amp;ocirc;i th&amp;eacute;p v&amp;agrave; c&#7843; th&amp;eacute;p ph&#7871; li&#7879;u tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng th&#7871; gi&#7899;i li&amp;ecirc;n t&#7909;c gi&#7843;m (t&#7893;ng m&#7913;c gi&#7843;m c&#7911;a gi&amp;aacute; ph&amp;ocirc;i th&amp;eacute;p kho&#7843;ng 100 USD/t&#7845;n). D&#7921; b&amp;aacute;o gi&amp;aacute; s&#7869; c&amp;ograve;n ti&#7871;p t&#7909;c gi&#7843;m m&#7841;nh t&#7915; nay &#273;&#7871;n cu&#7889;i n&#259;m v&amp;agrave; k&amp;eacute;o d&amp;agrave;i &#273;&#7871;n nh&#7919;ng th&amp;aacute;ng &#273;&#7847;u n&#259;m 2012.&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&#7871; nh&#432;ng gi&amp;aacute; th&amp;eacute;p th&amp;agrave;nh ph&#7849;m trong n&#432;&#7899;c v&#7851;n &#7903; m&#7913;c cao. C&amp;aacute;c h&amp;atilde;ng th&amp;eacute;p gi&#7843;i th&amp;iacute;ch ch&#432;a th&#7875; gi&#7843;m l&amp;agrave; do hi&#7879;n nay c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y v&#7851;n c&amp;ograve;n s&#7917; d&#7909;ng ngu&#7891;n nguy&amp;ecirc;n li&#7879;u nh&#7853;p kh&#7849;u tr&#432;&#7899;c &#273;&amp;oacute; v&#7899;i gi&amp;aacute; cao; n&#7871;u gi&#7843;m gi&amp;aacute; b&amp;aacute;n s&#7869; b&#7883; l&#7895; n&#7863;ng. C&amp;ograve;n theo gi&#7899;i kinh doanh, s&#7903; d&#297; c&amp;aacute;c h&amp;atilde;ng th&amp;eacute;p ch&#432;a ch&#7883;u gi&#7843;m gi&amp;aacute; l&amp;agrave; do s&#7913;c ti&amp;ecirc;u th&#7909; &#273;ang qu&amp;aacute; th&#7845;p, n&#7871;u gi&#7843;m l&amp;uacute;c n&amp;agrave;y r&#7845;t d&#7877; t&amp;aacute;c &#273;&#7897;ng t&amp;acirc;m l&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng  mu&#7889;n ch&#7901; gi&amp;aacute; xu&#7889;ng n&#7919;a m&#7899;i mua, l&amp;uacute;c &#273;&amp;oacute; s&#7869; g&amp;acirc;y b&#7845;t l&#7907;i cho nh&amp;agrave; s&#7843;n xu&#7845;t.&lt;br /&gt;\r\nTheo t&amp;iacute;nh to&amp;aacute;n c&#7911;a gi&#7899;i chuy&amp;ecirc;n m&amp;ocirc;n: Th&#7901;i &#273;i&#7875;m th&amp;aacute;ng 10, khi gi&amp;aacute; ph&amp;ocirc;i th&amp;eacute;p c&amp;ograve;n &#7903; m&#7913;c 660 USD - 670 USD/t&#7845;n th&amp;igrave; gi&amp;aacute; th&amp;agrave;nh s&#7843;n xu&#7845;t th&amp;eacute;p x&amp;acirc;y d&#7921;ng ch&#7881; kho&#7843;ng 16 tri&#7879;u &#273;&#7891;ng/t&#7845;n. &#272;&#7871;n nay, gi&amp;aacute; ph&amp;ocirc;i ch&#7881; c&amp;ograve;n 600 USD/t&#7845;n, t&amp;iacute;nh ra gi&amp;aacute; th&amp;agrave;nh s&#7843;n xu&#7845;t ch&#432;a t&#7899;i 14 tri&#7879;u &#273;&#7891;ng/t&#7845;n. N&#7871;u c&#7897;ng c&#7843; thu&#7871; GTGT th&amp;igrave; gi&amp;aacute; b&amp;aacute;n c&#361;ng ch&#432;a t&#7899;i 15,4 tri&#7879;u &#273;&#7891;ng/t&#7845;n. Trong khi &#273;&amp;oacute; gi&amp;aacute; b&amp;aacute;n l&#7867; tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng hi&#7879;n nay l&amp;ecirc;n &#273;&#7871;n 18,5 tri&#7879;u &#273;&#7891;ng/t&#7845;n, m&#7897;t m&#7913;c c&amp;aacute;ch bi&#7879;t qu&amp;aacute; l&#7899;n &#273;&#7889;i v&#7899;i m&#7863;t h&amp;agrave;ng th&amp;eacute;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;T&#259;ng gi&amp;aacute; r&#7891;i l&#7841;i khuy&#7871;n m&amp;atilde;i&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Th&amp;ocirc;ng tin t&#7915; c&amp;aacute;c c&#7917;a h&amp;agrave;ng v&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng t&#7841;i Tp.HCM, s&#7913;c ti&amp;ecirc;u th&#7909; xi m&#259;ng trong nhi&#7873;u th&amp;aacute;ng qua gi&#7843;m r&#7845;t m&#7841;nh, so v&#7899;i c&amp;ugrave;ng k&#7923; n&#259;m ngo&amp;aacute;i &#273;&amp;atilde; gi&#7843;m h&#417;n 50%. Tuy s&#7913;c ti&amp;ecirc;u th&#7909; gi&#7843;m nh&#432;ng g&#7847;n &#273;&amp;acirc;y, m&#7897;t s&#7889; nh&amp;agrave; m&amp;aacute;y xi m&#259;ng l&#7841;i &#273;&#7849;y gi&amp;aacute; b&amp;aacute;n t&#259;ng th&amp;ecirc;m kho&#7843;ng 7.500 &#273;&#7891;ng/bao 50 kg. Hi&#7879;n xi m&#259;ng H&amp;agrave; Ti&amp;ecirc;n 1 c&amp;oacute; gi&amp;aacute; b&amp;aacute;n l&#7867; 93.000 &#273;&#7891;ng/bao, Holcim 92.000 &#273;&#7891;ng/bao&amp;hellip; Do ti&amp;ecirc;u th&#7909; y&#7871;u n&amp;ecirc;n nhi&#7873;u h&amp;atilde;ng xi m&#259;ng khuy&#7871;n m&amp;atilde;i mua 100 bao t&#7863;ng 2 - 5 bao, t&amp;ugrave;y h&amp;atilde;ng.&lt;/p&gt;\r\n','28/11/2011','28/11/2011','/home/uploads/image/news/11-2011/Thep-xay-dung-lai-bi-lam-gia.jpg',23,2,1781,'huyennt',1,'thep-xay-dung-lai-bi-lam-gia','x&acirc;y d&#7921;ng, l&agrave;m gi&aacute;, s&#7913;c ti&ecirc;u th&#7909;, th&eacute;p, th&eacute;p ph&#7871; li&#7879;u, gi&#7843;m, t&#259;ng',NULL,NULL,NULL,'x&acirc;y d&#7921;ng, l&agrave;m gi&aacute;, s&#7913;c ti&ecirc;u th&#7909;, th&eacute;p, th&eacute;p ph&#7871; li&#7879;u, gi&#7843;m, t&#259;ng',0,0,0,'','','huyennt',3,'http://www.xaydungvietnam.vn',1,0,0,0),(106,'Doanh nghi&#7879;p xi m&#259;ng: Ph&aacute; s&#7843;n g&#7847;n k&#7873;','C&oacute; &iacute;t nh&#7845;t ba nh&agrave; m&aacute;y xi m&#259;ng &#273;&atilde; &#273;&#432;&#7907;c c&aacute;c ch&#7911; &#273;&#7847;u t&#432; rao b&aacute;n &#273;&#7875; l&#7845;y ti&#7873;n tr&#7843; n&#7907;. R&#7845;t nhi&#7873;u c&ocirc;ng ty xi m&#259;ng kh&aacute;c c&oacute; t&igrave;nh h&igrave;nh t&agrave;i ch&iacute;nh &#273;ang c&#259;ng nh&#432; d&acirc;y &#273;&agrave;n. Trong ng&agrave;nh, c&#7909;m t&#7915; &ldquo;ph&aacute; s&#7843;n&rdquo; &#273;ang &#273;&#432;&#7907;c nh&#7919;ng ng&#432;&#7901;i trong cu&#7897;c n&oacute;i &#273;&#7871;n, trong khi ch&#7881; m&#7899;i h&#417;n m&#7897;t n&#259;m tr&#432;&#7899;c nhi&#7873;u doanh nghi&#7879;p v&#7851;n mu&#7889;n &#273;&#7847;u t&#432; v&agrave;o xi m&#259;ng.','\r\n\r\n&lt;p&gt;hi x&amp;acirc;y d&#7921;ng k&#7871; ho&#7841;ch kinh doanh cho n&#259;m 2011 v&amp;agrave;o cu&#7889;i n&#259;m ngo&amp;aacute;i, T&#7893;ng c&amp;ocirc;ng ty C&amp;ocirc;ng nghi&#7879;p xi m&#259;ng Vi&#7879;t Nam (Vicem) &#273;&amp;atilde; l&#432;&#7901;ng tr&#432;&#7899;c nhi&#7873;u kh&amp;oacute; kh&#259;n &#273;ang ch&#7901; &#7903; ph&amp;iacute;a tr&#432;&#7899;c, n&amp;ecirc;n ch&#7881; &#273;&#7863;t ra m&#7909;c ti&amp;ecirc;u l&#7907;i nhu&#7853;n &#7903; m&#7913;c 1.251 t&#7881; &#273;&#7891;ng (gi&#7843;m g&#7847;n 350 t&#7881; &#273;&#7891;ng so v&#7899;i n&#259;m 2010 v&amp;agrave; kho&#7843;ng 883 t&#7881; &#273;&#7891;ng so v&#7899;i n&#259;m 2009). C&amp;aacute;c &#273;&#417;n v&#7883; th&amp;agrave;nh vi&amp;ecirc;n c&#7911;a t&#7893;ng c&amp;ocirc;ng ty c&#361;ng kh&amp;ocirc;ng d&amp;aacute;m k&#7923; v&#7885;ng nhi&#7873;u v&amp;agrave;o m&#7913;c t&#259;ng ti&amp;ecirc;u th&#7909;, n&amp;ecirc;n c&#361;ng ch&#7881; &#273;&#432;a ra m&#7913;c doanh thu t&#259;ng kho&#7843;ng 5-10%. K&#7871; ho&#7841;ch &#273;&amp;oacute; nay &#273;&amp;atilde; ph&amp;aacute; s&#7843;n. V&#7845;n &#273;&#7873; Vicem v&amp;agrave; c&amp;aacute;c th&amp;agrave;nh vi&amp;ecirc;n &#273;ang &#273;&#7889;i &#273;&#7847;u l&amp;agrave; l&amp;agrave;m sao &#273;&#7875; kh&amp;ocirc;ng b&#7883; thua l&#7895; v&amp;agrave; c&amp;oacute; ngu&#7891;n thu &#273;&#7911; &#273;&#7875; tr&#7843; n&#7907;. Nh&#432;ng xem ra vi&#7879;c gi&#7843;i b&amp;agrave;i to&amp;aacute;n n&amp;agrave;y c&#361;ng kh&amp;ocirc;ng d&#7877;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&#7871;n th&amp;aacute;ng 10-2011, k&#7871;t qu&#7843; kinh doanh c&#7911;a Vicem &#273;&amp;atilde; l&#7895; &#273;&#7871;n 220 t&#7881; &#273;&#7891;ng, &#273;i&#7873;u ch&#432;a t&#7915;ng x&#7843;y ra trong nhi&#7873;u n&#259;m qua. Nhi&#7873;u doanh nghi&#7879;p th&amp;agrave;nh vi&amp;ecirc;n c&#361;ng ph&#7843;i c&#7855;t gi&#7843;m k&#7871; ho&#7841;ch ti&amp;ecirc;u th&#7909; 10-20%. M&#7897;t doanh nghi&#7879;p c&amp;oacute; n&#7873;n t&#7843;ng th&#7883; tr&#432;&#7901;ng v&#7919;ng ch&#7855;c, v&#7899;i nhi&#7873;u th&#432;&#417;ng hi&#7879;u &#273;&amp;atilde; kh&#7859;ng &#273;&#7883;nh &#273;&#432;&#7907;c ch&#7895; &#273;&#7913;ng v&#7919;ng ch&#7855;c tr&amp;ecirc;n th&#7883; tr&#432;&#7901;ng xi m&#259;ng, nh&#432; Vicem m&amp;agrave; c&amp;ograve;n thua l&#7895; kh&amp;aacute; n&#7863;ng nh&#432; v&#7853;y, th&amp;igrave; nh&#7919;ng doanh nghi&#7879;p m&#7899;i tham gia th&#7883; tr&#432;&#7901;ng, ch&#432;a c&amp;oacute; t&amp;ecirc;n tu&#7893;i, c&amp;ograve;n bi &#273;&amp;aacute;t h&#417;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Sau b&#7889;n doanh nghi&#7879;p &#273;&#7847;u ti&amp;ecirc;n trong ng&amp;agrave;nh xi m&#259;ng m&#7845;t kh&#7843; n&#259;ng tr&#7843; n&#7907;, ph&#7843;i c&#7847;u c&#7913;u B&#7897; T&amp;agrave;i ch&amp;iacute;nh tr&#7843; thay nh&#7919;ng kho&#7843;n vay &#273;&#7847;u t&#432;, nay l&#7841;i c&amp;oacute; th&amp;ecirc;m m&#7897;t s&#7889; &#273;&#417;n v&#7883; kh&amp;aacute;c r&#417;i v&amp;agrave;o t&amp;igrave;nh tr&#7841;ng m&#7845;t thanh kho&#7843;n. Trong s&#7889; &#273;&amp;oacute;, ba &#273;&#417;n v&#7883; &#273;ang &#273;&#7873; ngh&#7883; b&amp;aacute;n l&#7841;i nh&amp;agrave; m&amp;aacute;y cho Vicem, nh&#432;ng b&#7843;n th&amp;acirc;n t&#7893;ng c&amp;ocirc;ng ty n&amp;agrave;y c&#361;ng &#273;ang ch&#7853;t v&#7853;t v&#7899;i c&amp;aacute;c kho&#7843;n n&#7907; &#273;&#7847;u t&#432; &#273;&amp;atilde; &#273;&#7871;n h&#7841;n, n&amp;ecirc;n c&amp;oacute; l&#7869; ch&#7881; c&amp;oacute; th&#7875; ti&#7871;p nh&#7853;n &#273;&#432;&#7907;c m&#7897;t trong s&#7889; &#273;&amp;oacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;C&amp;aacute;c doanh nghi&#7879;p ng&amp;agrave;nh xi m&#259;ng d&#7921; b&amp;aacute;o, s&#7913;c mua c&#7911;a th&#7883; tr&#432;&#7901;ng v&amp;agrave;o n&#259;m t&#7899;i, v&amp;agrave; c&amp;oacute; th&#7875; l&amp;agrave; n&#259;m t&#7899;i n&#7919;a, ch&#432;a c&amp;oacute; d&#7845;u hi&#7879;u g&amp;igrave; s&amp;aacute;ng s&#7911;a h&#417;n n&#259;m nay. Do th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n &#273;ang lao d&#7889;c, ngu&#7891;n v&#7889;n tr&amp;aacute;i phi&#7871;u ch&amp;iacute;nh ph&#7911; c&#7845;p cho c&amp;aacute;c d&#7921; &amp;aacute;n &#273;&#7847;u t&#432; gi&#7843;m m&#7841;nh, l&amp;atilde;i su&#7845;t ng&amp;acirc;n h&amp;agrave;ng cao ng&#7845;t ng&#432;&#7903;ng, n&amp;ecirc;n s&#7869; c&amp;oacute; th&amp;ecirc;m nhi&#7873;u doanh nghi&#7879;p xi m&#259;ng r&#417;i v&amp;agrave;o kh&amp;oacute; kh&#259;n, th&#7853;m ch&amp;iacute; ph&amp;aacute; s&#7843;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;S&#7913;c mua c&#7911;a th&#7883; tr&#432;&#7901;ng gi&#7843;m s&amp;uacute;t l&amp;agrave; &#273;&amp;ograve;n m&#7841;nh gi&amp;aacute;ng v&amp;agrave;o ng&amp;agrave;nh xi m&#259;ng &#273;ang tr&amp;ecirc;n &#273;&amp;agrave; m&#7903; r&#7897;ng n&#259;ng l&#7921;c s&#7843;n xu&#7845;t. Nh&#7919;ng doanh nghi&#7879;p l&#7841;c quan nh&#7845;t c&#361;ng ch&#7881; d&amp;aacute;m hy v&#7885;ng m&#7913;c ti&amp;ecirc;u th&#7909; xi m&#259;ng c&#7911;a to&amp;agrave;n th&#7883; tr&#432;&#7901;ng n&#259;m nay b&#7857;ng v&#7899;i n&#259;m ngo&amp;aacute;i, t&#7913;c kho&#7843;ng 50 tri&#7879;u t&#7845;n. Mu&#7889;n &#273;&#432;&#7907;c nh&#432; th&#7871;, trong hai th&amp;aacute;ng 11 v&amp;agrave; 12, m&#7895;i th&amp;aacute;ng ph&#7843;i b&amp;aacute;n &#273;&#432;&#7907;c &amp;iacute;t nh&#7845;t 5 tri&#7879;u t&#7845;n. Nh&#432;ng kh&#7843; n&#259;ng n&amp;agrave;y r&#7845;t kh&amp;oacute;, khi m&amp;agrave; s&#7913;c mua trong nh&#7919;ng th&amp;aacute;ng qua li&amp;ecirc;n t&#7909;c gi&#7843;m, t&#7915; kho&#7843;ng 4,8 tri&#7879;u t&#7845;n/th&amp;aacute;ng trong nh&#7919;ng th&amp;aacute;ng &#273;&#7847;u n&#259;m, &#273;&#7871;n nay ch&#7881; c&amp;ograve;n 3,5 - 3,6 tri&#7879;u t&#7845;n/th&amp;aacute;ng. Trong khi &#273;&amp;oacute;, n&#259;ng l&#7921;c s&#7843;n xu&#7845;t xi m&#259;ng th&amp;igrave; t&#259;ng li&amp;ecirc;n t&#7909;c. Hi&#7879;n c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; &#273;&amp;atilde; l&amp;ecirc;n &#273;&#7871;n 65 tri&#7879;u t&#7845;n v&amp;agrave; c&amp;ograve;n &#273;ang t&#259;ng ti&#7871;p, v&amp;igrave; nhi&#7873;u nh&amp;agrave; m&amp;aacute;y xi m&#259;ng s&#7855;p x&amp;acirc;y xong, &#273;ang chu&#7849;n b&#7883; &#273;&#432;a v&amp;agrave;o v&#7853;n h&amp;agrave;nh.&lt;/p&gt;\r\n\r\n&lt;p&gt;Chi ph&amp;iacute; &#273;&#7847;u v&amp;agrave;o t&#259;ng m&#7841;nh &#273;&amp;atilde; h&#7841; g&#7909;c nh&#7919;ng doanh nghi&#7879;p &#273;ang ng&#7855;c ngo&#7843;i v&amp;agrave; khi&#7871;n nhi&#7873;u doanh nghi&#7879;p m&#7841;nh l&amp;acirc;m b&#7879;nh. Chi ph&amp;iacute; x&#259;ng d&#7847;u t&#259;ng 32-43%, &#273;i&#7879;n t&#259;ng 15,28%, t&#7927; gi&amp;aacute; ngo&#7841;i t&#7879; 9%, v&#7887; bao t&#259;ng 25% v&amp;agrave; than, ngu&#7891;n n&#259;ng l&#432;&#7907;ng ch&amp;iacute;nh s&#7917; d&#7909;ng trong ng&amp;agrave;nh xi m&#259;ng, t&#259;ng g&#7847;n 90%. &amp;ldquo;Hi&#7879;n nay ch&#7859;ng doanh nghi&#7879;p n&amp;agrave;o d&amp;aacute;m ngh&#297; &#273;&#7871;n vi&#7879;c t&#259;ng gi&amp;aacute; b&amp;aacute;n. L&amp;agrave;m nh&#432; v&#7853;y s&#7869; l&#7853;p t&#7913;c m&#7845;t th&#7883; tr&#432;&#7901;ng v&amp;agrave; ch&#7871;t ngay. Ch&#7845;p nh&#7853;n b&amp;aacute;n d&#432;&#7899;i gi&amp;aacute; th&amp;agrave;nh, ch&#7883;u l&#7895;, d&amp;ugrave; sao c&#361;ng c&amp;ograve;n c&amp;oacute; th&#7875; ho&#7841;t &#273;&#7897;ng lay l&#7855;t, k&amp;eacute;o d&amp;agrave;i &#273;&#432;&#7907;c ng&amp;agrave;y n&amp;agrave;o hay ng&amp;agrave;y &#273;&amp;oacute;&amp;rdquo;, gi&amp;aacute;m &#273;&#7889;c m&#7897;t c&amp;ocirc;ng ty xi m&#259;ng chua ch&amp;aacute;t n&amp;oacute;i.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/Doanh-nghiep-xi-mang-Pha-san-gan-ke.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;em&gt;C&amp;aacute;c doanh nghi&#7879;p ng&amp;agrave;nh xi m&#259;ng d&#7921; b&amp;aacute;o, s&#7913;c mua c&#7911;a th&#7883; tr&#432;&#7901;ng v&amp;agrave;o n&#259;m t&#7899;i n&#7919;a, ch&#432;a c&amp;oacute; d&#7845;u hi&#7879;u g&amp;igrave; s&amp;aacute;ng s&#7911;a h&#417;n n&#259;m nay&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nC&amp;oacute; th&#7875; n&amp;oacute;i, ng&amp;agrave;nh xi m&#259;ng &#273;ang r&#417;i v&amp;agrave;o t&amp;igrave;nh th&#7871; ti&#7871;n tho&amp;aacute;i l&#432;&#7905;ng nan. Nh&#7845;t l&amp;agrave; nh&#7919;ng doanh nghi&#7879;p c&amp;ograve;n &#273;ang x&amp;acirc;y d&#7921;ng nh&amp;agrave; m&amp;aacute;y d&#7903; dang. T&#7845;t c&#7843; doanh nghi&#7879;p &#273;&#7847;u t&#432; nh&amp;agrave; m&amp;aacute;y xi m&#259;ng &#273;&#7873;u s&#7917; d&#7909;ng ngu&#7891;n v&#7889;n vay. Trong &#273;&amp;oacute; c&amp;oacute; nh&#7919;ng &#273;&#417;n v&#7883; vay &#273;&#7871;n 80% t&#7893;ng ngu&#7891;n v&#7889;n &#273;&#7847;u t&#432;. Bi k&#7883;ch nh&#7845;t l&amp;agrave; nh&#7919;ng c&amp;ocirc;ng ty s&#7917; d&#7909;ng ngu&#7891;n v&#7889;n vay trong n&#432;&#7899;c, v&#7899;i l&amp;atilde;i su&#7845;t 19-21,5%/n&#259;m. &#272;&amp;acirc;y l&amp;agrave; nh&#7919;ng doanh nghi&#7879;p s&#7869; ch&#7871;t tr&#432;&#7899;c. V&#7899;i nh&#7919;ng d&#7921; &amp;aacute;n ch&#432;a ho&amp;agrave;n th&amp;agrave;nh, doanh nghi&#7879;p c&amp;oacute; mu&#7889;n &#273;&#7847;u t&#432; ti&#7871;p c&#361;ng kh&amp;ocirc;ng &#273;&#432;&#7907;c, v&amp;igrave; kh&amp;ocirc;ng ng&amp;acirc;n h&amp;agrave;ng n&amp;agrave;o d&amp;aacute;m gi&#7843;i ng&amp;acirc;n trong b&#7889;i c&#7843;nh kinh t&#7871; kh&amp;oacute; kh&#259;n nh&#432; hi&#7879;n nay. Nh&#432;ng ng&#7915;ng l&#7841;i c&#361;ng kh&amp;ocirc;ng xong, v&amp;igrave; n&#7871;u kh&amp;ocirc;ng th&#7875; &#273;&#432;a nh&amp;agrave; m&amp;aacute;y v&amp;agrave;o ho&#7841;t &#273;&#7897;ng, s&#7869; kh&amp;ocirc;ng c&amp;oacute; d&amp;ograve;ng ti&#7873;n v&amp;agrave;o &#273;&#7875; tr&#7843; n&#7907; g&#7889;c v&amp;agrave; l&amp;atilde;i. Nhi&#7873;u d&#7921; &amp;aacute;n &#273;&amp;atilde; &#273;&#432;a v&amp;agrave;o v&#7853;n h&amp;agrave;nh m&#7897;t v&amp;agrave;i n&#259;m g&#7847;n &#273;&amp;acirc;y c&#361;ng ch&#7859;ng kh&amp;aacute; h&#417;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;H&amp;agrave;ng s&#7843;n xu&#7845;t v&amp;agrave; b&amp;aacute;n d&#432;&#7899;i gi&amp;aacute; th&amp;agrave;nh, n&amp;ecirc;n c&amp;agrave;ng l&amp;agrave;m c&amp;agrave;ng l&#7895; n&#7863;ng v&amp;agrave; n&#7907; n&#7847;n th&amp;ecirc;m ch&#7891;ng ch&#7845;t. Mu&#7889;n b&amp;aacute;n l&#7841;i nh&amp;agrave; m&amp;aacute;y &#273;&#7875; c&#7855;t l&#7895; c&#361;ng kh&amp;ocirc;ng &#273;&#432;&#7907;c, v&amp;igrave; ngay c&#7843; nh&#7919;ng doanh nghi&#7879;p c&amp;oacute; th&#7921;c l&#7921;c nh&#7845;t c&#361;ng ch&#432;a th&#7875; v&#7853;n h&amp;agrave;nh h&#7871;t c&amp;ocirc;ng su&#7845;t, th&amp;igrave; c&amp;ograve;n ai d&amp;aacute;m m&#7841;o hi&#7875;m &#273;&#7875; mua.&lt;/p&gt;\r\n\r\n&lt;p&gt;M&#7897;t b&#7847;u kh&amp;ocirc;ng kh&amp;iacute; u &amp;aacute;m &#273;ang bao tr&amp;ugrave;m l&amp;ecirc;n ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p xi m&#259;ng. Nh&#432;ng m&#7897;t s&#7889; ng&#432;&#7901;i trong ng&amp;agrave;nh cho r&#7857;ng, &#273;&amp;oacute; m&#7899;i ch&#7881; l&amp;agrave; kh&#7903;i &#273;&#7847;u c&#7911;a m&#7897;t c&#417;n b&amp;atilde;o kh&#7911;ng ho&#7843;ng &#273;ang &#7853;p &#273;&#7871;n. V&#7899;i t&amp;igrave;nh tr&#7841;ng m&#7845;t thanh kho&#7843;n, thi&#7871;u kh&#7843; n&#259;ng tr&#7843; n&#7907; &#273;ang lan ra nhanh ch&amp;oacute;ng, s&#7869; c&amp;oacute; kh&amp;ocirc;ng &amp;iacute;t doanh nghi&#7879;p ph&#7843;i t&#7915; gi&amp;atilde; ng&amp;agrave;nh xi m&#259;ng.&lt;/p&gt;','28/11/2011','28/11/2011','',23,2,1802,'huyennt',1,'doanh-nghiep-xi-mang-pha-san-gan-ke','xi m&#259;ng, VLXD, ph&aacute; s&#7843;n',NULL,NULL,NULL,'xi m&#259;ng, VLXD, ph&aacute; s&#7843;n',0,0,0,'','','',3,'http://www.thesaigontimes.vn',1,0,0,0),(107,'Th&ecirc;m d&#7921; &aacute;n th&eacute;p &#273;&#432;&#7907;c quy ho&#7841;ch','Th&#7911; t&#432;&#7899;ng Ch&iacute;nh ph&#7911; Nguy&#7877;n T&#7845;n D&#361;ng v&#7915;a ch&#7845;p thu&#7853;n ch&#7911; tr&#432;&#417;ng b&#7893; sung d&#7921; &aacute;n nh&agrave; m&aacute;y luy&#7879;n c&aacute;n th&eacute;p ch&#7845;t l&#432;&#7907;ng cao t&#7841;i t&#7881;nh Ninh B&igrave;nh v&agrave;o Quy ho&#7841;ch ph&aacute;t tri&#7875;n ng&agrave;nh th&eacute;p giai &#273;o&#7841;n 2007 &ndash; 2015, c&oacute; x&eacute;t &#273;&#7871;n n&#259;m 2025.','\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;450&quot; height=&quot;389&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/11-2011/thep-quy-hoach.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;Theo th&amp;ocirc;ng tin &#273;&#259;ng t&#7843;i tr&amp;ecirc;n C&#7893;ng th&amp;ocirc;ng tin Ch&amp;iacute;nh ph&#7911; &#273;i&#7879;n t&#7917; chi&#7873;u ng&amp;agrave;y 22-11, d&#7921; &amp;aacute;n nh&amp;agrave; m&amp;aacute;y luy&#7879;n c&amp;aacute;n th&amp;eacute;p ch&#7845;t l&#432;&#7907;ng cao t&#7841;i t&#7881;nh Ninh B&amp;igrave;nh c&amp;oacute; c&amp;ocirc;ng su&#7845;t 1 tri&#7879;u t&#7845;n ph&amp;ocirc;i th&amp;eacute;p vu&amp;ocirc;ng/n&#259;m v&amp;agrave; 500.000 t&#7845;n th&amp;eacute;p c&amp;aacute;n h&#7907;p kim d&#7921; &#7913;ng l&#7921;c/n&#259;m do C&amp;ocirc;ng ty tr&amp;aacute;ch nhi&#7879;m h&#7919;u h&#7841;n C&amp;aacute;n th&amp;eacute;p Tam&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&#272;i&#7879;m l&amp;agrave;m ch&#7911; &#273;&#7847;u t&#432;.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;\r\nTrao &#273;&#7893;i v&#7899;i Th&#7901;i b&amp;aacute;o Kinh t&#7871; S&amp;agrave;i G&amp;ograve;n Online ng&amp;agrave;y 23-11 v&#7873; d&#7921; &amp;aacute;n n&amp;oacute;i tr&amp;ecirc;n, &amp;ocirc;ng Nguy&#7877;n Ti&#7871;n Nghi, Ph&amp;oacute; ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i th&amp;eacute;p Vi&#7879;t Nam (VSA), cho bi&#7871;t hi&#7879;p h&#7897;i m&#7899;i &#273;&amp;acirc;y c&#361;ng c&amp;oacute; v&#259;n b&#7843;n g&#7903;i Ch&amp;iacute;nh ph&#7911; n&amp;ecirc;u &amp;yacute; ki&#7871;n c&#7911;a hi&#7879;p h&#7897;i l&amp;agrave; &#273;&#7891;ng t&amp;igrave;nh  tri&#7875;n khai d&#7921; &amp;aacute;n th&amp;eacute;p n&amp;agrave;y b&#7903;i hi&#7879;n ph&amp;ocirc;i th&amp;eacute;p s&#7843;n xu&#7845;t trong n&#432;&#7899;c v&#7851;n ch&#432;a &#273;&#7911; v&amp;agrave; th&amp;eacute;p c&amp;aacute;n h&#7907;p kim d&#7921; &#7913;ng l&#7921;c d&amp;ugrave;ng trong x&amp;acirc;y d&#7921;ng v&#7851;n c&amp;ograve;n thi&#7871;u so v&#7899;i nhu c&#7847;u.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo &amp;ocirc;ng Nghi, hi&#7879;n t&#7893;ng c&amp;ocirc;ng su&#7845;t thi&#7871;t k&#7871; ph&amp;ocirc;i th&amp;eacute;p c&#7843; n&#432;&#7899;c l&amp;agrave; 6 tri&#7879;u t&#7845;n, tuy nhi&amp;ecirc;n trong n&#259;m 2011 ch&#7881; c&amp;oacute; th&#7875; s&#7843;n xu&#7845;t kho&#7843;ng 4,3 tri&#7879;u t&#7845;n, c&amp;ograve;n l&#7841;i ph&#7843;i nh&#7853;p kh&#7849;u tr&amp;ecirc;n 1 tri&#7879;u t&#7845;n n&#7919;a. Trong khi &#273;&amp;oacute;, c&#7843; n&#432;&#7899;c c&#361;ng m&#7899;i ch&#7881; c&amp;oacute; m&#7897;t nh&amp;agrave; m&amp;aacute;y th&amp;eacute;p c&amp;aacute;n h&#7907;p kim d&#7921; &#7913;ng l&#7921;c c&#7911;a C&amp;ocirc;ng ty Th&amp;eacute;p Vi&#7879;t n&amp;ecirc;n c&#7847;n th&amp;ecirc;m nh&amp;agrave; m&amp;aacute;y s&#7843;n xu&#7845;t lo&#7841;i s&#7843;n ph&#7849;m th&amp;eacute;p ch&#7845;t l&#432;&#7907;ng cao n&amp;agrave;y.&lt;/p&gt;','30/11/2011','30/11/2011','/home/uploads/image/news/11-2011/thep-quy-hoach.jpg',23,2,1742,'huyennt',1,'them-du-an-thep-duoc-quy-hoach',' Th&ecirc;m d&#7921; &aacute;n th&eacute;p &#273;&#432;&#7907;c quy ho&#7841;ch',NULL,NULL,NULL,' Th&ecirc;m d&#7921; &aacute;n th&eacute;p &#273;&#432;&#7907;c quy ho&#7841;ch',0,0,0,'','','',3,'',1,0,0,0),(108,'Ng&agrave;nh xi m&#259;ng v&agrave; b&agrave;i to&aacute;n v&#432;&#7907;t kh&oacute; cho n&#259;m 2012','Gi&#7843;i ph&aacute;p n&agrave;o &#273;&#7875; ng&agrave;nh xi m&#259;ng v&#432;&#7907;t kh&oacute; trong b&#7889;i c&#7843;nh n&#259;m 2012 ti&#7871;p t&#7909;c th&#7855;t ch&#7863;t t&iacute;n d&#7909;ng, &#273;&#7847;u t&#432; c&ocirc;ng v&#7851;n h&#7841;n ch&#7871;. V&agrave; v&#7873; l&acirc;u d&agrave;i, ng&agrave;nh xi m&#259;ng kh&ocirc;ng ch&#7881; ph&#7843;i c&#7841;nh tranh &#7903; trong n&#432;&#7899;c m&agrave; c&ograve;n c&#7843; trong khu v&#7921;c khi mu&#7889;n &#273;&#7849;y m&#7841;nh xu&#7845;t kh&#7849;u?','\r\n&lt;p&gt;V&#7873; t&amp;igrave;nh h&amp;igrave;nh s&#7843;n xu&#7845;t kinh doanh 9 th&amp;aacute;ng n&#259;m 2011, T&#7893;ng C&amp;ocirc;ng ty xi m&#259;ng Vi&#7879;t Nam (VICEM) c&amp;ocirc;ng b&#7889; l&#7895; 219 t&#7881; &#273;&#7891;ng. Theo VICEM, nguy&amp;ecirc;n nh&amp;acirc;n l&#7895; l&amp;agrave; do c&amp;aacute;c chi ph&amp;iacute; &#273;&#7847;u v&amp;agrave;o t&#259;ng cao nh&#432; l&amp;atilde;i su&#7845;t vay v&#7889;n ng&amp;acirc;n h&amp;agrave;ng tr&amp;ecirc;n d&#432;&#7899;i 20%, gi&amp;aacute; than t&#259;ng tr&amp;ecirc;n 40%, x&#259;ng d&#7847;u t&#259;ng tr&amp;ecirc;n 32%...&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo th&#7841;c s&#297; L&#432;&#417;ng Xu&amp;acirc;n Tu&amp;acirc;n, Hi&#7879;p h&#7897;i V&#7853;t li&#7879;u x&amp;acirc;y d&#7921;ng (VLXD), nguy&amp;ecirc;n nh&amp;acirc;n n&#7919;a khi&#7871;n ng&amp;agrave;nh xi m&#259;ng b&#7883; l&#7895; l&amp;agrave; do ch&amp;iacute;nh s&amp;aacute;ch th&#7855;t ch&#7863;t t&amp;iacute;n d&#7909;ng l&amp;agrave;m th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n tr&#7847;m l&#7855;ng. B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute; l&amp;agrave; ch&#7911; tr&#432;&#417;ng c&#7855;t gi&#7843;m &#273;&#7847;u t&#432; c&amp;ocirc;ng &#273;&amp;atilde; h&#7841;n ch&#7871; nhu c&#7847;u ti&amp;ecirc;u th&#7909; xi m&#259;ng. Theo VICEM, t&#7891;n kho c&#7911;a ng&amp;agrave;nh xi m&#259;ng &#273;ang t&#259;ng l&amp;ecirc;n m&#7913;c lo ng&#7841;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;B&amp;ecirc;n c&#7841;nh kh&amp;oacute; kh&#259;n v&#7873; th&#7883; tr&#432;&#7901;ng ti&amp;ecirc;u th&#7909;, Hi&#7879;p h&#7897;i Xi m&#259;ng cho bi&#7871;t, nhi&#7873;u d&#7921; &amp;aacute;n xi m&#259;ng, ch&#7911; y&#7871;u l&amp;agrave; c&amp;ocirc;ng ngh&#7879; c&#7911;a Trung Qu&#7889;c &#273;i v&amp;agrave;o s&#7843;n xu&#7845;t giai &#273;o&#7841;n 2006 &amp;ndash; 2008 &#273;&amp;atilde; &#273;&#7871;n th&#7901;i k&#7923; tr&#7909;c tr&#7863;c. V&amp;iacute; d&#7909; xi m&#259;ng Lam Th&#7841;ch II, Y&amp;ecirc;n B&amp;igrave;nh, H&amp;ograve;a Ph&amp;aacute;t, &#272;&#7891;ng B&amp;agrave;nh, S&amp;ocirc;ng Thao&amp;hellip; Theo th&#7889;ng k&amp;ecirc;, do d&amp;acirc;y chuy&#7873;n thi&#7871;u &#273;&#7891;ng b&#7897;, thi&#7871;t b&#7883; k&amp;eacute;m ch&#7845;t l&#432;&#7907;ng&amp;hellip; n&amp;ecirc;n h&#7847;u h&#7871;t c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y m&#7899;i &#273;i v&amp;agrave;o s&#7843;n xu&#7845;t &#273;&#7873;u g&#7863;p tr&#7909;c tr&#7863;c trong qu&amp;aacute; tr&amp;igrave;nh s&#7843;n xu&#7845;t, ti&amp;ecirc;u hao nhi&amp;ecirc;n li&#7879;u l&#7899;n l&amp;agrave;m t&#259;ng chi ph&amp;iacute; s&#7843;n xu&#7845;t. Chi ph&amp;iacute; s&#7843;n xu&#7845;t t&#259;ng, th&#7883; tr&#432;&#7901;ng ch&#7853;m ti&amp;ecirc;u th&#7909; l&amp;agrave; nguy&amp;ecirc;n nh&amp;acirc;n khi&#7871;n nhi&#7873;u d&#7921; &amp;aacute;n xi m&#259;ng l&amp;acirc;m v&amp;agrave;o t&amp;igrave;nh tr&#7841;ng l&#7895; nh&#432; nh&amp;agrave; m&amp;aacute;y xi m&#259;ng Ho&amp;agrave;ng Mai, Tam &#272;i&#7879;p, Th&amp;aacute;i Nguy&amp;ecirc;n v&amp;agrave; &#272;&#7891;ng B&amp;agrave;nh&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;328&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/12-2011/nganh-xi-mang.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;span style=&quot;color: rgb(128, 0, 0); &quot;&gt;&lt;em&gt;M&#7897;t nh&amp;agrave; m&amp;aacute;y xi m&#259;ng s&#7917; d&#7909;ng c&amp;ocirc;ng ngh&#7879; l&amp;ograve; quay&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nTrong m&#7897;t b&amp;aacute;o c&amp;aacute;o c&#7911;a B&#7897; T&amp;agrave;i ch&amp;iacute;nh g&#7917;i Th&#7911; t&#432;&#7899;ng Ch&amp;iacute;nh ph&#7911; &#273;&amp;aacute;nh gi&amp;aacute; t&amp;igrave;nh h&amp;igrave;nh tr&#7843; n&#7907; c&#7911;a c&amp;aacute;c d&#7921; &amp;aacute;n xi m&#259;ng &#273;&#432;&#7907;c Ch&amp;iacute;nh ph&#7911; b&#7843;o l&amp;atilde;nh cho bi&#7871;t, c&amp;aacute;c d&#7921; &amp;aacute;n vay v&#7889;n n&#432;&#7899;c ngo&amp;agrave;i &#273;&#7875; &#273;&#7847;u t&#432; trong giai &#273;o&#7841;n 1998 &amp;ndash; 2007 &#273;&#7871;n nay &#273;&amp;atilde; gi&#7843;i ng&amp;acirc;n xong v&amp;agrave; &#273;&#7871;n h&#7841;n tr&#7843; n&#7907;. Theo nh&#7853;n &#273;&#7883;nh c&#7911;a B&#7897; T&amp;agrave;i ch&amp;iacute;nh, trong v&amp;ograve;ng 3 &amp;ndash; 5 n&#259;m t&#7899;i, s&#7869; c&amp;oacute; th&amp;ecirc;m nhi&#7873;u d&#7921; &amp;aacute;n xi m&#259;ng kh&amp;oacute; kh&#259;n trong vi&#7879;c tr&#7843; n&#7907; do k&#7871;t qu&#7843; kinh doanh kh&amp;ocirc;ng kh&#7843; quan d&#7851;n &#273;&#7871;n m&#7845;t c&amp;acirc;n &#273;&#7889;i v&#7873; ngu&#7891;n &#273;&#7875; tr&#7843; n&#7907; v&#7889;n vay.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&amp;aacute;nh gi&amp;aacute; t&#7891;n t&#7841;i c&#7911;a ng&amp;agrave;nh xi m&#259;ng Vi&#7879;t Nam, Th&#7913; tr&#432;&#7903;ng B&#7897; X&amp;acirc;y d&#7921;ng Nguy&#7877;n Tr&#7847;n Nam cho r&#7857;ng, t&#7891;n t&#7841;i c&#417; b&#7843;n nh&#7845;t c&#7911;a ng&amp;agrave;nh n&amp;agrave;y l&amp;agrave; chi ph&amp;iacute; s&#7843;n xu&#7845;t c&amp;ograve;n cao. C&#7909; th&#7875;, m&#7913;c ti&amp;ecirc;u hao &#273;i&#7879;n, than, d&#7847;u c&amp;ograve;n qu&amp;aacute; l&#7899;n khi&#7871;n gi&amp;aacute; th&amp;agrave;nh s&#7843;n ph&#7849;m b&#7883; &#273;&#7897;i l&amp;ecirc;n. B&amp;ecirc;n c&#7841;nh &#273;&amp;oacute;, n&#259;ng su&#7845;t lao &#273;&#7897;ng c&#7911;a ng&amp;agrave;nh c&#361;ng &#273;ang k&amp;eacute;m nh&#7845;t trong kh&#7889;i doanh nghi&#7879;p li&amp;ecirc;n doanh, Nh&amp;agrave; n&#432;&#7899;c v&amp;agrave; t&#432; nh&amp;acirc;n, th&#7853;m ch&amp;iacute; th&#7845;p h&#417;n th&#7871; gi&#7899;i &#273;&#7871;n 10 l&#7847;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo TS Tr&#7847;n V&#259;n Huynh, Ch&#7911; t&#7883;ch Hi&#7879;p h&#7897;i VLXD, c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y xi m&#259;ng ph&#7843;i t&amp;igrave;m c&amp;aacute;ch t&#7853;n d&#7909;ng nhi&#7879;t th&#7843;i, kh&amp;iacute; th&#7915;a &#273;&#7875; gi&#7843;m &#273;&#432;&#7907;c chi ph&amp;iacute; &#273;&#7847;u v&amp;agrave;o. Ti&#7871;t ki&#7879;m &#273;&#432;&#7907;c chi ph&amp;iacute; n&amp;agrave;y, ng&amp;agrave;nh xi m&#259;ng s&#7869; t&#259;ng s&#7913;c c&#7841;nh tranh &#273;&amp;aacute;ng k&#7875;. &#272;&#7875; vi&#7879;c ti&#7871;t ki&#7879;m &#273;&#432;&#7907;c &#273;&#7849;y nhanh, Ch&amp;iacute;nh ph&#7911; v&amp;agrave; B&#7897; ch&#7911; qu&#7843;n c&amp;oacute; th&#7875; ph&#7843;i ban h&amp;agrave;nh c&amp;aacute;c ch&#7871; t&amp;agrave;i m&#7841;nh, b&#7855;t bu&#7897;c c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y xi m&#259;ng c&amp;oacute; c&amp;ocirc;ng su&#7845;t 2.500 t&#7845;n clinker/ng&amp;agrave;y tr&#7903; l&amp;ecirc;n (c&#7843; c&#361; v&amp;agrave; chu&#7849;n b&#7883; &#273;&#7847;u t&#432;) ph&#7843;i l&#7855;p &#273;&#7863;t h&#7879; th&#7889;ng t&#7853;n d&#7909;ng nhi&#7879;t kh&amp;iacute; th&#7843;i l&amp;ograve; nung &#273;&#7875; ph&amp;aacute;t &#273;i&#7879;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;N&#7871;u &#273;&#7871;n n&#259;m 2015, nh&amp;agrave; m&amp;aacute;y n&amp;agrave;o kh&amp;ocirc;ng th&#7921;c hi&#7879;n th&amp;igrave; ki&amp;ecirc;n quy&#7871;t c&#7855;t gi&#7843;m 25% s&#7843;n l&#432;&#7907;ng &#273;i&#7879;n cung c&#7845;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;Th&#7913; hai, &lt;/em&gt;ph&#7843;i h&#7841;n ch&#7871; cho ph&amp;eacute;p &#273;&#7847;u t&#432; c&amp;aacute;c d&#7921; &amp;aacute;n s&#7843;n xu&#7845;t xi m&#259;ng c&amp;ocirc;ng su&#7845;t nh&#7887; (350.000 &amp;ndash; 600.000 t&#7845;n/n&#259;m) v&amp;igrave; c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y n&amp;agrave;y s&#7869; ti&amp;ecirc;u hao n&#259;ng l&#432;&#7907;ng nhi&#7873;u g&#7845;p 1,5 &amp;ndash; 1,6 l&#7847;n so v&#7899;i c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y hi&#7879;n &#273;&#7841;i. Ch&#7845;m d&#7913;t c&#7843; vi&#7879;c chuy&#7875;n &#273;&#7893;i c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y xi m&#259;ng l&amp;ograve; &#273;&#7913;ng sang l&amp;ograve; quay c&amp;ocirc;ng su&#7845;t nh&#7887; v&amp;igrave; theo quy ho&#7841;ch t&#7893;ng th&#7875; ph&amp;aacute;t tri&#7875;n ng&amp;agrave;nh, giai &#273;o&#7841;n 2011 &amp;ndash; 2015, ti&#7871;p t&#7909;c &#273;&#7847;u t&#432; 8 d&#7921; &amp;aacute;n c&amp;oacute; c&amp;ocirc;ng su&#7845;t 900.000 t&#7845;n/n&#259;m. Giai &#273;o&#7841;n 2016 &amp;ndash; 2020 l&amp;agrave; 7 d&#7921; &amp;aacute;n v&amp;agrave; &#273;&#7871;n giai &#273;o&#7841;n 2021 &amp;ndash; 2030 v&#7851;n c&amp;ograve;n 2 d&#7921; &amp;aacute;n c&amp;ocirc;ng su&#7845;t nh&#7887;. Nh&#7919;ng nh&amp;agrave; m&amp;aacute;y c&amp;oacute; c&amp;ocirc;ng su&#7845;t nh&#7887; &#273;&#7873;u l&amp;agrave; c&amp;aacute;c nh&amp;agrave; m&amp;aacute;y c&amp;oacute; ngh&#7879; th&#7845;p, kh&amp;ocirc;ng ph&amp;ugrave; h&#7907;p v&#7899;i m&#7909;c ti&amp;ecirc;u c&amp;ocirc;ng nghi&#7879;p h&amp;oacute;a, hi&#7879;n &#273;&#7841;i h&amp;oacute;a ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p xi m&#259;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;Th&#7913; ba&lt;/em&gt;, v&#7873; l&amp;acirc;u d&amp;agrave;i, quy ho&#7841;ch ph&amp;aacute;t tri&#7875;n ng&amp;agrave;nh xi m&#259;ng c&#7847;n ph&#7843;i kh&#7855;c ph&#7909;c hi&#7879;n t&#432;&#7907;ng n&#417;i c&#7847;n th&amp;igrave; kh&amp;ocirc;ng &#273;&#7847;u t&#432;, n&#417;i &#273;&amp;atilde; th&#7915;a v&#7851;n &#273;&#7847;u t&#432;. V&amp;iacute; d&#7909;, ng&amp;agrave;nh xi m&#259;ng &#273;ang t&#7853;p trung &#273;&#7847;u t&#432; qu&amp;aacute; nhi&#7873;u v&amp;agrave;o m&#7897;t s&#7889; v&amp;ugrave;ng &#273;i&#7873;u ki&#7879;n v&#7853;n t&#7843;i kh&amp;oacute; kh&#259;n nh&#432; H&amp;agrave; Nam, Ninh B&amp;igrave;nh. Hi&#7879;n c&amp;ocirc;ng su&#7845;t t&#7841;i khu v&#7921;c n&amp;agrave;y &#273;&amp;atilde; l&amp;ecirc;n t&#7899;i 20 tri&#7879;u t&#7845;n/n&#259;m. Trong khi m&#7897;t s&#7889; n&#417;i c&#7847;n &#273;&#7847;u t&#432; s&#7899;m l&#7841;i kh&amp;ocirc;ng &#273;&#432;&#7907;c tri&#7875;n khai nh&#432; d&#7921; &amp;aacute;n xi m&#259;ng &#7903; Th&#7841;nh M&#7929;, t&#7881;nh Qu&#7843;ng Nam. D&#7921; &amp;aacute;n n&amp;agrave;y c&amp;oacute; m&#7887; &#273;&amp;aacute; v&amp;ocirc;i l&#7899;n &#7903; A S&#7901; - Th&#7841;nh M&#7929;, tr&#7919; l&#432;&#7907;ng 350 tri&#7879;u t&#7845;n, r&#7845;t hi&#7871;m c&amp;oacute; &#7903; mi&#7873;n Trung m&amp;agrave; kh&amp;ocirc;ng &#273;&#432;&#7907;c &#273;&#7847;u t&#432; khai th&amp;aacute;c s&#7899;m &#273;&#7875; ph&#7909;c v&#7909; khu v&#7921;c mi&#7873;n Trung, T&amp;acirc;y Nguy&amp;ecirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Quy ho&#7841;ch t&#7893;ng th&#7875; ph&amp;aacute;t tri&#7875;n ng&amp;agrave;nh c&amp;ocirc;ng nghi&#7879;p xi m&#259;ng Vi&#7879;t Nam giai &#273;o&#7841;n 2011 &amp;ndash; 2020 v&amp;agrave; &#273;&#7883;nh h&#432;&#7899;ng &#273;&#7871;n 2030 &#273;&amp;atilde; &#273;&#432;&#7907;c Th&#7911; t&#432;&#7899;ng Ch&amp;iacute;nh ph&#7911; ph&amp;ecirc; duy&#7879;t (th&amp;aacute;ng 8/2011), B&#7897; X&amp;acirc;y d&#7921;ng c&#7847;n s&#7899;m qu&amp;aacute;n tri&#7879;t quy ho&#7841;ch n&amp;agrave;y &#273;&#7875; c&amp;aacute;c ch&#7911; &#273;&#7847;u t&#432;, ch&amp;iacute;nh quy&#7873;n c&amp;aacute;c &#273;&#7883;a ph&#432;&#417;ng n&#7855;m &#273;&#432;&#7907;c quy ho&#7841;ch v&amp;agrave; quy&#7871;t t&amp;acirc;m th&#7921;c hi&#7879;n. B&#7897; c&#7847;n ph&#7843;i k&#7883;p th&#7901;i &#273;i&#7873;u ch&#7881;nh ngay c&amp;aacute;c d&#7921; &amp;aacute;n kh&amp;ocirc;ng ph&amp;ugrave; h&#7907;p ho&#7863;c c&amp;aacute;c ch&#7911; &#273;&#7847;u t&#432; kh&amp;ocirc;ng c&amp;oacute; n&#259;ng l&#7921;c tri&#7875;n khai d&#7921; &amp;aacute;n.&lt;/p&gt;','06/12/2011','06/12/2011','/home/uploads/image/news/12-2011/nganh-xi-mang.jpg',23,2,1860,'huyennt',1,'nganh-xi-mang-va-bai-toan-vuot-kho-cho-nam-2012','xi m&#259;ng, VLXD, b&agrave;i to&aacute;n v&#432;&#7907;t kh&oacute;',NULL,NULL,NULL,'xi m&#259;ng, VLXD, b&agrave;i to&aacute;n v&#432;&#7907;t kh&oacute;',0,0,0,'','','',3,'http://dothi.net',1,1,0,0),(109,'Detech Land &#273;&#7847;u t&#432; khu &#273;&ocirc; th&#7883; quy m&ocirc; 22 ha t&#7841;i B&#7855;c Giang.','UBND t&#7881;nh B&#7855;c Giang v&#7915;a ra quy&#7871;t ph&ecirc; duy&#7879;t Quy ho&#7841;ch 1/500 d&#7921; &aacute;n Khu d&acirc;n c&#432; s&#7889; 3 th&#7883; tr&#7845;n Th&#7855;ng, huy&#7879;n Hi&#7879;p H&ograve;a, t&#7881;nh B&#7855;c Giang.','&lt;p&gt;&lt;br /&gt;\r\nTheo &#273;&amp;oacute;, khu v&#7921;c l&#7853;p quy ho&#7841;ch chi ti&#7871;t l&amp;agrave; khu d&amp;acirc;n c&#432; m&#7899;i, trung t&amp;acirc;m d&#7883;ch v&#7909; th&#432;&#417;ng m&#7841;i, v&#259;n h&amp;oacute;a th&#7875; thao, vui ch&#417;i gi&#7843;i tr&amp;iacute; c&#7845;p khu nh&amp;agrave; &#7903;.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;500&quot; height=&quot;318&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/news/12-2011/hiephoa12-11.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Quy m&amp;ocirc; c&#7911;a d&#7921; &amp;aacute;n: 22.87ha. Quy m&amp;ocirc; d&amp;acirc;n s&#7889; kho&#7843;ng 3.700 ng&#432;&#7901;i. V&#7883; tr&amp;iacute; n&#7857;m t&#7841;i ph&amp;iacute;a T&amp;acirc;y c&#7911;a th&#7883; tr&#7845;n Th&#7855;ng, tr&amp;ecirc;n &#273;&#7883;a b&amp;agrave;n x&amp;atilde; &#272;&#7913;c Th&#7855;ng, huy&#7879;n Hi&#7879;p H&amp;ograve;a, N&#7857;m t&#7841;i trung t&amp;acirc;m c&#7911;a th&#7883; tr&#7845;n Th&#7855;ng m&#7903; r&#7897;ng theo quy ho&#7841;ch chung c&#7911;a Huy&#7879;n &#273;&amp;atilde; &#273;&#432;&#7907;c ph&amp;ecirc; duy&#7879;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nD&#7921; &amp;aacute;n c&amp;oacute; giao th&amp;ocirc;ng thu&#7853;n ti&#7879;n gi&amp;aacute;p c&amp;aacute;c tr&#7909;c giao th&amp;ocirc;ng ch&amp;iacute;nh c&#7911;a Huy&#7879;n: QL37 B&#7855;c Giang &amp;ndash; Th&amp;aacute;i Nguy&amp;ecirc;n, t&#7881;nh l&#7897; 296, t&#7881;nh l&#7897; 295 n&#7889;i huy&#7879;n Hi&#7879;p H&amp;ograve;a &#273;i S&amp;oacute;c S&#417;n, H&amp;agrave; N&#7897;i v&amp;agrave; B&#7855;c Ninh. Ranh gi&#7899;i nghi&amp;ecirc;n c&#7913;u: ph&amp;iacute;a B&#7855;c gi&amp;aacute;p &#273;&#432;&#7901;ng t&#7881;nh l&#7897; 296; ph&amp;iacute;a Nam gi&amp;aacute;p ru&#7897;ng canh t&amp;aacute;c; ph&amp;iacute;a T&amp;acirc;y gi&amp;aacute;p ru&#7897;ng v&amp;agrave; khu &#273;&#7845;t d&#7919; tr&#7919; ph&amp;aacute;t tri&#7875;n &#273;&amp;ocirc; th&#7883;; ph&amp;iacute;a &#272;&amp;ocirc;ng gi&amp;aacute;p khu d&amp;acirc;n c&#432; v&amp;agrave; chi c&#7909;c Thu&#7871; huy&#7879;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&#7911; &#273;&#7847;u t&#432; l&#7853;p quy ho&#7841;ch l&amp;agrave; C&amp;ocirc;ng ty c&#7893; ph&#7847;n b&#7845;t &#273;&#7897;ng s&#7843;n Detech Land.&lt;/p&gt;','06/12/2011','06/12/2011','/home/uploads/image/news/12-2011/hiephoa12-11.jpg',29,8,1749,'huyennt',1,'detech-land-dau-tu-khu-do-thi-quy-mo-22-ha-tai-bac-giang-','d&#7921; &aacute;n, khu &#273;&ocirc; th&#7883;, Detech Land &#273;&#7847;u t&#432; khu &#273;&ocirc; th&#7883; quy m&ocirc; 22 ha',NULL,NULL,NULL,'d&#7921; &aacute;n, khu &#273;&ocirc; th&#7883;, Detech Land &#273;&#7847;u t&#432; khu &#273;&ocirc; th&#7883; quy m&ocirc; 22 ha',0,0,0,'','','',3,'http://dothi.net',1,0,0,0),(110,'&#272;&#7847;u t&#432; c&#259;n h&#7897; d&#7883;ch v&#7909;: c&#417; h&#7897;i &#273;&#7891;ng h&agrave;nh th&#7917; th&aacute;ch','&Ocirc;ng Marc Townsen tr&igrave;nh b&agrave;y t&#7841;i h&#7897;i th&#7843;oC&#7841;nh tranh kh&#7889;c li&#7879;t t&#7915; ph&acirc;n kh&uacute;c bi&#7879;t th&#7921; v&agrave; c&aacute;c c&#259;n h&#7897;-mua-&#273;&#7875;-cho-thu&ecirc;-l&#7841;i thu&#7897;c s&#7903; h&#7919;u c&aacute; nh&acirc;n s&#7869; v&#7851;n ti&#7871;p di&#7877;n tr&ecirc;n kh&#7855;p Vi&#7879;t Nam, &#273;&oacute; l&agrave; nh&#7853;n &#273;&#7883;nh &#273;&#432;&#7907;c &ocirc;ng Marc Townsend gi&aacute;m &#273;&#7889;c &#273;i&#7873;u h&agrave;nh CBRE Vi&#7879;t Nam &#273;&#432;a ra t&#7841;i h&#7897;i th&#7843;o &quot;&#272;&#7847;u t&#432; c&#259;n h&#7897; d&#7883;ch v&#7909; Vi&#7879;t Nam 2011: c&#417; h&#7897;i v&agrave; th&aacute;ch th&#7913;c&quot; di&#7877;n ra ng&agrave;y 1-12 t&#7841;i TP.HCM.','&lt;p&gt;Xu h&#432;&#7899;ng n&amp;agrave;y s&#7869; gi&amp;uacute;p b&amp;igrave;nh &#7893;n, th&#7853;m ch&amp;iacute; gi&#7843;m gi&amp;aacute; th&#7921;c thu&amp;ecirc; t&#7841;i nhi&#7873;u d&#7921; &amp;aacute;n nh&#7845;t l&amp;agrave; nh&#7919;ng d&#7921; &amp;aacute;n kh&amp;ocirc;ng c&amp;oacute; nhi&#7873;u d&#7883;ch v&#7909;.&lt;/p&gt;\r\n\r\n\r\n&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img alt=&quot;&quot; src=&quot;/home/uploads/image/news/12-2011/dau-tu-can-ho-dich-vu.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nh&#7857;m t&#7841;o ngu&#7891;n thu nh&#7853;p &#7893;n &#273;&#7883;nh v&#7873; l&amp;acirc;u d&amp;agrave;i, TP.HCM, &#272;&amp;agrave; N&#7861;ng, H&amp;agrave; N&#7897;i &#273;ang ch&#7913;ng ki&#7871;n c&amp;aacute;c d&#7921; &amp;aacute;n boutique ti&#7871;p t&#7909;c t&#259;ng &#7903; khu trung t&amp;acirc;m. M&#7897;t s&#7889; d&#7921; &amp;aacute;n c&amp;oacute; th&#7875; t&#7841;o s&#7921; k&#7871;t h&#7907;p tuy&#7879;t v&#7901;i gi&#7919;a v&#7883; tr&amp;iacute; &#273;&#7855;c &#273;&#7883;a v&amp;agrave; c&#259;n h&#7897; c&amp;oacute; thi&#7871;t k&#7871; &#273;&#7865;p, n&#7897;i th&#7845;t sang tr&#7885;ng, d&#7883;ch v&#7909; ch&#7845;t l&#432;&#7907;ng 5 sao.&lt;/p&gt;\r\n\r\n&lt;p&gt;&#272;&amp;oacute; c&#361;ng l&amp;agrave; l&amp;yacute; do c&amp;aacute;c d&#7921; &amp;aacute;n c&#259;n h&#7897; khu trung t&amp;acirc;m ho&#7863;c khu v&#7921;c l&amp;acirc;n c&#7853;n th&#432;&#7901;ng gi&#7919; l&#7841;i m&#7897;t s&#7889; c&#259;n h&#7897; l&amp;agrave;m c&#259;n h&#7897; d&#7883;ch v&#7909; v&amp;agrave; nhu c&#7847;u cho thu&amp;ecirc; c&amp;aacute;c c&#259;n h&#7897; d&#7883;ch v&#7909; v&#7899;i &#273;&#7847;y &#273;&#7911; c&amp;aacute;c d&#7883;ch v&#7909; v&amp;agrave; ti&#7879;n &amp;iacute;ch nh&#432; h&#7891; b&#417;i, ph&amp;ograve;ng gym v&amp;agrave; c&amp;aacute;c d&#7883;ch v&#7909; v&#259;n ph&amp;ograve;ng v&#7851;n t&#259;ng d&amp;ugrave; gi&amp;aacute; c&#7843; cao ng&#7845;t ng&#432;&#7903;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nh&amp;igrave;n nh&#7853;n v&#7845;n &#273;&#7873; n&amp;agrave;y, &amp;ocirc;ng Marc Townsend cho r&#7857;ng trong t&amp;igrave;nh h&amp;igrave;nh b&#7845;t &#273;&#7897;ng s&#7843;n suy tho&amp;aacute;i nh&#432; hi&#7879;n nay, th&#7883; tr&#432;&#7901;ng c&#259;n h&#7897; d&#7883;ch v&#7909; t&#7841;i Vi&#7879;t Nam c&#361;ng &#273;ang &#273;&#7889;i m&#7863;t v&#7899;i nh&#7919;ng th&amp;aacute;ch th&#7913;c l&#7899;n khi gi&amp;aacute; cho thu&amp;ecirc; s&#7909;t gi&#7843;m, kh&amp;aacute;ch h&amp;agrave;ng thu&amp;ecirc; c&#259;n h&#7897; d&#7883;ch v&#7909; thay &#273;&#7893;i do t&amp;igrave;nh h&amp;igrave;nh kinh t&#7871; kh&amp;oacute; kh&#259;n, c&#361;ng nh&#432; s&#7921; c&#7841;nh tranh gay g&#7855;t t&#7915; nh&#7919;ng c&amp;aacute; nh&amp;acirc;n mua c&#259;n h&#7897; &#273;&#7875; cho thu&amp;ecirc; l&#7841;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tuy nhi&amp;ecirc;n, v&#7899;i ngu&#7891;n v&#7889;n FDI d&#7921; ki&#7871;n ti&#7871;p t&#7909;c &#273;&#7893; v&amp;agrave;o Vi&#7879;t Nam khi n&#7873;n kinh t&#7871; h&#7891;i ph&#7909;c l&amp;agrave; nh&#7919;ng c&#417; h&#7897;i t&#7889;t &#273;&#7875; &#273;&#7847;u t&#432; v&amp;agrave;o nh&#7919;ng d&#7921; &amp;aacute;n c&#259;n h&#7897; d&#7883;ch v&#7909; khi th&#7883; tr&#432;&#7901;ng b&#7845;t &#273;&#7897;ng s&#7843;n ph&#7909;c h&#7891;i. Th&#7921;c t&#7871;, di&#7877;n bi&#7871;n th&#7883; tr&#432;&#7901;ng &#272;&amp;agrave; N&#7861;ng qu&amp;yacute; 3/2011 cho th&#7845;y gi&amp;aacute; thu&amp;ecirc; t&#259;ng nh&#7865; &#7903; c&#7843; hai ph&amp;acirc;n h&#7841;ng B v&amp;agrave; C. Nhi&#7873;u ng&#432;&#7901;i n&#432;&#7899;c ngo&amp;agrave;i tr&#432;&#7899;c &#273;&amp;acirc;y thu&amp;ecirc; nh&amp;agrave; ri&amp;ecirc;ng, nay c&#361;ng &#273;ang t&#7853;p trung v&#7873; &#7903; t&#7841;i c&amp;aacute;c t&amp;ograve;a nh&amp;agrave; c&#259;n h&#7897; d&#7883;ch v&#7909; &#273;&#7875; t&#7841;o n&amp;ecirc;n c&#7897;ng &#273;&#7891;ng ri&amp;ecirc;ng. Trong th&#7901;i gian t&#7899;i, l&#432;&#7907;ng kh&amp;aacute;ch n&#432;&#7899;c ngo&amp;agrave;i &#273;&#7871;n l&amp;agrave;m vi&#7879;c t&#7841;i Vi&#7879;t Nam s&#7869; nhi&#7873;u h&#417;n, nhu c&#7847;u v&#7873; c&#259;n h&#7897; d&#7883;ch v&#7909; s&#7869; c&amp;agrave;ng t&#259;ng, nh&#432;ng ph&amp;acirc;n kh&amp;uacute;c c&#361;ng nh&#432; c&amp;aacute;c ph&#432;&#417;ng &amp;aacute;n &#273;&#7875; ph&amp;aacute;t tri&#7875;n v&#7873; th&#7883; tr&#432;&#7901;ng ti&#7873;m n&#259;ng n&amp;agrave;y hi&#7879;n &#273;ang qu&amp;aacute; h&#7841;n ch&#7871; so v&#7899;i l&#432;&#7907;ng c&#7847;u.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo th&#7889;ng k&amp;ecirc; c&#7911;a CBRE, TP.HCM v&#7851;n &#273;ang d&#7851;n &#273;&#7847;u s&#7889; l&#432;&#7907;ng c&#259;n h&#7897; d&#7883;ch v&#7909; v&#7899;i 3.590 c&#259;n, ti&#7871;p &#273;&#7871;n l&amp;agrave; H&amp;agrave; N&#7897;i v&#7899;i 2.300 c&#259;n, c&amp;ograve;n th&amp;agrave;nh ph&#7889; &#273;&#7913;ng th&#7913; ba l&amp;agrave; &#272;&amp;agrave; N&#7861;ng hi&#7879;n ch&#7881; c&amp;oacute; 146 c&#259;n. V&amp;igrave; th&#7871;, &#273;&#7847;u t&#432; ph&amp;aacute;t tri&#7875;n c&#259;n h&#7897; d&#7883;ch v&#7909; nh&#432; th&#7871; n&amp;agrave;o, s&amp;aacute;t nh&#7853;p mua b&amp;aacute;n t&#7915;ng ph&#7847;n ho&#7863;c to&amp;agrave;n ph&#7847;n m&#7897;t d&#7921; &amp;aacute;n c&#259;n h&#7897; d&#7883;ch v&#7909;&amp;hellip; nhi&#7873;u v&#7845;n &#273;&#7873; c&#7909; th&#7875;, n&amp;oacute;ng s&#7889;t &#273;&amp;atilde; &#273;&#432;&#7907;c c&amp;aacute;c di&#7877;n gi&#7843;, c&amp;aacute;c chuy&amp;ecirc;n gia h&amp;agrave;ng &#273;&#7847;u trong l&#297;nh v&#7921;c &#273;&#7847;u t&#432;, t&#432; v&#7845;n, qu&#7843;n l&amp;yacute; v&amp;agrave; &#273;i&#7873;u h&amp;agrave;nh c&#259;n h&#7897; d&#7883;ch v&#7909; &#273;&#7871;n t&#7915; c&amp;aacute;c t&#7853;p &#273;o&amp;agrave;n qu&#7889;c t&#7871; CBRE Vietnam, Fraser Hospitality, Jones Lasalles Hotels, DFDL&amp;hellip; chia s&#7867; m&#7897;t c&amp;aacute;ch chuy&amp;ecirc;n nghi&#7879;p.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ti&#7873;m n&#259;ng c&#7911;a th&#7883; tr&#432;&#7901;ng &#273;&#7847;u t&#432; c&#259;n h&#7897; d&#7883;ch v&#7909; t&#7841;i Vi&#7879;t Nam v&#7851;n c&amp;ograve;n r&#7845;t l&#7899;n, nh&#432;ng &#273;&#7891;ng ngh&#297;a v&#7899;i c&#417; h&#7897;i v&#7851;n c&amp;ograve;n nhi&#7873;u nh&#7919;ng th&amp;aacute;ch th&#7913;c m&amp;agrave; mu&#7889;n v&#432;&#7907;t qua, c&amp;aacute;c doanh nghi&#7879;p ph&#7843;i c&#7847;n r&#7845;t nhi&#7873;u kinh nghi&#7879;m v&amp;agrave; nh&#7919;ng b&amp;agrave;i h&#7885;c th&#7921;c ti&#7877;n. &#272;&amp;oacute; l&amp;agrave; nh&#7919;ng b&amp;agrave;i h&#7885;c kh&amp;ocirc;ng m&#7899;i nh&#432;ng &#273;&amp;atilde;, &#273;ang v&amp;agrave; s&#7869; kh&amp;ocirc;ng bao gi&#7901; l&amp;agrave; c&#361;.&lt;/p&gt;','06/12/2011','06/12/2011','/home/uploads/image/news/12-2011/dau-tu-can-ho-dich-vu.jpg',36,8,1592,'huyennt',1,'dau-tu-can-ho-dich-vu-co-hoi-dong-hanh-thu-thach',' &#272;&#7847;u t&#432; c&#259;n h&#7897; d&#7883;ch v&#7909;: c&#417; h&#7897;i &#273;&#7891;ng h&agrave;nh th&#7917; th&aacute;ch, d&#7921; &aacute;n &#273;&#7847;u t&#432;',NULL,NULL,NULL,' &#272;&#7847;u t&#432; c&#259;n h&#7897; d&#7883;ch v&#7909;: c&#417; h&#7897;i &#273;&#7891;ng h&agrave;nh th&#7917; th&aacute;ch, d&#7921; &aacute;n &#273;&#7847;u t&#432;',0,0,0,'','','',3,'diaoconlien.vn',1,0,0,0),(111,'30 m&#7851;u ph&ograve;ng kh&aacute;ch &#273;&#7865;p &#273;&oacute;n xu&acirc;n','N&#259;m m&#7899;i s&#7855;p t